  INSERT INTO words (word, word_type, description) VALUES  (    'Kickable', 'a.', 'Capable or deserving of being kicked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kickapoos', 'n. pl.', 'A tribe of Indians which formerly occupied the\n   region of Northern Illinois, allied in language to the Sacs and Foxes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kicker', 'n.', 'One who, or that which, kicks.');
