  INSERT INTO words (word, word_type, description) VALUES  (    'Kill', 'n.', 'A channel or arm of the sea; a river; a stream; as, the\n   channel between Staten Island and Bergen Neck is the Kill van Kull, or\n   the Kills; -- used also in composition; as, Schuylkill, Catskill, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Killed', 'imp. & p. p.', 'of Kill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Killing', 'p. pr. & vb. n.', 'of Kill');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kill', 'v. t.', 'To deprive of life, animal or vegetable, in any manner or\n   by any means; to render inanimate; to put to death; to slay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kill', 'v. t.', 'To destroy; to ruin; as, to kill ones chances; to kill\n   the sale of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kill', 'v. t.', 'To cause to cease; to quell; to calm; to still; as, in\n   seamens language, a shower of rain kills the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kill', 'v. t.', 'To destroy the effect of; to counteract; to neutralize;\n   as, alkali kills acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Killdee', 'n.', 'Alt. of Killdeer');
