  INSERT INTO words (word, word_type, description) VALUES  (    'Killer', 'n.', 'A voracious, toothed whale of the genus Orca, of which\n   several species are known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Killesse', 'n.', 'A gutter, groove, or channel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Killesse', 'n.', 'A hipped roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Killifish', 'n.', 'Any one of several small American cyprinodont fishes of\n   the genus Fundulus and allied genera. They live equally well in fresh\n   and brackish water, or even in the sea. They are usually striped or\n   barred with black. Called also minnow, and brook fish. See Minnow.');
