  INSERT INTO words (word, word_type, description) VALUES  (    'Killikinick', 'n.', 'See Kinnikinic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Killing', 'a.', 'Literally, that kills; having power to kill; fatal; in a\n   colloquial sense, conquering; captivating; irresistible.');
