  INSERT INTO words (word, word_type, description) VALUES  (    'Kiln', 'n.', 'A large stove or oven; a furnace of brick or stone, or a\n   heated chamber, for the purpose of hardening, burning, or drying\n   anything; as, a kiln for baking or hardening earthen vessels; a kiln\n   for drying grain, meal, lumber, etc.; a kiln for calcining limestone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiln', 'n.', 'A furnace for burning bricks; a brickkiln.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiln-dry', 'v. t.', 'To dry in a kiln; as, to kiln-dry meal or grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kilnhole', 'n.', 'The mouth or opening of an oven or kiln.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kilos', 'pl. ', 'of Kilo');
