  INSERT INTO words (word, word_type, description) VALUES  (    'Kilogram', 'n.', 'Alt. of Kilogramme');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kilogramme', 'n.', 'A measure of weight, being a thousand grams, equal to\n   2.2046 pounds avoirdupois (15,432.34 grains). It is equal to the weight\n   of a cubic decimeter of distilled water at the temperature of maximum\n   density, or 39 Fahrenheit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kilogrammeter', 'n.', 'Alt. of Kilogrammetre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kilogrammetre', 'n.', 'A measure of energy or work done, being the amount\n   expended in raising one kilogram through the height of one meter, in\n   the latitude of Paris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiloliter', 'n.', 'Alt. of Kilolitre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kilolitre', 'n.', 'A measure of capacity equal to a cubic meter, or a\n   thousand liters. It is equivalent to 35.315 cubic feet, and to 220.04\n   imperial gallons, or 264.18 American gallons of 321 cubic inches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kilometer', 'n.', 'Alt. of Kilometre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kilometre', 'n.', 'A measure of length, being a thousand meters. It is\n   equal to 3,280.8 feet, or 62137 of a mile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kilostere', 'n.', 'A cubic measure containing 1000 cubic meters, and\n   equivalent to 35,315 cubic feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kilowatt', 'n.', 'One thousand watts.');
