  INSERT INTO words (word, word_type, description) VALUES  (    'Kindled', 'imp. & p. p.', 'of Kindle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindling', 'p. pr. & vb. n.', 'of Kindle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindle', 'v. t.', 'To set on fire; to cause to burn with flame; to ignite;\n   to cause to begin burning; to start; to light; as, to kindle a match,\n   or shavings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindle', 'v. t.', 'Fig.: To inflame, as the passions; to rouse; to provoke;\n   to excite to action; to heat; to fire; to animate; to incite; as, to\n   kindle anger or wrath; to kindle the flame of love, or love into a\n   flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindle', 'v. i.', 'To take fire; to begin to burn with flame; to start as a\n   flame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindle', 'v. i.', 'Fig.: To begin to be excited; to grow warm or animated;\n   to be roused or exasperated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindler', 'n.', 'One who, or that which, kindles, stirs up, or sets on\n   fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindless', 'a.', 'Destitute of kindness; unnatural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindliness', 'n.', 'Natural inclination; natural course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindliness', 'n.', 'The quality or state of being kindly; benignity;\n   benevolence; gentleness; tenderness; as, kindliness of disposition, of\n   treatment, or of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindliness', 'n.', 'Softness; mildness; propitiousness; as, kindliness of\n   weather, or of a season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinding', 'n.', 'The of causing to burn, or of exciting or inflaming the\n   passions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinding', 'n.', 'Materials, easily lighted, for starting a fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindly', 'n.', 'According to the kind or nature; natural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindly', 'n.', 'Humane; congenial; sympathetic; hence, disposed to do good\n   to; benevolent; gracious; kind; helpful; as, kindly affections, words,\n   acts, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindly', 'n.', 'Favorable; mild; gentle; auspicious; beneficent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindly', 'adv.', 'Naturally; fitly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindly', 'adv.', 'In a kind manner; congenially; with good will; with a\n   disposition to make others happy, or to oblige.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindness', 'a.', 'The state or quality of being kind, in any of its various\n   senses; manifestation of kind feeling or disposition beneficence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindness', 'a.', 'A kind act; an act of good will; as, to do a great\n   kindness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindred', 'n.', 'Relationship by birth or marriage; consanguinity;\n   affinity; kin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindred', 'n.', 'Relatives by blood or marriage, more properly the former;\n   relations; persons related to each other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kindred', 'a.', 'Related; congenial; of the like nature or properties; as,\n   kindred souls; kindred skies; kindred propositions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kine', 'n. pl.', 'Cows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinematic', 'a.', 'Alt. of Kinematical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinematical', 'a.', 'Of or pertaining to kinematics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinematics', 'n.', 'The science which treats of motions considered in\n   themselves, or apart from their causes; the comparison and relation of\n   motions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinepox', 'n.', 'See Cowpox.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinepox', 'n.', 'See Kinetoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinesiatrics', 'n.', 'A mode of treating disease by appropriate muscular\n   movements; -- also termed kinesitherapy, kinesipathy, lingism, and the\n   movement cure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinesipathy', 'n.', 'See Kinesiatrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinesitherapy', 'n.', 'See Kinesiatrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinesipathy', 'n.', 'See Kinesiatrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinesodic', 'a.', 'Conveying motion; as; kinesodic substance; -- applied\n   esp. to the spinal cord, because it is capable of conveying doth\n   voluntary and reflex motor impulses, without itself being affected by\n   motor impulses applied to it directly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinetic', 'q.', 'Moving or causing motion; motory; active, as opposed to\n   latent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinetics', 'n.', 'See Dynamics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinetogenesis', 'n.', 'An instrument for producing curves by the\n   combination of circular movements; -- called also kinescope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'King', 'n.', 'A Chinese musical instrument, consisting of resonant stones\n   or metal plates, arranged according to their tones in a frame of wood,\n   and struck with a hammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'King', 'n.', 'A chief ruler; a sovereign; one invested with supreme\n   authority over a nation, country, or tribe, usually by hereditary\n   succession; a monarch; a prince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'King', 'n.', 'One who, or that which, holds a supreme position or rank; a\n   chief among competitors; as, a railroad king; a money king; the king of\n   the lobby; the king of beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'King', 'n.', 'A playing card having the picture of a king; as, the king of\n   diamonds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'King', 'n.', 'The chief piece in the game of chess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'King', 'n.', 'A crowned man in the game of draughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'King', 'n.', 'The title of two historical books in the Old Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinged', 'imp. & p. p.', 'of King');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinging', 'p. pr. & vb. n.', 'of King');
  INSERT INTO words (word, word_type, description) VALUES  (    'King', 'v. i.', 'To supply with a king; to make a king of; to raise to\n   royalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingbird', 'n.', 'A small American bird (Tyrannus tyrannus, or T.\n   Carolinensis), noted for its courage in attacking larger birds, even\n   hawks and eagles, especially when they approach its nest in the\n   breeding season. It is a typical tyrant flycatcher, taking various\n   insects upon the wing. It is dark ash above, and blackish on the head\n   and tail. The quills and wing coverts are whitish at the edges. It is\n   white beneath, with a white terminal band on the tail. The feathers on\n   the head of the adults show a bright orange basal spot when erected.\n   Called also bee bird, and bee martin. Several Southern and Western\n   species of Tyrannus are also called king birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingbird', 'n.', 'The king tody. See under King.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingbolt', 'n.', 'A vertical iron bolt, by which the forward axle and\n   wheels of a vehicle or the trucks of a railroad car are connected with\n   the other parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'King Charles spaniel', '', 'A variety of small pet dogs, having, drooping\n   ears, a high, dome-shaped forehead, pug nose, large, prominent eyes,\n   and long, wavy hair. The color is usually black and tan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingcraft', 'n.', 'The craft of kings; the art of governing as a sovereign;\n   royal policy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingcup', 'n.', 'The common buttercup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingdom', 'n.', 'The rank, quality, state, or attributes of a king; royal\n   authority; sovereign power; rule; dominion; monarchy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingdom', 'n.', 'The territory or country subject to a king or queen; the\n   dominion of a monarch; the sphere in which one is king or has control.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingdom', 'n.', 'An extensive scientific division distinguished by leading\n   or ruling characteristics; a principal division; a department; as, the\n   mineral kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingdomed', 'a.', 'Having a kingdom or the dignity of a king; like a\n   kingdom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingfish', 'n.', 'An American marine food fish of the genus Menticirrus,\n   especially M. saxatilis, or M. nebulosos, of the Atlantic coast; --\n   called also whiting, surf whiting, and barb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingfish', 'n.', 'The opah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingfish', 'n.', 'The common cero; also, the spotted cero. See Cero.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingfish', 'n.', 'The queenfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingfisher', 'n.', 'Any one of numerous species of birds constituting the\n   family Alcedinidae. Most of them feed upon fishes which they capture by\n   diving and seizing then with the beak; others feed only upon reptiles,\n   insects, etc. About one hundred and fifty species are known. They are\n   found in nearly all parts of the world, but are particularly abundant\n   in the East Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinghood', 'n.', 'The state of being a king; the attributes of a king;\n   kingship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingless', 'a.', 'Having no king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinglet', 'n.', 'A little king; a weak or insignificant king.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinglet', 'n.', 'Any one of several species of small singing birds of the\n   genus Regulus and family Sylviidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinglihood', 'n.', 'King-liness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingliness', 'n.', 'The state or quality of being kingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingling', 'n.', 'Same as Kinglet, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingly', 'superl.', 'Belonging to, suitable to, or becoming, a king;\n   characteristic of, resembling, a king; directed or administered by a\n   king; monarchical; royal; sovereign; regal; august; noble; grand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingly', 'adv.', 'In a kingly or kinglike manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'King-post', 'n.', 'A member of a common form of truss, as a roof truss. It\n   is strictly a tie, intended to prevent the sagging of the tiebeam in\n   the middle. If there are struts, supporting the main rafters, they\n   often bear upon the foot of the king-post. Called also crown-post.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kings Bench', '', 'Formerly, the highest court of common law in England;\n   -- so called because the king used to sit there in person. It consisted\n   of a chief justice and four puisne, or junior, justices. During the\n   reign of a queen it was called the Queens Bench. Its jurisdiction was\n   transferred by the judicature acts of 1873 and 1875 to the high court\n   of justice created by that legislation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingship', 'n.', 'The state, office, or dignity of a king; royalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingston', 'n.', 'Alt. of Kingstone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingstone', 'n.', 'The black angel fish. See Angel fish, under Angel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingston metal', '', 'An alloy of tin, copper, and mercury, sometimes used\n   for the bearings and packings of machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingston valve', '', 'A conical valve, opening outward, to close the mouth\n   of a pipe which passes through the side of a vessel below the water\n   line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kingtruss', '', 'A truss, framed with a king-post; -- used in roofs,\n   bridges, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinic', 'a.', 'See Quinic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kink', 'n.', 'A twist or loop in a rope or thread, caused by a spontaneous\n   doubling or winding upon itself; a close loop or curl; a doubling in a\n   cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kink', 'n.', 'An unreasonable notion; a crotchet; a whim; a caprice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinked', 'imp. & p. p.', 'of Kink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinking', 'p. pr. & vb. n.', 'of Kink');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kink', 'v. i.', 'To wind into a kink; to knot or twist spontaneously upon\n   itself, as a rope or thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kink', 'n.', 'A fit of coughing; also, a convulsive fit of laughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinkajou', 'n.', 'A nocturnal carnivorous mammal (Cercoleptes\n   caudivolvulus) of South America, about as large as a full-grown cat. It\n   has a prehensile tail and lives in trees. It is the only representative\n   of a distinct family (Cercoleptidae) allied to the raccoons. Called\n   also potto, and honey bear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinkhaust', 'n.', 'Whooping cough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinkle', 'n.', 'Same as 3d Kink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinky', 'a.', 'Full of kinks; liable to kink or curl; as, kinky hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinky', 'a.', 'Queer; eccentric; crotchety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinnikinic', 'n.', 'Prepared leaves or bark of certain plants; -- used by\n   the Indians of the Northwest for smoking, either mixed with tobacco or\n   as a substitute for it. Also, a plant so used, as the osier cornel\n   (Cornus stolonijra), and the bearberry (Arctostaphylus Uva-ursi).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kino', 'n.', 'The dark red dried juice of certain plants, used variously in\n   tanning, in dyeing, and as an astringent in medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinology', 'n.', 'That branch of physics which treats of the laws of\n   motion, or of moving bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinone', 'n.', 'See Quinone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinoyl', 'n.', 'See Quinoyl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinrede', 'n.', 'Kindred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinsfolk', 'n.', 'Relatives; kindred; kin; persons of the same family or\n   closely or closely related families.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinship', 'n.', 'Family relationship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinsmen', 'pl. ', 'of Kinsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinsman', 'n.', 'A man of the same race or family; one related by blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinsmanship', 'n.', 'Kinship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinswomen', 'pl. ', 'of Kinswoman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kinswoman', 'n.', 'A female relative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kintlidge', 'n.', 'See Kentledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiosk', 'n.', 'A Turkish open summer house or pavilion, supported by\n   pillars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kioways', 'n. pl.', 'A tribe of Indians distantly related to the Shoshones.\n   They formerly inhabited the region about the head waters of the North\n   Platte.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kip', 'n.', 'The hide of a young or small beef creature, or leather made\n   from it; kipskin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kipe', 'n.', 'An osier basket used for catching fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kipper', 'n.', 'A salmon after spawning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kipper', 'n.', 'A salmon split open, salted, and dried or smoked; -- so\n   called because salmon after spawning were usually so cured, not being\n   good when fresh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kippered', 'imp. & p. p.', 'of Kipper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kippering', 'p. pr. & vb. n.', 'of Kipper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kipper', 'v. t.', 'To cure, by splitting, salting, and smoking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kipper', 'a.', 'Amorous; also, lively; light-footed; nimble; gay;\n   sprightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kippernut', 'n.', 'A name given to earthnuts of several kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kipskin', 'n.', 'Leather prepared from the skin of young or small cattle,\n   intermediate in grade between calfskin and cowhide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kirk', 'n.', 'A church or the church, in the various senses of the word;\n   esp., the Church of Scotland as distinguished from other reformed\n   churches, or from the Roman Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kirked', 'a.', 'Turned upward; bent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kirkmen', 'pl. ', 'of Kirkman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kirkman', 'n.', 'A clergyman or officer in a kirk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kirkman', 'n.', 'A member of the Church of Scotland, as distinguished from\n   a member of another communion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kirkyard', 'n.', 'A churchyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kirmess', 'n.', 'In Europe, particularly in Belgium and Holland, and\n   outdoor festival and fair; in the United States, generally an indoor\n   entertainment and fair combined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kirschwasser', 'n.', 'An alcoholic liquor, obtained by distilling the\n   fermented juice of the small black cherry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kirsome', 'a.', 'Christian; christened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kirtle', 'n.', 'A garment varying in form and use at different times, and\n   worn doth by men and women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kirtled', 'a.', 'Wearing a kirtle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kirumbo', 'n.', 'A bird of Madagascar (Leptosomus discolor), the only\n   living type of a family allied to the rollers. It has a pair of loral\n   plumes. The male is glossy green above, with metallic reflections; the\n   female is spotted with brown and black.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kish', 'n.', 'A workmans name for the graphite which forms incidentally in\n   iron smelting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kismet', 'n.', 'Destiny; fate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kissed', 'imp. & p. p.', 'of Kiss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kissing', 'p. pr. & vb. n.', 'of Kiss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiss', 'v. t.', 'To salute with the lips, as a mark of affection,\n   reverence, submission, forgiveness, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiss', 'v. t.', 'To touch gently, as if fondly or caressingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiss', 'v. i.', 'To make or give salutation with the lips in token of love,\n   respect, etc.; as, kiss and make friends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiss', 'v. i.', 'To meet; to come in contact; to touch fondly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiss', 'v.', 'A salutation with the lips, as a token of affection, respect,\n   etc.; as, a parting kiss; a kiss of reconciliation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiss', 'v.', 'A small piece of confectionery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kisser', 'n.', 'One who kisses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kissingcrust', 'n.', 'The portion of the upper crust of a loaf which has\n   touched another loaf in baking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kist', 'n.', 'A chest; hence, a coffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kist', 'n.', 'A stated payment, especially a payment of rent for land;\n   hence, the time for such payment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kistvaen', 'n.', 'A Celtic monument, commonly known as a dolmen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitte', 'imp.', 'of Kit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kit', 'v. t.', 'To cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kit', 'n.', 'A kitten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kit', 'n.', 'A small violin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kit', 'm.', 'A large bottle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kit', 'm.', 'A wooden tub or pail, smaller at the top than at the bottom;\n   as, a kit of butter, or of mackerel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kit', 'm.', 'straw or rush basket for fish; also, any kind of basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kit', 'm.', 'A box for working implements; hence, a working outfit, as of a\n   workman, a soldier, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kit', 'm.', 'A group of separate parts, things, or individuals; -- used\n   with whole, and generally contemptuously; as, the whole kit of them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitcat', 'a.', 'Designating a club in London, to which Addison and Steele\n   belonged; -- so called from Christopher Cat, a pastry cook, who served\n   the club with mutton pies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitcat', 'a.', 'Designating a canvas used for portraits of a peculiar size,\n   viz., twenty-right or twenty-nine inches by thirty-six; -- so called\n   because that size was adopted by Sir Godfrey Kneller for the portraits\n   he painted of the members of the Kitcat Club.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitcat', 'n.', 'A game played by striking with a stick small piece of wood,\n   called a cat, shaped like two cones united at their bases; tipcat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitchen', 'n.', 'A cookroom; the room of a house appropriated to cookery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitchen', 'n.', 'A utensil for roasting meat; as, a tin kitchen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitchen', 'v. t.', 'To furnish food to; to entertain with the fare of the\n   kitchen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitchener', 'n.', 'A kitchen servant; a cook.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitchenmaid', 'n.', 'A woman employed in the kitchen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitchen middens', '', 'Relics of neolithic man found on the coast of\n   Denmark, consisting of shell mounds, some of which are ten feet high,\n   one thousand feet long, and two hundred feet wide. The name is applied\n   also to similar mounds found on the American coast from Canada to\n   Florida, made by the North American Indians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitchen-ry', 'n.', 'The body of servants employed in the kitchen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kite', 'n.', 'Any raptorial bird of the subfamily Milvinae, of which many\n   species are known. They have long wings, adapted for soaring, and\n   usually a forked tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kite', 'n.', 'Fig. : One who is rapacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kite', 'n.', 'A light frame of wood or other material covered with paper or\n   cloth, for flying in the air at the end of a string.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kite', 'n.', 'A lofty sail, carried only when the wind is light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kite', 'n.', 'A quadrilateral, one of whose diagonals is an axis of\n   symmetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kite', 'n.', 'Fictitious commercial paper used for raising money or to\n   sustain credit, as a check which represents no deposit in bank, or a\n   bill of exchange not sanctioned by sale of goods; an accommodation\n   check or bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kite', 'n.', 'The brill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kite', 'v. i.', 'To raise money by \"kites;\" as, kiting transactions. See\n   Kite, 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kite', 'n.', 'The belly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiteflying', 'n.', 'A mode of raising money, or sustaining ones credit, by\n   the use of paper which is merely nominal; -- called also kiting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiteflier', 'n.', 'See Kite, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kith', 'n.', 'Acquaintance; kindred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kithara', 'n.', 'See Cithara.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kithe', 'v. t.', 'See Kythe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitish', 'a.', 'Like or relating to a kite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitling', 'n.', 'A young kitten; a whelp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitte', 'imp.', 'of Kit to cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kittel', 'v. t.', 'See Kittle, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitten', 'n.', 'A young cat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kittened', 'imp. & p. p.', 'of Kitten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kittening', 'p. pr. & vb. n.', 'of Kitten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kitten', 'v. t. & i.', 'To bring forth young, as a cat; to bring forth, as\n   kittens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kittenish', 'a.', 'Resembling a kitten; playful; as, a kittenish\n   disposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kittiwake', 'n.', 'A northern gull (Rissa tridactyla), inhabiting the\n   coasts of Europe and America. It is white, with black tips to the\n   wings, and has but three toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kittle', 'v. i.', 'To bring forth young, as a cat; to kitten; to litter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kittle', 'v. t.', 'To tickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kittle', 'a.', 'Ticklish; not easily managed; troublesome; difficult;\n   variable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kittlish', 'a.', 'Ticklish; kittle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kittysol', 'n.', 'The Chinese paper parasol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kive', 'n.', 'A mash vat. See Keeve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiver', 'v. t.', 'To cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiver', 'n.', 'A cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kivikivies', 'pl. ', 'of Kiwikiwi');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiwikiwies', 'pl. ', 'of Kiwikiwi');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kivikivi', 'n.', 'Alt. of Kiwikiwi');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kiwikiwi', 'n.', 'Any species of Apteryx, esp. A. australis; -- so called\n   in imitation of its notes. Called also kiwi. See Apteryx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kjoekken moeddings', '', 'See Kitchen middens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Klamaths', 'n. pl.', 'A collective name for the Indians of several tribes\n   formerly living along the Klamath river, in California and Oregon, but\n   now restricted to a reservation at Klamath Lake; -- called also Clamets\n   and Hamati.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kleeneboc', 'n.', '(Zool.) An antelope (Cerphalopus pygmaeus), found in\n   South Africa. It is of very small size, being but one foot high at\n   shoulder. It is remarkable for its activity, and for its mild and timid\n   disposition. Called also guevi, and pygmy antelope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kleptomania', 'n.', 'A propensity to steal, claimed to be irresistible.\n   This does not constitute legal irresponsibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kleptomaniac', 'n.', 'A person affected with kleptomania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Klick', 'n. & v.', 'See Click.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Klicket', 'n.', 'A small postern or gate in a palisade, for the passage of\n   sallying parties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Klinkstone', 'n.', 'See Clinkstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Klinometer', 'n.', 'See Clinometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Klipdas', 'n.', 'Alt. of Klipdachs');
  INSERT INTO words (word, word_type, description) VALUES  (    'Klipdachs', 'n.', 'A small mammal (Hyrax Capensis), found in South Africa.\n   It is of about the size of a rabbit, and closely resembles the daman.\n   Called also rock rabbit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Klipfish', 'n.', 'Dried cod, exported from Norway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Klipspringer', 'n.', 'A small, graceful South African antelope (Nanotragus\n   oreotragus), which, like the chamois, springs from one crag to another\n   with great agility; -- called also kainsi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kloof', 'n.', 'A glen; a ravine closed at its upper end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Klopemania', 'n.', 'See Kleptomania.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knabbed', 'imp. & p. p.', 'of Knab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knabbing', 'p. pr. & vb. n.', 'of Knab');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knab', 'v. t.', 'To seize with the teeth; to gnaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knab', 'v. t.', 'To nab. See Nab, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knabble', 'v. i.', 'To bite or nibble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knack', 'v. i.', 'To crack; to make a sharp, abrupt noise to chink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knack', 'v. i.', 'To speak affectedly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knack', 'n.', 'A petty contrivance; a toy; a plaything; a knickknack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knack', 'n.', 'A readiness in performance; aptness at doing something;\n   skill; facility; dexterity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knack', 'n.', 'Something performed, or to be done, requiring aptness and\n   dexterity; a trick; a device.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knacker', 'n.', 'One who makes knickknacks, toys, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knacker', 'n.', 'One of two or more pieces of bone or wood held loosely\n   between the fingers, and struck together by moving the hand; -- called\n   also clapper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knacker', 'n.', 'a harness maker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knacker', 'n.', 'One who slaughters worn-out horses and sells their flesh\n   for dogs meat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knackish', 'a.', 'Trickish; artful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knack-kneed', 'a.', 'See Knock-kneed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knacky', 'a.', 'Having a knack; cunning; crafty; trickish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knag', 'n.', 'A knot in wood; a protuberance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knag', 'n.', 'A wooden peg for hanging things on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knag', 'n.', 'The prong of an antler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knag', 'n.', 'The rugged top of a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knagged', 'a.', 'Full of knots; knaggy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knaggy', 'a.', 'Knotty; rough; figuratively, rough in temper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knap', 'n.', 'A protuberance; a swelling; a knob; a button; hence, rising\n   ground; a summit. See Knob, and Knop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knapped', 'imp. & p. p.', 'of Knap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knapping', 'p. pr. & vb. n.', 'of Knap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knap', 'v. t.', 'To bite; to bite off; to break short.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knap', 'v. t.', 'To strike smartly; to rap; to snap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knap', 'v. i.', 'To make a sound of snapping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knap', 'n.', 'A sharp blow or slap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knapbottle', 'n.', 'The bladder campion (Silene inflata).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knappish', 'a.', 'Snappish; peevish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knapple', 'v.', 'To break off with an abrupt, sharp noise; to bite; to\n   nibble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knappy', 'a.', 'Having knaps; full of protuberances or humps; knobby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knapsack', 'v. t.', 'A case of canvas or leather, for carrying on the back\n   a soldiers necessaries, or the clothing, etc., of a traveler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knapweed', 'n.', 'The black centaury (Centaurea nigra); -- so called from\n   the knoblike heads of flowers. Called also bullweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knar', 'n.', 'See Gnar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knarl', 'n.', 'A knot in wood. See Gnarl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knarled', 'a.', 'Knotted. See Gnarled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knarred', 'a.', 'Knotty; gnarled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knarry', 'a.', 'Knotty; gnarled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knave', 'n.', 'A boy; especially, a boy servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knave', 'n.', 'Any male servant; a menial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knave', 'n.', 'A tricky, deceitful fellow; a dishonest person; a rogue; a\n   villain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knave', 'n.', 'A playing card marked with the figure of a servant or\n   soldier; a jack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knaveries', 'pl. ', 'of Knavery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knavery', 'n.', 'The practices of a knave; petty villainy; fraud; trickery;\n   a knavish action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knavery', 'n.', 'Roguish or mischievous tricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knaveship', 'n.', 'A small due, in meal, established by usage, which is\n   paid to the under miller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knavess', 'n.', 'A knavish woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knavish', 'a.', 'Like or characteristic of a knave; given to knavery;\n   trickish; fraudulent; dishonest; villainous; as, a knavish fellow, or a\n   knavish trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knavish', 'a.', 'Mischievous; roguish; waggish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knavishly', 'adv.', 'In a knavish manner; dishonestly; fraudulently.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knavishly', 'adv.', 'Mischievously; waggishly; roguishly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'KNavishness', 'n.', 'The quality or state of being knavish; knavery;\n   dishonesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knaw', 'v. t.', 'See Gnaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knawel', 'n.', 'A low, spreading weed (Scleranthus annuus), common in sandy\n   soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneaded', 'imp. & p. p.', 'of Knead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneading', 'p. pr. & vb. n.', 'of Knead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knead', 'v. t.', 'To work and press into a mass, usually with the hands;\n   esp., to work, as by repeated pressure with the knuckles, into a well\n   mixed mass, as the materials of bread, cake, etc.; as, to knead dough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knead', 'v. t.', 'Fig.: To treat or form as by kneading; to beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneadable', 'a.', 'That may be kneaded; capable of being worked into a\n   mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneader', 'n.', 'One who kneads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneadingly', 'adv.', 'In the manner of one kneading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knebelite', 'n.', 'A mineral of a gray, red, brown, or green color, and\n   glistening luster. It is a silicate of iron and manganese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneck', 'n.', 'The twisting of a rope or cable, as it is running out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knee', 'n.', 'In man, the joint in the middle part of the leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knee', 'n.', 'The joint, or region of the joint, between the thigh and leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knee', 'n.', 'In the horse and allied animals, the carpal joint,\n   corresponding to the wrist in man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knee', 'n.', 'A piece of timber or metal formed with an angle somewhat in\n   the shape of the human knee when bent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knee', 'n.', 'A bending of the knee, as in respect or courtesy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knee', 'v. t.', 'To supplicate by kneeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneebrush', 'n.', 'A tuft or brush of hair on the knees of some species of\n   antelopes and other animals; -- chiefly used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneebrush', 'n.', 'A thick mass or collection of hairs on the legs of bees,\n   by aid of which they carry the collected pollen to the hive or nest; --\n   usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneecap', 'n.', 'The kneepan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneecap', 'n.', 'A cap or protection for the knee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knee-crooking', 'a.', 'Obsequious; fawning; cringing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneed', 'a.', 'Having knees;- used chiefly in composition; as, in-kneed;\n   out-kneed; weak-kneed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneed', 'a.', 'Geniculated; forming an obtuse angle at the joints, like the\n   knee when a little bent; as, kneed grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knee-deep', 'a.', 'Rising to the knees; knee-high; as, water or snow\n   knee-deep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knee-deep', 'a.', 'Sunk to the knees; as, men knee-deep in water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knee-high', 'a.', 'Rising or reaching upward to the knees; as, the water is\n   knee-high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneejoint', 'n.', 'The joint of the knee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneejoint', 'n.', 'A toggle joint; -- so called because consisting of two\n   pieces jointed to each other end to end, making an angle like the knee\n   when bent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneejointed', 'a.', 'Geniculate; kneed. See Kneed, a., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knelt', 'imp. & p. p.', 'of Kneel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneeled', '', 'of Kneel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneeling', 'p. pr. & vb. n.', 'of Kneel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneel', 'v. i.', 'To bend the knee; to fall or rest on the knees; --\n   sometimes with down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneeler', 'n.', 'One who kneels or who worships by or while kneeling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneeler', 'n.', 'A cushion or stool to kneel on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneeler', 'n.', 'A name given to certain catechumens and penitents who were\n   permitted to join only in parts of church worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneelingly', 'adv.', 'In a kneeling position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneepan', 'n.', 'A roundish, flattened, sesamoid bone in the tendon in\n   front of the knee joint; the patella; the kneecap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kneepiece', 'n.', 'A piece shaped like a knee; as, the kneepieces or ears\n   of a boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knell', 'n.', 'The stoke of a bell tolled at a funeral or at the death of a\n   person; a death signal; a passing bell; hence, figuratively, a warning\n   of, or a sound indicating, the passing away of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knelled', 'imp. & p. p.', 'of Knell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knelling', 'p. pr. & vb. n.', 'of Knell');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knell', 'n.', 'To sound as a knell; especially, to toll at a death or\n   funeral; hence, to sound as a warning or evil omen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knell', 'v. t.', 'To summon, as by a knell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knelt', 'imp. & p. p.', 'of Kneel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knew', 'imp.', 'of Know.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knicker', 'n.', 'A small ball of clay, baked hard and oiled, used as a\n   marble by boys in playing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knickerbockers', 'n. pl.', 'The name for a style of short breeches;\n   smallclothes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knickknack', 'n.', 'A trifle or toy; a bawble; a gewgaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knickknackatory', 'n.', 'A collection of knickknacks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knickknackery', 'n.', 'Knickknacks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knives', 'pl. ', 'of Knife');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knife', 'n.', 'An instrument consisting of a thin blade, usually of steel\n   and having a sharp edge for cutting, fastened to a handle, but of many\n   different forms and names for different uses; as, table knife, drawing\n   knife, putty knife, pallet knife, pocketknife, penknife, chopping\n   knife, etc..');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knife', 'n.', 'A sword or dagger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knifed', 'imp. & p. p.', 'of Knife');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knifing', 'p. pr. & vb. n.', 'of Knife');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knife', 'v. t.', 'To prune with the knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knife', 'v. t.', 'To cut or stab with a knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knifeboard', 'n.', 'A board on which knives are cleaned or polished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knife-edge', 'n.', 'A piece of steel sharpened to an acute edge or angle,\n   and resting on a smooth surface, serving as the axis of motion of a\n   pendulum, scale beam, or other piece required to oscillate with the\n   least possible friction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight', 'n.', 'A young servant or follower; a military attendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight', 'n.', 'In feudal times, a man-at-arms serving on horseback and\n   admitted to a certain military rank with special ceremonies, including\n   an oath to protect the distressed, maintain the right, and live a\n   stainless life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight', 'n.', 'One on whom knighthood, a dignity next below that of\n   baronet, is conferred by the sovereign, entitling him to be addressed\n   as Sir; as, Sir John.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight', 'n.', 'A champion; a partisan; a lover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight', 'n.', 'A piece used in the game of chess, usually bearing a\n   horses head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight', 'n.', 'A playing card bearing the figure of a knight; the knave or\n   jack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knighted', 'imp. & p. p.', 'of Knight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knighting', 'p. pr. & vb. n.', 'of Knight');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight', 'v. t.', 'To dub or create (one) a knight; -- done in England by\n   the sovereign only, who taps the kneeling candidate with a sword,\n   saying: Rise, Sir ---.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knightage', 'n.', 'To body of knights, taken collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knights bachelors', 'pl. ', 'of Knight bachelor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight bachelor', '', 'A knight of the most ancient, but lowest, order of\n   English knights, and not a member of any order of chivalry. See\n   Bachelor, 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knights bannerets', 'pl. ', 'of Knight banneret');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight banneret', '', 'A knight who carried a banner, who possessed fiefs\n   to a greater amount than the knight bachelor, and who was obliged to\n   serve in war with a greater number of attendants. The dignity was\n   sometimes conferred by the sovereign in person on the field of battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight baro-net', '', 'See Baronet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight-errants', 'pl. ', 'of Knight-errant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knights-errant', 'pl. ', 'of Knight-errant');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight-errant', 'n.', 'A wandering knight; a knight who traveled in search\n   of adventures, for the purpose of exhibiting military skill, prowess,\n   and generosity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight-errantries', 'pl. ', 'of Knight-errantry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight-errantry', 'n.', 'The character or actions of wandering knights; the\n   practice of wandering in quest of adventures; chivalry; a quixotic or\n   romantic adventure or scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight-er-ratic', 'a.', 'Pertaining to a knight-errant or to\n   knight-errantry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knighthead', 'n.', 'A bollard timber. See under Bollard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knighthood', 'n.', 'The character, dignity, or condition of a knight, or of\n   knights as a class; hence, chivalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knighthood', 'n.', 'The whole body of knights.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knightless', 'a.', 'Unbecoming a knight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knightliness', 'n.', 'The character or bearing suitable for a knight;\n   chivalry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knightly', 'a.', 'Of or pertaining to a knight; becoming a knight;\n   chivalrous; as, a knightly combat; a knightly spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knightly', 'adv.', 'In a manner becoming a knight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight marshal', '', 'An officer in the household of the British sovereign,\n   who has cognizance of transgressions within the royal household and\n   verge, and of contracts made there, a member of the household being one\n   of the parties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight service', '', 'A tenure of lands held by knights on condition of\n   performing military service. See Chivalry, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knights Templars', 'pl. ', 'of Knight Templar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knight Templar', '', 'See Commandery, n., 3, and also Templar, n., 1 and 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knit', 'imp. & p. p.', 'of Knit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knitted', '', 'of Knit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knitting', 'p. pr. & vb. n.', 'of Knit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knit', 'v. t.', 'To form into a knot, or into knots; to tie together, as\n   cord; to fasten by tying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knit', 'v. t.', 'To form, as a textile fabric, by the interlacing of yarn\n   or thread in a series of connected loops, by means of needles, either\n   by hand or by machinery; as, to knit stockings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knit', 'v. t.', 'To join; to cause to grow together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knit', 'v. t.', 'To unite closely; to connect; to engage; as, hearts knit\n   together in love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knit', 'v. t.', 'To draw together; to contract into wrinkles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knit', 'v. i.', 'To form a fabric by interlacing yarn or thread; to weave\n   by making knots or loops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knit', 'v. i.', 'To be united closely; to grow together; as, broken bones\n   will in time knit and become sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knit', 'n.', 'Union knitting; texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knitback', 'n.', 'The plant comfrey; -- so called from its use as a\n   restorative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knitch', 'n.', 'Alt. of Knitchet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knitchet', 'n.', 'A number of things tied or knit together; a bundle; a\n   fagot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knits', 'n. pl.', 'Small particles of ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knitster', 'n.', 'A woman who knits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knitter', 'n.', 'One who, or that which, knits, joins, or unites; a\n   knitting machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knitting', 'n.', 'The work of a knitter; the network formed by knitting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knitting', 'n.', 'Union formed by knitting, as of bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knittle', 'n.', 'A string that draws together a purse or bag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knittle', 'n.', 'See Nettles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knives', 'n. pl.', 'of Knife. See Knife.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knob', 'n.', 'A hard protuberance; a hard swelling or rising; a bunch; a\n   lump; as, a knob in the flesh, or on a bone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knob', 'n.', 'A knoblike ornament or handle; as, the knob of a lock, door,\n   or drawer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knob', 'n.', 'A rounded hill or mountain; as, the Pilot Knob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knob', 'n.', 'See Knop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knob', 'v. i.', 'To grow into knobs or bunches; to become knobbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knobbed', 'a.', 'Containing knobs; full of knobs; ending in a nob. See\n   Illust of Antenna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knobber', 'n.', 'See Knobbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knobbing', 'n.', 'Rough dressing by knocking off knobs or projections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knobbler', 'n.', 'The hart in its second year; a young deer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knobbling fire', '', 'A bloomery fire. See Bloomery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knobby', 'a.', 'Full of, or covered with, knobs or hard protuberances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knobby', 'a.', 'Irregular; stubborn in particulars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knobby', 'a.', 'Abounding in rounded hills or mountains; hilly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knobstick', 'n.', 'One who refuses to join, or withdraws from, a trades\n   union.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knocked', 'imp. & p. p.', 'of Knock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knocking', 'p. pr. & vb. n.', 'of Knock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knock', 'v. i.', 'To drive or be driven against something; to strike\n   against something; to clash; as, one heavy body knocks against another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knock', 'v. i.', 'To strike or beat with something hard or heavy; to rap;\n   as, to knock with a club; to knock on the door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knock', 'v. t.', 'To strike with something hard or heavy; to move by\n   striking; to drive (a thing) against something; as, to knock a ball\n   with a bat; to knock the head against a post; to knock a lamp off the\n   table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knock', 'v. t.', 'To strike for admittance; to rap upon, as a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knock', 'n.', 'A blow; a stroke with something hard or heavy; a jar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knock', 'n.', 'A stroke, as on a door for admittance; a rap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knockdown', 'n.', 'A felling by a knock, as of a combatant, or of an\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knockdown', 'a.', 'Of force sufficient to fell or completely overthrow; as,\n   a knockdown blow; a knockdown argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knocker', 'n.', 'One who, or that which, knocks; specifically, an\n   instrument, or kind of hammer, fastened to a door, to be used in\n   seeking for admittance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knocking', 'n.', 'A beating; a rap; a series of raps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knockings', 'n. pl.', 'Large lumps picked out of the sieve, in dressing\n   ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knock-knee', 'n.', 'A condition in which the knees are bent in so as to\n   touch each other in walking; inknee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knock-kneed', 'a.', 'Having the legs bent inward so that the knees touch in\n   walking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knockstone', 'n.', 'A block upon which ore is broken up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knoll', 'n.', 'A little round hill; a mound; a small elevation of earth;\n   the top or crown of a hill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knolled', 'imp. & p. p.', 'of Knoll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knolling', 'p. pr. & vb. n.', 'of Knoll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knoll', 'v. t.', 'To ring, as a bell; to strike a knell upon; to toll; to\n   proclaim, or summon, by ringing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knoll', 'v. i.', 'To sound, as a bell; to knell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knoll', 'n.', 'The tolling of a bell; a knell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knoller', 'n.', 'One who tolls a bell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knop', 'n.', 'A knob; a bud; a bunch; a button.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knop', 'n.', 'Any boldly projecting sculptured ornament; esp., the\n   ornamental termination of a pinnacle, and then synonymous with finial;\n   -- called also knob, and knosp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knopped', 'a.', 'Having knops or knobs; fastened as with buttons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knoppern', 'n.', 'A kind of gall produced by a gallfly on the cup of an\n   acorn, -- used in tanning and dyeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knopweed', 'n.', 'Same as Knapweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knor', 'n.', 'See Knur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knosp', 'n.', 'Same as Knop,2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'A fastening together of the pars or ends of one or more\n   threads, cords, ropes, etc., by any one of various ways of tying or\n   entangling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'A lump or loop formed in a thread, cord, rope. etc., as at\n   the end, by tying or interweaving it upon itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'An ornamental tie, as of a ribbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'A bond of union; a connection; a tie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'Something not easily solved; an intricacy; a difficulty; a\n   perplexity; a problem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'A figure the lines of which are interlaced or intricately\n   interwoven, as in embroidery, gardening, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'A cluster of persons or things; a collection; a group; a\n   hand; a clique; as, a knot of politicians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'A portion of a branch of a tree that forms a mass of woody\n   fiber running at an angle with the grain of the main stock and making a\n   hard place in the timber. A loose knot is generally the remains of a\n   dead branch of a tree covered by later woody growth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'A knob, lump, swelling, or protuberance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'A protuberant joint in a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'The point on which the action of a story depends; the gist of\n   a matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'See Node.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'A division of the log line, serving to measure the rate of\n   the vessels motion. Each knot on the line bears the same proportion to\n   a mile that thirty seconds do to an hour. The number of knots which run\n   off from the reel in half a minute, therefore, shows the number of\n   miles the vessel sails in an hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'A nautical mile, or 6080.27 feet; as, when a ship goes eight\n   miles an hour, her speed is said to be eight knots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'A kind of epaulet. See Shoulder knot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'n.', 'A sandpiper (Tringa canutus), found in the northern parts of\n   all the continents, in summer. It is grayish or ashy above, with the\n   rump and upper tail coverts white, barred with dusky. The lower parts\n   are pale brown, with the flanks and under tail coverts white. When fat\n   it is prized by epicures. Called also dunne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotted', 'imp. & p. p.', 'of Knot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotting', 'p. pr. & vb. n.', 'of Knot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'v. t.', 'To tie in or with, or form into, a knot or knots; to form\n   a knot on, as a rope; to entangle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'v. t.', 'To unite closely; to knit together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'v. t.', 'To entangle or perplex; to puzzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'v. i.', 'To form knots or joints, as in a cord, a plant, etc.; to\n   become entangled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'v. i.', 'To knit knots for fringe or trimming.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knot', 'v. i.', 'To copulate; -- said of toads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotberry', 'n.', 'The cloudberry (Rudus Chamaemorus); -- so called from\n   its knotted stems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotgrass', 'n.', 'a common weed with jointed stems (Polygonum aviculare);\n   knotweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotgrass', 'n.', 'The dog grass. See under Dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotless', 'a.', 'Free from knots; without knots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotted', 'a.', 'Full of knots; having knots knurled; as, a knotted cord;\n   the knotted oak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotted', 'a.', 'Interwoven; matted; entangled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotted', 'a.', 'Having intersecting lines or figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotted', 'a.', 'Characterized by small, detached points, chiefly composed\n   of mica, less decomposable than the mass of the rock, and forming knots\n   in relief on the weathered surface; as, knotted rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotted', 'a.', 'Entangled; puzzling; knotty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knottiness', 'n.', 'The quality or state of being knotty or full of knots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knottiness', 'n.', 'Difficulty of solution; intricacy; complication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotty', 'superl.', 'Full of knots; knotted; having many knots; as, knotty\n   timber; a knotty rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotty', 'superl.', 'Hard; rugged; as, a knotty head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotty', 'superl.', 'Difficult; intricate; perplexed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotweed', 'n.', 'See Knot/rass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knotwort', 'n.', 'A small, herbaceous, trailing plant, of the genus\n   Illecebrum (I. verticillatum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knout', 'n.', 'A kind of whip for flogging criminals, formerly much used in\n   Russia. The last is a tapering bundle of leather thongs twisted with\n   wire and hardened, so that it mangles the flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knout', 'v. t.', 'To punish with the knout.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Know', 'n.', 'Knee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knew', 'imp.', 'of Know');
  INSERT INTO words (word, word_type, description) VALUES  (    'Known', 'p. p.', 'of Know');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowing', 'p. pr. & vb. n.', 'of Know');
  INSERT INTO words (word, word_type, description) VALUES  (    'Know', 'v. i.', 'To perceive or apprehend clearly and certainly; to\n   understand; to have full information of; as, to know ones duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Know', 'v. i.', 'To be convinced of the truth of; to be fully assured of;\n   as, to know things from information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Know', 'v. i.', 'To be acquainted with; to be no stranger to; to be more or\n   less familiar with the person, character, etc., of; to possess\n   experience of; as, to know an author; to know the rules of an\n   organization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Know', 'v. i.', 'To recognize; to distinguish; to discern the character of;\n   as, to know a persons face or figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Know', 'v. i.', 'To have sexual commerce with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Know', 'v. i.', 'To have knowledge; to have a clear and certain perception;\n   to possess wisdom, instruction, or information; -- often with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Know', 'v. i.', 'To be assured; to feel confident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowable', 'a.', 'That may be known; capable of being discovered,\n   understood, or ascertained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowa bleness', 'n.', 'The state or quality of being knowable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Know-all', 'n.', 'One who knows everything; hence, one who makes pretension\n   to great knowledge; a wiseacre; -- usually ironical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knower', 'n.', 'One who knows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowing', 'a.', 'Skilful; well informed; intelligent; as, a knowing man; a\n   knowing dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowing', 'a.', 'Artful; cunning; as, a knowing rascal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowing', 'n.', 'Knowledge; hence, experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowingly', 'adv.', 'With knowledge; in a knowing manner; intelligently;\n   consciously; deliberately; as, he would not knowingly offend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowingly', 'adv.', 'By experience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowingness', 'n.', 'The state or quality of being knowing or intelligent;\n   shrewdness; skillfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowleche', 'n. & v.', 'See Knowl, edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowleching', 'n.', 'Knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowledge', 'v. i.', 'The act or state of knowing; clear perception of\n   fact, truth, or duty; certain apprehension; familiar cognizance;\n   cognition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowledge', 'v. i.', 'That which is or may be known; the object of an act\n   of knowing; a cognition; -- chiefly used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowledge', 'v. i.', 'That which is gained and preserved by knowing;\n   instruction; acquaintance; enlightenment; learning; scholarship;\n   erudition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowledge', 'v. i.', 'That familiarity which is gained by actual\n   experience; practical skill; as, a knowledge of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowledge', 'v. i.', 'Scope of information; cognizance; notice; as, it has\n   not come to my knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowledge', 'v. i.', 'Sexual intercourse; -- usually preceded by carnal;\n   as, carnal knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knowledge', 'v. t.', 'To acknowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Known', 'p. p.', 'of Know.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Know-nothing', 'n.', 'A member of a secret political organization in the\n   United States, the chief objects of which were the proscription of\n   foreigners by the repeal of the naturalization laws, and the exclusive\n   choice of native Americans for office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Know-nothingism', 'n.', 'The doctrines, principles, or practices, of the\n   Know-nothings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knubs', 'n. pl.', 'Waste silk formed in winding off the threads from a\n   cocoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knuckle', 'n.', 'The joint of a finger, particularly when made prominent by\n   the closing of the fingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knuckle', 'n.', 'The kneejoint, or middle joint, of either leg of a\n   quadruped, especially of a calf; -- formerly used of the kneejoint of a\n   human being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knuckle', 'n.', 'The joint of a plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knuckle', 'n.', 'The joining pars of a hinge through which the pin or rivet\n   passes; a knuckle joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knuckle', 'n.', 'A convex portion of a vessels figure where a sudden\n   change of shape occurs, as in a canal boat, where a nearly vertical\n   side joins a nearly flat bottom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knuckle', 'n.', 'A contrivance, usually of brass or iron, and furnished\n   with points, worn to protect the hand, to add force to a blow, and to\n   disfigure the person struck; as, brass knuckles; -- called also knuckle\n   duster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knuckled', 'imp. & p. p.', 'of Knuckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knuckling', 'p. pr. & vb. n.', 'of Knuckle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knuckle', 'v. i.', 'To yield; to submit; -- used with down, to, or under.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knuckle', 'v. t.', 'To beat with the knuckles; to pommel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knuckled', 'a.', 'Jointed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knuff', 'n.', 'A lout; a clown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knur', 'n.', 'A knurl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knurl', 'n.', 'A contorted knot in wood; a crossgrained protuberance; a\n   nodule; a boss or projection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knurl', 'n.', 'One who, or that which, is crossgrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knurl', 'v. t.', 'To provide with ridges, to assist the grasp, as in the\n   edge of a flat knob, or coin; to mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knurled', 'a.', 'Full of knots; gnarled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knurled', 'a.', 'Milled, as the head of a screw, or the edge of a coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knurly', 'superl.', 'Full of knots; hard; tough; hence, capable of enduring\n   or resisting much.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Knurry', 'a.', 'Full of knots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koaita', 'n.', 'Same as Coaita.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koala', 'n.', 'A tailless marsupial (Phascolarctos cinereus), found in\n   Australia. The female carries her young on the back of her neck. Called\n   also Australian bear, native bear, and native sloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kob', 'n.', 'Alt. of Koba');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koba', 'n.', 'Any one of several species of African antelopes of the genus\n   Kobus, esp. the species Kobus sing-sing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kobalt', 'n.', 'See Cobalt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kobellite', 'n.', 'A blackish gray mineral, a sulphide of antimony,\n   bismuth, and lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kobold', 'n.', 'A kind of domestic spirit in German mythology,\n   corresponding to the Scottish brownie and the English Robin Goodfellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kodak', 'n.', 'A kind of portable camera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koel', 'n.', 'Any one of several species of cuckoos of the genus Eudynamys,\n   found in India, the East Indies, and Australia. They deposit their eggs\n   in the nests of other birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koff', 'n.', 'A two-masted Dutch vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kohinoor', 'n.', 'Alt. of Kohnur');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kohnur', 'n.', 'A famous diamond, surrendered to the British crown on the\n   annexation of the Punjab. According to Hindoo legends, it was found in\n   a Golconda mine, and has been the property of various Hindoo and\n   Persian rulers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kohl', 'n.', 'A mixture of soot and other ingredients, used by Egyptian and\n   other Eastern women to darken the edges of the eyelids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kohl-rabies', 'pl. ', 'of Kohl-rabi');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kohl-rabi', 'n.', 'A variety of cabbage, in which the edible part is a\n   large, turnip-shaped swelling of the stem, above the surface of the\n   ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kokama', 'n.', 'The gemsbok.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koklass', 'n.', 'Any pheasant of the genus Pucrasia. The birds of this\n   genus inhabit India and China, and are distinguished by having a long\n   central and two lateral crests on the head. Called also pucras.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kokoon', 'n.', 'The gnu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kolarian', 'n.', 'An individual of one of the races of aboriginal\n   inhabitants which survive in Hindostan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kolarian', 'a.', 'Of or pertaining to the Kolarians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Komenic', 'a.', 'Of or pertaining to, or designating, an acid derived from\n   meconic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Komtok', 'n.', 'An African freshwater fish (Protopterus annectens),\n   belonging to the Dipnoi. It can breathe air by means of its lungs, and\n   when waters dry up, it encases itself in a nest of hard mud, where it\n   remains till the rainy season. It is used as food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kon', 'v. t.', 'To know. See Can, and Con.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Konite', 'n.', 'See Conite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Konze', 'n.', 'A large African antelope (Alcelaphus Lichtensteini), allied\n   to the hartbeest, but having shorter and flatter horns, and lacking a\n   black patch on the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koodoo', 'n.', 'A large South African antelope (Strepsiceros kudu). The\n   males have graceful spiral horns, sometimes four feet long. The general\n   color is reddish or grayish brown, with eight or nine white bands on\n   each side, and a pale dorsal stripe. The old males become dark bluish\n   gray, due to the skin showing through the hair. The females are\n   hornless. Called also nellut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kookoom', 'n.', 'The oryx or gemsbok.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koolokamba', 'n.', 'A west African anthropoid ape (Troglodytes koolokamba,\n   or T. Aubryi), allied to the chimpanzee and gorilla, and, in some\n   respects, intermediate between them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koolslaa', 'n.', 'See Coleslaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koord', 'n.', 'See Kurd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koordish', 'n.', 'See Kurdish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koorilian', 'a & n.', 'Same as Kurilian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kopeck', 'n.', 'A small Russian coin. One hundred kopecks make a rouble,\n   worth about sixty cents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koran', 'n.', 'The Scriptures of the Mohammedans, containing the professed\n   revelations to Mohammed; -- called also Alcoran.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Korin', 'n.', 'The gazelle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Korrigum', 'n.', 'A West African antelope (Damalis Senegalensis), allied to\n   the sassaby. It is reddish gray, with a black face, and a black stripe\n   on the outside of the legs above the knees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kosmos', 'n.', 'See Cosmos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kotow', 'n.', 'The prostration made by mandarins and others to their\n   superiors, either as homage or worship, by knocking the forehead on the\n   ground. There are degrees in the rite, the highest being expressed by\n   three knockings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kotowed', 'imp. & p. p.', 'of Kotow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kotowing', 'p. pr. & vb. n.', 'of Kotow');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kotow', 'v. i.', 'To perform the kotow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koulan', 'n.', 'A wild horse (Equus, / Asinus, onager) inhabiting the\n   plants of Central Asia; -- called also gour, khur, and onager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Koumiss', 'n.', 'An intoxicating fermented or distilled liquor originally\n   made by the Tartars from mares or camels milk. It can be obtained\n   from any kind of milk, and is now largely made in Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kousso', 'n.', 'An Abyssinian rosaceous tree (Brayera anthelmintica), the\n   flowers of which are used as a vermifuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kowtow', 'n. & v. i.', 'The same as Kotow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kra', 'n.', 'A long-tailed ape (Macacus cynomolgus) of India and Sumatra.\n   It is reddish olive, spotted with black, and has a black tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kraal', 'n.', 'A collection of huts within a stockade; a village;\n   sometimes, a single hut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kraal', 'n.', 'An inclosure into which are driven wild elephants which are\n   to be tamed and educated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krait', 'n.', 'A very venomous snake of India (Bungarus coeruleus), allied\n   to the cobra. Its upper parts are bluish or brownish black, often with\n   narrow white streaks; the belly is whitish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kraken', 'n.', 'A fabulous Scandinavian sea monster, often represented as\n   resembling an island, but sometimes as resembling an immense octopus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krakowiak', 'n.', 'A lively Polish dance. See Cracovienne.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krameria', 'n.', 'A genus of spreading shrubs with many stems, from one\n   species of which (K. triandra), found in Peru, rhatany root, used as a\n   medicine, is obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krameric', 'a.', 'Pertaining to, or derived from, Krameria (rhatany); as,\n   krameric acid, usually called ratanhia-tannic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krang', 'n.', 'The carcass of a whale after the blubber has been removed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kranging hook', '', 'A hook for holding the blubber while cutting it away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kreatic', 'a.', 'See Creatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kreatin', 'n.', 'See Creatin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kreatinin', 'n.', 'See Creatinin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kreel', 'n.', 'See Creel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kremlin', 'n.', 'The citadel of a town or city; especially, the citadel of\n   Moscow, a large inclosure which contains imperial palaces, cathedrals,\n   churches, an arsenal, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krems', 'n.', 'A variety of white lead. See Krems lead, under Lead, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kreng', 'n.', 'See Krang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kreosote', 'n.', 'See Creosote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kreutzer', 'n.', 'A small copper coin formerly used in South Germany; also,\n   a small Austrian copper coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kriegsspiel', 'n.', 'A game of war, played for practice, on maps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kris', 'n.', 'A Malay dagger. See Creese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krishna', 'n.', 'The most popular of the Hindoo divinities, usually held to\n   be the eighth incarnation of the god Vishnu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kritarchy', 'n.', 'The rule of the judges over Israel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krokidolite', 'n.', 'See Crocidolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krone', 'n.', 'A coin of Denmark, Norway, and Sweden, of the value of about\n   twenty-eight cents. See Crown, n., 9.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kroomen', 'pl. ', 'of Krooman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krooman', 'n.', 'One of a negro tribe of Liberia and the adjacent coast,\n   whose members are much employed on shipboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kruller', 'n.', 'See Cruller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krummhorn', 'n.', 'Alt. of Krumhorn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krumhorn', 'n.', 'A reed instrument of music of the cornet kind, now\n   obsolete (see Cornet, 1, a.).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krumhorn', 'a.', 'A reed stop in the organ; -- sometimes called cremona.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Krupp gun', '', 'A breech-loading steel cannon manufactured at the works of\n   Friedrich Krupp, at Essen in Prussia. Guns of over eight-inch bore are\n   made up of several concentric cylinders; those of a smaller size are\n   forged solid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kryolite', 'n.', 'See Cryolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ksar', 'n.', 'See Czar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kshatriya', 'n.', 'Alt. of Kshatruya');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kshatruya', 'n.', 'The military caste, the second of the four great Hindoo\n   castes; also, a member of that caste. See Caste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kuda', 'n.', 'The East Indian tapir. See Tapir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kudos', 'n.', 'Glory; fame; renown; praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kudos', 'v. t.', 'To praise; to extol; to glorify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kudu', 'n.', 'See Koodoo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kufic', 'a.', 'See Cufic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kukang', 'n.', 'The slow lemur. See Lemur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kuklux', 'n.', 'The name adopted in the southern part of the United States\n   by a secret political organization, active for several years after the\n   close of the Civil War, and having for its aim the repression of the\n   political power of the freedmen; -- called also Kuklux Klan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kulan', 'n.', 'See Koulan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kumish', 'n.', 'Alt. of Kumiss');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kumiss', 'n.', 'See Koumiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kummel', 'n.', 'A Russian and German liqueur, consisting of a sweetened\n   spirit flavored with caraway seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kumquat', 'n.', 'A small tree of the genus Citrus (C. Japonica) growing in\n   China and Japan; also, its small acid, orange-colored fruit used for\n   preserves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kupfernickel', 'n.', 'Copper-nickel; niccolite. See Niccolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kurd', 'n.', 'A native or inhabitant of a mountainous region of Western\n   Asia belonging to the Turkish and Persian monarchies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kurdish', 'a.', 'Of or pertaining to the Kurds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kurilian', 'a.', 'Of or pertaining to the Kurile Islands, a chain of\n   islands in the Pacific ocean, extending from the southern extremity of\n   Kamschatka to Yesso.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kurilian', 'n.', 'A native or an inhabitant of the Kurile Islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kursaal', 'n.', 'A public hall or room, for the use of visitors at watering\n   places and health resorts in Germany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kusimanse', 'n.', 'A carnivorous animal (Crossarchus obscurus) of tropical\n   Africa. It its allied to the civets. Called also kusimansel, and\n   mangue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kuskus', '', 'See Vetiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kussier', 'n.', '(Mus.) A Turkish instrument of music, with a hollow body\n   covered with skin, over which five strings are stretched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kutauss', 'n.', 'The India civet (Viverra zibetha).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kutch', 'n.', 'The packet of vellum leaves in which the gold is first\n   beaten into thin sheets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kutch', 'n.', 'See Catechu.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ky', 'n. pl.', 'Kine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyaboca wood', '', 'Amboyna wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyaboca wood', '', 'Sandalwood (Santalum album).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyannite', 'n.', 'See Cyanite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyanized', 'imp. & p. p.', 'of Kyanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyanizing', 'p. pr. & vb. n.', 'of Kyanize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyanize', 'v. t.', 'To render (wood) proof against decay by saturating with\n   a solution of corrosive sublimate in open tanks, or under pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyanol', 'n.', 'Aniline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyanol', 'n.', 'A base obtained from coal tar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyanophyll', 'n.', 'Same as Cyanophyll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyar', 'n.', 'Cocoanut fiber, or the cordage made from it. See Coir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyaw', 'n.', 'A daw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyd', '', 'p. p. of Kythe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kydde', '', 'imp. of Kythe, to show.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyke', 'v. i.', 'To look steadfastly; to gaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyley', 'n.', 'A variety of the boomerang.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyloes', 'n. pl.', 'The cattle of the Hebrides, or of the Highlands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kymnel', 'n.', 'See Kimnel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kymograph', 'n.', 'An instrument for measuring, and recording graphically,\n   the pressure of the blood in any of the blood vessels of a living\n   animal; -- called also kymographion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kymographic', 'a.', 'Of or pertaining to a kymograph; as, a kymographic\n   tracing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kymric', 'a & n.', 'See Cymric, a. & n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kymry', 'n.', 'See Cymry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kynrede', 'n.', 'Kindred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kynurenic', 'a.', 'Pertaining to, or designating, an acid obtained from the\n   urine of dogs. By decomposition the acid yields a nitrogenous base\n   (called kynurin) and carbonic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyrie', 'n.', 'See Kyrie eleison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyrie eleison', '', 'Greek words, meaning \"Lord, have mercy upon us,\" used\n   in the Mass, the breviary offices, the litany of the saints, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyrie eleison', '', 'The name given to the response to the Commandments, in\n   the service of the Church of England and of the Protestant Episcopal\n   Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyrielle', 'n.', 'A litany beginning with the words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyriolexy', 'n.', 'Alt. of Kyriology');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyriology', 'n.', 'The use of literal or simple expressions, as\n   distinguished from the use of figurative or obscure ones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kyriological', 'a.', 'Serving to denote objects by conventional signs or\n   alphabetical characters; as, the original Greek alphabet of sixteen\n   letters was called kyriologic, because it represented the pure\n   elementary sounds. See Curiologic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kydde', 'imp.', 'of Kithe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kidde', '', 'of Kithe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kythed', 'p. p.', 'of Kithe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kything', 'p. pr. & vb. n.', 'of Kithe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kythe', 'v. t.', 'Alt. of Kithe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kithe', 'v. t.', 'To make known; to manifest; to show; to declare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kythe', 'v. t.', 'To come into view; to appear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kytomiton', 'n.', 'See Karyomiton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Kytoplasma', 'n.', 'See Karyoplasma.\n   L () L is the twelfth letter of the English alphabet, and a vocal\n   consonant. It is usually called a semivowel or liquid. Its form and\n   value are from the Greek, through the Latin, the form of the Greek\n   letter being from the Phoenician, and the ultimate origin prob.\n   Egyptian. Etymologically, it is most closely related to r and u; as in\n   pilgrim, peregrine, couch (fr. collocare), aubura (fr. LL. alburnus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'L', '', 'As a numeral, L stands for fifty in the English, as in the Latin\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'L', 'n.', 'An extension at right angles to the length of a main building,\n   giving to the ground plan a form resembling the letter L; sometimes\n   less properly applied to a narrower, or lower, extension in the\n   direction of the length of the main building; a wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'L', 'n.', 'A short right-angled pipe fitting, used in connecting two pipes\n   at right angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'La', 'n.', 'A syllable applied to the sixth tone of the scale in music in\n   solmization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'La', 'n.', 'The tone A; -- so called among the French and Italians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'La', 'interj.', 'Look; see; behold; -- sometimes followed by you.');
  INSERT INTO words (word, word_type, description) VALUES  (    'La', 'interj.', 'An exclamation of surprise; -- commonly followed by me;\n   as, La me!');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laas', 'n.', 'A lace. See Lace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lab', 'v. i.', 'To prate; to gossip; to babble; to blab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lab', 'n.', 'A telltale; a prater; a blabber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labadist', 'n.', 'A follower of Jean de Labadie, a religious teacher of the\n   17th century, who left the Roman Catholic Church and taught a kind of\n   mysticism, and the obligation of community of property among\n   Christians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labarraques solution', '', 'An aqueous solution of hypochlorite of sodium,\n   extensively used as a disinfectant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labara', 'pl. ', 'of Labarum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labarum', 'n.', 'The standard adopted by the Emperor Constantine after his\n   conversion to Christianity. It is described as a pike bearing a silk\n   banner hanging from a crosspiece, and surmounted by a golden crown. It\n   bore a monogram of the first two letters (CHR) of the name of Christ in\n   its Greek form. Later, the name was given to various modifications of\n   this standard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labdanum', 'n.', 'See Ladanum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labefaction', 'n.', 'The act of labefying or making weak; the state of\n   being weakened; decay; ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labefy', 'v. t.', 'To weaken or impair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Label', 'n.', 'A tassel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Label', 'n.', 'A slip of silk, paper, parchment, etc., affixed to anything,\n   usually by an inscription, the contents, ownership, destination, etc.;\n   as, the label of a bottle or a package.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Label', 'n.', 'A slip of ribbon, parchment, etc., attached to a document to\n   hold the appended seal; also, the seal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Label', 'n.', 'A writing annexed by way of addition, as a codicil added to\n   a will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Label', 'n.', 'A barrulet, or, rarely, a bendlet, with pendants, or points,\n   usually three, especially used as a mark of cadency to distinguish an\n   eldest or only son while his father is still living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Label', 'n.', 'A brass rule with sights, formerly used, in connection with\n   a circumferentor, to take altitudes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Label', 'n.', 'The name now generally given to the projecting molding by\n   the sides, and over the tops, of openings in mediaeval architecture. It\n   always has a /quare form, as in the illustration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Label', 'n.', 'In mediaeval art, the representation of a band or scroll\n   containing an inscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labeled', 'imp. & p. p.', 'of Label');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labelled', '', 'of Label');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labeling', 'p. pr. & vb. n.', 'of Label');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labelling', '', 'of Label');
  INSERT INTO words (word, word_type, description) VALUES  (    'Label', 'v. t.', 'To affix a label to; to mark with a name, etc.; as, to\n   label a bottle or a package.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Label', 'v. t.', 'To affix in or on a label.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labeler', 'n.', 'One who labels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labella', 'pl. ', 'of Labellum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labellums', 'pl. ', 'of Labellum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labellum', 'n.', 'The lower or apparently anterior petal of an orchidaceous\n   flower, often of a very curious shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labellum', 'n.', 'A small appendage beneath the upper lip or labrum of\n   certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labent', 'a.', 'Slipping; sliding; gliding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labia', 'n. pl.', 'See Labium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labial', 'a.', 'Of or pertaining to the lips or labia; as, labial veins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labial', 'a.', 'Furnished with lips; as, a labial organ pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labial', 'a.', 'Articulated, as a consonant, mainly by the lips, as b, p,\n   m, w.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labial', 'a.', 'Modified, as a vowel, by contraction of the lip opening, as\n   / (f/d), / (/ld), etc., and as eu and u in French, and o, u in German.\n   See Guide to Pronunciation, // 11, 178.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labial', 'a.', 'Of or pertaining to the labium; as, the labial palpi of\n   insects. See Labium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labial', 'n.', 'A letter or character representing an articulation or sound\n   formed or uttered chiefly with the lips, as b, p, w.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labial', 'n.', 'An organ pipe that is furnished with lips; a flue pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labial', 'n.', 'One of the scales which border the mouth of a fish or\n   reptile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labialism', 'n.', 'The quality of being labial; as, the labialism of an\n   articulation; conversion into a labial, as of a sound which is\n   different in another language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labialization', 'n.', 'The modification of an articulation by contraction\n   of the lip opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labialize', 'v. t.', 'To modify by contraction of the lip opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labially', 'adv.', 'In a labial manner; with, or by means of, the lips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labiate', 'v. t.', 'To labialize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labiate', 'a.', 'Having the limb of a tubular corolla or calyx divided into\n   two unequal parts, one projecting over the other like the lips of a\n   mouth, as in the snapdragon, sage, and catnip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labiate', 'a.', 'Belonging to a natural order of plants (Labiatae), of\n   which the mint, sage, and catnip are examples. They are mostly aromatic\n   herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labiate', 'n.', 'A plant of the order Labiatae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labiated', 'a.', 'Same as Labiate, a. (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labiatifloral', 'a.', 'Alt. of Labiatifloral');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labiatifloral', 'a.', 'Having labiate flowers, as the snapdragon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labidometer', 'n.', 'A forceps with a measuring attachment for ascertaining\n   the size of the fetal head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labile', 'a.', 'Liable to slip, err, fall, or apostatize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lability', 'n.', 'Liability to lapse, err, or apostatize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labimeter', 'n.', 'See Labidometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labiodental', 'a.', 'Formed or pronounced by the cooperation of the lips\n   and teeth, as f and v.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labiodental', 'n.', 'A labiodental sound or letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labionasal', 'a.', 'Formed by the lips and the nose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labionasal', 'n.', 'A labionasal sound or letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labiose', 'a.', 'Having the appearance of being labiate; -- said of certain\n   polypetalous corollas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labipalpi', 'pl. ', 'of Labipalpus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labipalpus', 'n.', 'One of the labial palpi of an insect. See Illust. under\n   Labium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labia', 'pl. ', 'of Labium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labiums', 'pl. ', 'of Labium');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labium', 'n.', 'A lip, or liplike organ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labium', 'n.', 'The lip of an organ pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labium', 'n.', 'The folds of integument at the opening of the vulva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labium', 'n.', 'The organ of insects which covers the mouth beneath, and\n   serves as an under lip. It consists of the second pair of maxillae,\n   usually closely united in the middle line, but bearing a pair of palpi\n   in most insects. It often consists of a thin anterior part (ligula or\n   palpiger) and a firmer posterior plate (mentum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labium', 'n.', 'Inner margin of the aperture of a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lablab', 'n.', 'an East Indian name for several twining leguminous plants\n   related to the bean, but commonly applied to the hyacinth bean\n   (Dolichos Lablab).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'n.', 'Physical toil or bodily exertion, especially when fatiguing,\n   irksome, or unavoidable, in distinction from sportive exercise; hard,\n   muscular effort directed to some useful end, as agriculture,\n   manufactures, and like; servile toil; exertion; work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'n.', 'Intellectual exertion; mental effort; as, the labor of\n   compiling a history.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'n.', 'That which requires hard work for its accomplishment; that\n   which demands effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'n.', 'Travail; the pangs and efforts of childbirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'n.', 'Any pang or distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'n.', 'The pitching or tossing of a vessel which results in the\n   straining of timbers and rigging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'n.', 'A measure of land in Mexico and Texas, equivalent to an area\n   of 177/ acres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labored', 'imp. & p. p.', 'of Labor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laboring', 'p. pr. & vb. n.', 'of Labor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'n.', 'To exert muscular strength; to exert ones strength with\n   painful effort, particularly in servile occupations; to work; to toil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'n.', 'To exert ones powers of mind in the prosecution of any\n   design; to strive; to take pains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'n.', 'To be oppressed with difficulties or disease; to do ones\n   work under conditions which make it especially hard, wearisome; to move\n   slowly, as against opposition, or under a burden; to be burdened; --\n   often with under, and formerly with of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'n.', 'To be in travail; to suffer the pangs of childbirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'n.', 'To pitch or roll heavily, as a ship in a turbulent sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'v. t.', 'To work at; to work; to till; to cultivate by toil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'v. t.', 'To form or fabricate with toil, exertion, or care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'v. t.', 'To prosecute, or perfect, with effort; to urge\n   stre/uously; as, to labor a point or argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor', 'v. t.', 'To belabor; to beat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laborant', 'n.', 'A chemist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laboratories', 'pl. ', 'of Laboratory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laboratory', 'n.', 'The workroom of a chemist; also, a place devoted to\n   experiments in any branch of natural science; as, a chemical, physical,\n   or biological laboratory. Hence, by extension, a place where something\n   is prepared, or some operation is performed; as, the liver is the\n   laboratory of the bile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labored', 'a.', 'Bearing marks of labor and effort; elaborately wrought;\n   not easy or natural; as, labored poetry; a labored style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laboredly', 'adv.', 'In a labored manner; with labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laborer', 'n.', 'One who labors in a toilsome occupation; a person who does\n   work that requires strength rather than skill, as distinguished from\n   that of an artisan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laboring', 'a.', 'That labors; performing labor; esp., performing coarse,\n   heavy work, not requiring skill also, set apart for labor; as, laboring\n   days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laboring', 'a.', 'Suffering pain or grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laborious', 'a.', 'Requiring labor, perseverance, or sacrifices; toilsome;\n   tiresome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laborious', 'a.', 'Devoted to labor; diligent; industrious; as, a laborious\n   mechanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laborless', 'a.', 'Not involving labor; not laborious; easy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laborous', 'a.', 'Laborious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labor-saving', 'a.', 'Saving labor; adapted to supersede or diminish the\n   labor of men; as, labor-saving machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laborsome', 'a.', 'Made with, or requiring, great labor, pains, or\n   diligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laborsome', 'a.', 'Likely or inclined to roll or pitch, as a ship in a\n   heavy sea; having a tendency to labor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labrador', 'n.', 'A region of British America on the Atlantic coast, north\n   of Newfoundland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labradorite', 'n.', 'A kind of feldspar commonly showing a beautiful play\n   of colors, and hence much used for ornamental purposes. The finest\n   specimens come from Labrador. See Feldspar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labras', 'n. pl.', 'Lips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labroid', 'a.', 'Like the genus Labrus; belonging to the family Labridae,\n   an extensive family of marine fishes, often brilliantly colored, which\n   are very abundant in the Indian and Pacific Oceans. The tautog and\n   cunner are American examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labrose', 'a.', 'Having thick lips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labra', 'pl. ', 'of Labrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labrums', 'pl. ', 'of Labrum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labrum', 'n.', 'A lip or edge, as of a basin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labrum', 'n.', 'An organ in insects and crustaceans covering the upper part\n   of the mouth, and serving as an upper lip. See Illust. of Hymenoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labrum', 'n.', 'The external margin of the aperture of a shell. See\n   Univalve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labri', 'pl. ', 'of Labrus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labrus', 'n.', 'A genus of marine fishes, including the wrasses of Europe.\n   See Wrasse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laburnic', 'a.', 'Of, pertaining to, or derived from, the laburnum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laburnine', 'n.', 'A poisonous alkaloid found in the unripe seeds of the\n   laburnum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laburnum', 'n.', 'A small leguminous tree (Cytisus Laburnum), native of the\n   Alps. The plant is reputed to be poisonous, esp. the bark and seeds. It\n   has handsome racemes of yellow blossoms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinth', 'n.', 'An edifice or place full of intricate passageways which\n   render it difficult to find the way from the interior to the entrance;\n   as, the Egyptian and Cretan labyrinths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinth', 'n.', 'Any intricate or involved inclosure; especially, an\n   ornamental maze or inclosure in a park or garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinth', 'n.', 'Any object or arrangement of an intricate or involved\n   form, or having a very complicated nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinth', 'n.', 'An inextricable or bewildering difficulty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinth', 'n.', 'The internal ear. See Note under Ear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinth', 'n.', 'A series of canals through which a stream of water is\n   directed for suspending, carrying off, and depositing at different\n   distances, the ground ore of a metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinth', 'n.', 'A pattern or design representing a maze, -- often inlaid\n   in the tiled floor of a church, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinthal', 'a.', 'Pertaining to, or resembling, a labyrinth; intricate;\n   labyrinthian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinthian', 'a.', 'Intricately winding; like a labyrinth; perplexed;\n   labyrinthal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinthibranch', 'a.', 'Of or pertaining to the Labyrinthici.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinthibranch', 'n.', 'One of the Labyrinthici.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinthic', 'a.', 'Alt. of Labyrinthical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinthical', 'a.', 'Like or pertaining to a labyrinth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinthici', 'n. pl.', 'An order of teleostean fishes, including the\n   Anabas, or climbing perch, and other allied fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinthiform', 'a.', 'Having the form of a labyrinth; intricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinthine', 'a.', 'Pertaining to, or like, a labyrinth; labyrinthal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinthodon', 'n.', 'A genus of very large fossil amphibians, of the\n   Triassic period, having bony plates on the under side of the body. It\n   is the type of the order Labyrinthodonta. Called also Mastodonsaurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinthodont', 'a.', 'Of or pertaining to the Labyrinthodonta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinthodont', 'n.', 'One of the Labyrinthodonta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Labyrinthodonta', 'n. pl.', 'An extinct order of Amphibia, including the\n   typical genus Labyrinthodon, and many other allied forms, from the\n   Carboniferous, Permian, and Triassic formations. By recent writers they\n   are divided into two or more orders. See Stegocephala.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lac', 'n.', 'Alt. of Lakh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lakh', 'n.', 'One hundred thousand; also, a vaguely great number; as, a lac\n   of rupees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lac', 'n.', 'A resinous substance produced mainly on the banyan tree, but\n   to some extent on other trees, by the Coccus lacca, a scale-shaped\n   insect, the female of which fixes herself on the bark, and exudes from\n   the margin of her body this resinous substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laccic', 'a.', 'Pertaining to lac, or produced from it; as, laccic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laccin', 'n.', 'A yellow amorphous substance obtained from lac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laccolite', 'n.', 'Alt. of Laccolith');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laccolith', 'n.', 'A mass of igneous rock intruded between sedimentary beds\n   and resulting in a mammiform bulging of the overlying strata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lace', 'n.', 'That which binds or holds, especially by being interwoven; a\n   string, cord, or band, usually one passing through eyelet or other\n   holes, and used in drawing and holding together parts of a garment, of\n   a shoe, of a machine belt, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lace', 'n.', 'A snare or gin, especially one made of interwoven cords; a\n   net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lace', 'n.', 'A fabric of fine threads of linen, silk, cotton, etc., often\n   ornamented with figures; a delicate tissue of thread, much worn as an\n   ornament of dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lace', 'n.', 'Spirits added to coffee or some other beverage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laced', 'imp. & p. p.', 'of Lace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacing', 'p. pr. & vb. n.', 'of Lace');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lace', 'v. t.', 'To fasten with a lace; to draw together with a lace passed\n   through eyelet holes; to unite with a lace or laces, or, figuratively.\n   with anything resembling laces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lace', 'v. t.', 'To adorn with narrow strips or braids of some decorative\n   material; as, cloth laced with silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lace', 'v. t.', 'To beat; to lash; to make stripes on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lace', 'v. t.', 'To add spirits to (a beverage).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lace', 'v. i.', 'To be fastened with a lace, or laces; as, these boots\n   lace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lace-bark', 'n.', 'A shrub in the West Indies (Lagetta Iintearia); -- so\n   called from the lacelike layers of its inner bark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laced', 'a.', 'Fastened with a lace or laces; decorated with narrow strips\n   or braid. See Lace, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laced', 'v. t.', 'Decorated with the fabric lace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacedaemonian', 'a.', 'Of or pertaining to Lacedaemon or Sparta, the chief\n   city of Laconia in the Peloponnesus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacedaemonian', 'n.', 'A Spartan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacemen', 'pl. ', 'of Laceman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laceman', 'n.', 'A man who deals in lace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacerable', 'a.', 'That can be lacerated or torn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacerated', 'imp. & p. p.', 'of Lacerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacerating', 'p. pr. & vb. n.', 'of Lacerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacerate', 'v. t.', 'To tear; to rend; to separate by tearing; to mangle;\n   as, to lacerate the flesh. Hence: To afflict; to torture; as, to\n   lacerate the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacerate', 'p. a.', 'Alt. of Lacerated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacerated', 'p. a.', 'Rent; torn; mangled; as, a lacerated wound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacerated', 'p. a.', 'Jagged, or slashed irregularly, at the end, or along\n   the edge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laceration', 'n.', 'The act of lacerating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laceration', 'n.', 'A breach or wound made by lacerating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacerative', 'a.', 'Lacerating, or having the power to lacerate; as,\n   lacerative humors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacert', 'n.', 'A muscle of the human body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacerta', 'n.', 'A fathom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacerta', 'n.', 'A genus of lizards. See Lizard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacerta', 'n.', 'The Lizard, a northern constellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacertian', 'a.', 'Like a lizard; of or pertaining to the Lacertilia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacertian', 'n.', 'One of the Lacertilia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacertilia', 'n. pl.', 'An order of Reptilia, which includes the lizards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacertilian', 'a. & n.', 'Same as Lacertian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacertiloid', 'a.', 'Like or belonging to the Lacertilia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacertine', 'a.', 'Lacertian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacerti', 'pl. ', 'of Lacertus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacertus', 'n.', 'A bundle or fascicle of muscular fibers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacewing', 'n.', 'Any one of several species of neuropterous insects of the\n   genus Chrysopa and allied genera. They have delicate, lacelike wings\n   and brilliant eyes. Their larvae are useful in destroying aphids.\n   Called also lace-winged fly, and goldeneyed fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lace-winged', 'a.', 'Having thin, transparent, reticulated wings; as, the\n   lace-winged flies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laches', 'n.', 'Alt. of Lache');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lache', 'n.', 'Neglect; negligence; remissness; neglect to do a thing at\n   the proper time; delay to assert a claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lachrymable', 'a.', 'Lamentable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lachrymae Christi', '', 'A rich, sweet, red Neapolitan wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lachrymal', 'a.', 'Of or pertaining to tears; as, lachrymal effusions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lachrymal', 'a.', 'Pertaining to, or secreting, tears; as, the lachrymal\n   gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lachrymal', 'a.', 'Pertaining to the lachrymal organs; as, lachrymal bone;\n   lachrymal duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacrymal', 'n.', 'Alt. of Lacrymal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacrymal', 'n.', 'See Lachrymatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lachrymary', 'a.', 'Containing, or intended to contain, tears; lachrymal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lachrymate', 'v. i.', 'To weep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lachrymation', 'n.', 'The act of shedding tears; weeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'ries', 'pl. ', 'of Lachrymatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lachrymatory', 'n.', 'A \"tear-bottle;\" a narrow-necked vessel found in\n   sepulchers of the ancient Romans; -- so called from a former notion\n   that the tears of the deceased persons friends were collected in it.\n   Called also lachrymal or lacrymal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lachrymiform', 'a.', 'Having the form of a tear; tear-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lachrymose', 'a.', 'Generating or shedding tears; given to shedding tears;\n   suffused with tears; tearful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacing', 'n.', 'The act of securing, fastening, or tightening, with a lace\n   or laces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacing', 'n.', 'A lace; specifically (Mach.), a thong of thin leather for\n   uniting the ends of belts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacing', 'n.', 'A rope or line passing through eyelet holes in the edge of\n   a sail or an awning to attach it to a yard, gaff, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacing', 'n.', 'A system of bracing bars, not crossing each other in the\n   middle, connecting the channel bars of a compound strut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laciniae', 'pl. ', 'of Lacinia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacinia', 'n.', 'One of the narrow, jagged, irregular pieces or divisions\n   which form a sort of fringe on the borders of the petals of some\n   flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacinia', 'n.', 'A narrow, slender portion of the edge of a monophyllous\n   calyx, or of any irregularly incised leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacinia', 'n.', 'The posterior, inner process of the stipes on the maxillae\n   of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laciniate', 'a.', 'Alt. of Laciniated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laciniated', 'a.', 'Fringed; having a fringed border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laciniated', 'a.', 'Cut into deep, narrow, irregular lobes; slashed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laciniolate', 'a.', 'Consisting of, or abounding in, very minute laciniae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacinulae', 'pl. ', 'of Lacinula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacinulas', 'pl. ', 'of Lacinula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacinula', 'n.', 'A diminutive lacinia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lack', 'n.', 'Blame; cause of blame; fault; crime; offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lack', 'n.', 'Deficiency; want; need; destitution; failure; as, a lack of\n   sufficient food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacked', 'imp. & p. p.', 'of Lack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacking', 'p. pr. & vb. n.', 'of Lack');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lack', 'v. t.', 'To blame; to find fault with.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lack', 'v. t.', 'To be without or destitute of; to want; to need.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lack', 'v. i.', 'To be wanting; often, impersonally, with of, meaning, to\n   be less than, short, not quite, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lack', 'v. i.', 'To be in want.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lack', 'interj.', 'Exclamation of regret or surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lackadaisical', 'a.', 'Affectedly pensive; languidly sentimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lackadaisy', 'interj.', 'An expression of languor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lackadaisy', 'a.', 'Lackadaisical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lackaday', 'interj.', 'Alack the day; alas; -- an expression of sorrow,\n   regret, dissatisfaction, or surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lackbrain', 'n.', 'One who is deficient in understanding; a witless person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacker', 'n.', 'One who lacks or is in want.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacker', 'n. & v.', 'See Lacquer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lackeys', 'pl. ', 'of Lackey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lackey', 'v.', 'An attending male servant; a footman; a servile follower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lackey', 'v. t.', 'To attend as a lackey; to wait upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lackeyed', 'imp. & p. p.', 'of Lackey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lackeying', 'p. pr. & vb. n.', 'of Lackey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lackey', 'v. i.', 'To act or serve as lackey; to pay servile attendance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lackluster', 'n.', 'Alt. of Lacklustre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacklustre', 'n.', 'A want of luster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacklustre', 'a.', 'Wanting luster or brightness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacmus', 'n.', 'See Litmus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laconian', 'a.', 'Of or pertaining to Laconia, a division of ancient\n   Greece; Spartan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laconian', 'n.', 'An inhabitant of Laconia; esp., a Spartan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laconic', 'a.', 'Alt. of Laconical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laconical', 'a.', 'Expressing much in few words, after the manner of the\n   Laconians or Spartans; brief and pithy; brusque; epigrammatic. In this\n   sense laconic is the usual form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laconical', 'a.', 'Laconian; characteristic of, or like, the Spartans;\n   hence, stern or severe; cruel; unflinching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laconic', 'n.', 'Laconism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laconical', 'a.', 'See Laconic, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laconically', 'adv.', 'In a laconic manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'LaconIcism', 'n.', 'Same as Laconism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laconism', 'n.', 'A vigorous, brief manner of expression; laconic style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laconism', 'n.', 'An instance of laconic style or expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laconized', 'imp. & p. p.', 'of Laconize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laconizing', 'p. pr. & vb. n.', 'of Laconize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laconize', 'v. i.', 'To imitate the manner of the Laconians, especially in\n   brief, pithy speech, or in frugality and austerity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacquer', 'n.', 'A varnish, consisting of a solution of shell-lac in\n   alcohol, often colored with gamboge, saffron, or the like; -- used for\n   varnishing metals, papier-mache, and wood. The name is also given to\n   varnishes made of other ingredients, esp. the tough, solid varnish of\n   the Japanese, with which ornamental objects are made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacquered', 'imp. & p. p.', 'of Lacquer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacquering', 'p. pr. & vb. n.', 'of Lacquer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacquer', 'v. t.', 'To cover with lacquer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacquerer', 'n.', 'One who lacquers, especially one who makes a business of\n   lacquering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacquering', 'n.', 'The act or business of putting on lacquer; also, the\n   coat of lacquer put on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacrimoso', 'a.', 'Plaintive; -- a term applied to a mournful or pathetic\n   movement or style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacrosse', 'n.', 'A game of ball, originating among the North American\n   Indians, now the popular field sport of Canada, and played also in\n   England and the United States. Each player carries a long-handled\n   racket, called a \"crosse\". The ball is not handled but caught with the\n   crosse and carried on it, or tossed from it, the object being to carry\n   it or throw it through one of the goals placed at opposite ends of the\n   field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacrymal', 'n. & a.', 'See Lachrymatory, n., and Lachrymal, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacrymary', '', 'Alt. of Lacrymose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacrytory', '', 'Alt. of Lacrymose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacrymose', '', 'See Lachrymary, Lachrymatory, Lachrymose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactage', 'n.', 'The produce of animals yielding milk; milk and that which\n   is made from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactam', 'n.', 'One of a series of anhydrides of an amido type, analogous\n   to the lactones, as oxindol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactamic', 'a.', 'Pertaining to, or designating, an amido acid related to\n   lactic acid, and called also amido-propionic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactamide', 'n.', 'An acid amide derived from lactic acid, and obtained as\n   a white crystalline substance having a neutral reaction. It is\n   metameric with alanine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactant', 'a.', 'Suckling; giving suck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactarene', 'n.', 'A preparation of casein from milk, used in printing\n   calico.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactary', 'a.', 'Milky; full of white juice like milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactary', 'n.', 'a dairyhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactate', 'n.', 'A salt of lactic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactation', 'n.', 'A giving suck; the secretion and yielding of milk by the\n   mammary gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacteal', 'a.', 'Pertaining to, or resembling, milk; milky; as, the lacteal\n   fluid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacteal', 'a.', 'Pertaining to, or containing, chyle; as, the lacteal\n   vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacteal', 'n.', 'One of the lymphatic vessels which convey chyle from the\n   small intestine through the mesenteric glands to the thoracic duct; a\n   chyliferous vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacteally', 'adv.', 'Milkily; in the manner of milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactean', 'a.', 'Milky; consisting of, or resembling, milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactean', 'a.', 'Lacteal; conveying chyle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacteous', 'a.', 'Milky; resembling milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacteous', 'a.', 'Lacteal; conveying chyle; as, lacteous vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacteously', 'adv.', 'In a lacteous manner; after the manner of milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactescence', 'n.', 'The state or quality of producing milk, or milklike\n   juice; resemblance to milk; a milky color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactescence', 'n.', 'The latex of certain plants. See Latex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactescent', 'a.', 'Having a milky look; becoming milky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactescent', 'a.', 'Producing milk or a milklike juice or fluid, as the\n   milkweed. See Latex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactic', 'a.', 'Of or pertaining to milk; procured from sour milk or whey;\n   as, lactic acid; lactic fermentation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactide', 'n.', 'A white, crystalline substance, obtained from also, by\n   extension, any similar substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactiferous', 'a.', 'Bearing or containing milk or a milky fluid; as, the\n   lactiferous vessels, cells, or tissue of various vascular plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactific', 'a.', 'Alt. of Lactifical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactifical', 'a.', 'Producing or yielding milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactifuge', 'n.', 'A medicine to check the secretion of milk, or to dispel\n   a supposed accumulation of milk in any part of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactim', 'n.', 'One of a series of anhydrides resembling the lactams, but\n   of an imido type; as, isatine is a lactim. Cf. Lactam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactimide', 'n.', 'A white, crystalline substance obtained as an anhydride\n   of alanine, and regarded as an imido derivative of lactic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactin', 'n.', 'See Lactose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactoabumin', 'n.', 'The albumin present on milk, apparently identical with\n   ordinary serum albumin. It is distinct from the casein of milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactobutyrometer', 'n.', 'An instrument for determining the amount of\n   butter fat contained in a given sample of milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactodensimeter', 'n.', 'A form of hydrometer, specially graduated, for\n   finding the density of milk, and thus discovering whether it has been\n   mixed with water or some of the cream has been removed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactometer', 'n.', 'An instrument for estimating the purity or richness of\n   milk, as a measuring glass, a specific gravity bulb, or other\n   apparatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactone', 'n.', 'One of a series of organic compounds, regarded as\n   anhydrides of certain hydroxy acids. In general, they are colorless\n   liquids, having a weak aromatic odor. They are so called because the\n   typical lactone is derived from lactic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactonic', 'a.', 'Of, pertaining to, or derived from, lactone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactonic', 'a.', 'Pertaining to, or designating, an acid obtained by the\n   oxidation of milk sugar (lactose).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactoprotein', 'n.', 'A peculiar albuminous body considered a normal\n   constituent of milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactory', 'a.', 'Lactiferous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactoscope', 'n.', 'An instrument for estimating the amount of cream\n   contained in milk by ascertaining its relative opacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactose', 'n.', 'Sugar of milk or milk sugar; a crystalline sugar present\n   in milk, and separable from the whey by evaporation and\n   crystallization. It has a slightly sweet taste, is dextrorotary, and is\n   much less soluble in water than either cane sugar or glucose. Formerly\n   called lactin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactose', 'n.', 'See Galactose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactuca', 'n.', 'A genus of composite herbs, several of which are\n   cultivated foe salad; lettuce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactucarium', 'n.', 'The inspissated juice of the common lettuce, sometimes\n   used as a substitute for opium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactucic', 'a.', 'Pertaining to, or derived from, the juice of the Lactuca\n   virosa; -- said of certain acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactucin', 'n.', 'A white, crystalline substance, having a bitter taste and\n   a neutral reaction, and forming one of the essential ingredients of\n   lactucarium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactucone', 'n.', 'A white, crystalline, tasteless substance, found in the\n   milky sap of species of Lactuca, and constituting an essential\n   ingredient of lactucarium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacturamic', 'a.', 'Pertaining to, or designating, an organic amido acid,\n   which is regarded as a derivative of lactic acid and urea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lactyl', 'n.', 'An organic residue or radical derived from lactic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacunae', 'pl. ', 'of Lacuna');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacunas', 'pl. ', 'of Lacuna');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacuna', 'n.', 'A small opening; a small pit or depression; a small blank\n   space; a gap or vacancy; a hiatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacuna', 'n.', 'A small opening; a small depression or cavity; a space, as\n   a vacant space between the cells of plants, or one of the spaces left\n   among the tissues of the lower animals, which serve in place of vessels\n   for the circulation of the body fluids, or the cavity or sac, usually\n   of very small size, in a mucous membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacunal', 'a.', 'Alt. of Lacunar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacunar', 'a.', 'Pertaining to, or having, lacunae; as, a lacunar\n   circulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacunars', 'pl. ', 'of Lacunar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacunaria', 'pl. ', 'of Lacunar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacunar', 'n.', 'The ceiling or under surface of any part, especially when\n   it consists of compartments, sunk or hollowed without spaces or bands\n   between the panels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacunar', 'n.', 'One of the sunken panels in such a ceiling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacune', 'n.', 'A lacuna.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacunose', 'a.', 'Alt. of Lacunous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacunous', 'a.', 'Furrowed or pitted; having shallow cavities or lacunae;\n   as, a lacunose leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacustral', 'a.', 'Alt. of Lacustrine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacustrine', 'a.', 'Found in, or pertaining to, lakes or ponds, or growing\n   in them; as, lacustrine flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacwork', 'n.', 'Ornamentation by means of lacquer painted or carved, or\n   simply colored, sprinkled with gold or the like; -- said especially of\n   Oriental work of this kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lad', '', 'p. p. of Lead, to guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lad', 'n.', 'A boy; a youth; a stripling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lad', 'n.', 'A companion; a comrade; a mate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladanum', 'n.', 'A gum resin gathered from certain Oriental species of\n   Cistus. It has a pungent odor and is chiefly used in making plasters,\n   and for fumigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladde', 'obs. imp.', 'of Lead, to guide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladder', 'v. i.', 'A frame usually portable, of wood, metal, or rope, for\n   ascent and descent, consisting of two side pieces to which are fastened\n   cross strips or rounds forming steps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladder', 'v. i.', 'That which resembles a ladder in form or use; hence,\n   that by means of which one attains to eminence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laddie', 'n.', 'A lad; a male sweetheart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laded', 'imp.', 'of Lade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laded', 'p. p.', 'of Lade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laded', '', 'of Lade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lading', 'p. pr. & vb. n.', 'of Lade');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lade', 'v. t.', 'To load; to put a burden or freight on or in; -- generally\n   followed by that which receives the load, as the direct object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lade', 'v. t.', 'To throw in out. with a ladle or dipper; to dip; as, to\n   lade water out of a tub, or into a cistern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lade', 'v. t.', 'To transfer (the molten glass) from the pot to the forming\n   table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lade', 'v. t.', 'To draw water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lade', 'v. t.', 'To admit water by leakage, as a ship, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lade', 'n.', 'The mouth of a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lade', 'n.', 'A passage for water; a ditch or drain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lademan', 'n.', 'One who leads a pack horse; a millers servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laden', 'p. & a.', 'Loaded; freighted; burdened; as, a laden vessel; a\n   laden heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladied', 'a.', 'Ladylike; not rough; gentle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladies eardrops', '', 'The small-flowered Fuchsia (F. coccinea), and other\n   closely related species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladify', 'v. t.', 'To make a lady of; to make ladylike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladin', 'n.', 'A Romansch dialect spoken in some parts of Switzerland and\n   the Tyrol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lading', 'n.', 'The act of loading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lading', 'n.', 'That which lades or constitutes a load or cargo; freight;\n   burden; as, the lading of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladinos', 'pl. ', 'of Ladino');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladino', 'n.', 'One of the half-breed descendants of whites and Indians; a\n   mestizo; -- so called throughout Central America. They are usually of a\n   yellowish orange tinge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladkin', 'n.', 'A little lad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladle', 'v. t.', 'A cuplike spoon, often of large size, with a long handle,\n   used in lading or dipping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladle', 'v. t.', 'A vessel to carry liquid metal from the furnace to the\n   mold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladle', 'v. t.', 'The float of a mill wheel; -- called also ladle board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladle', 'v. t.', 'An instrument for drawing the charge of a cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladle', 'v. t.', 'A ring, with a handle or handles fitted to it, for\n   carrying shot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladled', 'imp. & p. p.', 'of Ladle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladling', 'p. pr. & vb. n.', 'of Ladle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladle', 'v. t.', 'To take up and convey in a ladle; to dip with, or as\n   with, a ladle; as, to ladle out soup; to ladle oatmeal into a kettle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladlefuls', 'pl. ', 'of Ladleful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladleful', 'n.', 'A quantity sufficient to fill a ladle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladrone', 'n.', 'A robber; a pirate; hence, loosely, a rogue or rascal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladies', 'pl. ', 'of Lady');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lady', 'n.', 'A woman who looks after the domestic affairs of a family; a\n   mistress; the female head of a household.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lady', 'n.', 'A woman having proprietary rights or authority; mistress; --\n   a feminine correlative of lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lady', 'n.', 'A woman to whom the particular homage of a knight was paid; a\n   woman to whom one is devoted or bound; a sweetheart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lady', 'n.', 'A woman of social distinction or position. In England, a\n   title prefixed to the name of any woman whose husband is not of lower\n   rank than a baron, or whose father was a nobleman not lower than an\n   earl. The wife of a baronet or knight has the title of Lady by\n   courtesy, but not by right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lady', 'n.', 'A woman of refined or gentle manners; a well-bred woman; --\n   the feminine correlative of gentleman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lady', 'n.', 'A wife; -- not now in approved usage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lady', 'n.', 'The triturating apparatus in the stomach of a lobster; -- so\n   called from a fancied resemblance to a seated female figure. It\n   consists of calcareous plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lady', 'a.', 'Belonging or becoming to a lady; ladylike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladybird', 'n.', 'Any one of numerous species of small beetles of the genus\n   Coccinella and allied genera (family Coccinellidae); -- called also\n   ladybug, ladyclock, lady cow, lady fly, and lady beetle. Coccinella\n   seplempunctata in one of the common European species. See Coccinella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladybug', 'n.', 'Same as Ladybird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladyclock', 'n.', 'See Ladyrird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lady', '', 'The day of the annunciation of the Virgin Mary, March 25. See\n   Annunciation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladyfish', 'n.', 'A large, handsome oceanic fish (Albula vulpes), found\n   both in the Atlantic and Pacific oceans; -- called also bonefish,\n   grubber, French mullet, and macabe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladyfish', 'n.', 'A labroid fish (Harpe rufa) of Florida and the West\n   Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladyhood', 'n.', 'The state or quality of being a lady; the personality of\n   a lady.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lady-killer', 'n.', 'A gallant who captivates the hearts of women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lady-killing', 'n.', 'The art or practice of captivating the hearts of\n   women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladykin', 'n.', 'A little lady; -- applied by the writers of Queen\n   Elizabeths time, in the abbreviated form Lakin, to the Virgin Mary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladylike', 'a.', 'Like a lady in appearance or manners; well-bred.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladylike', 'a.', 'Becoming or suitable to a lady; as, ladylike manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladylike', 'a.', 'Delicate; tender; feeble; effeminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladylikeness', 'n.', 'The quality or state of being ladylike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladylove', 'n.', 'A sweetheart or mistress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys bedstraw', '', 'The common bedstraw (Galium verum); also, a\n   slender-leaved East Indian shrub (Pharnaceum Mollugo), with white\n   flowers in umbels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys bower', '', 'A climbing plant with fragrant blossoms (Clematis\n   vitalba).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys comb', '', 'An umbelliferous plant (Scandix Pecten-Veneris), its\n   clusters of long slender fruits remotely resembling a comb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys cushion', '', 'An herb growing in dense tufts; the thrift (Armeria\n   vulgaris).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys finger', '', 'The kidney vetch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys finger', '', 'A variety of small cake of about the dimensions of a\n   finger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys finger', '', 'A long, slender variety of the potato.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys finger', '', 'One of the branchiae of the lobster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys garters', '', 'Ribbon grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys hair', '', 'A plant of the genus Briza (B. media); a variety of\n   quaking grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladyship', 'n.', 'The rank or position of a lady; -- given as a title\n   (preceded by her or your).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys laces', '', 'A slender climbing plant; dodder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys looking-glass', '', 'See Venuss looking-glass, under Venus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys mantle', '', 'A genus of rosaceous herbs (Alchemilla), esp. the\n   European A. vulgaris, which has leaves with rounded and finely serrated\n   lobes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys seal', '', 'The European Solomons seal (Polygonatum verticillatum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys seal', '', 'The black bryony (Tamus communis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys slipper', '', 'Any orchidaceous plant of the genus Cypripedium, the\n   labellum of which resembles a slipper. Less commonly, in the United\n   States, the garden balsam (Impatiens Balsamina).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys smock', '', 'A plant of the genus Cardamine (C. pratensis); cuckoo\n   flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys thimble', '', 'The harebell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys thumb', '', 'An annual weed (Polygonum Persicaria), having a\n   lanceolate leaf with a dark spot in the middle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladys traces', '', 'Alt. of Ladies tresses');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ladies tresses', '', 'A name given to several species of the orchidaceous\n   genus Spiranthes, in which the white flowers are set in spirals about a\n   slender axis and remotely resemble braided hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laelaps', 'n.', 'A genus of huge, carnivorous, dinosaurian reptiles from\n   the Cretaceous formation of the United States. They had very large hind\n   legs and tail, and are supposed to have been bipedal. Some of the\n   species were about eighteen feet high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laemmergeyer', 'n.', 'See Lammergeir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laemodipod', 'n.', 'One of the Laemodipoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laemodipoda', 'n. pl.', 'A division of amphipod Crustacea, in which the\n   abdomen is small or rudimentary and the legs are often reduced to five\n   pairs. The whale louse, or Cyamus, and Caprella are examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laemodipodous', 'a.', 'Of or pertaining to the Laemodipoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laetere Sunday', '', 'The fourth Sunday of Lent; -- so named from the Latin\n   word Laetare (rejoice), the first word in the antiphone of the introit\n   sung that day in the Roman Catholic service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laevigate', 'a.', 'Having a smooth surface, as if polished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laevo-', '', 'A prefix. See Levo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laevorotatory', 'a.', 'Same as Levorotatory. Cf. Dextrorotatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laevulose', 'n.', 'See Levulose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lafayette', 'n.', 'The dollar fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lafayette', 'n.', 'A market fish, the goody, or spot (Liostomus xanthurus),\n   of the southern coast of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laft', '', 'p. p. of Leave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lafte', '', 'imp. of Leave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lag', 'a.', 'Coming tardily after or behind; slow; tardy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lag', 'a.', 'Last; long-delayed; -- obsolete, except in the phrase lag end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lag', 'a.', 'Last made; hence, made of refuse; inferior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lag', 'n.', 'One who lags; that which comes in last.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lag', 'n.', 'The fag-end; the rump; hence, the lowest class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lag', 'n.', 'The amount of retardation of anything, as of a valve in a\n   steam engine, in opening or closing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lag', 'n.', 'A stave of a cask, drum, etc.; especially (Mach.), one of the\n   narrow boards or staves forming the covering of a cylindrical object,\n   as a boiler, or the cylinder of a carding machine or a steam engine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lag', 'n.', 'See Graylag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagged', 'imp. & p. p.', 'of Lag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagging', 'p. pr. & vb. n.', 'of Lag');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lag', 'v. i.', 'To walk or more slowly; to stay or fall behind; to linger\n   or loiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lag', 'v. t.', 'To cause to lag; to slacken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lag', 'v. t.', 'To cover, as the cylinder of a steam engine, with lags. See\n   Lag, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lag', 'n.', 'One transported for a crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lag', 'v. t.', 'To transport for crime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagan', 'n. & v.', 'See Ligan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagarto', 'n.', 'An alligator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagenae', 'pl. ', 'of Lagena');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagenas', 'pl. ', 'of Lagena');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagena', 'n.', 'The terminal part of the cochlea in birds and most\n   reptiles; an appendage of the sacculus, corresponding to the cochlea,\n   in fishes and amphibians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagenian', 'a.', 'Like, or pertaining to, Lagena, a genus of Foraminifera\n   having a straight, chambered shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lageniform', 'a.', 'Shaped like a bottle or flask; flag-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lager', 'n.', 'Lager beer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lager beer', '', 'Originally a German beer, but now also made in immense\n   quantities in the United States; -- so called from its being laid up or\n   stored for some months before use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lager wine', '', 'Wine which has been kept for some time in the cellar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laggard', 'a.', 'Slow; sluggish; backward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laggard', 'n.', 'One who lags; a loiterer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagger', 'n.', 'A laggard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagging', 'n.', 'The clothing (esp., an outer, wooden covering), as of a\n   steam cylinder, applied to prevent the radiation of heat; a covering of\n   lags; -- called also deading and cleading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagging', 'n.', 'Lags, collectively; narrow planks extending from one rib\n   to another in the centering of arches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laggingly', 'adv.', 'In a lagging manner; loiteringly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagly', 'adv.', 'Laggingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagomorph', 'n.', 'One of the Lagomorpha.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagemorpha', 'n. pl.', 'A group of rodents, including the hares. They have\n   four incisors in the upper jaw. Called also Duplicidentata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagoon', 'n.', 'A shallow sound, channel, pond, or lake, especially one\n   into which the sea flows; as, the lagoons of Venice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagoon', 'n.', 'A lake in a coral island, often occupying a large portion\n   of its area, and usually communicating with the sea. See Atoll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagophthalmia', 'n.', 'Alt. of Lagophthalmos');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagophthalmos', 'n.', 'A morbid condition in which the eye stands wide\n   open, giving a peculiar staring appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagopous', 'a.', 'Having a dense covering of long hair, like the foot of a\n   hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lagune', 'n.', 'See Lagoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laic', 'a.', 'Alt. of Laical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laical', 'a.', 'Of or pertaining to a layman or the laity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laic', 'n.', 'A layman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laicality', 'n.', 'The state or quality of being laic; the state or\n   condition of a layman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laically', 'adv.', 'As a layman; after the manner of a layman; as, to treat\n   a matter laically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laid', 'imp. & p. p.', 'of Lay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laidly', 'a.', 'Ugly; loathsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lain', 'p. p.', 'of Lie, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lainere', 'n.', 'See Lanier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lair', 'n.', 'A place in which to lie or rest; especially, the bed or couch\n   of a wild beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lair', 'n.', 'A burying place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lair', 'n.', 'A pasture; sometimes, food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laird', 'n.', 'A lord; a landholder, esp. one who holds land directly of\n   the crown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lairdship', 'n.', 'The state of being a laird; an estate; landed property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laism', 'n.', 'See Lamaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laissez faire', '', 'Noninterference; -- an axiom of some political\n   economists, deprecating interference of government by attempts to\n   foster or regulate commerce, manufactures, etc., by bounty or by\n   restriction; as, the doctrine of laissez faire; the laissez faire\n   system government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laity', 'a.', 'The people, as distinguished from the clergy; the body of\n   the people not in orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laity', 'a.', 'The state of a layman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laity', 'a.', 'Those who are not of a certain profession, as law or\n   medicine, in distinction from those belonging to it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lakao', 'n.', 'Sap green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lake', 'n.', 'A pigment formed by combining some coloring matter, usually\n   by precipitation, with a metallic oxide or earth, esp. with aluminium\n   hydrate; as, madder lake; Florentine lake; yellow lake, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lake', 'n.', 'A kind of fine white linen, formerly in use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lake', 'v. i.', 'To play; to sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lake', 'n.', 'A large body of water contained in a depression of the\n   earths surface, and supplied from the drainage of a more or less\n   extended area.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lake-dweller', 'n.', 'See Lake dwellers, under Lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lakelet', 'n.', 'A little lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lakeweed', 'n.', 'The water pepper (Polygonum Hydropiper), an aquatic plant\n   of Europe and North America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lakh', 'n.', 'Same as Lac, one hundred thousand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lakin', 'n.', 'See Ladykin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lakke', 'n. & v.', 'See Lack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laky', 'a.', 'Pertaining to a lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laky', 'a.', 'Transparent; -- said of blood rendered transparent by the\n   action of some solvent agent on the red blood corpuscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lallation', 'n.', 'An imperfect enunciation of the letter r, in which it\n   sounds like l.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lalo', 'n.', 'The powdered leaves of the baobab tree, used by the Africans\n   to mix in their soup, as the southern negroes use powdered sassafras.\n   Cf. Couscous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lammed', 'imp. & p. p.', 'of Lam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamming', 'p. pr. & vb. n.', 'of Lam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lam', 'v. t.', 'To beat soundly; to thrash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lama', 'n.', 'See Llama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lama', 'n.', 'In Thibet, Mongolia, etc., a priest or monk of the belief\n   called Lamaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamaic', 'a.', 'Of or pertaining to Lamaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamaism', 'n.', 'A modified form of Buddhism which prevails in Thibet,\n   Mongolia, and some adjacent parts of Asia; -- so called from the name\n   of its priests. See 2d Lama.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamaist', 'n.', 'Alt. of Lamaite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamaite', 'n.', 'One who believes in Lamaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamaistic', 'a.', 'Of or pertaining to Lamaism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamantin', 'n.', 'The manatee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamarckian', 'a.', 'Pertaining to, or involved in, the doctrines of\n   Lamarckianism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamarckianism', 'n.', 'Lamarckism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamarckism', 'n.', 'The theory that structural variations, characteristic\n   of species and genera, are produced in animals and plants by the direct\n   influence of physical environments, and esp., in the case of animals,\n   by effort, or by use or disuse of certain organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamasery', 'n.', 'A monastery or convent of lamas, in Thibet, Mongolia,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamb', 'n.', 'The young of the sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamb', 'n.', 'Any person who is as innocent or gentle as a lamb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamb', 'n.', 'A simple, unsophisticated person; in the cant of the Stock\n   Exchange, one who ignorantly speculates and is victimized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambed', 'imp. & p. p.', 'of Lamb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambing', 'p. pr. & vb. n.', 'of Lamb');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamb', 'v. i.', 'To bring forth a lamb or lambs, as sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambale', 'n.', 'A feast at the time of shearing lambs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambaste', 'v. t.', 'To beat severely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambative', 'a.', 'Taken by licking with the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambative', 'n.', 'A medicine taken by licking with the tongue; a lincture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambda', 'n.', 'The name of the Greek letter /, /, corresponding with the\n   English letter L, l.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambda', 'n.', 'The point of junction of the sagittal and lambdoid sutures\n   of the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambdacism', 'n.', 'A fault in speaking or in composition, which consists\n   in too frequent use of the letter l, or in doubling it erroneously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambdacism', 'n.', 'A defect in pronunciation of the letter l when doubled,\n   which consists in giving it a sound as if followed by y, similar to\n   that of the letters lli in billion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambdacism', 'n.', 'The use of the sound of l for that of r in\n   pronunciation; lallation; as, Amelican for American.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambdoid', 'a.', 'Shaped like the Greek letter lambda (/); as, the lambdoid\n   suture between the occipital and parietal bones of the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambdoidal', 'a.', 'Same as Lambdoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambent', 'a.', 'Playing on the surface; touching lightly; gliding over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambent', 'a.', 'Twinkling or gleaming; fickering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambert pine', '', 'The gigantic sugar pine of California and Oregon (Pinus\n   Lambertiana). It has the leaves in fives, and cones a foot long. The\n   timber is soft, and like that of the white pine of the Eastern States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambkin', 'n.', 'A small lamb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamblike', 'a.', 'Like a lamb; gentle; meek; inoffensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamboys', 'n. pl.', 'Same as Base, n., 19.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambrequin', 'n.', 'A kind of pendent scarf or covering attached to the\n   helmet, to protect it from wet or heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambrequin', 'n.', 'A leather flap hanging from a cuirass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambrequin', 'n.', 'A piece of ornament drapery or short decorative\n   hanging, pendent from a shelf or from the casing above a window, hiding\n   the curtain fixtures, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambskin', 'n.', 'The skin of a lamb; especially, a skin dressed with the\n   wool on, and used as a mat. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambskin', 'n.', 'A kind of woolen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambskinnet', 'n.', 'See Lansquenet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lambs-quarters', 'n.', 'A name given to several plants of the Goosefoot\n   family, sometimes used as pot herbs, as Chenopodium album and Atriplex\n   patulsa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamdoidal', 'a.', 'Lambdoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lame', 'superl.', 'Moving with pain or difficulty on account of injury,\n   defect, or temporary obstruction of a function; as, a lame leg, arm, or\n   muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lame', 'superl.', 'To some degree disabled by reason of the imperfect\n   action of a limb; crippled; as, a lame man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lame', 'superl.', 'Hence, hobbling; limping; inefficient; imperfect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamed', 'imp. & p. p.', 'of Lame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laming', 'p. pr. & vb. n.', 'of Lame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lame', 'v. t.', 'To make lame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamel', 'n.', 'See Lamella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellae', 'pl. ', 'of Lamella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellas', 'pl. ', 'of Lamella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamella', 'n.', 'a thin plate or scale of anything, as a thin scale growing\n   from the petals of certain flowers; or one of the thin plates or scales\n   of which certain shells are composed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellar', 'a.', 'Flat and thin; lamelliform; composed of lamellae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellarly', 'adv.', 'In thin plates or scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellary', 'a.', 'Of or pertaining to lamella or to lamellae; lamellar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellate', 'a.', 'Alt. of Lamellated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellated', 'a.', 'Composed of, or furnished with, thin plates or scales.\n   See Illust. of Antennae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellibranch', 'n.', 'One of the Lamellibranchia. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellibranchia', 'n. pl.', 'Alt. of Lamellibranchiata');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellibranchiata', 'n. pl.', 'A class of Mollusca including all those that\n   have bivalve shells, as the clams, oysters, mussels, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellibranchiate', 'a.', 'Having lamellar gills; belonging to the\n   Lamellibranchia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellibranchiate', 'n.', 'One of the Lamellibranchia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellicorn', 'a.', 'Having antennae terminating in a group of flat\n   lamellae; -- said of certain coleopterous insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellicorn', 'a.', 'Terminating in a group of flat lamellae; -- said of\n   antennae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellicorn', 'n.', 'A lamellicorn insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellicornia', 'n. pl.', 'A group of lamellicorn, plant-eating beetles; --\n   called also Lamellicornes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamelliferous', 'a.', 'Bearing, or composed of, lamellae, or thin layers,\n   plates, or scales; foliated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamelliform', 'a.', 'Thin and flat; scalelike; lamellar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellirostral', 'a.', 'Having a lamellate bill, as ducks and geese.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellirostres', 'n. pl.', 'A group of birds embracing the Anseres and\n   flamingoes, in which the bill is lamellate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamellose', 'a.', 'Composed of, or having, lamellae; lamelliform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamely', 'adv.', 'An a lame, crippled, disabled, or imperfect manner; as,\n   to walk lamely; a figure lamely drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lameness', 'n.', 'The condition or quality of being lame; as, the lameness\n   of an excuse or an argument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lament', 'v. i.', 'To express or feel sorrow; to weep or wail; to mourn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamented', 'imp. & p. p.', 'of Lament');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamenting', 'p. pr. & vb. n.', 'of Lament');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lament', 'v. t.', 'To mourn for; to bemoan; to bewail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lament', 'v.', 'Grief or sorrow expressed in complaints or cries;\n   lamentation; a wailing; a moaning; a weeping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lament', 'v.', 'An elegy or mournful ballad, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamentable', 'a.', 'Mourning; sorrowful; expressing grief; as, a lamentable\n   countenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamentable', 'a.', 'Fitted to awaken lament; to be lamented; sorrowful;\n   pitiable; as, a lamentable misfortune, or error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamentable', 'a.', 'Miserable; pitiful; paltry; -- in a contemptuous or\n   ridiculous sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamentation', 'n.', 'The act of bewailing; audible expression of sorrow;\n   wailing; moaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamentation', 'n.', 'A book of the Old Testament attributed to the prophet\n   Jeremiah, and taking its name from the nature of its contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamented', 'a.', 'Mourned for; bewailed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamenter', 'n.', 'One who laments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamentin', 'n.', 'See Lamantin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamenting', 'n.', 'Lamentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamentingly', 'adv.', 'In a lamenting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lames', 'n. pl.', 'Small steel plates combined together so as to slide one\n   upon the other and form a piece of armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lametta', 'n.', 'Foil or wire made of gold, silver, or brass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamia', 'n.', 'A monster capable of assuming a womans form, who was said\n   to devour human beings or suck their blood; a vampire; a sorceress; a\n   witch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminae', 'pl. ', 'of Lamina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminas', 'pl. ', 'of Lamina');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamina', 'n.', 'A thin plate or scale; a layer or coat lying over another;\n   -- said of thin plates or platelike substances, as of bone or minerals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamina', 'n.', 'The blade of a leaf; the broad, expanded portion of a petal\n   or sepal of a flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamina', 'n.', 'A thin plate or scale; specif., one of the thin, flat\n   processes composing the vane of a feather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminability', 'n.', 'The quality or state of being laminable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminable', 'a.', 'Capable of being split into laminae or thin plates, as\n   mica; capable of being extended under pressure into a thin plate or\n   strip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminar', 'a.', 'Alt. of Laminal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminal', 'a.', 'In, or consisting of, thin plates or layers; having the\n   form of a thin plate or lamina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminaria', 'n.', 'A genus of great seaweeds with long and broad fronds;\n   kelp, or devils apron. The fronds commonly grow in clusters, and are\n   sometimes from thirty to fifty feet in length. See Illust. of Kelp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminarian', 'a.', 'Pertaining to seaweeds of the genus Laminaria, or to\n   that zone of the sea (from two to ten fathoms in depth) where the\n   seaweeds of this genus grow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminarite', 'n.', 'A broad-leafed fossil alga.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminary', 'a.', 'Laminar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminate', 'a.', 'Consisting of, or covered with, laminae, or thin plates,\n   scales, or layers, one over another; laminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminated', 'imp. & p. p.', 'of Laminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminating', 'p. pr. & vb. n.', 'of Laminate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminate', 'v. t.', 'To cause to separate into thin plates or layers; to\n   divide into thin plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminate', 'v. t.', 'To form, as metal, into a thin plate, as by rolling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminate', 'v. i.', 'To separate into laminae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminated', 'a.', 'Laminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminating', 'a.', 'Forming, or separating into, scales or thin layers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamination', 'n.', 'The process of laminating, or the state of being\n   laminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminiferous', 'a.', 'Having a structure consisting of laminae, or thin\n   layers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminiplantar', 'a.', 'Having the tarsus covered behind with a horny sheath\n   continuous on both sides, as in most singing birds, except the larks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laminitis', 'n.', 'Inflammation of the laminae or fleshy plates along the\n   coffin bone of a horse; founder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamish', 'a.', 'Somewhat lame.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamm', 'v. t.', 'See Lam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lammas', 'n.', 'The first day of August; -- called also Lammas day, and\n   Lammastide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lammergeir', 'n.', 'Alt. of Lammergeier');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lammergeier', 'n.', 'A very large vulture (Gypaetus barbatus), which\n   inhabits the mountains of Southern Europe, Asia, and Northern Africa.\n   When full-grown it is nine or ten feet in extent of wings. It is\n   brownish black above, with the under parts and neck rusty yellow; the\n   forehead and crown white; the sides of the head and beard black. It\n   feeds partly on carrion and partly on small animals, which it kills. It\n   has the habit of carrying tortoises and marrow bones to a great height,\n   and dropping them on stones to obtain the contents, and is therefore\n   called bonebreaker and ossifrage. It is supposed to be the ossifrage of\n   the Bible. Called also bearded vulture and bearded eagle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamnunguia', 'n. pl.', 'Same as Hyracoidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamp', 'n.', 'A thin plate or lamina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamp', 'n.', 'A light-producing vessel, instrument or apparatus;\n   especially, a vessel with a wick used for the combustion of oil or\n   other inflammable liquid, for the purpose of producing artificial\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamp', 'n.', 'Figuratively, anything which enlightens intellectually or\n   morally; anything regarded metaphorically a performing the uses of a\n   lamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamp', 'n.', 'A device or mechanism for producing light by electricity. See\n   Incandescent lamp, under Incandescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampad', 'n.', 'A lamp or candlestick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampadist', 'n.', 'One who gained the prize in the lampadrome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampadrome', 'n.', 'A race run by young men with lighted torches in their\n   hands. He who reached the goal first, with his torch unextinguished,\n   gained the prize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampas', 'n.', 'An inflammation and swelling of the soft parts of the roof\n   of the mouth immediately behind the fore teeth in the horse; -- called\n   also lampers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampate', 'n.', 'A supposed salt of lampic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampblack', 'n.', 'The fine impalpable soot obtained from the smoke of\n   carbonaceous substances which have been only partly burnt, as in the\n   flame of a smoking lamp. It consists of finely divided carbon, with\n   sometimes a very small proportion of various impurities. It is used as\n   an ingredient of printers ink, and various black pigments and cements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamper eel', '', 'See Lamprey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampern', 'n.', 'The river lamprey (Ammocoetes, / Lampetra, fluviatilis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampers', 'n.', 'See Lampas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampic', 'a.', 'Pertaining to, or produced by, a lamp; -- formerly said of\n   a supposed acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamping', 'a.', 'Shining; brilliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampless', 'a.', 'Being without a lamp, or without light; hence, being\n   without appreciation; dull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamplight', 'n.', 'Light from a lamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamplighter', 'n.', 'One who, or that which, lights a lamp; esp., a person\n   who lights street lamps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamplighter', 'n.', 'The calico bass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampoon', 'n.', 'A personal satire in writing; usually, malicious and\n   abusive censure written only to reproach and distress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampooned', 'imp. & p. p.', 'of Lampoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampooning', 'p. pr. & vb. n.', 'of Lampoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampoon', 'v. t.', 'To subject to abusive ridicule expressed in writing; to\n   make the subject of a lampoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampooner', 'n.', 'The writer of a lampoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampoonry', 'n.', 'The act of lampooning; a lampoon, or lampoons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamp-post', 'n.', 'A post (generally a pillar of iron) supporting a lamp or\n   lantern for lighting a street, park, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamprel', 'n.', 'See Lamprey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampreys', 'pl. ', 'of Lamprey');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lamprey', 'n.', 'An eel-like marsipobranch of the genus Petromyzon, and\n   allied genera. The lampreys have a round, sucking mouth, without jaws,\n   but set with numerous minute teeth, and one to three larger teeth on\n   the palate (see Illust. of Cyclostomi). There are seven small branchial\n   openings on each side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampron', 'n.', 'See Lamprey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampyrine', 'n.', 'An insect of the genus Lampyris, or family Lampyridae.\n   See Lampyris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lampyris', 'n.', 'A genus of coleopterous insects, including the glowworms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanarkite', 'n.', 'A mineral consisting of sulphate of lead, occurring\n   either massive or in long slender prisms, of a greenish white or gray\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanary', 'n.', 'A place for storing wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanate', '', 'Alt. of Lanated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanated', '', 'Wooly; covered with fine long hair, or hairlike filaments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacashire boiler', '', 'A steam boiler having two flues which contain the\n   furnaces and extend through the boiler from end to end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lacasterian', 'a.', 'Of or pertaining to the monitorial system of\n   instruction followed by Joseph Lancaster, of England, in which advanced\n   pupils in a school teach pupils below them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lance', 'n.', 'A weapon of war, consisting of a long shaft or handle and a\n   steel blade or head; a spear carried by horsemen, and often decorated\n   with a small flag; also, a spear or harpoon used by whalers and\n   fishermen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lance', 'n.', 'A soldier armed with a lance; a lancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lance', 'n.', 'A small iron rod which suspends the core of the mold in\n   casting a shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lance', 'n.', 'An instrument which conveys the charge of a piece of\n   ordnance and forces it home.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lance', 'n.', 'One of the small paper cases filled with combustible\n   composition, which mark the outlines of a figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanced', 'imp. & p. p.', 'of Lance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancing', 'p. pr. & vb. n.', 'of Lance');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lance', 'v. t.', 'To pierce with a lance, or with any similar weapon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lance', 'v. t.', 'To open with a lancet; to pierce; as, to lance a vein or\n   an abscess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lance', 'v. t.', 'To throw in the manner of a lance. See Lanch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lance fish', '', 'A slender marine fish of the genus Ammodytes, especially\n   Ammodytes tobianus of the English coast; -- called also sand lance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancegay', 'n.', 'Alt. of Lancegaye');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancegaye', 'n.', 'A kind of spear anciently used. Its use was prohibited\n   by a statute of Richard II.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancelet', 'n.', 'A small fishlike animal (Amphioxus lanceolatus),\n   remarkable for the rudimentary condition of its organs. It is the type\n   of the class Leptocardia. See Amphioxus, Leptocardia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancely', 'a.', 'Like a lance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanceolar', 'a.', 'Lanceolate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanceolate', 'a.', 'Alt. of Lanceolated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanceolated', 'a.', 'Rather narrow, tapering to a point at the apex, and\n   sometimes at the base also; as, a lanceolate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancepesade', 'n.', 'An assistant to a corporal; a private performing the\n   duties of a corporal; -- called also lance corporal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancer', 'n.', 'One who lances; one who carries a lance; especially, a\n   member of a mounted body of men armed with lances, attached to the\n   cavalry service of some nations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancer', 'n.', 'A lancet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancer', 'n.', 'A set of quadrilles of a certain arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancet', 'n.', 'A surgical instrument of various forms, commonly\n   sharp-pointed and two-edged, used in venesection, and in opening\n   abscesses, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancet', 'n.', 'An iron bar used for tapping a melting furnace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancewood', 'n.', 'A tough, elastic wood, often used for the shafts of\n   gigs, archery bows, fishing rods, and the like. Also, the tree which\n   produces this wood, Duguetia Quitarensis (a native of Guiana and Cuba),\n   and several other trees of the same family (Anonaseae).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanched', 'imp. & p. p.', 'of Lanch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanching', 'p. pr. & vb. n.', 'of Lanch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanch', 'v. t.', 'To throw, as a lance; to let fly; to launch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanciferous', 'a.', 'Bearing a lance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanciform', 'a.', 'Having the form of a lance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancinated', 'imp. & p. p.', 'of Lanciname');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancinating', 'p. pr. & vb. n.', 'of Lanciname');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanciname', 'v. t.', 'To tear; to lacerate; to pierce or stab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancinating', 'a.', 'Piercing; seeming to pierce or stab; as, lancinating\n   pains (i.e., severe, darting pains).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lancination', 'n.', 'A tearing; laceration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'n.', 'Urine. See Lant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'n.', 'The solid part of the surface of the earth; -- opposed to\n   water as constituting a part of such surface, especially to oceans and\n   seas; as, to sight land after a long voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'n.', 'Any portion, large or small, of the surface of the earth,\n   considered by itself, or as belonging to an individual or a people, as\n   a country, estate, farm, or tract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'n.', 'Ground, in respect to its nature or quality; soil; as, wet\n   land; good or bad land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'n.', 'The inhabitants of a nation or people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'n.', 'The mainland, in distinction from islands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'n.', 'The ground or floor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'n.', 'The ground left unplowed between furrows; any one of several\n   portions into which a field is divided for convenience in plowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'n.', 'Any ground, soil, or earth whatsoever, as meadows, pastures,\n   woods, etc., and everything annexed to it, whether by nature, as trees,\n   water, etc., or by the hand of man, as buildings, fences, etc.; real\n   estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'n.', 'The lap of the strakes in a clinker-built boat; the lap of\n   plates in an iron vessel; -- called also landing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'n.', 'In any surface prepared with indentations, perforations, or\n   grooves, that part of the surface which is not so treated, as the level\n   part of a millstone between the furrows, or the surface of the bore of\n   a rifled gun between the grooves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landed', 'imp. & p. p.', 'of Land');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landing', 'p. pr. & vb. n.', 'of Land');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'v. t.', 'To set or put on shore from a ship or other water craft;\n   to disembark; to debark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'v. t.', 'To catch and bring to shore; to capture; as, to land a\n   fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'v. t.', 'To set down after conveying; to cause to fall, alight, or\n   reach; to bring to the end of a course; as, he landed the quoit near\n   the stake; to be thrown from a horse and landed in the mud; to land one\n   in difficulties or mistakes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land', 'v. i.', 'To go on shore from a ship or boat; to disembark; to come\n   to the end of a course.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landamman', 'n.', 'A chief magistrate in some of the Swiss cantons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landamman', 'n.', 'The president of the diet of the Helvetic republic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landau', 'n.', 'A four-wheeled covered vehicle, the top of which is divided\n   into two sections which can be let down, or thrown back, in such a\n   manner as to make an open carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landaulet', 'n.', 'A small landau.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landed', 'a.', 'Having an estate in land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landed', 'a.', 'Consisting in real estate or land; as, landed property;\n   landed security.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lander', 'n.', 'One who lands, or makes a landing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lander', 'n.', 'A person who waits at the mouth of the shaft to receive the\n   kibble of ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landfall', 'n.', 'A sudden transference of property in land by the death of\n   its owner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landfall', 'n.', 'Sighting or making land when at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landflood', 'n.', 'An overflowing of land by river; an inundation; a\n   freshet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landgrave', 'n.', 'A German nobleman of a rank corresponding to that of an\n   earl in England and of a count in France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landgraviate', 'n.', 'The territory held by a landgrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landgraviate', 'n.', 'The office, jurisdiction, or authority of a\n   landgrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landgravine', 'n.', 'The wife of a landgrave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landholder', 'n.', 'A holder, owner, or proprietor of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landing', 'a.', 'Of, pertaining to or used for, setting, bringing, or\n   going, on shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landing', 'n.', 'A going or bringing on shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landing', 'n.', 'A place for landing, as from a ship, a carriage. etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landing', 'n.', 'The level part of a staircase, at the top of a flight of\n   stairs, or connecting one flight with another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landladies', 'pl. ', 'of Landlady');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landlady', 'n.', 'A woman having real estate which she leases to a tenant\n   or tenants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landlady', 'n.', 'The mistress of an inn or lodging house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landleaper', 'n.', 'See Landlouper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landless', 'a.', 'Having no property in land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landlock', 'v. t.', 'To inclose, or nearly inclose, as a harbor or a\n   vessel, with land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landlocked', 'a.', 'Inclosed, or nearly inclosed, by land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landlocked', 'a.', 'Confined to a fresh-water lake by reason of waterfalls\n   or dams; -- said of fishes that would naturally seek the sea, after\n   spawning; as, the landlocked salmon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landloper', 'n.', 'Same as Landlouper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landlord', 'n.', 'The lord of a manor, or of land; the owner of land or\n   houses which he leases to a tenant or tenants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landlord', 'n.', 'The master of an inn or of a lodging house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landlordism', 'n.', 'The state of being a landlord; the characteristics of\n   a landlord; specifically, in Great Britain, the relation of landlords\n   to tenants, especially as regards leased agricultural lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landlordry', 'n.', 'The state of a landlord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landlouper', 'n.', 'A vagabond; a vagrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landlouping', 'a.', 'Vagrant; wandering about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landlubber', 'n.', 'One who passes his life on land; -- so called among\n   seamen in contempt or ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landmen', 'pl. ', 'of Landman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landman', 'n.', 'A man who lives or serves on land; -- opposed to seaman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landman', 'n.', 'An occupier of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landmark', 'n.', 'A mark to designate the boundary of land; any , mark or\n   fixed object (as a marked tree, a stone, a ditch, or a heap of stones)\n   by which the limits of a farm, a town, or other portion of territory\n   may be known and preserved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landmark', 'n.', 'Any conspicuous object on land that serves as a guide;\n   some prominent object, as a hill or steeple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landowner', 'n.', 'An owner of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landowning', 'n.', 'The owning of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landowning', 'a.', 'Having property in land; of or pertaining to\n   landowners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Land-poor', 'a.', 'Pecuniarily embarrassed through owning much unprofitable\n   land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landreeve', 'n.', 'A subordinate officer on an extensive estate, who acts\n   as an assistant to the steward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landscape', 'n.', 'A portion of land or territory which the eye can\n   comprehend in a single view, including all the objects it contains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landscape', 'n.', 'A picture representing a scene by land or sea, actual or\n   fancied, the chief subject being the general aspect of nature, as\n   fields, hills, forests, water. etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landscape', 'n.', 'The pictorial aspect of a country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landscapist', 'n.', 'A painter of landscapes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landskip', 'n.', 'A landscape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landslip', 'n.', 'Alt. of Landslide');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landslide', 'n.', 'The slipping down of a mass of land from a mountain,\n   hill, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landslide', 'n.', 'The land which slips down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landsmen', 'pl. ', 'of Landsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landsman', 'n.', 'One who lives on the land; -- opposed to seaman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landsman', 'n.', 'A sailor on his first voyage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landstreight', 'n.', 'A narrow strip of land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landsturm', 'n.', 'That part of the reserve force in Germany which is\n   called out last.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landtag', 'n.', 'The diet or legislative body; as, the Landtag of Prussia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landwaiter', 'n.', 'See Landing waiter, under Landing, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landward', 'adv. & a.', 'Toward the land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Landwehr', 'n.', 'That part of the army, in Germany and Austria, which has\n   completed the usual military service and is exempt from duty in time of\n   peace, except that it is called out occasionally for drill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lane', 'a.', 'Alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lane', 'n.', 'A passageway between fences or hedges which is not traveled\n   as a highroad; an alley between buildings; a narrow way among trees,\n   rocks, and other natural obstructions; hence, in a general sense, a\n   narrow passageway; as, a lane between lines of men, or through a field\n   of ice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lang', 'a. & adv.', 'Long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langaha', 'n.', 'A curious colubriform snake of the genus Xyphorhynchus,\n   from Madagascar. It is brownish red, and its nose is prolonged in the\n   form of a sharp blade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langarey', 'n.', 'One of numerous species of long-winged, shrikelike birds\n   of Australia and the East Indies, of the genus Artamus, and allied\n   genera; called also wood swallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langate', 'n.', 'A linen roller used in dressing wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langdak', 'n.', 'A wolf (Canis pallipes), found in India, allied to the\n   jackal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langrage', 'n.', 'Alt. of Langrel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langrel', 'n.', 'A kind of shot formerly used at sea for tearing sails and\n   rigging. It consisted of bolts, nails, and other pieces of iron\n   fastened together or inclosed in a canister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langret', 'n.', 'A kind of loaded die.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langridge', 'n.', 'See Langrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langsyne', 'adv. & n.', 'Long since; long ago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langteraloo', 'n.', 'An old game at cards. See Loo (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Language', 'n.', 'Any means of conveying or communicating ideas;\n   specifically, human speech; the expression of ideas by the voice;\n   sounds, expressive of thought, articulated by the organs of the throat\n   and mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Language', 'n.', 'The expression of ideas by writing, or any other\n   instrumentality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Language', 'n.', 'The forms of speech, or the methods of expressing ideas,\n   peculiar to a particular nation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Language', 'n.', 'The characteristic mode of arranging words, peculiar to\n   an individual speaker or writer; manner of expression; style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Language', 'n.', 'The inarticulate sounds by which animals inferior to man\n   express their feelings or their wants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Language', 'n.', 'The suggestion, by objects, actions, or conditions, of\n   ideas associated therewith; as, the language of flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Language', 'n.', 'The vocabulary and phraseology belonging to an art or\n   department of knowledge; as, medical language; the language of\n   chemistry or theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Language', 'n.', 'A race, as distinguished by its speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languaged', 'imp. & p. p.', 'of Language');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languaging', 'p. pr. & vb. n.', 'of Language');
  INSERT INTO words (word, word_type, description) VALUES  (    'Language', 'v. t.', 'To communicate by language; to express in language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languaged', 'a.', 'Having a language; skilled in language; -- chiefly used\n   in composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languageless', 'a.', 'Lacking or wanting language; speechless; silent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langued', 'a.', 'Tongued; having the tongue visible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langue doc', '', 'The dialect, closely akin to French, formerly spoken\n   south of the Loire (in which the word for \"yes\" was oc); Provencal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langue doil', '', 'The dialect formerly spoken north of the Loire (in\n   which the word for \"yes\" was oil, F. oui).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languente', 'adv.', 'In a languishing manner; pathetically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languet', 'n.', 'Anything resembling the tongue in form or office; specif.,\n   the slip of metal in an organ pipe which turns the current of air\n   toward its mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languet', 'n.', 'That part of the hilt, in certain kinds of swords, which\n   overlaps the scabbard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languid', 'a.', 'Drooping or flagging from exhaustion; indisposed to\n   exertion; without animation; weak; weary; heavy; dull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languid', 'a.', 'Slow in progress; tardy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languid', 'a.', 'Promoting or indicating weakness or heaviness; as, a\n   languid day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languished', 'imp. & p. p.', 'of Languish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languishing', 'p. pr. & vb. n.', 'of Languish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languish', 'v. i.', 'To become languid or weak; to lose strength or\n   animation; to be or become dull, feeble or spiritless; to pine away; to\n   wither or fade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languish', 'v. i.', 'To assume an expression of weariness or tender grief,\n   appealing for sympathy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languish', 'v. i.', 'To cause to droop or pine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languish', 'n.', 'See Languishiment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languisher', 'n.', 'One who languishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languishing', 'a.', 'Becoming languid and weak; pining; losing health and\n   strength.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languishing', 'a.', 'Amorously pensive; as, languishing eyes, or look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languishingly', 'adv.', 'In a languishing manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languishment', 'n.', 'The state of languishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languishment', 'n.', 'Tenderness of look or mien; amorous pensiveness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languishness', 'n.', 'Languishment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languor', 'n.', 'A state of the body or mind which is caused by exhaustion\n   of strength and characterized by a languid feeling; feebleness;\n   lassitude; laxity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languor', 'n.', 'Any enfeebling disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languor', 'n.', 'Listless indolence; dreaminess. Pope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Languorous', 'a.', 'Producing, or tending to produce, languor;\n   characterized by languor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langure', 'v. i.', 'To languish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Langya', 'n.', 'One of several species of East Indian and Asiatic\n   fresh-water fishes of the genus Ophiocephalus, remarkable for their\n   power of living out of water, and for their tenacity of life; -- called\n   also walking fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laniard', 'n.', 'See Lanyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laniariform', 'a.', 'Shaped like a laniary, or canine, tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laniary', 'a.', 'Lacerating or tearing; as, the laniary canine teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laniary', 'a.', 'The shambles; a place of slaughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laniary', 'a.', 'A laniary, or canine, tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laniate', 'v. t.', 'To tear in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laniation', 'n.', 'A tearing in pieces.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanier', 'n.', 'A thong of leather; a whip lash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanier', 'n.', 'A strap used to fasten together parts of armor, to hold the\n   shield by, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laniferous', 'n.', 'Bearing or producing wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanifical', 'a.', 'Working in wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanifice', 'n.', 'Anything made of wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanigerous', 'a.', 'Bearing or producing wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanioid', 'a.', 'Of or pertaining to the shrikes (family Laniidae).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lank', 'superl.', 'Slender and thin; not well filled out; not plump;\n   shrunken; lean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lank', 'superl.', 'Languid; drooping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lank', 'v. i. & t.', 'To become lank; to make lank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lankiness', 'n.', 'The condition or quality or being lanky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lankly', 'adv.', 'In a lank manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lankness', 'n.', 'The state or quality of being lank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanky', 'a.', 'Somewhat lank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanner', 'n. m.', 'Alt. of Lanneret');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanneret', 'n. m.', 'A long-tailed falcon (Falco lanarius), of Southern\n   Europe, Asia, and Northern Africa, resembling the American prairie\n   falcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanolin', 'n.', 'A peculiar fatlike body, made up of cholesterin and\n   certain fatty acids, found in feathers, hair, wool, and keratin tissues\n   generally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanseh', 'n.', 'The small, whitish brown fruit of an East Indian tree\n   (Lansium domesticum). It has a fleshy pulp, with an agreeable subacid\n   taste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lansquenet', 'n.', 'A German foot soldier in foreign service in the 15th\n   and 16th centuries; a soldier of fortune; -- a term used in France and\n   Western Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lansquenet', 'n.', 'A game at cards, vulgarly called lambskinnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lant', 'n.', 'Urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lant', 'n.', 'Any one of several species of small, slender, marine fishes\n   of the genus Ammedytes. The common European species (A. tobianus) and\n   the American species (A. Americanus) live on sandy shores, buried in\n   the sand, and are caught in large quantities for bait. Called also\n   launce, and sand eel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lant', 'n.', 'See Lanterloo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lantanium', 'n.', 'Alt. of Lantanum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lantanum', 'n.', 'See Lanthanum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lantanuric', 'a.', 'Pertaining to, or designating, a nitrogenous organic\n   acid of the uric acid group, obtained by the decomposition of\n   allantoin, and usually called allanturic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanterloo', 'n.', 'An old name of loo (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lantern', 'n.', 'Something inclosing a light, and protecting it from wind,\n   rain, etc. ; -- sometimes portable, as a closed vessel or case of horn,\n   perforated tin, glass, oiled paper, or other material, having a lamp or\n   candle within; sometimes fixed, as the glazed inclosure of a street\n   light, or of a lighthouse light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lantern', 'n.', 'An open structure of light material set upon a roof, to\n   give light and air to the interior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lantern', 'n.', 'A cage or open chamber of rich architecture, open below\n   into the building or tower which it crowns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lantern', 'n.', 'A smaller and secondary cupola crowning a larger one, for\n   ornament, or to admit light; such as the lantern of the cupola of the\n   Capitol at Washington, or that of the Florence cathedral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lantern', 'n.', 'A lantern pinion or trundle wheel. See Lantern pinion\n   (below).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lantern', 'n.', 'A kind of cage inserted in a stuffing box and surrounding\n   a piston rod, to separate the packing into two parts and form a chamber\n   between for the reception of steam, etc. ; -- called also lantern\n   brass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lantern', 'n.', 'A perforated barrel to form a core upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lantern', 'n.', 'See Aristotles lantern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanterned', 'imp. & p. p.', 'of Lantern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanterning', 'p. pr. & vb. n.', 'of Lantern');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lantern', 'v. t.', 'To furnish with a lantern; as, to lantern a lighthouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lantern-jawed', 'a.', 'Having lantern jaws or long, thin jaws; as, a\n   lantern-jawed person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanthanite', 'n.', 'Hydrous carbonate of lanthanum, found in tabular while\n   crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanthanum', 'n.', 'A rare element of the group of the earth metals, allied\n   to aluminium. It occurs in certain rare minerals, as cerite,\n   gadolinite, orthite, etc., and was so named from the difficulty of\n   separating it from cerium, didymium, and other rare elements with which\n   it is usually associated. Atomic weight 138.5. Symbol La.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanthopine', 'n.', 'An alkaloid found in opium in small quantities, and\n   extracted as a white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanthorn', 'n.', 'See Lantern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanuginose', 'a.', 'Alt. of Lanuginous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanuginous', 'a.', 'Covered with down, or fine soft hair; downy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanugo', 'n.', 'The soft woolly hair which covers most parts of the mammal\n   fetus, and in man is shed before or soon after birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanyard', 'n.', 'A short piece of rope or line for fastening something in\n   ships; as, the lanyards of the gun ports, of the buoy, and the like;\n   esp., pieces passing through the dead-eyes, and used to extend shrouds,\n   stays, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanyard', 'n.', 'A strong cord, about twelve feet long, with an iron hook\n   at one end a handle at the other, used in firing cannon with a friction\n   tube.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lanyer', 'n.', 'See Lanier.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laocoon', 'n.', 'A priest of Apollo, during the Trojan war. (See 2.)');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laocoon', 'n.', 'A marble group in the Vatican at Rome, representing the\n   priest Laocoon, with his sons, infolded in the coils of two serpents,\n   as described by Virgil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laodicean', 'a.', 'Of or pertaining to Laodicea, a city in Phrygia Major;\n   like the Christians of Laodicea; lukewarm in religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'The loose part of a coat; the lower part of a garment that\n   plays loosely; a skirt; an apron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'An edge; a border; a hem, as of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'The part of the clothing that lies on the knees or thighs when\n   one sits down; that part of the person thus covered; figuratively, a\n   place of rearing and fostering; as, to be reared in the lap of luxury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'That part of any substance or fixture which extends over, or\n   lies upon, or by the side of, a part of another; as, the lap of a\n   board; also, the measure of such extension over or upon another thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'The amount by which a slide valve at its half stroke overlaps\n   a port in the seat, being equal to the distance the valve must move\n   from its mid stroke position in order to begin to open the port. Used\n   alone, lap refers to outside lap. See Outside lap (below).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'The state or condition of being in part extended over or by\n   the side of something else; or the extent of the overlapping; as, the\n   second boat got a lap of half its length on the leader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'One circuit around a race track, esp. when the distance is a\n   small fraction of a mile; as, to run twenty laps; to win by three laps.\n   See Lap, to fold, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'In card playing and other games, the points won in excess of\n   the number necessary to complete a game; -- so called when they are\n   counted in the score of the following game.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'A sheet, layer, or bat, of cotton fiber prepared for the\n   carding machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'A piece of brass, lead, or other soft metal, used to hold a\n   cutting or polishing powder in cutting glass, gems, and the like, or in\n   polishing cutlery, etc. It is usually in the form of wheel or disk,\n   which revolves on a vertical axis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapped', 'imp. & p. p.', 'of Lap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapping', 'p. pr. & vb. n.', 'of Lap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'v. t.', 'To rest or recline in a lap, or as in a lap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'v. t.', 'To cut or polish with a lap, as glass, gems, cutlery, etc.\n   See 1st Lap, 10.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'To fold; to bend and lay over or on something; as, to lap a\n   piece of cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'To wrap or wind around something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'To infold; to hold as in ones lap; to cherish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'To lay or place over anything so as to partly or wholly cover\n   it; as, to lap one shingle over another; to lay together one partly\n   over another; as, to lap weather-boards; also, to be partly over, or by\n   the side of (something); as, the hinder boat lapped the foremost one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'To lay together one over another, as fleeces or slivers for\n   further working.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'v. i.', 'To be turned or folded; to lie partly upon or by the side\n   of something, or of one another; as, the cloth laps back; the boats\n   lap; the edges lap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'v. i.', 'To take up drink or food with the tongue; to drink or feed\n   by licking up something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'v. i.', 'To make a sound like that produced by taking up drink with\n   the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'v. t.', 'To take into the mouth with the tongue; to lick up with a\n   quick motion of the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'The act of lapping with, or as with, the tongue; as, to take\n   anything into the mouth with a lap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap', 'n.', 'The sound of lapping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laparocele', 'n.', 'A rupture or hernia in the lumbar regions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laparotomy', 'n.', 'A cutting through the walls of the abdomen, as in the\n   Caesarean section.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapboard', 'n.', 'A board used on the lap as a substitute for a table, as\n   by tailors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapdog', 'n.', 'A small dog fondled in the lap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapel', 'n.', 'That part of a garment which is turned back; specifically,\n   the lap, or fold, of the front of a coat in continuation of collar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapelled', 'a.', 'Furnished with lapels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapfuls', 'pl. ', 'of Lapful');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapful', 'n.', 'As much as the lap can contain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapicide', 'n.', 'A stonecutter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidarian', 'a.', 'Of or pertaining to stone; inscribed on stone; as, a\n   lapidarian record.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidarious', 'a.', 'Consisting of stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidaries', 'pl. ', 'of Lapidary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidary', 'n.', 'An artificer who cuts, polishes, and engraves precious\n   stones; hence, a dealer in precious stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidary', 'n.', 'A virtuoso skilled in gems or precious stones; a\n   connoisseur of lapidary work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidary', 'a.', 'Of or pertaining to the art of cutting stones, or\n   engraving on stones, either gems or monuments; as, lapidary\n   ornamentation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidary', 'a.', 'Of or pertaining to monumental inscriptions; as, lapidary\n   adulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidate', 'v. t.', 'To stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidation', 'n.', 'The act of stoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapideous', 'a.', 'Of the nature of stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidescence', 'n.', 'The state or quality of being lapidescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidescence', 'n.', 'A hardening into a stone substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidescence', 'n.', 'A stony concretion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidescent', 'a.', 'Undergoing the process of becoming stone; having the\n   capacity of being converted into stone; having the quality of\n   petrifying bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidescent', 'n.', 'Any substance which has the quality of petrifying\n   other bodies, or of converting or being converted into stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidific', 'a.', 'Alt. of Lapidifical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidifical', 'a.', 'Forming or converting into stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidification', 'n.', 'The act or process of lapidifying; fossilization;\n   petrifaction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidified', 'imp. & p. p.', 'of Lapidify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidifying', 'p. pr. & vb. n.', 'of Lapidify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidify', 'v. t.', 'To convert into stone or stony material; to petrify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidify', 'v. i.', 'To become stone or stony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapidist', 'n.', 'A lapidary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapillation', 'n.', 'The state of being, or the act of making, stony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapilli', 'n. pl.', 'Volcanic ashes, consisting of small, angular, stony\n   fragments or particles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapides', 'pl. ', 'of Lapis');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapis', 'n.', 'A stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapis lazuli', '', 'An albuminous mineral of a rich blue color. Same as\n   Lazuli, which see.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap-jointed', 'a.', 'Having a lap joint, or lap joints, as many kinds of\n   woodwork and metal work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laplander', 'n.', 'A native or inhabitant of Lapland; -- called also Lapp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laplandish', 'a.', 'Of or pertaining to Lapland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapling', 'n.', 'One who has been fondled to excess; one fond of ease and\n   sensual delights; -- a term of contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapp', 'n.', 'Same as Laplander. Cf. Lapps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lappaceous', 'a.', 'Resembling the capitulum of burdock; covered with\n   forked points.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapper', 'n.', 'One who takes up food or liquid with his tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lappet', 'n.', 'A small decorative fold or flap, esp, of lace or muslin, in\n   a garment or headdress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lappeted', 'imp. & p. p.', 'of Lappet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lappeting', 'p. pr. & vb. n.', 'of Lappet');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lappet', 'v. t.', 'To decorate with, or as with, a lappet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lappic', 'a.', 'Of or pertaining to Lapland, or the Lapps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lappic', 'n.', 'The language of the Lapps. See Lappish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapping', 'n.', 'A kind of machine blanket or wrapping material used by\n   calico printers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lappish', 'a.', 'Of or pertaining to the Lapps; Laplandish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lappish', 'n.', 'The language spoken by the Lapps in Lapland. It is related\n   to the Finnish and Hungarian, and is not an Aryan language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapponian', 'a.', 'Alt. of Lapponic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapponic', 'a.', 'Laplandish; Lappish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapps', 'n. pl.', 'A branch of the Mongolian race, now living in the\n   northern parts of Norway, Sweden, and the adjacent parts of Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapsable', 'a.', 'Lapsible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapse', 'n.', 'A gliding, slipping, or gradual falling; an unobserved or\n   imperceptible progress or passing away,; -- restricted usually to\n   immaterial things, or to figurative uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapse', 'n.', 'A slip; an error; a fault; a failing in duty; a slight\n   deviation from truth or rectitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapse', 'n.', 'The termination of a right or privilege through neglect to\n   exercise it within the limited time, or through failure of some\n   contingency; hence, the devolution of a right or privilege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapse', 'n.', 'A fall or apostasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapsed', 'imp. & p. p.', 'of Lapse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapsing', 'p. pr. & vb. n.', 'of Lapse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapse', 'v. i.', 'To pass slowly and smoothly downward, backward, or away;\n   to slip downward, backward, or away; to glide; -- mostly restricted to\n   figurative uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapse', 'v. i.', 'To slide or slip in moral conduct; to fail in duty; to\n   fall from virtue; to deviate from rectitude; to commit a fault by\n   inadvertence or mistake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapse', 'v. i.', 'To fall or pass from one proprietor to another, or from\n   the original destination, by the omission, negligence, or failure of\n   some one, as a patron, a legatee, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapse', 'v. i.', 'To become ineffectual or void; to fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapse', 'v. t.', 'To let slip; to permit to devolve on another; to allow to\n   pass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapse', 'v. t.', 'To surprise in a fault or error; hence, to surprise or\n   catch, as an offender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapsed', 'a.', 'Having slipped downward, backward, or away; having lost\n   position, privilege, etc., by neglect; -- restricted to figurative\n   uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapsed', 'a.', 'Ineffectual, void, or forfeited; as, a lapsed policy of\n   insurance; a lapsed legacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapsible', 'a.', 'Liable to lapse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapsided', 'a.', 'See Lopsided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapstone', 'n.', 'A stone for the lap, on which shoemakers beat leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapstreak', 'a.', 'Alt. of Lapstrake');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapstrake', 'a.', 'Made with boards whose edges lap one over another;\n   clinker-built; -- said of boats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laputan', 'a.', 'Of or pertaining to Laputa, an imaginary flying island\n   described in Gullivers Travels as the home of chimerical philosophers.\n   Hence, fanciful; preposterous; absurd in science or philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lap-welded', 'a.', 'Having edges or ends united by a lap weld; as, a\n   lap-welded pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapwing', 'n.', 'A small European bird of the Plover family (Vanellus\n   cristatus, or V. vanellus). It has long and broad wings, and is noted\n   for its rapid, irregular fight, upwards, downwards, and in circles. Its\n   back is coppery or greenish bronze. Its eggs are the \"plovers eggs\" of\n   the London market, esteemed a delicacy. It is called also peewit,\n   dastard plover, and wype. The gray lapwing is the Squatarola cinerea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lapwork', 'n.', 'Work in which one part laps over another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laguay', 'n.', 'A lackey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laquearia', 'pl. ', 'of Laquear');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laquear', 'n.', 'A lacunar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laqueary', 'a.', 'Using a noose, as a gladiator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lares', 'pl. ', 'of Lar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lars', 'pl. ', 'of Lar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lar', 'n.', 'A tutelary deity; a deceased ancestor regarded as a protector\n   of the family. The domestic Lares were the tutelar deities of a house;\n   household gods. Hence, Eng.: Hearth or dwelling house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lar', 'n.', 'A species of gibbon (Hylobates lar), found in Burmah. Called\n   also white-handed gibbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laramie group', '', 'An extensive series of strata, principally developed\n   in the Rocky Mountain region, as in the Laramie Mountains, and formerly\n   supposed to be of the Tertiary age, but now generally regarded as\n   Cretaceous, or of intermediate and transitional character. It contains\n   beds of lignite, often valuable for coal, and is hence also called the\n   lignitic group. See Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larboard', 'n.', 'The left-hand side of a ship to one on board facing\n   toward the bow; port; -- opposed to starboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larboard', 'a.', 'On or pertaining to the left-hand side of a vessel; port;\n   as, the larboard quarter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larcener', 'n.', 'Alt. of Larcenist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larcenist', 'n.', 'One who commits larceny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larcenous', 'a.', 'Having the character of larceny; as, a larcenous act;\n   committing larceny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larcenies', 'pl. ', 'of Larceny');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larceny', 'n.', 'The unlawful taking and carrying away of things personal\n   with intent to deprive the right owner of the same; theft. Cf.\n   Embezzlement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larch', 'n.', 'A genus of coniferous trees, having deciduous leaves, in\n   fascicles (see Illust. of Fascicle).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larchen', 'a.', 'Of or pertaining to the larch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lard', 'n.', 'Bacon; the flesh of swine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lard', 'n.', 'The fat of swine, esp. the internal fat of the abdomen; also,\n   this fat melted and strained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larded', 'imp. & p. p.', 'of Lard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larding', 'p. pr. & vb. n.', 'of Lard');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lard', 'n.', 'To stuff with bacon; to dress or enrich with lard; esp., to\n   insert lardons of bacon or pork in the surface of, before roasting; as,\n   to lard poultry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lard', 'n.', 'To fatten; to enrich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lard', 'n.', 'To smear with lard or fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lard', 'n.', 'To mix or garnish with something, as by way of improvement;\n   to interlard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lard', 'v. i.', 'To grow fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lardacein', 'n.', 'A peculiar amyloid substance, colored blue by iodine and\n   sulphuric acid, occurring mainly as an abnormal infiltration into the\n   spleen, liver, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lardaceous', 'a.', 'Consisting of, or resembling, lard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larder', 'n.', 'A room or place where meat and other articles of food are\n   kept before they are cooked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larderer', 'n.', 'One in charge of the larder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lardery', 'n.', 'A larder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lardon', 'n.', 'Alt. of Lardoon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lardoon', 'n.', 'A bit of fat pork or bacon used in larding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lardry', 'n.', 'A larder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lardy', 'a.', 'Containing, or resembling, lard; of the character or\n   consistency of lard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lare', 'n.', 'Lore; learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lare', 'n.', 'Pasture; feed. See Lair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lare', 'v. t.', 'To feed; to fatten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lares', 'n. pl.', 'See 1st Lar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Large', 'superl.', 'Exceeding most other things of like kind in bulk,\n   capacity, quantity, superficial dimensions, or number of constituent\n   units; big; great; capacious; extensive; -- opposed to small; as, a\n   large horse; a large house or room; a large lake or pool; a large jug\n   or spoon; a large vineyard; a large army; a large city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Large', 'superl.', 'Abundant; ample; as, a large supply of provisions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Large', 'superl.', 'Full in statement; diffuse; full; profuse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Large', 'superl.', 'Having more than usual power or capacity; having broad\n   sympathies and generous impulses; comprehensive; -- said of the mind\n   and heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Large', 'superl.', 'Free; unembarrassed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Large', 'superl.', 'Unrestrained by decorum; -- said of language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Large', 'superl.', 'Prodigal in expending; lavish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Large', 'superl.', 'Crossing the line of a ships course in a favorable\n   direction; -- said of the wind when it is abeam, or between the beam\n   and the quarter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Large', 'adv.', 'Freely; licentiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Large', 'n.', 'A musical note, formerly in use, equal to two longs, four\n   breves, or eight semibreves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Large-acred', 'a.', 'Possessing much land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Large-handed', 'a.', 'Having large hands, Fig.: Taking, or giving, in large\n   quantities; rapacious or bountiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Large-hearted', 'a.', 'Having a large or generous heart or disposition;\n   noble; liberal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Largely', 'adv.', 'In a large manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Largeness', 'n.', 'The quality or state of being large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Largess', 'a.', 'Alt. of Largesse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Largesse', 'a.', 'Liberality; generosity; bounty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Largesse', 'a.', 'A present; a gift; a bounty bestowed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larget', 'n.', 'A sport piece of bar iron for rolling into a sheet; a small\n   billet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larghetto', 'a. & adv.', 'Somewhat slow or slowly, but not so slowly as\n   largo, and rather more so than andante.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Largifical', 'a.', 'Generous; ample; liberal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Largifluous', 'a.', 'Flowing copiously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Largiloquent', 'a.', 'Grandiloquent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Largish', 'a.', 'Somewhat large.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Largition', '', 'The bestowment of a largess or gift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Largo', 'a. & adv.', 'Slow or slowly; -- more so than adagio; next in\n   slowness to grave, which is also weighty and solemn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Largo', 'n.', 'A movement or piece in largo time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lariat', 'n.', 'A long, slender rope made of hemp or strips of hide, esp.\n   one with a noose; -- used as a lasso for catching cattle, horses, etc.,\n   and for picketing a horse so that he can graze without wandering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lariated', 'imp. & p. p.', 'of Lariat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lariating', 'p. pr. & vb. n.', 'of Lariat');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lariat', 'v. t.', 'To secure with a lariat fastened to a stake, as a horse\n   or mule for grazing; also, to lasso or catch with a lariat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larine', 'a.', 'Of or pertaining to the Gull family (Laridae).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larixinic', 'a.', 'Of, or derived from, the larch (Larix); as, larixinic\n   acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lark', 'v. i.', 'A frolic; a jolly time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larked', 'imp. & p. p.', 'of Lark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larking', 'p. pr. & vb. n.', 'of Lark');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lark', 'v. i.', 'To sport; to frolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lark', 'n.', 'Any one numerous species of singing birds of the genus Alauda\n   and allied genera (family Alaudidae). They mostly belong to Europe,\n   Asia, and Northern Africa. In America they are represented by the shore\n   larks, or horned by the shore larks, or horned larks, of the genus\n   Otocoris. The true larks have holaspidean tarsi, very long hind claws,\n   and usually, dull, sandy brown colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lark', 'v. i.', 'To catch larks; as, to go larking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lark-colored', 'a.', 'Having the sandy brown color of the European larks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larker', 'n.', 'A catcher of larks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larker', 'n.', 'One who indulges in a lark or frolic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larks-heel', 'n.', 'Indian cress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larkspur', 'n.', 'A genus of ranunculaceous plants (Delphinium), having\n   showy flowers, and a spurred calyx. They are natives of the North\n   Temperate zone. The commonest larkspur of the gardens is D. Consolida.\n   The flower of the bee larkspur (D. elatum) has two petals bearded with\n   yellow hairs, and looks not unlike a bee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larmier', 'n.', 'See Tearpit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laroid', 'a.', 'Like or belonging to the Gull family (Laridae).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larruped', 'imp. & p. p.', 'of Larrup');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larruping', 'p. pr. & vb. n.', 'of Larrup');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larrup', 'v. t.', 'To beat or flog soundly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larry', 'n.', 'Same as Lorry, or Lorrie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larum', 'n.', 'See Alarum, and Alarm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larvae', 'pl. ', 'of Larva');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larvas', 'pl. ', 'of Larva');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larva', 'n.', 'Any young insect from the time that it hatches from the egg\n   until it becomes a pupa, or chrysalis. During this time it usually\n   molts several times, and may change its form or color each time. The\n   larvae of many insects are much like the adults in form and habits, but\n   have no trace of wings, the rudimentary wings appearing only in the\n   pupa stage. In other groups of insects the larvae are totally unlike\n   the parents in structure and habits, and are called caterpillars,\n   grubs, maggots, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larva', 'n.', 'The early, immature form of any animal when more or less of\n   a metamorphosis takes place, before the assumption of the mature shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larval', 'a.', 'Of or pertaining to a larva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larvalia', 'n. pl.', 'An order of Tunicata, including Appendicularia, and\n   allied genera; -- so called because certain larval features are\n   retained by them through life. Called also Copelata. See\n   Appendicularia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larvated', 'a.', 'Masked; clothed as with a mask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larves', 'pl. ', 'of Larve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larve', 'n.', 'A larva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larviform', 'a.', 'Having the form or structure of a larva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larviparous', 'a.', 'Depositing living larvae, instead of eggs; -- said of\n   certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lary', 'n.', 'A guillemot; -- called also lavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngeal', 'a.', 'Of or pertaining to the larynx; adapted to operations on\n   the larynx; as, laryngeal forceps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngean', 'a.', 'See Laryngeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larypgismus', 'n.', 'A spasmodic state of the glottis, giving rise to\n   contraction or closure of the opening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngitis', 'n.', 'Inflammation of the larynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngological', 'a.', 'Of or pertaining to laryngology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngologist', 'n.', 'One who applies himself to laryngology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngology', 'n.', 'Systematized knowledge of the action and functions of\n   the larynx; in pathology, the department which treats of the diseases\n   of the larynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngophony', 'n.', 'The sound of the voice as heard through a stethoscope\n   when the latter is placed upon the larynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larungoscope', 'n.', 'An instrument, consisting of an arrangement of two\n   mirrors, for reflecting light upon the larynx, and for examining its\n   image.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngoscopic', 'a.', 'Of or pertaining to the inspection of the larynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngoscopist', 'n.', 'One skilled in laryngoscopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngoscopy', 'n.', 'The art of using the laryngoscope; investigations\n   made with the laryngoscope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngotome', 'n.', 'An instrument for performing laryngotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngotomy', 'n.', 'The operation of cutting into the larynx, from the\n   outside of the neck, for assisting respiration when obstructed, or for\n   removing foreign bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngotracheal', 'a.', 'Pertaining to both larynx and trachea; as, the\n   laryngotracheal cartilage in the frog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laryngotracheotomy', 'n.', 'The operation of cutting into the larynx and\n   the upper part of the trachea, -- a frequent operation for obstruction\n   to breathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Larynx', 'n.', 'The expanded upper end of the windpipe or trachea,\n   connected with the hyoid bone or cartilage. It contains the vocal\n   cords, which produce the voice by their vibrations, when they are\n   stretched and a current of air passes between them. The larynx is\n   connected with the pharynx by an opening, the glottis, which, in\n   mammals, is protected by a lidlike epiglottis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Las', 'n.', 'A lace. See Lace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Las', 'a. & adv.', 'Less.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lascar', 'n.', 'A native sailor, employed in European vessels; also, a\n   menial employed about arsenals, camps, camps, etc.; a camp follower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lascious', 'a.', 'Loose; lascivious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasciviency', 'n.', 'Lasciviousness; wantonness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lascivient', 'a.', 'Lascivious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lascivious', 'a.', 'Wanton; lewd; lustful; as, lascivious men; lascivious\n   desires.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lascivious', 'a.', 'Tending to produce voluptuous or lewd emotions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laserwort', 'n.', 'Any plant of the umbelliferous genus Laserpitium, of\n   several species (as L. glabrum, and L. siler), the root of which yields\n   a resinous substance of a bitter taste. The genus is mostly European.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lash', 'n.', 'The thong or braided cord of a whip, with which the blow is\n   given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lash', 'n.', 'A leash in which an animal is caught or held; hence, a snare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lash', 'n.', 'A stroke with a whip, or anything pliant and tough; as, the\n   culprit received thirty-nine lashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lash', 'n.', 'A stroke of satire or sarcasm; an expression or retort that\n   cuts or gives pain; a cut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lash', 'n.', 'A hair growing from the edge of the eyelid; an eyelash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lash', 'n.', 'In carpet weaving, a group of strings for lifting\n   simultaneously certain yarns, to form the figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lashed', 'imp. & p. p.', 'of Lash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lashng', 'p. pr. & vb. n.', 'of Lash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lash', 'v. t.', 'To strike with a lash ; to whip or scourge with a lash, or\n   with something like one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lash', 'v. t.', 'To strike forcibly and quickly, as with a lash; to beat,\n   or beat upon, with a motion like that of a lash; as, a whale lashes the\n   sea with his tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lash', 'v. t.', 'To throw out with a jerk or quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lash', 'v. t.', 'To scold; to berate; to satirize; to censure with\n   severity; as, to lash vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lash', 'v. i.', 'To ply the whip; to strike; to utter censure or sarcastic\n   language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lash', 'n.', 'To bind with a rope, cord, thong, or chain, so as to fasten;\n   as, to lash something to a spar; to lash a pack on a horses back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasher', 'n.', 'One who whips or lashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasher', 'n.', 'A piece of rope for binding or making fast one thing to\n   another; -- called also lashing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasher', 'n.', 'A weir in a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lashing', 'n.', 'The act of one who, or that which, lashes; castigation;\n   chastisement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lashing', 'n.', 'See 2d Lasher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lask', 'n.', 'A diarrhea or flux.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasket', 'n.', 'latching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lass', 'n.', 'A youth woman; a girl; a sweetheart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasse', 'a. & adv.', 'Less.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lassie', 'n.', 'A young girl; a lass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lassitude', 'n.', 'A condition of the body, or mind, when its voluntary\n   functions are performed with difficulty, and only by a strong exertion\n   of the will; languor; debility; weariness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasslorn', '', 'Forsaken by a lass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lassos', 'pl. ', 'of Lasso');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasso', 'n.', 'A rope or long thong of leather with, a running noose, used\n   for catching horses, cattle, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lassoed', 'imp. & p. p.', 'of Lasso');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lassoing', 'p. pr. & vb. n.', 'of Lasso');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasso', 'v. t.', 'To catch with a lasso.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', '3d pers. sing. pres.', 'of Last, to endure, contracted from\n   lasteth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'a.', 'Being after all the others, similarly classed or considered,\n   in time, place, or order of succession; following all the rest; final;\n   hindmost; farthest; as, the last year of a century; the last man in a\n   line of soldiers; the last page in a book; his last chance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'a.', 'Next before the present; as, I saw him last week.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'a.', 'Supreme; highest in degree; utmost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'a.', 'Lowest in rank or degree; as, the last prize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'a.', 'Farthest of all from a given quality, character, or\n   condition; most unlikely; having least fitness; as, he is the last\n   person to be accused of theft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'a.', 'At a time or on an occasion which is the latest of all those\n   spoken of or which have occurred; the last time; as, I saw him last in\n   New York.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'a.', 'In conclusion; finally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'a.', 'At a time next preceding the present time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasted', 'imp. & p. p.', 'of Last');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasting', 'p. pr. & vb. n.', 'of Last');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'v. i.', 'To continue in time; to endure; to remain in existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'v. i.', 'To endure use, or continue in existence, without\n   impairment or exhaustion; as, this cloth lasts better than that; the\n   fuel will last through the winter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'v. i.', 'A wooden block shaped like the human foot, on which boots\n   and shoes are formed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'v. t.', 'To shape with a last; to fasten or fit to a last; to place\n   smoothly on a last; as, to last a boot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'n.', 'A load; a heavy burden; hence, a certain weight or measure,\n   generally estimated at 4,000 lbs., but varying for different articles\n   and in different countries. In England, a last of codfish, white\n   herrings, meal, or ashes, is twelve barrels; a last of corn, ten\n   quarters, or eighty bushels, in some parts of England, twenty-one\n   quarters; of gunpowder, twenty-four barrels, each containing 100 lbs;\n   of red herrings, twenty cades, or 20,000; of hides, twelve dozen; of\n   leather, twenty dickers; of pitch and tar, fourteen barrels; of wool,\n   twelve sacks; of flax or feathers, 1,700 lbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Last', 'n.', 'The burden of a ship; a cargo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lastage', 'n.', 'A duty exacted, in some fairs or markets, for the right to\n   carry things where one will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lastage', 'n.', 'A tax on wares sold by the last.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lastage', 'n.', 'The lading of a ship; also, ballast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lastage', 'n.', 'Room for stowing goods, as in a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laste', 'obs. imp.', 'of Last, to endure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laster', 'n.', 'A workman whose business it is to shape boots or shoes, or\n   place leather smoothly, on lasts; a tool for stretching leather on a\n   last.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lastery', 'n.', 'A red color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasting', 'a.', 'Existing or continuing a long while; enduring; as, a\n   lasting good or evil; a lasting color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasting', 'n.', 'Continuance; endurance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasting', 'n.', 'A species of very durable woolen stuff, used for womens\n   shoes; everlasting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasting', 'n.', 'The act or process of shaping on a last.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lasting', 'adv.', 'In a lasting manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lastly', 'adv.', 'In the last place; in conclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lastly', 'adv.', 'at last; finally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lat', 'v. t.', 'To let; to allow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latakia', 'n.', 'A superior quality of Turkish smoking tobacco, so called\n   from the place where produced, the ancient Laodicea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latch', 'v. t.', 'To smear; to anoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latch', 'n.', 'That which fastens or holds; a lace; a snare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latch', 'n.', 'A movable piece which holds anything in place by entering a\n   notch or cavity; specifically, the catch which holds a door or gate\n   when closed, though it be not bolted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latch', 'n.', 'A latching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latch', 'n.', 'A crossbow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latched', 'imp. & p. p.', 'of Latch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latching', 'p. pr. & vb. n.', 'of Latch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latch', 'n.', 'To catch so as to hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latch', 'n.', 'To catch or fasten by means of a latch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latchet', 'n.', 'The string that fastens a shoe; a shoestring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latching', 'n.', 'A loop or eye formed on the head rope of a bonnet, by\n   which it is attached to the foot of a sail; -- called also latch and\n   lasket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latchkey', 'n.', 'A key used to raise, or throw back, the latch of a door,\n   esp. a night latch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latchstring', 'n.', 'A string for raising the latch of a door by a person\n   outside. It is fastened to the latch and passed through a hole above it\n   in the door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Late', 'v.', 'Coming after the time when due, or after the usual or proper\n   time; not early; slow; tardy; long delayed; as, a late spring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Late', 'v.', 'Far advanced toward the end or close; as, a late hour of the\n   day; a late period of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Late', 'v.', 'Existing or holding some position not long ago, but not now;\n   lately deceased, departed, or gone out of office; as, the late bishop\n   of London; the late administration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Late', 'v.', 'Not long past; happening not long ago; recent; as, the late\n   rains; we have received late intelligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Late', 'v.', 'Continuing or doing until an advanced hour of the night; as,\n   late revels; a late watcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Late', 'a.', 'After the usual or proper time, or the time appointed; after\n   delay; as, he arrived late; -- opposed to early.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Late', 'a.', 'Not long ago; lately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Late', 'a.', 'Far in the night, day, week, or other particular period; as,\n   to lie abed late; to sit up late at night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lated', 'a.', 'Belated; too late.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lateen', 'a.', 'Of or pertaining to a peculiar rig used in the\n   Mediterranean and adjacent waters, esp. on the northern coast of\n   Africa. See below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lately', 'adv.', 'Not long ago; recently; as, he has lately arrived from\n   Italy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latence', 'n.', 'Latency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latency', 'n.', 'The state or quality of being latent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lateness', 'n.', 'The state, condition, or quality, of being late; as, the\n   lateness of his arrival; the lateness of the hour; the lateness of the\n   season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latent', 'a.', 'Not visible or apparent; hidden; springs of action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latently', 'adv.', 'In a secret or concealed manner; invisibly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lateres', 'pl. ', 'of Later');
  INSERT INTO words (word, word_type, description) VALUES  (    'Later', 'n.', 'A brick or tile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Later', 'a.', 'Compar. of Late, a. & adv.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laterad', 'adv.', 'Toward the side; away from the mesial plane; -- opposed\n   to mesiad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lateral', 'a.', 'Of or pertaining to the sides; as, the lateral walls of a\n   house; the lateral branches of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lateral', 'a.', 'Lying at, or extending toward, the side; away from the\n   mesial plane; external; -- opposed to mesial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lateral', 'a.', 'Directed to the side; as, a lateral view of a thing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laterality', 'n.', 'The state or condition of being lateral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laterally', 'adv.', 'By the side; sidewise; toward, or from, the side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lateran', 'n.', 'The church and palace of St. John Lateran, the church\n   being the cathedral church of Rome, and the highest in rank of all\n   churches in the Catholic world.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latered', 'a.', 'Inclined to delay; dilatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laterifolious', 'a.', 'Growing from the stem by the side of a leaf; as, a\n   laterifolious flower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laterite', 'n.', 'An argillaceous sandstone, of a red color, and much\n   seamed; -- found in India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lateritic', 'a.', 'Consisting of, containing, or characterized by,\n   laterite; as, lateritic formations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lateritious', 'a.', 'Like bricks; of the color of red bricks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lates', 'n.', 'A genus of large percoid fishes, of which one species (Lates\n   Niloticus) inhabits the Nile, and another (L. calcarifer) is found in\n   the Ganges and other Indian rivers. They are valued as food fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latescence', 'n.', 'A slight withdrawal from view or knowledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latescent', 'a.', 'Slightly withdrawn from view or knowledge; as, a\n   latescent meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latewake', 'n.', 'See Lich wake, under Lich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lateward', 'a. & adv.', 'Somewhat late; backward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latex', 'n.', 'A milky or colored juice in certain plants in cavities\n   (called latex cells or latex tubes). It contains the peculiar\n   principles of the plants, whether aromatic, bitter, or acid, and in\n   many instances yields caoutchouc upon coagulation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laths', 'pl. ', 'of Lath');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lath', 'n.', 'A thin, narrow strip of wood, nailed to the rafters, studs,\n   or floor beams of a building, for the purpose of supporting the tiles,\n   plastering, etc. A corrugated metallic strip or plate is sometimes\n   used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lathed', 'imp. & p. p.', 'of Lath');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lathing', 'p. pr. & vb. n.', 'of Lath');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lath', 'v. t.', 'To cover or line with laths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lathe', 'n.', 'Formerly, a part or division of a county among the\n   Anglo-Saxons. At present it consists of four or five hundreds, and is\n   confined to the county of Kent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lathe', 'n.', 'A granary; a barn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lathe', 'n.', 'A machine for turning, that is, for shaping articles of\n   wood, metal, or other material, by causing them to revolve while acted\n   upon by a cutting tool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lathe', 'n.', 'The movable swing frame of a loom, carrying the reed for\n   separating the warp threads and beating up the weft; -- called also lay\n   and batten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lather', 'n.', 'Foam or froth made by soap moistened with water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lather', 'n.', 'Foam from profuse sweating, as of a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lathered', 'imp. & p. p.', 'of Lather');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lathering', 'p. pr. & vb. n.', 'of Lather');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lather', 'n.', 'To spread over with lather; as, to lather the face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lather', 'v. i.', 'To form lather, or a froth like lather; to accumulate\n   foam from profuse sweating, as a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lather', 'v. t.', 'To beat severely with a thong, strap, or the like; to\n   flog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lathereeve', 'n.', 'Alt. of Lathreeve');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lathreeve', 'n.', 'Formerly, the head officer of a lathe. See 1st Lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lathing', 'n.', 'The act or process of covering with laths; laths,\n   collectively; a covering of laths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lath-shaped', 'a.', 'Having a slender elongated form, like a lath; -- said\n   of the feldspar of certain igneous rocks, as diabase, as seen in\n   microscopic sections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lathwork', 'n.', 'Same as Lathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lathy', 'a.', 'Like a lath; long and slender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latian', 'a.', 'Belonging, or relating, to Latium, a country of ancient\n   Italy. See Latin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latibulized', 'imp. & p. p.', 'of Latibulize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latibulizing', 'p. pr. & vb. n.', 'of Latibulize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latibulize', 'v. i.', 'To retire into a den, or hole, and lie dormant in\n   winter; to retreat and lie hid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latibula', 'pl. ', 'of Latibulum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latibulum', 'n.', 'A concealed hiding place; a burrow; a lair; a hole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laticiferous', 'a.', 'Containing the latex; -- applied to the tissue or\n   tubular vessels in which the latex of the plant is found.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laticlave', 'n.', 'A broad stripe of purple on the fore part of the tunic,\n   worn by senators in ancient Rome as an emblem of office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laticostate', 'a.', 'Broad-ribbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latidentate', 'a.', 'Broad-toothed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latifoliate', 'a.', 'Alt. of Latifolious');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latifolious', 'a.', 'Having broad leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latimer', 'n.', 'An interpreter. [Obs.] Coke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latin', 'a.', 'Of or pertaining to Latium, or to the Latins, a people of\n   Latium; Roman; as, the Latin language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latin', 'a.', 'Of, pertaining to, or composed in, the language used by the\n   Romans or Latins; as, a Latin grammar; a Latin composition or idiom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latin', 'n.', 'A native or inhabitant of Latium; a Roman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latin', 'n.', 'The language of the ancient Romans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latin', 'n.', 'An exercise in schools, consisting in turning English into\n   Latin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latin', 'n.', 'A member of the Roman Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latin', 'v. t.', 'To write or speak in Latin; to turn or render into Latin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinism', 'n.', 'A Latin idiom; a mode of speech peculiar to Latin; also,\n   a mode of speech in another language, as English, formed on a Latin\n   model.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinist', 'n.', 'One skilled in Latin; a Latin scholar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinistic', 'a.', 'Of, pertaining to, or derived from, Latin; in the Latin\n   style or idiom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinitaster', 'n.', 'One who has but a smattering of Latin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinity', 'n.', 'The Latin tongue, style, or idiom, or the use thereof;\n   specifically, purity of Latin style or idiom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinization', 'n.', 'The act or process of Latinizing, as a word,\n   language, or country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinized', 'imp. & p. p.', 'of Latinize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinizing', 'p. pr. & vb. n.', 'of Latinize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinize', 'v. t.', 'To give Latin terminations or forms to, as to foreign\n   words, in writing Latin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinize', 'v. t.', 'To bring under the power or influence of the Romans or\n   Latins; to affect with the usages of the Latins, especially in speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinize', 'v. t.', 'To make like the Roman Catholic Church or diffuse its\n   ideas in; as, to Latinize the Church of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinize', 'v. i.', 'To use words or phrases borrowed from the Latin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinize', 'v. i.', 'To come under the influence of the Romans, or of the\n   Roman Catholic Church.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latinly', 'adv.', 'In the manner of the Latin language; in correct Latin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lation', 'n.', 'Transportation; conveyance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latirostral', 'a.', 'Alt. of Latirostrous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latirostrous', 'a.', 'Having a broad beak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latirostres', 'n. pl.', 'The broad-billed singing birds, such as the\n   swallows, and their allies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latish', 'a.', 'Somewhat late.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latisternal', 'a.', 'Having a broad breastbone, or sternum; -- said of\n   anthropoid apes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitancy', 'n.', 'Act or state of lying hid, or lurking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitant', 'a.', 'Lying hid; concealed; latent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitat', 'n.', 'A writ based upon the presumption that the person summoned\n   was hiding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitation', 'n.', 'A lying in concealment; hiding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitude', 'n.', 'Extent from side to side, or distance sidewise from a\n   given point or line; breadth; width.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitude', 'n.', 'Room; space; freedom from confinement or restraint;\n   hence, looseness; laxity; independence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitude', 'n.', 'Extent or breadth of signification, application, etc.;\n   extent of deviation from a standard, as truth, style, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitude', 'n.', 'Extent; size; amplitude; scope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitude', 'n.', 'Distance north or south of the equator, measured on a\n   meridian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitude', 'n.', 'The angular distance of a heavenly body from the\n   ecliptic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitudinal', 'a.', 'Of or pertaining to latitude; in the direction of\n   latitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitudinarian', 'a.', 'Not restrained; not confined by precise limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitudinarian', 'a.', 'Indifferent to a strict application of any standard\n   of belief or opinion; hence, deviating more or less widely from such\n   standard; lax in doctrine; as, latitudinarian divines; latitudinarian\n   theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitudinarian', 'a.', 'Lax in moral or religious principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitudinarian', 'n.', 'One who is moderate in his notions, or not\n   restrained by precise settled limits in opinion; one who indulges\n   freedom in thinking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitudinarian', 'n.', 'A member of the Church of England, in the time of\n   Charles II., who adopted more liberal notions in respect to the\n   authority, government, and doctrines of the church than generally\n   prevailed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitudinarian', 'n.', 'One who departs in opinion from the strict\n   principles of orthodoxy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitudinarianism', 'n.', 'A latitudinarian system or condition; freedom of\n   opinion in matters pertaining to religious belief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latitudinous', 'a.', 'Having latitude, or wide extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laton', 'n.', 'Alt. of Latoun');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latoun', 'n.', 'Latten, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latrant', 'a.', 'Barking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latrate', 'v. i.', 'To bark as a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latration', 'n.', 'A barking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latreutical', 'a.', 'Acting as a hired servant; serving; ministering;\n   assisting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latreutical', 'a.', 'Of or pertaining to latria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latria', 'n.', 'The highest kind of worship, or that paid to God; --\n   distinguished by the Roman Catholics from dulia, or the inferior\n   worship paid to saints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latrine', 'n.', 'A privy, or water-closet, esp. in a camp, hospital, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latrociny', 'n.', 'Theft; larceny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latten', 'n.', 'A kind of brass hammered into thin sheets, formerly much\n   used for making church utensils, as candlesticks, crosses, etc.; --\n   called also latten brass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latten', 'n.', 'Sheet tin; iron plate, covered with tin; also, any metal in\n   thin sheets; as, gold latten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latter', 'a.', 'Later; more recent; coming or happening after something\n   else; -- opposed to former; as, the former and latter rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latter', 'a.', 'Of two things, the one mentioned second.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latter', 'a.', 'Recent; modern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latter', 'a.', 'Last; latest; final.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latter-day saint', '', 'A Mormon; -- the Church of Jesus Christ of\n   Latter-day Saints being the name assumed by the whole body of Mormons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latterkin', 'n.', 'A pointed wooden tool used in glazing leaden lattice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latterly', 'adv.', 'Lately; of late; recently; at a later, as distinguished\n   from a former, period.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lattermath', 'n.', 'The latter, or second, mowing; the aftermath.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lattice', 'n.', 'Any work of wood or metal, made by crossing laths, or thin\n   strips, and forming a network; as, the lattice of a window; -- called\n   also latticework.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lattice', 'n.', 'The representation of a piece of latticework used as a\n   bearing, the bands being vertical and horizontal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latticed', 'imp. & p. p.', 'of Lattice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latticing', 'p. pr. & vb. n.', 'of Lattice');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lattice', 'v. i.', 'To make a lattice of; as, to lattice timbers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lattice', 'v. i.', 'To close, as an opening, with latticework; to furnish\n   with a lattice; as, to lattice a window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latticework', 'n.', 'Same as Lattice, n., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latticing', 'n.', 'The act or process of making a lattice of, or of fitting\n   a lattice to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latticing', 'n.', 'A system of bars crossing in the middle to form braces\n   between principal longitudinal members, as of a strut.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Latus rectum', '', 'The line drawn through a focus of a conic section\n   parallel to the directrix and terminated both ways by the curve. It is\n   the parameter of the principal axis. See Focus, and Parameter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laud', 'v. i.', 'High commendation; praise; honor; exaltation; glory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laud', 'v. i.', 'A part of divine worship, consisting chiefly of praise; --\n   usually in the pl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laud', 'v. i.', 'Music or singing in honor of any one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lauded', 'imp. & p. p.', 'of Laud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lauding', 'p. pr. & vb. n.', 'of Laud');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laud', 'v. i.', 'To praise in words alone, or with words and singing; to\n   celebrate; to extol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laudability', 'n.', 'Laudableness; praiseworthiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laudable', 'v. i.', 'Worthy of being lauded; praiseworthy; commendable; as,\n   laudable motives; laudable actions; laudable ambition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laudable', 'v. i.', 'Healthy; salubrious; normal; having a disposition to\n   promote healing; not noxious; as, laudable juices of the body; laudable\n   pus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laudableness', 'n.', 'The quality of being laudable; praiseworthiness;\n   commendableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laudably', 'adv.', 'In a laudable manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laudanine', 'n.', 'A white organic base, resembling morphine, and obtained\n   from certain varieties of opium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laudanum', 'n.', 'Tincture of opium, used for various medical purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laudation', 'v. t.', 'The act of lauding; praise; high commendation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laudative', 'a.', 'Laudatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laudative', 'n.', 'A panegyric; a eulogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laudator', 'n.', 'One who lauds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laudator', 'n.', 'An arbitrator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laudatory', 'a.', 'Of or pertaining praise, or to the expression of praise;\n   as, laudatory verses; the laudatory powers of Dryden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lauder', 'n.', 'One who lauds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laughed', 'imp. & p. p.', 'of Laugh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laughing', 'p. pr. & vb. n.', 'of Laugh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laugh', 'v. i.', 'To show mirth, satisfaction, or derision, by peculiar\n   movement of the muscles of the face, particularly of the mouth, causing\n   a lighting up of the face and eyes, and usually accompanied by the\n   emission of explosive or chuckling sounds from the chest and throat; to\n   indulge in laughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laugh', 'v. i.', 'Fig.: To be or appear gay, cheerful, pleasant, mirthful,\n   lively, or brilliant; to sparkle; to sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laugh', 'v. t.', 'To affect or influence by means of laughter or ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laugh', 'v. t.', 'To express by, or utter with, laughter; -- with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laugh', 'n.', 'An expression of mirth peculiar to the human species; the\n   sound heard in laughing; laughter. See Laugh, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laughable', 'a.', 'Fitted to excite laughter; as, a laughable story; a\n   laughable scene.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laugher', 'n.', 'One who laughs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laugher', 'n.', 'A variety of the domestic pigeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laughing', 'a. & n.', 'from Laugh, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laughingly', 'adv.', 'With laughter or merriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laughingstock', 'n.', 'An object of ridicule; a butt of sport.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laughsome', 'a.', 'Exciting laughter; also, addicted to laughter; merry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laughter', 'v. i.', 'A movement (usually involuntary) of the muscles of the\n   face, particularly of the lips, with a peculiar expression of the eyes,\n   indicating merriment, satisfaction, or derision, and usually attended\n   by a sonorous and interrupted expulsion of air from the lungs. See\n   Laugh, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laughterless', 'a.', 'Not laughing; without laughter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laughworthy', 'a.', 'Deserving to be laughed at.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laumontite', 'n.', 'A mineral, of a white color and vitreous luster. It is\n   a hydrous silicate of alumina and lime. Exposed to the air, it loses\n   water, becomes opaque, and crumbles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launce', 'n.', 'A lance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launce', 'n.', 'A balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launce', 'n.', 'See Lant, the fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launcegaye', 'n.', 'See Langegaye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launched', 'imp. & p. p.', 'of Launch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launching', 'p. pr. & vb. n.', 'of Launch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launch', 'v. i.', 'To throw, as a lance or dart; to hurl; to let fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launch', 'v. i.', 'To strike with, or as with, a lance; to pierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launch', 'v. i.', 'To cause to move or slide from the land into the water;\n   to set afloat; as, to launch a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launch', 'v. i.', 'To send out; to start (one) on a career; to set going;\n   to give a start to (something); to put in operation; as, to launch a\n   son in the world; to launch a business project or enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launch', 'v. i.', 'To move with force and swiftness like a sliding from the\n   stocks into the water; to plunge; to make a beginning; as, to launch\n   into the current of a stream; to launch into an argument or discussion;\n   to launch into lavish expenditures; -- often with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launch', 'n.', 'The act of launching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launch', 'n.', 'The movement of a vessel from land into the water;\n   especially, the sliding on ways from the stocks on which it is built.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launch', 'n.', 'The boat of the largest size belonging to a ship of war;\n   also, an open boat of any size driven by steam, naphtha, electricity,\n   or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laund', 'n.', 'A plain sprinkled with trees or underbrush; a glade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launder', 'n.', 'A washerwoman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launder', 'n.', 'A trough used by miners to receive the powdered ore from\n   the box where it is beaten, or for carrying water to the stamps, or\n   other apparatus, for comminuting, or sorting, the ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laundered', 'imp. & p. p.', 'of Launder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laundering', 'p. pr. & vb. n.', 'of Launder');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launder', 'v. i.', 'To wash, as clothes; to wash, and to smooth with a\n   flatiron or mangle; to wash and iron; as, to launder shirts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launder', 'v. i.', 'To lave; to wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Launderer', 'n.', 'One who follows the business of laundering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laundering', 'n.', 'The act, or occupation, of one who launders; washing\n   and ironing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laundress', 'n.', 'A woman whose employment is laundering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laundress', 'v. i.', 'To act as a laundress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laundries', 'pl. ', 'of Laundry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laundry', 'n.', 'A laundering; a washing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laundry', 'n.', 'A place or room where laundering is done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laundrymen', 'pl. ', 'of Laundryman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laundryman', 'n.', 'A man who follows the business of laundering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laura', 'n.', 'A number of hermitages or cells in the same neighborhood\n   occupied by anchorites who were under the same superior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lauraceous', 'a.', 'Belonging to, or resembling, a natural order\n   (Lauraceae) of trees and shrubs having aromatic bark and foliage, and\n   including the laurel, sassafras, cinnamon tree, true camphor tree, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laurate', 'n.', 'A salt of lauric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laureate', 'a.', 'Crowned, or decked, with laurel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laureate', 'n.', 'One crowned with laurel; a poet laureate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laureated', 'imp. & p. p.', 'of Laureate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laureating', 'p. pr. & vb. n.', 'of Laureate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laureate', 'v. i.', 'To honor with a wreath of laurel, as formerly was done\n   in bestowing a degree at the English universities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laureateship', 'n.', 'State, or office, of a laureate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laureation', 'n.', 'The act of crowning with laurel; the act of conferring\n   an academic degree, or honorary title.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laurel', 'n.', 'An evergreen shrub, of the genus Laurus (L. nobilis),\n   having aromatic leaves of a lanceolate shape, with clusters of small,\n   yellowish white flowers in their axils; -- called also sweet bay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laurel', 'n.', 'A crown of laurel; hence, honor; distinction; fame; --\n   especially in the plural; as, to win laurels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laurel', 'n.', 'An English gold coin made in 1619, and so called because\n   the kings head on it was crowned with laurel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laureled', 'a.', 'Crowned with laurel, or with a laurel wreath; laureate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laurentian', 'a.', 'Pertaining to, or near, the St. Lawrence River; as, the\n   Laurentian hills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laurer', 'n.', 'Laurel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laurestine', 'n.', 'The Viburnum Tinus, an evergreen shrub or tree of the\n   south of Europe, which flowers during the winter mouths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lauric', 'a.', 'Pertaining to, or derived from, the European bay or laurel\n   (Laurus nobilis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lauriferous', 'a.', 'Producing, or bringing, laurel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laurin', 'n.', 'A white crystalline substance extracted from the fruit of\n   the bay (Laurus nobilis), and consisting of a complex mixture of\n   glycerin ethers of several organic acids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laurinol', 'n.', 'Ordinary camphor; -- so called in allusion to the family\n   name (Lauraceae) of the camphor trees. See Camphor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lauriol', 'n.', 'Spurge laurel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laurite', 'n.', 'A rare sulphide of osmium and ruthenium found with\n   platinum in Borneo and Oregon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laurone', 'n.', 'The ketone of lauric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laurus', 'n.', 'A genus of trees including, according to modern authors,\n   only the true laurel (Laurus nobilis), and the larger L. Canariensis of\n   Madeira and the Canary Islands. Formerly the sassafras, the camphor\n   tree, the cinnamon tree, and several other aromatic trees and shrubs,\n   were also referred to the genus Laurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laus', 'a.', 'Loose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lava', 'n.', 'The melted rock ejected by a volcano from its top or fissured\n   sides. It flows out in streams sometimes miles in length. It also\n   issues from fissures in the earths surface, and forms beds covering\n   many square miles, as in the Northwestern United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavaret', 'n.', 'A European whitefish (Coregonus laveretus), found in the\n   mountain lakes of Sweden, Germany, and Switzerland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavatic', 'a.', 'Like lava, or composed of lava; lavic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavation', 'n.', 'A washing or cleansing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavatory', 'a.', 'Washing, or cleansing by washing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavatories', 'pl. ', 'of Lavatory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavatory', 'n.', 'A place for washing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavatory', 'n.', 'A basin or other vessel for washing in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavatory', 'n.', 'A wash or lotion for a diseased part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavatory', 'n.', 'A place where gold is obtained by washing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavature', 'n.', 'A wash or lotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laved', 'imp. & p. p.', 'of Lave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laving', 'p. pr. & vb. n.', 'of Lave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lave', 'v. t.', 'To wash; to bathe; as, to lave a bruise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lave', 'v. i.', 'To bathe; to wash ones self.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lave', 'v. t.', 'To lade, dip, or pour out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lave', 'n.', 'The remainder; others.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lave-eared', 'a.', 'Having large, pendent ears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laveer', 'v. i.', 'To beat against the wind; to tack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavement', 'n.', 'A washing or bathing; also, a clyster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavender', 'n.', 'An aromatic plant of the genus Lavandula (L. vera),\n   common in the south of Europe. It yields and oil used in medicine and\n   perfumery. The Spike lavender (L. Spica) yields a coarser oil (oil of\n   spike), used in the arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavender', 'n.', 'The pale, purplish color of lavender flowers, paler and\n   more delicate than lilac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laver', 'n.', 'A vessel for washing; a large basin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laver', 'n.', 'A large brazen vessel placed in the court of the Jewish\n   tabernacle where the officiating priests washed their hands and feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laver', 'n.', 'One of several vessels in Solomons Temple in which the\n   offerings for burnt sacrifices were washed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laver', 'n.', 'That which washes or cleanses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laver', 'n.', 'One who laves; a washer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laver', 'n.', 'The fronds of certain marine algae used as food, and for\n   making a sauce called laver sauce. Green laver is the Ulva latissima;\n   purple laver, Porphyra laciniata and P. vulgaris. It is prepared by\n   stewing, either alone or with other vegetables, and with various\n   condiments; -- called also sloke, or sloakan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laverock', 'n.', 'The lark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavic', 'a.', 'See Lavatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavish', 'a.', 'Expending or bestowing profusely; profuse; prodigal; as,\n   lavish of money; lavish of praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavish', 'a.', 'Superabundant; excessive; as, lavish spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavished', 'imp. & p. p.', 'of Lavish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavishing', 'p. pr. & vb. n.', 'of Lavish');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavish', 'v. t.', 'To expend or bestow with profusion; to use with\n   prodigality; to squander; as, to lavish money or praise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavisher', 'n.', 'One who lavishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavishly', 'adv.', 'In a lavish manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavishment', 'n.', 'The act of lavishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavishness', 'n.', 'The quality or state of being lavish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lav/sium', 'n.', 'A supposed new metallic element. It is said to have been\n   discovered in pyrites, and some other minerals, and to be of a\n   silver-white color, and malleable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavolt', 'n.', 'Alt. of Lavolta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavolta', 'n.', 'An old dance, for two persons, being a kind of waltz, in\n   which the woman made a high spring or bound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavoltateer', 'n.', 'A dancer of the lavolta.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavour', 'n.', 'A laver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lavrock', 'n.', 'Same as Laverock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'n.', 'In general, a rule of being or of conduct, established by an\n   authority able to enforce its will; a controlling regulation; the mode\n   or order according to which an agent or a power acts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'n.', 'In morals: The will of God as the rule for the disposition and\n   conduct of all responsible beings toward him and toward each other; a\n   rule of living, conformable to righteousness; the rule of action as\n   obligatory on the conscience or moral nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'n.', 'The Jewish or Mosaic code, and that part of Scripture where it\n   is written, in distinction from the gospel; hence, also, the Old\n   Testament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'n.', 'An organic rule, as a constitution or charter, establishing\n   and defining the conditions of the existence of a state or other\n   organized community.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'n.', 'Any edict, decree, order, ordinance, statute, resolution,\n   judicial, decision, usage, etc., or recognized, and enforced, by the\n   controlling authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'n.', 'In philosophy and physics: A rule of being, operation, or\n   change, so certain and constant that it is conceived of as imposed by\n   the will of God or by some controlling authority; as, the law of\n   gravitation; the laws of motion; the law heredity; the laws of thought;\n   the laws of cause and effect; law of self-preservation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'n.', 'In matematics: The rule according to which anything, as the\n   change of value of a variable, or the value of the terms of a series,\n   proceeds; mode or order of sequence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'n.', 'In arts, works, games, etc.: The rules of construction, or of\n   procedure, conforming to the conditions of success; a principle, maxim;\n   or usage; as, the laws of poetry, of architecture, of courtesy, or of\n   whist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'n.', 'Collectively, the whole body of rules relating to one subject,\n   or emanating from one source; -- including usually the writings\n   pertaining to them, and judicial proceedings under them; as, divine\n   law; English law; Roman law; the law of real property; insurance law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'n.', 'Legal science; jurisprudence; the principles of equity;\n   applied justice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'n.', 'Trial by the laws of the land; judicial remedy; litigation;\n   as, to go law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'n.', 'An oath, as in the presence of a court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'v. t.', 'Same as Lawe, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law', 'interj.', 'An exclamation of mild surprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Law-abiding', 'a.', 'Abiding the law; waiting for the operation of law for\n   the enforcement of rights; also, abiding by the law; obedient to the\n   law; as, law-abiding people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawbreaker', 'n.', 'One who disobeys the law; a criminal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawe', 'v. t.', 'To cut off the claws and balls of, as of a dogs fore\n   feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawer', 'n.', 'A lawyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawful', 'a.', 'Conformable to law; allowed by law; legitimate; competent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawful', 'a.', 'Constituted or authorized by law; rightful; as, the lawful\n   owner of lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawgiver', 'n.', 'One who makes or enacts a law or system of laws; a\n   legislator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawgiving', 'a.', 'Enacting laws; legislative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawing', 'n.', 'Going to law; litigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawing', 'n.', 'Expeditation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawless', 'a.', 'Contrary to, or unauthorized by, law; illegal; as, a\n   lawless claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawless', 'a.', 'Not subject to, or restrained by, the law of morality or\n   of society; as, lawless men or behavior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawless', 'a.', 'Not subject to the laws of nature; uncontrolled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawmaker', 'n.', 'A legislator; a lawgiver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lammaking', 'a.', 'Enacting laws; legislative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lammaking', 'n.', 'The enacting of laws; legislation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawmonger', 'n.', 'A trader in law; one who practices law as if it were a\n   trade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawn', 'n.', 'An open space between woods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawn', 'n.', 'Ground (generally in front of or around a house) covered with\n   grass kept closely mown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawm', 'n.', 'A very fine linen (or sometimes cotton) fabric with a rather\n   open texture. Lawn is used for the sleeves of a bishops official dress\n   in the English Church, and, figuratively, stands for the office itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawnd', 'n.', 'See Laund.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawny', 'a.', 'Having a lawn; characterized by a lawn or by lawns; like a\n   lawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawny', 'a.', 'Made of lawn or fine linen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawsonia', 'n.', 'An Asiatic and North African shrub (Lawsonia inermis),\n   with smooth oval leaves, and fragrant white flowers. Henna is prepared\n   from the leaves and twigs. In England the shrub is called Egyptian\n   privet, and in the West Indies, Jamaica mignonette.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawsuit', 'n.', 'An action at law; a suit in equity or admiralty; any legal\n   proceeding before a court for the enforcement of a claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawyer', 'n.', 'One versed in the laws, or a practitioner of law; one whose\n   profession is to conduct lawsuits for clients, or to advise as to\n   prosecution or defence of lawsuits, or as to legal rights and\n   obligations in other matters. It is a general term, comprehending\n   attorneys, counselors, solicitors, barristers, sergeants, and\n   advocates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawyer', 'n.', 'The black-necked stilt. See Stilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawyer', 'n.', 'The bowfin (Amia calva).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawyer', 'n.', 'The burbot (Lota maculosa).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawyerlike', 'a.', 'Alt. of Lawyerly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lawyerly', 'a.', 'Like, or becoming, a lawyer; as, lawyerlike sagacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lax', 'v. t.', 'Not tense, firm, or rigid; loose; slack; as, a lax bandage;\n   lax fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lax', 'v. t.', 'Not strict or stringent; not exact; loose; weak; vague;\n   equivocal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lax', 'v. t.', 'Having a looseness of the bowels; diarrheal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lax', 'n.', 'A looseness; diarrhea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laxation', 'n.', 'The act of loosening or slackening, or the state of being\n   loosened or slackened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laxative', 'a.', 'Having a tendency to loosen or relax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laxative', 'a.', 'Having the effect of loosening or opening the intestines,\n   and relieving from constipation; -- opposed to astringent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laxative', 'n.', 'A laxative medicine. See the Note under Cathartic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laxativeness', 'n.', 'The quality of being laxative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laxator', 'n.', 'That which loosens; -- esp., a muscle which by its\n   contraction loosens some part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laxity', 'a.', 'The state or quality of being lax; want of tenseness,\n   strictness, or exactness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laxly', 'adv.', 'In a lax manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laxness', 'n.', 'The state of being lax; laxity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'imp.', 'of Lie, to recline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'a.', 'Of or pertaining to the laity, as distinct from the clergy;\n   as, a lay person; a lay preacher; a lay brother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'a.', 'Not educated or cultivated; ignorant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'a.', 'Not belonging to, or emanating from, a particular profession;\n   unprofessional; as, a lay opinion regarding the nature of a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'n.', 'The laity; the common people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'n.', 'A meadow. See Lea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'n.', 'Faith; creed; religious profession.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'n.', 'A law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'n.', 'An obligation; a vow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'a.', 'A song; a simple lyrical poem; a ballad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'a.', 'A melody; any musical utterance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laid', 'imp. & p. p.', 'of Lay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laying', 'p. pr. & vb. n.', 'of Lay');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To cause to lie down, to be prostrate, or to lie against\n   something; to put or set down; to deposit; as, to lay a book on the\n   table; to lay a body in the grave; a shower lays the dust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To place in position; to establish firmly; to arrange with\n   regularity; to dispose in ranks or tiers; as, to lay a corner stone; to\n   lay bricks in a wall; to lay the covers on a table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To prepare; to make ready; to contrive; to provide; as, to\n   lay a snare, an ambush, or a plan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To spread on a surface; as, to lay plaster or paint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To cause to be still; to calm; to allay; to suppress; to\n   exorcise, as an evil spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To cause to lie dead or dying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To deposit, as a wager; to stake; to risk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To bring forth and deposit; as, to lay eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To apply; to put.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To impose, as a burden, suffering, or punishment; to\n   assess, as a tax; as, to lay a tax on land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To impute; to charge; to allege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To impose, as a command or a duty; as, to lay commands on\n   one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To present or offer; as, to lay an indictment in a\n   particular county; to lay a scheme before one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To state; to allege; as, to lay the venue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To point; to aim; as, to lay a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To put the strands of (a rope, a cable, etc.) in their\n   proper places and twist or unite them; as, to lay a cable or rope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To place and arrange (pages) for a form upon the imposing\n   stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'To place (new type) properly in the cases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. i.', 'To produce and deposit eggs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. i.', 'To take a position; to come or go; as, to lay forward; to\n   lay aloft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. i.', 'To lay a wager; to bet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'n.', 'That which lies or is laid or is conceived of as having been\n   laid or placed in its position; a row; a stratum; a layer; as, a lay of\n   stone or wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'A wager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'A job, price, or profit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'A share of the proceeds or profits of an enterprise; as,\n   when a man ships for a whaling voyage, he agrees for a certain lay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'A measure of yarn; a lea. See 1st Lea (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'The lathe of a loom. See Lathe, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'v. t.', 'A plan; a scheme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Layer', 'n.', 'One who, or that which, lays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Layer', 'n.', 'That which is laid; a stratum; a bed; one thickness, course,\n   or fold laid over another; as, a layer of clay or of sand in the earth;\n   a layer of bricks, or of plaster; the layers of an onion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Layer', 'n.', 'A shoot or twig of a plant, not detached from the stock,\n   laid under ground for growth or propagation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Layer', 'n.', 'An artificial oyster bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Layering', 'n.', 'A propagating by layers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laying', 'n.', 'The act of one who, or that which, lays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laying', 'n.', 'The act or period of laying eggs; the eggs laid for one\n   incubation; a clutch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laying', 'n.', 'The first coat on laths of plasterers two-coat work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Layland', 'n.', 'Land lying untilled; fallow ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laymen', 'pl. ', 'of Layman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Layman', 'n.', 'One of the people, in distinction from the clergy; one of\n   the laity; sometimes, a man not belonging to some particular\n   profession, in distinction from those who do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Layman', 'n.', 'A lay figure. See under Lay, n. (above).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Layner', 'n.', 'A whiplash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Layship', 'n.', 'The condition of being a layman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laystall', 'n.', 'A place where rubbish, dung, etc., are laid or deposited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laystall', 'n.', 'A place where milch cows are kept, or cattle on the way\n   to market are lodged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazar', 'n.', 'A person infected with a filthy or pestilential disease; a\n   leper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazaret', 'n.', 'Alt. of Lazaretto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazaretto', 'n.', 'A public building, hospital, or pesthouse for the\n   reception of diseased persons, particularly those affected with\n   contagious diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazarist', 'n.', 'Alt. of Lazarite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazarite', 'n.', 'One of the Congregation of the Priests of the Mission, a\n   religious institute founded by Vincent de Paul in 1624, and popularly\n   called Lazarists or Lazarites from the College of St. Lazare in Paris,\n   which was occupied by them until 1792.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazarlike', 'a.', 'Alt. of Lazarly');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazarly', 'a.', 'Full of sores; leprous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazaroni', 'n. pl.', 'See Lazzaroni.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazarwort', 'n.', 'Laserwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazed', 'imp. & p. p.', 'of Laze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazing', 'p. pr. & vb. n.', 'of Laze');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laze', 'v. i.', 'To be lazy or idle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laze', 'v. t.', 'To waste in sloth; to spend, as time, in idleness; as, to\n   laze away whole days.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazily', 'adv.', 'In a lazy manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laziness', 'n.', 'The state or quality of being lazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazuli', 'n.', 'A mineral of a fine azure-blue color, usually in small\n   rounded masses. It is essentially a silicate of alumina, lime, and\n   soda, with some sodium sulphide, is often marked by yellow spots or\n   veins of sulphide of iron, and is much valued for ornamental work.\n   Called also lapis lazuli, and Armenian stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazulite', 'n.', 'A mineral of a light indigo-blue color, occurring in\n   small masses, or in monoclinic crystals; blue spar. It is a hydrous\n   phosphate of alumina and magnesia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazy', 'superl.', 'Disinclined to action or exertion; averse to labor;\n   idle; shirking work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazy', 'superl.', 'Inactive; slothful; slow; sluggish; as, a lazy stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazy', 'superl.', 'Wicked; vicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazyback', 'n.', 'A support for the back, attached to the seat of a\n   carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazybones', 'n.', 'A lazy person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lazzaroni', 'n. pl.', 'The homeless idlers of Naples who live by chance\n   work or begging; -- so called from the Hospital of St. Lazarus, which\n   serves as their refuge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lea', 'n.', 'A measure of yarn; for linen, 300 yards; for cotton, 120\n   yards; a lay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lea', 'n.', 'A set of warp threads carried by a loop of the heddle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lea', 'n.', 'A meadow or sward land; a grassy field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leach', 'n.', 'See 3d Leech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leach', 'n.', 'A quantity of wood ashes, through which water passes, and\n   thus imbibes the alkali.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leach', 'n.', 'A tub or vat for leaching ashes, bark, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leached', 'imp. & p. p.', 'of Leach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaching', 'p. pr. & vb. n.', 'of Leach');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leach', 'v. t.', 'To remove the soluble constituents from by subjecting to\n   the action of percolating water or other liquid; as, to leach ashes or\n   coffee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leach', 'v. t.', 'To dissolve out; -- often used with out; as, to leach out\n   alkali from ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leach', 'v. i.', 'To part with soluble constituents by percolation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leach', 'n.', 'See Leech, a physician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leachy', 'a.', 'Permitting liquids to pass by percolation; not capable of\n   retaining water; porous; pervious; -- said of gravelly or sandy soils,\n   and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'One of the elements, a heavy, pliable, inelastic metal,\n   having a bright, bluish color, but easily tarnished. It is both\n   malleable and ductile, though with little tenacity, and is used for\n   tubes, sheets, bullets, etc. Its specific gravity is 11.37. It is\n   easily fusible, forms alloys with other metals, and is an ingredient of\n   solder and type metal. Atomic weight, 206.4. Symbol Pb (L. Plumbum). It\n   is chiefly obtained from the mineral galena, lead sulphide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'An article made of lead or an alloy of lead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'A plummet or mass of lead, used in sounding at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'A thin strip of type metal, used to separate lines of type in\n   printing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'Sheets or plates of lead used as a covering for roofs; hence,\n   pl., a roof covered with lead sheets or terne plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'A small cylinder of black lead or plumbago, used in pencils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaded', 'imp. & p. p.', 'of Lead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leading', 'p. pr. & vb. n.', 'of Lead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'v. t.', 'To cover, fill, or affect with lead; as, continuous firing\n   leads the grooves of a rifle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'v. t.', 'To place leads between the lines of; as, to lead a page;\n   leaded matter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Led', 'imp. & p. p.', 'of Lead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leading', 'p. pr. & vb. n.', 'of Lead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'v. t.', 'To guide or conduct with the hand, or by means of some\n   physical contact connection; as, a father leads a child; a jockey leads\n   a horse with a halter; a dog leads a blind man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'v. t.', 'To guide or conduct in a certain course, or to a certain\n   place or end, by making the way known; to show the way, esp. by going\n   with or going in advance of. Hence, figuratively: To direct; to\n   counsel; to instruct; as, to lead a traveler; to lead a pupil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'v. t.', 'To conduct or direct with authority; to have direction or\n   charge of; as, to lead an army, an exploring party, or a search; to\n   lead a political party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'v. t.', 'To go or to be in advance of; to precede; hence, to be\n   foremost or chief among; as, the big sloop led the fleet of yachts; the\n   Guards led the attack; Demosthenes leads the orators of all ages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'v. t.', 'To draw or direct by influence, whether good or bad; to\n   prevail on; to induce; to entice; to allure; as, to lead one to espouse\n   a righteous cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'v. t.', 'To guide or conduct ones self in, through, or along (a\n   certain course); hence, to proceed in the way of; to follow the path or\n   course of; to pass; to spend. Also, to cause (one) to proceed or follow\n   in (a certain course).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'v. t.', 'To begin a game, round, or trick, with; as, to lead\n   trumps; the double five was led.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'v. i.', 'To guide or conduct, as by accompanying, going before,\n   showing, influencing, directing with authority, etc.; to have\n   precedence or preeminence; to be first or chief; -- used in most of the\n   senses of lead, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'v. t.', 'To tend or reach in a certain direction, or to a certain\n   place; as, the path leads to the mill; gambling leads to other vices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'The act of leading or conducting; guidance; direction; as, to\n   take the lead; to be under the lead of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'precedence; advance position; also, the measure of\n   precedence; as, the white horse had the lead; a lead of a boats\n   length, or of half a second.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'The act or right of playing first in a game or round; the\n   card suit, or piece, so played; as, your partner has the lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'An open way in an ice field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'A lode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'The course of a rope from end to end.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'The width of port opening which is uncovered by the valve,\n   for the admission or release of steam, at the instant when the piston\n   is at end of its stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'the distance of haul, as from a cutting to an embankment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lead', 'n.', 'The action of a tooth, as a tooth of a wheel, in impelling\n   another tooth or a pallet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaded', 'a.', 'Fitted with lead; set in lead; as, leaded windows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaded', 'a.', 'Separated by leads, as the lines of a page.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaden', 'a.', 'Made of lead; of the nature of lead; as, a leaden ball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaden', 'a.', 'Like lead in color, etc. ; as, a leaden sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaden', 'a.', 'Heavy; dull; sluggish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leader', 'n.', 'One who, or that which, leads or conducts; a guide; a\n   conductor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leader', 'n.', 'One who goes first.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leader', 'n.', 'One having authority to direct; a chief; a commander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leader', 'n.', 'A performer who leads a band or choir in music; also, in an\n   orchestra, the principal violinist; the one who plays at the head of\n   the first violins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leader', 'n.', 'A block of hard wood pierced with suitable holes for\n   leading ropes in their proper places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leader', 'n.', 'The principal wheel in any kind of machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leader', 'n.', 'A horse placed in advance of others; one of the forward\n   pair of horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leader', 'n.', 'A pipe for conducting rain water from a roof to a cistern\n   or to the ground; a conductor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leader', 'n.', 'A net for leading fish into a pound, weir, etc. ; also, a\n   line of gut, to which the snell of a fly hook is attached.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leader', 'n.', 'A branch or small vein, not important in itself, but\n   indicating the proximity of a better one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leader', 'n.', 'The first, or the principal, editorial article in a\n   newspaper; a leading or main editorial article.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leader', 'n.', 'A type having a dot or short row of dots upon its face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leader', 'n.', 'a row of dots, periods, or hyphens, used in tables of\n   contents, etc., to lead the eye across a space to the right word or\n   number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leadership', 'n.', 'The office of a leader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leadhillite', 'n.', 'A mineral of a yellowish or greenish white color,\n   consisting of the sulphate and carbonate of lead; -- so called from\n   having been first found at Leadhills, Scotland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leading', 'a.', 'Guiding; directing; controlling; foremost; as, a leading\n   motive; a leading man; a leading example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leading', 'n.', 'The act of guiding, directing, governing, or enticing;\n   guidance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leading', 'n.', 'Suggestion; hint; example.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leadmen', 'pl. ', 'of Leadman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leadman', 'n.', 'One who leads a dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leadsmen', 'pl. ', 'of Leadsman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leadsman', 'n.', 'The man who heaves the lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leadwort', 'n.', 'A genus of maritime herbs (Plumbago). P. Europaea has\n   lead-colored spots on the leaves, and nearly lead-colored flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leady', 'a.', 'Resembling lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaves', 'pl. ', 'of Leaf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaf', 'n.', 'A colored, usually green, expansion growing from the side of\n   a stem or rootstock, in which the sap for the use of the plant is\n   elaborated under the influence of light; one of the parts of a plant\n   which collectively constitute its foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaf', 'n.', 'A special organ of vegetation in the form of a lateral\n   outgrowth from the stem, whether appearing as a part of the foliage, or\n   as a cotyledon, a scale, a bract, a spine, or a tendril.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaf', 'n.', 'Something which is like a leaf in being wide and thin and\n   having a flat surface, or in being attached to a larger body by one\n   edge or end; as : (a) A part of a book or folded sheet containing two\n   pages upon its opposite sides. (b) A side, division, or part, that\n   slides or is hinged, as of window shutters, folding doors, etc. (c) The\n   movable side of a table. (d) A very thin plate; as, gold leaf. (e) A\n   portion of fat lying in a separate fold or layer. (f) One of the teeth\n   of a pinion, especially when small.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leafed', 'imp. & p. p.', 'of Leaf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leafing', 'p. pr. & vb. n.', 'of Leaf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaf', 'v. i.', 'To shoot out leaves; to produce leaves; to leave; as, the\n   trees leaf in May.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leafage', 'n.', 'Leaves, collectively; foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leafcup', 'n.', 'A coarse American composite weed (Polymnia Uvedalia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leafed', 'a.', 'Having (such) a leaf or (so many) leaves; -- used in\n   composition; as, broad-leafed; four-leafed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leafet', 'n.', 'A leaflet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaf-footed', 'a.', 'Having leaflike expansions on the legs; -- said of\n   certain insects; as, the leaf-footed bug (Leptoglossus phyllopus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leafiness', 'n.', 'The state of being leafy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leafless', 'a.', 'Having no leaves or foliage; bearing no foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaflet', 'n.', 'A little leaf; also, a little printed leaf or a tract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaflet', 'n.', 'One of the divisions of a compound leaf; a foliole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaflet', 'n.', 'A leaflike organ or part; as, a leaflet of the gills of\n   fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaf-nosed', 'n.', 'Having a leaflike membrane on the nose; -- said of\n   certain bats, esp. of the genera Phyllostoma and Rhinonycteris. See\n   Vampire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leafstalk', 'n.', 'The stalk or petiole which supports a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leafy', 'superl', 'Full of leaves; abounding in leaves; as, the leafy\n   forest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leafy', 'superl', 'Consisting of leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'League', 'n.', 'A measure of length or distance, varying in different\n   countries from about 2.4 to 4.6 English statute miles of 5.280 feet\n   each, and used (as a land measure) chiefly on the continent of Europe,\n   and in the Spanish parts of America. The marine league of England and\n   the United States is equal to three marine, or geographical, miles of\n   6080 feet each.');
  INSERT INTO words (word, word_type, description) VALUES  (    'League', 'n.', 'A stone erected near a public road to mark the distance of\n   a league.');
  INSERT INTO words (word, word_type, description) VALUES  (    'League', 'n.', 'An alliance or combination of two or more nations, parties,\n   or persons, for the accomplishment of a purpose which requires a\n   continued course of action, as for mutual defense, or for furtherance\n   of commercial, religious, or political interests, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leagued', 'imp. & p. p.', 'of League');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaguing', 'p. pr. & vb. n.', 'of League');
  INSERT INTO words (word, word_type, description) VALUES  (    'League', 'v. i.', 'To unite in a league or confederacy; to combine for\n   mutual support; to confederate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'League', 'v. t.', 'To join in a league; to cause to combine for a joint\n   purpose; to combine; to unite; as, common interests will league\n   heterogeneous elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaguer', 'n.', 'The camp of a besieging army; a camp in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaguer', 'n.', 'A siege or beleaguering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaguer', 'v. t.', 'To besiege; to beleaguer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaguerer', 'n.', 'A besieger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leak', 'v.', 'A crack, crevice, fissure, or hole which admits water or\n   other fluid, or lets it escape; as, a leak in a roof; a leak in a boat;\n   a leak in a gas pipe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leak', 'v.', 'The entrance or escape of a fluid through a crack, fissure,\n   or other aperture; as, the leak gained on the ships pumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leak', 'a.', 'Leaky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaked', 'imp. & p. p.', 'of Leak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaking', 'p. pr. & vb. n.', 'of Leak');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leak', 'n.', 'To let water or other fluid in or out through a hole,\n   crevice, etc.; as, the cask leaks; the roof leaks; the boat leaks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leak', 'n.', 'To enter or escape, as a fluid, through a hole, crevice, etc.\n   ; to pass gradually into, or out of, something; -- usually with in or\n   out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leakage', 'n.', 'A leaking; also, the quantity that enters or issues by\n   leaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leakage', 'n.', 'An allowance of a certain rate per cent for the leaking of\n   casks, or waste of liquors by leaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leakiness', 'n.', 'The quality of being leaky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaky', 'superl.', 'Permitting water or other fluid to leak in or out; as,\n   a leaky roof or cask.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaky', 'superl.', 'Apt to disclose secrets; tattling; not close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leal', 'a.', 'Faithful; loyal; true.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leam', 'n. & v. i.', 'See Leme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leam', 'n.', 'A cord or strap for leading a dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leamer', 'n.', 'A dog held by a leam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean', 'v. t.', 'To conceal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaned', 'imp. & p. p.', 'of Lean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leant', '', 'of Lean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaning', 'p. pr. & vb. n.', 'of Lean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean', 'v. i.', 'To incline, deviate, or bend, from a vertical position; to\n   be in a position thus inclining or deviating; as, she leaned out at the\n   window; a leaning column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean', 'v. i.', 'To incline in opinion or desire; to conform in conduct; --\n   with to, toward, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean', 'v. i.', 'To rest or rely, for support, comfort, and the like; --\n   with on, upon, or against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean', 'v. i.', 'To cause to lean; to incline; to support or rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean', 'v. i.', 'Wanting flesh; destitute of or deficient in fat; not\n   plump; meager; thin; lank; as, a lean body; a lean cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean', 'v. i.', 'Wanting fullness, richness, sufficiency, or\n   productiveness; deficient in quality or contents; slender; scant;\n   barren; bare; mean; -- used literally and figuratively; as, the lean\n   harvest; a lean purse; a lean discourse; lean wages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean', 'v. i.', 'Of a character which prevents the compositor from earning\n   the usual wages; -- opposed to fat; as, lean copy, matter, or type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean', 'n.', 'That part of flesh which consist principally of muscle\n   without the fat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean', 'n.', 'Unremunerative copy or work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean-faced', 'a.', 'Having a thin face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean-faced', 'a.', 'slender or narrow; -- said of type the letters of which\n   have thin lines, or are unusually narrow in proportion to their height.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaning', 'n.', 'The act, or state, of inclining; inclination; tendency;\n   as, a leaning towards Calvinism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leanly', 'adv.', 'Meagerly; without fat or plumpness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leanness', 'n.', 'The condition or quality of being lean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean-to', 'a.', 'Having only one slope or pitch; -- said of a roof.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean-to', 'n.', 'A shed or slight building placed against the wall of a\n   larger structure and having a single-pitched roof; -- called also\n   penthouse, and to-fall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lean-witted', 'a.', 'Having but little sense or shrewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leany', 'a.', 'Lean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leap', 'n.', 'A basket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leap', 'n.', 'A weel or wicker trap for fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaped', 'imp. & p. p.', 'of Leap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leapt', '', 'of Leap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaping', 'p. pr. & vb. n.', 'of Leap');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leap', 'v. i.', 'To spring clear of the ground, with the feet; to jump; to\n   vault; as, a man leaps over a fence, or leaps upon a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leap', 'v. i.', 'To spring or move suddenly, as by a jump or by jumps; to\n   bound; to move swiftly. Also Fig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leap', 'v. t.', 'To pass over by a leap or jump; as, to leap a wall, or a\n   ditch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leap', 'v. t.', 'To copulate with (a female beast); to cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leap', 'v. t.', 'To cause to leap; as, to leap a horse across a ditch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leap', 'n.', 'The act of leaping, or the space passed by leaping; a jump; a\n   spring; a bound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leap', 'n.', 'Copulation with, or coverture of, a female beast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leap', 'n.', 'A fault.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leap', 'n.', 'A passing from one note to another by an interval, especially\n   by a long one, or by one including several other and intermediate\n   intervals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaper', 'n.', 'One who, or that which, leaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaper', 'n.', 'A kind of hooked instrument for untwisting old cordage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leapfrog', 'n.', 'A play among boys, in which one stoops down and another\n   leaps over him by placing his hands on the shoulders of the former.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leapful', 'n.', 'A basketful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaping', 'a. & n.', 'from Leap, to jump.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leapingly', 'adv.', 'By leaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leap year', '', 'Bissextile; a year containing 366 days; every fourth year\n   which leaps over a day more than a common year, giving to February\n   twenty-nine days. See Bissextile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lear', 'v. t.', 'To learn. See Lere, to learn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lear', 'n.', 'Lore; lesson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lear', 'a.', 'See Leer, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lear', 'n.', 'An annealing oven. See Leer, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Learned', 'imp. & p. p.', 'of Learn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Learnt', '', 'of Learn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Learning', 'p. pr. & vb. n.', 'of Learn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Learn', 'v. t.', 'To gain knowledge or information of; to ascertain by\n   inquiry, study, or investigation; to receive instruction concerning; to\n   fix in the mind; to acquire understanding of, or skill; as, to learn\n   the way; to learn a lesson; to learn dancing; to learn to skate; to\n   learn the violin; to learn the truth about something.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Learn', 'v. t.', 'To communicate knowledge to; to teach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Learn', 'v. i.', 'To acquire knowledge or skill; to make progress in\n   acquiring knowledge or skill; to receive information or instruction;\n   as, this child learns quickly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Learnable', 'a.', 'Such as can be learned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Learned', 'a.', 'Of or pertaining to learning; possessing, or characterized\n   by, learning, esp. scholastic learning; erudite; well-informed; as, a\n   learned scholar, writer, or lawyer; a learned book; a learned theory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Learner', 'n.', 'One who learns; a scholar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Learning', 'n.', 'The acquisition of knowledge or skill; as, the learning\n   of languages; the learning of telegraphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Learning', 'n.', 'The knowledge or skill received by instruction or study;\n   acquired knowledge or ideas in any branch of science or literature;\n   erudition; literature; science; as, he is a man of great learning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leasable', 'a.', 'Such as can be leased.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lease', 'v. i.', 'To gather what harvesters have left behind; to glean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leased', 'imp. & p. p.', 'of Lease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leasing', 'p. pr. & vb. n.', 'of Lease');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lease', 'v. t.', 'To grant to another by lease the possession of, as of\n   lands, tenements, and hereditaments; to let; to demise; as, a landowner\n   leases a farm to a tenant; -- sometimes with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lease', 'v. t.', 'To hold under a lease; to take lease of; as, a tenant\n   leases his land from the owner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lease', 'v. t.', 'A demise or letting of lands, tenements, or hereditaments\n   to another for life, for a term of years, or at will, or for any less\n   interest than that which the lessor has in the property, usually for a\n   specified rent or compensation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lease', 'v. t.', 'The contract for such letting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lease', 'v. t.', 'Any tenure by grant or permission; the time for which\n   such a tenure holds good; allotted time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leasehold', 'a.', 'Held by lease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leasehold', 'n.', 'A tenure by lease; specifically, land held as personalty\n   under a lease for years.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaseholder', 'n.', 'A tenant under a lease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaser', 'n.', 'One who leases or gleans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaser', 'n.', 'A liar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leash', 'n.', 'A thong of leather, or a long cord, by which a falconer\n   holds his hawk, or a courser his dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leash', 'n.', 'A brace and a half; a tierce; three; three creatures of any\n   kind, especially greyhounds, foxes, bucks, and hares; hence, the number\n   three in general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leash', 'n.', 'A string with a loop at the end for lifting warp threads, in\n   a loom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leashed', 'imp. & p. p.', 'of Leash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leashing', 'p. pr. & vb. n.', 'of Leash');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leash', 'v. t.', 'To tie together, or hold, with a leash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leasing', 'a.', 'The act of lying; falsehood; a lie or lies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leasow', 'n.', 'A pasture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Least', 'a.', 'Smallest, either in size or degree; shortest; lowest; most\n   unimportant; as, the least insect; the least mercy; the least space.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Least', 'adv.', 'In the smallest or lowest degree; in a degree below all\n   others; as, to reward those who least deserve it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Least', 'conj.', 'See Lest, conj.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leastways', 'adv.', 'Alt. of Leastwise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leastwise', 'adv.', 'At least; at all events.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leasy', 'a.', 'Flimsy; vague; deceptive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leat', 'n.', 'An artificial water trench, esp. one to or from a mill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leather', 'n.', 'The skin of an animal, or some part of such skin, tanned,\n   tawed, or otherwise dressed for use; also, dressed hides, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leather', 'n.', 'The skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leathered', 'imp. & p. p.', 'of Leather');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leathering', 'p. pr. & vb. n.', 'of Leather');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leather', 'v. t.', 'To beat, as with a thong of leather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leatherback', 'n.', 'A large sea turtle (Sphargis coriacea), having no bony\n   shell on its back. It is common in the warm and temperate parts of the\n   Atlantic, and sometimes weighs over a thousand pounds; -- called also\n   leather turtle, leathery turtle, leather-backed tortoise, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leatheret', 'n.', 'Alt. of Leatherette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leatherette', 'n.', 'An imitation of leather, made of paper and cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leatherhead', 'n.', 'The friar bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leathern', 'a.', 'Made of leather; consisting of. leather; as, a leathern\n   purse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leatherneck', 'n.', 'The sordid friar bird of Australia (Tropidorhynchus\n   sordidus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leatherwood', 'n.', 'A small branching shrub (Dirca palustris), with a\n   white, soft wood, and a tough, leathery bark, common in damp woods in\n   the Northern United States; -- called also moosewood, and wicopy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leathery', 'a.', 'Resembling leather in appearance or consistence; tough.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaved', 'imp. & p. p.', 'of Leave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaving', 'p. pr. & vb. n.', 'of Leave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave', 'v. i.', 'To send out leaves; to leaf; -- often with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave', 'v. t.', 'To raise; to levy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave', 'n.', 'Liberty granted by which restraint or illegality is removed;\n   permission; allowance; license.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave', 'n.', 'The act of leaving or departing; a formal parting; a\n   leaving; farewell; adieu; -- used chiefly in the phrase, to take leave,\n   i. e., literally, to take permission to go.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Left', 'imp. & p. p.', 'of Leave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaving', 'p. pr. & vb. n.', 'of Leave');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave', 'v.', 'To withdraw ones self from; to go away from; to depart\n   from; as, to leave the house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave', 'v.', 'To let remain unremoved or undone; to let stay or continue,\n   in distinction from what is removed or changed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave', 'v.', 'To cease from; to desist from; to abstain from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave', 'v.', 'To desert; to abandon; to forsake; hence, to give up; to\n   relinquish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave', 'v.', 'To let be or do without interference; as, I left him to his\n   reflections; I leave my hearers to judge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave', 'v.', 'To put; to place; to deposit; to deliver; to commit; to\n   submit -- with a sense of withdrawing ones self from; as, leave your\n   hat in the hall; we left our cards; to leave the matter to arbitrators.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave', 'v.', 'To have remaining at death; hence, to bequeath; as, he left\n   a large estate; he left a good name; he left a legacy to his niece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave', 'v. i.', 'To depart; to set out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave', 'v. i.', 'To cease; to desist; to leave off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaved', 'a.', 'Bearing, or having, a leaf or leaves; having folds; -- used\n   in combination; as, a four-leaved clover; a two-leaved gate;\n   long-leaved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaveless', 'a.', 'Leafless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaven', 'n.', 'Any substance that produces, or is designed to produce,\n   fermentation, as in dough or liquids; esp., a portion of fermenting\n   dough, which, mixed with a larger quantity of dough, produces a general\n   change in the mass, and renders it light; yeast; barm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaven', 'n.', 'Anything which makes a general assimilating (especially a\n   corrupting) change in the mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leavened', 'imp. & p. p.', 'of Leaven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leavening', 'p. pr. & vb. n.', 'of Leaven');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaven', 'v. t.', 'To make light by the action of leaven; to cause to\n   ferment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaven', 'v. t.', 'To imbue; to infect; to vitiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leavening', 'n.', 'The act of making light, or causing to ferment, by means\n   of leaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leavening', 'n.', 'That which leavens or makes light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leavenous', 'a.', 'Containing leaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaver', 'n.', 'One who leaves, or withdraws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaves', 'n.', 'pl. of Leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leave-taking', 'n.', 'Taking of leave; parting compliments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leaviness', 'n.', 'Leafiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leavings', 'n. pl.', 'Things left; remnants; relics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leavings', 'n. pl.', 'Refuse; offal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leavy', 'a.', 'Leafy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leban', 'n.', 'Alt. of Lebban');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lebban', 'n.', 'Coagulated sour milk diluted with water; -- a common\n   beverage among the Arabs. Also, a fermented liquor made of the same.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecama', 'n.', 'The hartbeest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecanomancy', 'n.', 'divination practiced with water in a basin, by\n   throwing three stones into it, and invoking the demon whose aid was\n   sought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecanoric', 'a.', 'Pertaining to, or designating, an organic acid which is\n   obtained from several varieties of lichen (Lecanora, Roccella, etc.),\n   as a white, crystalline substance, and is called also orsellic, /\n   diorsellinic acid, lecanorin, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecanorin', 'n.', 'See Lecanoric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lech', 'v. t.', 'To lick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leche', 'n.', 'See water buck, under 3d Buck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecher', 'n.', 'A man given to lewdness; one addicted, in an excessive\n   degree, to the indulgence of sexual desire, or to illicit commerce with\n   women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lechered', 'imp. & p. p.', 'of Lecher');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lechering', 'p. pr. & vb. n.', 'of Lecher');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecher', 'v. i.', 'To practice lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecherer', 'n.', 'See Lecher, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecherous', 'a.', 'Like a lecher; addicted to lewdness; lustful; also,\n   lust-provoking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lechery', 'n.', 'Free indulgence of lust; lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lechery', 'n.', 'Selfish pleasure; delight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecithin', 'n.', 'A complex, nitrogenous phosphorized substance widely\n   distributed through the animal body, and especially conspicuous in the\n   brain and nerve tissue, in yolk of eggs, and in the white blood\n   corpuscles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'lectern', 'n.', 'See Lecturn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecticae', 'pl. ', 'of Lectica');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lectica', 'n.', 'A kind of litter or portable couch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lection', 'n.', 'A lesson or selection, esp. of Scripture, read in divine\n   service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lection', 'n.', 'A reading; a variation in the text.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Lectionary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lectionary', 'n.', 'A book, or a list, of lections, for reading in divine\n   service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lector', 'n.', 'A reader of lections; formerly, a person designated to read\n   lessons to the illiterate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lectual', 'a.', 'Confining to the bed; as, a lectual disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecture', 'n.', 'The act of reading; as, the lecture of Holy Scripture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecture', 'n.', 'A discourse on any subject; especially, a formal or\n   methodical discourse, intended for instruction; sometimes, a familiar\n   discourse, in contrast with a sermon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecture', 'n.', 'A reprimand or formal reproof from one having authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecture', 'n.', 'A rehearsal of a lesson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lectured', 'imp. & p. p.', 'of Lecture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecturing', 'p. pr. & vb. n.', 'of Lecture');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecture', 'v. t.', 'To read or deliver a lecture to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecture', 'v. t.', 'To reprove formally and with authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecture', 'v. i.', 'To deliver a lecture or lectures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecturer', 'n.', 'One who lectures; an assistant preacher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lectureship', 'n.', 'The office of a lecturer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecturn', 'n.', 'A choir desk, or reading desk, in some churches, from\n   which the lections, or Scripture lessons, are chanted or read; hence, a\n   reading desk. [Written also lectern and lettern.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lecythis', 'n.', 'A genus of gigantic trees, chiefly Brazilian, of the\n   order Myrtaceae, having woody capsules opening by an apical lid.\n   Lecythis Zabucajo yields the delicious sapucaia nuts. L. Ollaria\n   produces the monkey-pots, its capsules. Its bark separates into thin\n   sheets, like paper, used by the natives for cigarette wrappers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Led', 'imp. & p. p.', 'of Lead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leden', 'n.', 'Alt. of Ledden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ledden', 'n.', 'Language; speech; voice; cry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ledge', 'n.', 'A shelf on which articles may be laid; also, that which\n   resembles such a shelf in form or use, as a projecting ridge or part,\n   or a molding or edge in joinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ledge', 'n.', 'A shelf, ridge, or reef, of rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ledge', 'n.', 'A layer or stratum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ledge', 'n.', 'A lode; a limited mass of rock bearing valuable mineral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ledge', 'n.', 'A piece of timber to support the deck, placed athwartship\n   between beams.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ledgement', 'n.', 'See Ledgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ledger', 'n.', 'A book in which a summary of accounts is laid up or\n   preserved; the final book of record in business transactions, in which\n   all debits and credits from the journal, etc., are placed under\n   appropriate heads.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ledger', 'n.', 'A large flat stone, esp. one laid over a tomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ledger', 'n.', 'A horizontal piece of timber secured to the uprights and\n   supporting floor timbers, a staircase, scaffolding, or the like. It\n   differs from an intertie in being intended to carry weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ledgment', 'n.', 'A string-course or horizontal suit of moldings, such as\n   the base moldings of a building.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ledgment', 'n.', 'The development of the surface of a body on a plane, so\n   that the dimensions of the different sides may be easily ascertained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ledgy', 'a.', 'Abounding in ledges; consisting of a ledge or reef; as, a\n   ledgy island.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lee', 'v. i.', 'To lie; to speak falsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lees', 'pl. ', 'of Lee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lee', 'n.', 'That which settles at the bottom, as of a cask of liquor (esp.\n   wine); sediment; dregs; -- used now only in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lee', 'n.', 'A sheltered place; esp., a place protected from the wind by\n   some object; the side sheltered from the wind; shelter; protection; as,\n   the lee of a mountain, an island, or a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lee', 'n.', 'That part of the hemisphere, as one stands on shipboard,\n   toward which the wind blows. See Lee, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lee', 'a.', 'Of or pertaining to the part or side opposite to that against\n   which the wind blows; -- opposed to weather; as, the lee side or lee\n   rail of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leeboard', 'n.', 'A board, or frame of planks, lowered over the side of a\n   vessel to lessen her leeway when closehauled, by giving her greater\n   draught.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leech', 'n.', 'See 2d Leach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leech', 'v. t.', 'See Leach, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leech', 'n.', 'The border or edge at the side of a sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leech', 'n.', 'A physician or surgeon; a professor of the art of healing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leech', 'n.', 'Any one of numerous genera and species of annulose worms,\n   belonging to the order Hirudinea, or Bdelloidea, esp. those species\n   used in medicine, as Hirudo medicinalis of Europe, and allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leech', 'n.', 'A glass tube of peculiar construction, adapted for drawing\n   blood from a scarified part by means of a vacuum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leeched', 'imp. & p. p.', 'of Leech');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leeching', 'p. pr. & vb. n.', 'of Leech');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leech', 'v. t.', 'To treat as a surgeon; to doctor; as, to leech wounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leech', 'v. t.', 'To bleed by the use of leeches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leechcraft', 'n.', 'The art of healing; skill of a physician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leed', 'n.', 'Alt. of Leede');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leede', 'n.', 'A caldron; a copper kettle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leef', 'a. & adv.', 'See Lief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leek', 'n.', 'A plant of the genus Allium (A. Porrum), having broadly\n   linear succulent leaves rising from a loose oblong cylindrical bulb.\n   The flavor is stronger than that of the common onion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leeme', 'v. & n.', 'See Leme.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leep', 'strong imp.', 'Leaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leer', 'v. t.', 'To learn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leer', 'a.', 'Empty; destitute; wanting');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leer', 'a.', 'Empty of contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leer', 'a.', 'Destitute of a rider; and hence, led, not ridden; as, a leer\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leer', 'a.', 'Wanting sense or seriousness; trifling; trivolous; as, leer\n   words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leer', 'n.', 'An oven in which glassware is annealed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leer', 'n.', 'The cheek.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leer', 'n.', 'Complexion; aspect; appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leer', 'n.', 'A distorted expression of the face, or an indirect glance of\n   the eye, conveying a sinister or immodest suggestion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leered', 'imp. & p. p.', 'of Leer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leering', 'p. pr. & vb. n.', 'of Leer');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leer', 'v. i.', 'To look with a leer; to look askance with a suggestive\n   expression, as of hatred, contempt, lust, etc. ; to cast a sidelong\n   lustful or malign look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leer', 'v. t.', 'To entice with a leer, or leers; as, to leer a man to\n   ruin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leere', 'n.', 'Tape or braid; an ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leeringly', 'adv.', 'In a leering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lees', 'n. pl.', 'Dregs. See 2d Lee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lees', 'n.', 'A leash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leese', 'v. t.', 'To lose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leese', 'v. t.', 'To hurt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leet', 'obs. imp.', 'of Let, to allow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leet', 'n.', 'A portion; a list, esp. a list of candidates for an office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leet', 'n.', 'A court-leet; the district within the jurisdiction of a\n   court-leet; the day on which a court-leet is held.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leet', 'n.', 'The European pollock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leetmen', 'pl. ', 'of Leetman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leetman', 'n.', 'One subject to the jurisdiction of a court-leet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leeward', 'a.', 'Pertaining to, or in the direction of, the part or side\n   toward which the wind blows; -- opposed to windward; as, a leeward\n   berth; a leeward ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leeward', 'n.', 'The lee side; the lee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leeward', 'adv.', 'Toward the lee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leeway', 'n.', 'The lateral movement of a ship to the leeward of her\n   course; drift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Left', 'imp. & p. p.', 'of Leave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Left', 'a.', 'Of or pertaining to that side of the body in man on which the\n   muscular action of the limbs is usually weaker than on the other side;\n   -- opposed to right, when used in reference to a part of the body; as,\n   the left hand, or arm; the left ear. Also said of the corresponding\n   side of the lower animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Left', 'n.', 'That part of surrounding space toward which the left side of\n   ones body is turned; as, the house is on the left when you face North.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Left', 'n.', 'Those members of a legislative assembly (as in France) who\n   are in the opposition; the advanced republicans and extreme radicals.\n   They have their seats at the left-hand side of the presiding officer.\n   See Center, and Right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Left-hand', 'a.', 'Situated on the left; nearer the left hand than the\n   right; as, the left-hand side; the left-hand road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Left-handed', 'a.', 'Having the left hand or arm stronger and more\n   dexterous than the right; using the left hand and arm with more\n   dexterity than the right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Left-handed', 'a.', 'Clumsy; awkward; unlucky; insincere; sinister;\n   malicious; as, a left-handed compliment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Left-handed', 'a.', 'Having a direction contrary to that of the hands of a\n   watch when seen in front; -- said of a twist, a rotary motion, etc.,\n   looked at from a given direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Left-handedness', 'n.', 'Alt. of Left-handiness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Left-handiness', 'n.', 'The state or quality of being left-handed;\n   awkwardness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Left-off', 'a.', 'Laid aside; cast-off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leftward', 'adv.', 'Toward or on the left side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leful', 'a.', 'See Leveful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leg', 'n.', 'A limb or member of an animal used for supporting the body,\n   and in running, climbing, and swimming; esp., that part of the limb\n   between the knee and foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leg', 'n.', 'That which resembles a leg in form or use; especially, any\n   long and slender support on which any object rests; as, the leg of a\n   table; the leg of a pair of compasses or dividers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leg', 'n.', 'The part of any article of clothing which covers the leg; as,\n   the leg of a stocking or of a pair of trousers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leg', 'n.', 'A bow, esp. in the phrase to make a leg; probably from drawing\n   the leg backward in bowing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leg', 'n.', 'A disreputable sporting character; a blackleg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leg', 'n.', 'The course and distance made by a vessel on one tack or\n   between tacks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leg', 'n.', 'An extension of the boiler downward, in the form of a narrow\n   space between vertical plates, sometimes nearly surrounding the furnace\n   and ash pit, and serving to support the boiler; -- called also water\n   leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leg', 'n.', 'The case containing the lower part of the belt which carries\n   the buckets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leg', 'n.', 'A fielder whose position is on the outside, a little in rear\n   of the batter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leg', 'v. t.', 'To use as a leg, with it as object');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leg', 'v. t.', 'To bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leg', 'v. t.', 'To run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legacies', 'pl. ', 'of Legacy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legacy', 'n.', 'A gift of property by will, esp. of money or personal\n   property; a bequest. Also Fig.; as, a legacy of dishonor or disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legacy', 'n.', 'A business with which one is intrusted by another; a\n   commission; -- obsolete, except in the phrases last legacy, dying\n   legacy, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legal', 'a.', 'Created by, permitted by, in conformity with, or relating\n   to, law; as, a legal obligation; a legal standard or test; a legal\n   procedure; a legal claim; a legal trade; anything is legal which the\n   laws do not forbid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legal', 'a.', 'According to the law of works, as distinguished from free\n   grace; or resting on works for salvation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legal', 'a.', 'According to the old or Mosaic dispensation; in accordance\n   with the law of Moses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legal', 'a.', 'Governed by the rules of law as distinguished from the rules\n   of equity; as, legal estate; legal assets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legalism', 'n.', 'Strictness, or the doctrine of strictness, in conforming\n   to law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legalist', 'n.', 'One who practices or advocates strict conformity to law;\n   in theology, one who holds to the law of works. See Legal, 2 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legality', 'n.', 'The state or quality of being legal; conformity to law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legality', 'n.', 'A conformity to, and resting upon, the letter of the law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legalization', 'n.', 'The act of making legal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legalized', 'imp. & p. p.', 'of Legalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legalizing', 'p. pr. & vb. n.', 'of Legalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legalize', 'v. t.', 'To make legal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legalize', 'v. t.', 'To interpret or apply in a legal spirit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legally', 'adv.', 'In a legal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legantine', 'a.', 'See Legatine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legatary', 'n.', 'A legatee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legate', 'n.', 'An ambassador or envoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legate', 'n.', 'An ecclesiastic representing the pope and invested with the\n   authority of the Holy See.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legate', 'n.', 'An official assistant given to a general or to the governor\n   of a province.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legate', 'n.', 'Under the emperors, a governor sent to a province.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legatee', 'n.', 'One to whom a legacy is bequeathed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legateship', 'n.', 'The office of a legate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legatine', 'a.', 'Of or pertaining to a legate; as, legatine power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legatine', 'a.', 'Made by, proceeding from, or under the sanction of, a\n   legate; as, a legatine constitution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legation', 'n.', 'The sending forth or commissioning one person to act for\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legation', 'n.', 'A legate, or envoy, and the persons associated with him\n   in his mission; an embassy; or, in stricter usage, a diplomatic\n   minister and his suite; a deputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legation', 'n.', 'The place of business or official residence of a\n   diplomatic minister at a foreign court or seat of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legation', 'n.', 'A district under the jurisdiction of a legate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legato', 'a.', 'Connected; tied; -- a term used when successive tones are\n   to be produced in a closely connected, smoothly gliding manner. It is\n   often indicated by a tie, thus /, /, or /, /, written over or under the\n   notes to be so performed; -- opposed to staccato.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legator', 'n.', 'A testator; one who bequeaths a legacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legatura', 'n.', 'A tie or brace; a syncopation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legature', 'n.', 'Legateship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lege', 'v. t.', 'To allege; to assert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legement', 'n.', 'See Ledgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legend', 'n.', 'That which is appointed to be read; especially, a chronicle\n   or register of the lives of saints, formerly read at matins, and in the\n   refectories of religious houses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legend', 'n.', 'A story respecting saints; especially, one of a marvelous\n   nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legend', 'n.', 'Any wonderful story coming down from the past, but not\n   verifiable by historical record; a myth; a fable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legend', 'n.', 'An inscription, motto, or title, esp. one surrounding the\n   field in a medal or coin, or placed upon an heraldic shield or beneath\n   an engraving or illustration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legend', 'v. t.', 'To tell or narrate, as a legend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legendary', 'a.', 'Of or pertaining to a legend or to legends; consisting\n   of legends; like a legend; fabulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legendary', 'n.', 'A book of legends; a tale or narrative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legendary', 'n.', 'One who relates legends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leger', 'n.', 'Anything that lies in a place; that which, or one who,\n   remains in a place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leger', 'n.', 'A minister or ambassador resident at a court or seat of\n   government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leger', 'n.', 'A ledger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leger', 'a.', 'Lying or remaining in a place; hence, resident; as, leger\n   ambassador.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leger', 'a.', 'Light; slender; slim; trivial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legerdemain', 'n.', 'Sleight of hand; a trick of sleight of hand; hence,\n   any artful deception or trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legerdemainist', 'n.', 'One who practices sleight of hand; a\n   prestidigitator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legerity', 'n.', 'Lightness; nimbleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legge', 'v. t.', 'To lay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legge', 'v. t.', 'To lighten; to allay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legged', 'a.', 'Having (such or so many) legs; -- used in composition; as,\n   a long-legged man; a two-legged animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leggiadro', 'a. & adv.', 'Alt. of Leggiero');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leggiero', 'a. & adv.', 'Light or graceful; in a light, delicate, and brisk\n   style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legging', 'n.', 'Alt. of Leggin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leggin', 'n.', 'A cover for the leg, like a long gaiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legging', '', 'a. & vb. n., from Leg, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leggy', 'a.', 'Having long legs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leghorn', 'n.', 'A straw plaiting used for bonnets and hats, made from the\n   straw of a particular kind of wheat, grown for the purpose in Tuscany,\n   Italy; -- so called from Leghorn, the place of exportation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legibility', 'n.', 'The quality of being legible; legibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legible', 'a.', 'Capable of being read or deciphered; distinct to the eye;\n   plain; -- used of writing or printing; as, a fair, legible manuscript.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legible', 'a.', 'Capable of being discovered or understood by apparent\n   marks or indications; as, the thoughts of men are often legible in\n   their countenances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legibleness', 'n.', 'The state or quality of being legible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legibly', 'adv.', 'In a legible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legific', 'a.', 'Of or pertaining to making laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legion', 'n.', 'A body of foot soldiers and cavalry consisting of different\n   numbers at different periods, -- from about four thousand to about six\n   thousand men, -- the cavalry being about one tenth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legion', 'n.', 'A military force; an army; military bands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legion', 'n.', 'A great number; a multitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legion', 'n.', 'A group of orders inferior to a class.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legionary', 'a.', 'Belonging to a legion; consisting of a legion or\n   legions, or of an indefinitely great number; as, legionary soldiers; a\n   legionary force.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legionaries', 'pl. ', 'of Legionary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legionary', 'n.', 'A member of a legion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legioned', 'a.', 'Formed into a legion or legions; legionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legionry', 'n.', 'A body of legions; legions, collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legislated', 'imp. & p. p.', 'of Legislate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legislating', 'p. pr. & vb. n.', 'of Legislate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legislate', 'v. i.', 'To make or enact a law or laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legislation', 'n.', 'The act of legislating; preparation and enactment of\n   laws; the laws enacted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legislative', 'a.', 'Making, or having the power to make, a law or laws;\n   lawmaking; -- distinguished from executive; as, a legislative act; a\n   legislative body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legislative', 'a.', 'Of or pertaining to the making of laws; suitable to\n   legislation; as, the transaction of legislative business; the\n   legislative style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legislatively', 'adv.', 'In a legislative manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legislator', 'n.', 'A lawgiver; one who makes laws for a state or\n   community; a member of a legislative body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legislatorial', 'a.', 'Of or pertaining to a legislator or legislature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legislatorship', 'n.', 'The office of a legislator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legislatress', 'n.', 'Alt. of Legislatrix');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legislatrix', 'n.', 'A woman who makes laws.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legislature', 'n.', 'The body of persons in a state or kingdom invested\n   with power to make and repeal laws; a legislative body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legist', 'n.', 'One skilled in the laws; a writer on law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitim', 'a.', 'The portion of movable estate to which the children are\n   entitled upon the death of the father.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimacy', 'a.', 'The state, or quality, of being legitimate, or in\n   conformity with law; hence, the condition of having been lawfully\n   begotten, or born in wedlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimate', 'a.', 'Accordant with law or with established legal forms and\n   requirements; lawful; as, legitimate government; legitimate rights; the\n   legitimate succession to the throne; a legitimate proceeding of an\n   officer; a legitimate heir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimate', 'a.', 'Lawfully begotten; born in wedlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimate', 'a.', 'Authorized; real; genuine; not false, counterfeit, or\n   spurious; as, legitimate poems of Chaucer; legitimate inscriptions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimate', 'a.', 'Conforming to known principles, or accepted rules; as,\n   legitimate reasoning; a legitimate standard, or method; a legitimate\n   combination of colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimate', 'a.', 'Following by logical sequence; reasonable; as, a\n   legitimate result; a legitimate inference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimated', 'imp. & p. p.', 'of Legitimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimating', 'p. pr. & vb. n.', 'of Legitimate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimate', 'v. t.', 'To make legitimate, lawful, or valid; esp., to put\n   in the position or state of a legitimate person before the law, by\n   legal means; as, to legitimate a bastard child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimately', 'adv.', 'In a legitimate manner; lawfully; genuinely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimateness', 'n.', 'The state or quality of being legitimate;\n   lawfulness; genuineness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimation', 'n.', 'The act of making legitimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimation', 'n.', 'Lawful birth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimatist', 'n.', 'See Legitimist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimatize', 'v. t.', 'To legitimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimism', 'n.', 'The principles or plans of legitimists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimist', 'n.', 'One who supports legitimate authority; esp., one who\n   believes in hereditary monarchy, as a divine right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimist', 'n.', 'Specifically, a supporter of the claims of the elder\n   branch of the Bourbon dynasty to the crown of France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimized', 'imp. & p. p.', 'of Legitimize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimizing', 'p. pr. & vb. n.', 'of Legitimize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legitimize', 'v. t.', 'To legitimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legless', 'a.', 'Not having a leg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lego-literary', 'a.', 'Pertaining to the literature of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leguleian', 'a.', 'Lawyerlike; legal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leguleian', 'n.', 'A lawyer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legume', 'n.', 'A pod dehiscent into two pieces or valves, and having the\n   seed attached at one suture, as that of the pea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legume', 'n.', 'The fruit of leguminous plants, as peas, beans, lupines;\n   pulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legumina', 'pl. ', 'of Legumen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legumens', 'pl. ', 'of Legumen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legumen', 'n.', 'Same as Legume.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Legumin', 'n.', 'An albuminous substance resembling casein, found as a\n   characteristic ingredient of the seeds of leguminous and grain-bearing\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leguminous', 'a.', 'Pertaining to pulse; consisting of pulse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leguminous', 'a.', 'Belonging to, or resembling, a very large natural order\n   of plants (Leguminosae), which bear legumes, including peas, beans,\n   clover, locust trees, acacias, and mimosas.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leiger', 'n.', 'See Leger, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leiotrichan', 'a.', 'Of or pertaining to the Leiotrichi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leiotrichan', 'n.', 'One of the Leiotrichi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leiotrichi', 'n. pl.', 'The division of mankind which embraces the\n   smooth-haired races.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leiotrichous', 'a.', 'Having smooth, or nearly smooth, hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leipoa', 'n.', 'A genus of Australian gallinaceous birds including but a\n   single species (Leipoa ocellata), about the size of a turkey. Its color\n   is variegated, brown, black, white, and gray. Called also native\n   pheasant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leipothymic', 'a.', 'See Lipothymic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leister', 'n.', 'Alt. of Lister');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lister', 'n.', 'A spear armed with three or more prongs, for striking fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leisurable', 'a.', 'Leisurely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leisurable', 'a.', 'Vacant of employment; not occupied; idle; leisure; as\n   leisurable hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leisurably', 'adv.', 'At leisure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leisure', 'n.', 'Freedom from occupation or business; vacant time; time\n   free from employment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leisure', 'n.', 'Time at ones command, free from engagement; convenient\n   opportunity; hence, convenience; ease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leisure', 'a.', 'Unemployed; as, leisure hours.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leisured', 'a.', 'Having leisure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leisurely', 'a.', 'Characterized by leisure; taking abundant time; not\n   hurried; as, a leisurely manner; a leisurely walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leisurely', 'adv.', 'In a leisurely manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leitmotif', 'n.', 'See Leading motive, under Leading, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leman', 'n.', 'A sweetheart, of either sex; a gallant, or a mistress; --\n   usually in a bad sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leme', 'n.', 'A ray or glimmer of light; a gleam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leme', 'v. i.', 'To shine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemmata', 'pl. ', 'of Lemma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemmas', 'pl. ', 'of Lemma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemma', 'n.', 'A preliminary or auxiliary proposition demonstrated or\n   accepted for immediate use in the demonstration of some other\n   proposition, as in mathematics or logic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemman', 'n.', 'A leman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemming', 'n.', 'Any one of several species of small arctic rodents of the\n   genera Myodes and Cuniculus, resembling the meadow mice in form. They\n   are found in both hemispheres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemnian', 'a.', 'Of or pertaining to the isle of Lemnos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemniscata', 'n.', 'Alt. of Lemniscate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemniscate', 'n.', 'A curve in the form of the figure 8, with both parts\n   symmetrical, generated by the point in which a tangent to an\n   equilateral hyperbola meets the perpendicular on it drawn from the\n   center.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemnisci', 'pl. ', 'of Lemniscus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemniscus', 'n.', 'One of two oval bodies hanging from the interior walls\n   of the body in the Acanthocephala.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemon', 'n.', 'An oval or roundish fruit resembling the orange, and\n   containing a pulp usually intensely acid. It is produced by a tropical\n   tree of the genus Citrus, the common fruit known in commerce being that\n   of the species C. Limonum or C. Medica (var. Limonum). There are many\n   varieties of the fruit, some of which are sweet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemon', 'n.', 'The tree which bears lemons; the lemon tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemonade', 'n.', 'A beverage consisting of lemon juice mixed with water and\n   sweetened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemur', 'n.', 'One of a family (Lemuridae) of nocturnal mammals allied to\n   the monkeys, but of small size, and having a sharp and foxlike muzzle,\n   and large eyes. They feed upon birds, insects, and fruit, and are\n   mostly natives of Madagascar and the neighboring islands, one genus\n   (Galago) occurring in Africa. The slow lemur or kukang of the East\n   Indies is Nycticebus tardigradus. See Galago, Indris, and Colugo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemures', 'n. pl.', 'Spirits or ghosts of the departed; specters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemuria', 'n.', 'A hypothetical land, or continent, supposed by some to\n   have existed formerly in the Indian Ocean, of which Madagascar is a\n   remnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemurid', 'a. & n.', 'Same as Lemuroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemuridous', 'a.', 'Alt. of Lemurine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemurine', 'a.', 'Lemuroid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemuroid', 'a.', 'Like or pertaining to the lemurs or the Lemuroidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemuroid', 'n.', 'One of the Lemuroidea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lemuroidea', 'n. pl.', 'A suborder of primates, including the lemurs, the\n   aye-aye, and allied species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lena', 'n.', 'A procuress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lent', 'imp. & p. p.', 'of Lend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lending', 'p. pr. & vb. n.', 'of Lend');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lend', 'v. t.', 'To allow the custody and use of, on condition of the\n   return of the same; to grant the temporary use of; as, to lend a book;\n   -- opposed to borrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lend', 'v. t.', 'To allow the possession and use of, on condition of the\n   return of an equivalent in kind; as, to lend money or some article of\n   food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lend', 'v. t.', 'To afford; to grant or furnish in general; as, to lend\n   assistance; to lend ones name or influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lend', 'v. t.', 'To let for hire or compensation; as, to lend a horse or\n   gig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lendable', 'a.', 'Such as can be lent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lender', 'n.', 'One who lends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lendes', 'n. pl.', 'See Lends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lending', 'n.', 'The act of one who lends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lending', 'n.', 'That which is lent or furnished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lends', 'n. pl.', 'Loins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lene', 'v. t.', 'To lend; to grant; to permit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lene', 'a.', 'Smooth; as, the lene breathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lene', 'a.', 'Applied to certain mute consonants, as p, k, and t (or Gr. /,\n   /, /).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lene', 'n.', 'The smooth breathing (spiritus lenis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lene', 'n.', 'Any one of the lene consonants, as p, k, or t (or Gr. /, /,\n   /).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenger', 'a.', 'Alt. of Lengest');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lengest', 'a.', 'Longer; longest; -- obsolete compar. and superl. of long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Length', 'a.', 'The longest, or longer, dimension of any object, in\n   distinction from breadth or width; extent of anything from end to end;\n   the longest line which can be drawn through a body, parallel to its\n   sides; as, the length of a church, or of a ship; the length of a rope\n   or line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Length', 'a.', 'A portion of space or of time considered as measured by its\n   length; -- often in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Length', 'a.', 'The quality or state of being long, in space or time;\n   extent; duration; as, some sea birds are remarkable for the length of\n   their wings; he was tired by the length of the sermon, and the length\n   of his walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Length', 'a.', 'A single piece or subdivision of a series, or of a number\n   of long pieces which may be connected together; as, a length of pipe; a\n   length of fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Length', 'a.', 'Detail or amplification; unfolding; continuance as, to\n   pursue a subject to a great length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Length', 'a.', 'Distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Length', 'v. t.', 'To lengthen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lengthened', 'imp. & p. p.', 'of Lengthen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lengthening', 'p. pr. & vb. n.', 'of Lengthen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lengthen', 'v. t.', 'To extent in length; to make longer in extent or\n   duration; as, to lengthen a line or a road; to lengthen life; --\n   sometimes followed by out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lengthen', 'v. i.', 'To become longer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lengthful', 'a.', 'Long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lengthily', 'adv.', 'In a lengthy manner; at great length or extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lengthiness', 'n.', 'The state or quality of being lengthy; prolixity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lengthways', 'adv.', 'Alt. of Lengthwise');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lengthwise', 'adv.', 'In the direction of the length; in a longitudinal\n   direction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lengthy', 'superl.', 'Having length; rather long or too long; prolix; not\n   brief; -- said chiefly of discourses, writings, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenience', 'n.', 'Alt. of Leniency');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leniency', 'n.', 'The quality or state of being lenient; lenity; clemency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenient', 'a.', 'Relaxing; emollient; softening; assuasive; -- sometimes\n   followed by of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenient', 'a.', 'Mild; clement; merciful; not rigorous or severe; as, a\n   lenient disposition; a lenient judge or sentence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenient', 'n.', 'A lenitive; an emollient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leniently', 'adv.', 'In a lenient manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenify', 'v. t.', 'To assuage; to soften; to mitigate; to alleviate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leniment', 'n.', 'An assuasive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenitive', 'a.', 'Having the quality of softening or mitigating, as pain or\n   acrimony; assuasive; emollient.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenitive', 'n.', 'A medicine or application that has the quality of easing\n   pain or protecting from the action of irritants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenitive', 'n.', 'A mild purgative; a laxative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenitive', 'n.', 'That which softens or mitigates; that which tends to\n   allay passion, excitement, or pain; a palliative.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenitiveness', 'n.', 'The quality of being lenitive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenitude', 'n.', 'The quality or habit of being lenient; lenity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenity', 'n.', 'The state or quality of being lenient; mildness of temper\n   or disposition; gentleness of treatment; softness; tenderness;\n   clemency; -- opposed to severity and rigor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenni-Lenape', 'n. pl.', 'A general name for a group of Algonquin tribes\n   which formerly occupied the coast region of North America from\n   Connecticut to Virginia. They included the Mohicans, Delawares,\n   Shawnees, and several other tribes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leno', 'n.', 'A light open cotton fabric used for window curtains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenocinant', 'a.', 'Given to lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenses', 'pl. ', 'of Lens');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lens', 'n.', 'A piece of glass, or other transparent substance, ground with\n   two opposite regular surfaces, either both curved, or one curved and\n   the other plane, and commonly used, either singly or combined, in\n   optical instruments, for changing the direction of rays of light, and\n   thus magnifying objects, or otherwise modifying vision. In practice,\n   the curved surfaces are usually spherical, though rarely cylindrical,\n   or of some other figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lent', '', 'imp. & p. p. of Lend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lent', 'n.', 'A fast of forty days, beginning with Ash Wednesday and\n   continuing till Easter, observed by some Christian churches as\n   commemorative of the fast of our Savior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lent', 'a.', 'Slow; mild; gentle; as, lenter heats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lent', 'a.', 'See Lento.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentamente', 'adv.', 'Slowly; in slow time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentando', 'a.', 'Slackening; retarding. Same as Rallentando.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenten', 'n.', 'Lent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenten', 'n.', 'Of or pertaining to the fast called Lent; used in, or\n   suitable to, Lent; as, the Lenten season.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenten', 'n.', 'Spare; meager; plain; somber; unostentatious; not abundant\n   or showy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lententide', 'n.', 'The season of Lenten or Lent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenticel', 'n.', 'One of the small, oval, rounded spots upon the stem or\n   branch of a plant, from which the underlying tissues may protrude or\n   roots may issue, either in the air, or more commonly when the stem or\n   branch is covered with water or earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenticel', 'n.', 'A small, lens-shaped gland on the under side of some\n   leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenticellate', 'a.', 'Producing lenticels; dotted with lenticels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenticelle', 'n.', 'Lenticel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenticulas', 'pl. ', 'of Lenticula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenticulae', 'pl. ', 'of Lenticula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenticula', 'n.', 'A kind of eruption upon the skin; lentigo; freckle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenticula', 'n.', 'A lens of small size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenticula', 'n.', 'A lenticel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenticular', 'a.', 'Resembling a lentil in size or form; having the form of\n   a double-convex lens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenticularly', 'adv.', 'In the manner of a lens; with a curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentiform', 'a.', 'Lenticular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentiginose', 'a.', 'Bearing numerous dots resembling freckles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentiginous', 'a.', 'Of or pertaining to lentigo; freckly; scurfy;\n   furfuraceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentigo', 'n.', 'A freckly eruption on the skin; freckles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentil', 'n.', 'A leguminous plant of the genus Ervum (Ervum Lens), of\n   small size, common in the fields in Europe. Also, its seed, which is\n   used for food on the continent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentiscus', 'n.', 'Alt. of Lentisk');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentisk', 'n.', 'A tree; the mastic. See Mastic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentitude', 'a.', 'Slowness; sluggishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lento', 'a. & adv.', 'Slow; in slow time; slowly; -- rarely written lente.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentoid', 'a.', 'Having the form of a lens; lens-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentor', 'a.', 'Tenacity; viscidity, as of fluids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentor', 'a.', 'Slowness; delay; sluggishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lentous', 'a.', 'Viscid; viscous; tenacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenvoi', 'n.', 'Alt. of Lenvoy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenvoy', 'n.', 'One or more detached verses at the end of a literary\n   composition, serving to convey the moral, or to address the poem to a\n   particular person; -- orig. employed in old French poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lenvoy', 'n.', 'A conclusion; a result.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leo', 'n.', 'The Lion, the fifth sign of the zodiac, marked thus [/] in\n   almanacs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leo', 'n.', 'A northern constellation east of Cancer, containing the bright\n   star Regulus at the end of the handle of the Sickle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leod', 'n.', 'People; a nation; a man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leon', 'n.', 'A lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leonced', 'a.', 'See Lionced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leonese', 'a.', 'Of or pertaining to Leon, in Spain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leonese', 'n. sing. & pl.', 'A native or natives of Leon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leonid', 'n.', 'One of the shooting stars which constitute the star shower\n   that recurs near the fourteenth of November at intervals of about\n   thirty-three years; -- so called because these shooting stars appear on\n   the heavens to move in lines directed from the constellation Leo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leonine', 'a.', 'Pertaining to, or characteristic of, the lion; as, a\n   leonine look; leonine rapacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leontodon', 'n.', 'A genus of liguliflorous composite plants, including the\n   fall dandelion (L. autumnale), and formerly the true dandelion; --\n   called also lions tooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leopard', 'n.', 'A large, savage, carnivorous mammal (Felis leopardus). It\n   is of a yellow or fawn color, with rings or roselike clusters of black\n   spots along the back and sides. It is found in Southern Asia and\n   Africa. By some the panther (Felis pardus) is regarded as a variety of\n   leopard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leopards bane', '', 'A name of several harmless plants, as Arnica montana,\n   Senecio Doronicum, and Paris quadrifolia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leopardwood', 'n.', 'See Letterwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lep', 'obs. strong imp.', 'of Leap. Leaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepadite', 'n.', 'Same as Lepadoid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepadoid', 'n.', 'A stalked barnacle of the genus Lepas, or family\n   Lepadidae; a goose barnacle. Also used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepal', 'n.', 'A sterile transformed stamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepas', 'n.', 'Any one of various species of Lepas, a genus of pedunculated\n   barnacles found attached to floating timber, bottoms of ships, Gulf\n   weed, etc.; -- called also goose barnacle. See Barnacle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leper', 'n.', 'A person affected with leprosy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepered', 'a.', 'Affected or tainted with leprosy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leperize', 'v. t.', 'To affect with leprosy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leperous', 'a.', 'Leprous; infectious; corrupting; poisonous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepid', 'a.', 'Pleasant; jocose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidine', 'n.', 'An organic base, C9H6.N.CH3, metameric with quinaldine,\n   and obtained by the distillation of cinchonine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidodendrid', 'n.', 'One of an extinct family of trees allied to the\n   modern club mosses, and including Lepidodendron and its allies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidodendroid', 'a.', 'Allied to, or resembling, Lepidodendron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidodendroid', 'n.', 'A lepidodendrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidodendron', 'n.', 'A genus of fossil trees of the Devonian and\n   Carboniferous ages, having the exterior marked with scars, mostly in\n   quincunx order, produced by the separation of the leafstalks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidoganoid', 'n.', 'Any one of a division (Lepidoganoidei) of ganoid\n   fishes, including those that have scales forming a coat of mail. Also\n   used adjectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidolite', 'n.', 'A species of mica, of a lilac or rose-violet color,\n   containing lithia. It usually occurs in masses consisting of small\n   scales. See Mica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidomelane', 'n.', 'An iron-potash mica, of a raven-black color, usually\n   found in granitic rocks in small six-sided tables, or as an aggregation\n   of minute opaque scales. See Mica.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidopter', 'n.', 'One of the Lepidoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidoptera', 'n. pl.', 'An order of insects, which includes the\n   butterflies and moths. They have broad wings, covered with minute\n   overlapping scales, usually brightly colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidopteral', 'a.', 'Alt. of Lepidopterous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidopterous', 'a.', 'Of or pertaining to the Lepidoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidopterist', 'n.', 'One who studies the Lepidoptera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidosauria', 'n. pl.', 'A division of reptiles, including the serpents\n   and lizards; the Plagiotremata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidosiren', 'n.', 'An eel-shaped ganoid fish of the order Dipnoi, having\n   both gills and lungs. It inhabits the rivers of South America. The name\n   is also applied to a related African species (Protopterus annectens).\n   The lepidosirens grow to a length of from four to six feet. Called also\n   doko.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidote', 'a.', 'Alt. of Lepidoted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepidoted', 'a.', 'Having a coat of scurfy scales, as the leaves of the\n   oleaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepisma', 'n.', 'A genus of wingless thysanurous insects having an\n   elongated flattened body, covered with shining scales and terminated by\n   seven unequal bristles. A common species (Lepisma saccharina) is found\n   in houses, and often injures books and furniture. Called also shiner,\n   silver witch, silver moth, and furniture bug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepismoid', 'a.', 'Like or pertaining to the Lepisma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leporine', 'a.', 'Of or pertaining to a hare; like or characteristic of, a\n   hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepra', 'n.', 'Leprosy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepre', 'n.', 'Leprosy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leprose', 'a.', 'Covered with thin, scurfy scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leprosity', 'n.', 'The state or quality of being leprous or scaly; also, a\n   scale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leprosy', 'n.', 'A cutaneous disease which first appears as blebs or as\n   reddish, shining, slightly prominent spots, with spreading edges. These\n   are often followed by an eruption of dark or yellowish prominent\n   nodules, frequently producing great deformity. In one variety of the\n   disease, anaesthesia of the skin is a prominent symptom. In addition\n   there may be wasting of the muscles, falling out of the hair and nails,\n   and distortion of the hands and feet with destruction of the bones and\n   joints. It is incurable, and is probably contagious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leprous', 'a.', 'Infected with leprosy; pertaining to or resembling\n   leprosy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leprous', 'a.', 'Leprose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lepry', 'n.', 'Leprosy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptiform', 'a.', 'Having a form somewhat like leptus; -- said of active\n   insect larvae having three pairs of legs. See Larva.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptocardia', 'n. pl.', 'The lowest class of Vertebrata, including only the\n   Amphioxus. The heart is represented only by a simple pulsating vessel.\n   The blood is colorless; the brain, renal organs, and limbs are wanting,\n   and the backbone is represented only by a simple, unsegmented\n   notochord. See Amphioxus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptocardian', 'a.', 'Of or pertaining to the Leptocardia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptocardian', 'n.', 'One of the Leptocardia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptodactyl', 'n.', 'A bird or other animal having slender toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptodactylous', 'a.', 'Having slender toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptology', 'n.', 'A minute and tedious discourse on trifling things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptomeningitis', 'n.', 'Inflammation of the pia mater or of the arachnoid\n   membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptorhine', 'a.', 'Having the nose narrow; -- said esp. of the skull.\n   Opposed to platyrhine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptostraca', 'n. pl.', 'An order of Crustacea, including Nebalia and\n   allied forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptothrix', 'n.', 'A genus of bacteria, characterized by having their\n   filaments very long, slender, and indistinctly articulated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptothrix', 'n.', 'Having the form of a little chain; -- applied to\n   bacteria when, as in multiplication by fission, they form a chain of\n   filiform individuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptus', 'n.', 'The six-legged young, or larva, of certain mites; --\n   sometimes used as a generic name. See Harvest mite, under Harvest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leptynite', 'n.', 'See Granulite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lere', 'n.', 'Learning; lesson; lore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lere', 'v. t. & i.', 'To learn; to teach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lere', 'a.', 'Empty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lere', 'n.', 'Flesh; skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lered', 'v. t.', 'Learned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lernaea', 'n.', 'A Linnaean genus of parasitic Entomostraca, -- the same as\n   the family Lernaeidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lernaeacea', 'n. pl.', 'A suborder of copepod Crustacea, including a large\n   number of remarkable forms, mostly parasitic on fishes. The young,\n   however, are active and swim freely. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lernean', 'n.', 'One of a family (Lernaeidae) of parasitic Crustacea found\n   attached to fishes and other marine animals. Some species penetrate the\n   skin and flesh with the elongated head, and feed on the viscera. See\n   Illust. in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lerot', 'n.', 'A small European rodent (Eliomys nitela), allied to the\n   dormouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Les', 'n.', 'A leash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lesbian', 'a.', 'Of or pertaining to the island anciently called Lesbos,\n   now Mitylene, in the Grecian Archipelago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lese', 'v. t.', 'To lose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lese-majesty', 'n.', 'See Leze majesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lesion', 'n.', 'A hurt; an injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lesion', 'n.', 'Loss sustained from failure to fulfill a bargain or\n   contract.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lesion', 'n.', 'Any morbid change in the exercise of functions or the\n   texture of organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-less', '', 'A privative adjective suffix, denoting without, destitute of,\n   not having; as witless, childless, fatherless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Less', 'conj.', 'Unless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Less', 'a.', 'Smaller; not so large or great; not so much; shorter;\n   inferior; as, a less quantity or number; a horse of less size or value;\n   in less time than before.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Less', 'adv.', 'Not so much; in a smaller or lower degree; as, less bright\n   or loud; less beautiful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Less', 'n.', 'A smaller portion or quantity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Less', 'n.', 'The inferior, younger, or smaller.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Less', 'v. t.', 'To make less; to lessen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lessee', 'v. t.', 'The person to whom a lease is given, or who takes an\n   estate by lease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lessened', 'imp. & p. p.', 'of Lessen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lessening', 'p. pr. & vb. n.', 'of Lessen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lessen', 'a.', 'To make less; to reduce; to make smaller, or fewer; to\n   diminish; to lower; to degrade; as, to lessen a kingdom, or a\n   population; to lessen speed, rank, fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lessen', 'v. i.', 'To become less; to shrink; to contract; to decrease; to\n   be diminished; as, the apparent magnitude of objects lessens as we\n   recede from them; his care, or his wealth, lessened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lessener', 'n.', 'One who, or that which, lessens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lesser', 'a.', 'Less; smaller; inferior.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lesser', 'adv.', 'Less.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lesses', 'v. t.', 'The leavings or dung of beasts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lesson', 'n.', 'Anything read or recited to a teacher by a pupil or\n   learner; something, as a portion of a book, assigned to a pupil to be\n   studied or learned at one time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lesson', 'n.', 'That which is learned or taught by an express effort;\n   instruction derived from precept, experience, observation, or\n   deduction; a precept; a doctrine; as, to take or give a lesson in\n   drawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lesson', 'n.', 'A portion of Scripture read in divine service for\n   instruction; as, here endeth the first lesson.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lesson', 'n.', 'A severe lecture; reproof; rebuke; warning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lesson', 'n.', 'An exercise; a composition serving an educational purpose;\n   a study.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lessoned', 'imp. & p. p.', 'of Lesson');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lessoning', 'p. pr. & vb. n.', 'of Lesson');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lesson', 'v. t.', 'To teach; to instruct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lessor', 'v. t.', 'One who leases; the person who lets to farm, or gives a\n   lease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lest', 'v. i.', 'To listen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lest', 'n.', 'Lust; desire; pleasure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lest', 'a.', 'Last; least.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lest', 'a.', 'For fear that; that . . . not; in order that . . . not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lest', 'a.', 'That (without the negative particle); -- after certain\n   expressions denoting fear or apprehension.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-let', '', 'A noun suffix having a diminutive force; as in streamlet,\n   wavelet, armlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let', 'v. t.', 'To retard; to hinder; to impede; to oppose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let', 'n.', 'A retarding; hindrance; obstacle; impediment; delay; -- common\n   in the phrase without let or hindrance, but elsewhere archaic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let', 'n.', 'A stroke in which a ball touches the top of the net in passing\n   over.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let', 'imp. & p. p.', 'of Let');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letted', '', 'of Let');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letting', 'p. pr. & vb. n.', 'of Let');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let', 'v. t.', 'To leave; to relinquish; to abandon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let', 'v. t.', 'To consider; to think; to esteem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let', 'v. t.', 'To cause; to make; -- used with the infinitive in the\n   active form but in the passive sense; as, let make, i. e., cause to be\n   made; let bring, i. e., cause to be brought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let', 'v. t.', 'To permit; to allow; to suffer; -- either affirmatively, by\n   positive act, or negatively, by neglecting to restrain or prevent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let', 'v. t.', 'To allow to be used or occupied for a compensation; to\n   lease; to rent; to hire out; -- often with out; as, to let a farm; to\n   let a house; to let out horses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let', 'v. t.', 'To give, grant, or assign, as a work, privilege, or\n   contract; -- often with out; as, to let the building of a bridge; to\n   let out the lathing and the plastering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let', 'v. i.', 'To forbear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let', 'v. i.', 'To be let or leased; as, the farm lets for $500 a year. See\n   note under Let, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let-alone', 'a.', 'Letting alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letch', 'v. & n.', 'See Leach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letch', 'n.', 'Strong desire; passion. (Archaic).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letchy', 'a.', 'See Leachy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lete', 'v. t.', 'To let; to leave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leten', '', 'p. p. of Lete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethal', 'n.', 'One of the higher alcohols of the paraffine series obtained\n   from spermaceti as a white crystalline solid. It is so called because\n   it occurs in the ethereal salt of lauric acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethal', 'a.', 'Deadly; mortal; fatal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethality', 'n.', 'The quality of being lethal; mortality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethargic', 'a.', 'Alt. of Lethargical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethargical', 'a.', 'Pertaining to, affected with, or resembling, lethargy;\n   morbidly drowsy; dull; heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethargized', 'imp. & p. p.', 'of Lethargize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethargizing', 'p. pr. & vb. n.', 'of Lethargize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethargize', 'v. t.', 'To make lethargic.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-gies', 'pl. ', 'of Lethargy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethargy', 'n.', 'Morbid drowsiness; continued or profound sleep, from\n   which a person can scarcely be awaked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethargy', 'n.', 'A state of inaction or indifference.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethargy', 'v. t.', 'To lethargize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethe', 'n.', 'Death.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethe', 'n.', 'A river of Hades whose waters when drunk caused\n   forgetfulness of the past.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethe', 'n.', 'Oblivion; a draught of oblivion; forgetfulness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethean', 'a.', 'Of or pertaining to Lethe; resembling in effect the water\n   of Lethe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letheed', 'a.', 'Caused by Lethe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letheon', 'n.', 'Sulphuric ether used as an anaesthetic agent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letheonize', 'v. t.', 'To subject to the influence of letheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethiferous', 'a.', 'Deadly; bringing death or destruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lethy', 'a.', 'Lethean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let-off', 'n.', 'A device for letting off, releasing, or giving forth, as\n   the warp from the cylinder of a loom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letted', 'imp. & p. p.', 'of Lette');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lette', 'v. t.', 'To let; to hinder. See Let, to hinder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letter', 'n.', 'One who lets or permits; one who lets anything for hire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letter', 'n.', 'One who retards or hinders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letter', 'n.', 'A mark or character used as the representative of a sound,\n   or of an articulation of the human organs of speech; a first element of\n   written language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letter', 'n.', 'A written or printed communication; a message expressed in\n   intelligible characters on something adapted to conveyance, as paper,\n   parchment, etc.; an epistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letter', 'n.', 'A writing; an inscription.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letter', 'n.', 'Verbal expression; literal statement or meaning; exact\n   signification or requirement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letter', 'n.', 'A single type; type, collectively; a style of type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letter', 'n.', 'Learning; erudition; as, a man of letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letter', 'n.', 'A letter; an epistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettered', 'imp. & p. p.', 'of Letter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettering', 'p. pr. & vb. n.', 'of Letter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letter', 'v. t.', 'To impress with letters; to mark with letters or words;\n   as, a book gilt and lettered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettered', 'a.', 'Literate; educated; versed in literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettered', 'a.', 'Of or pertaining to learning or literature; learned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettered', 'a.', 'Inscribed or stamped with letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letterer', 'n.', 'One who makes, inscribes, or engraves, alphabetical\n   letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettering', 'n.', 'The act or business of making, or marking with, letters,\n   as by cutting or painting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettering', 'n.', 'The letters made; as, the lettering of a sign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letterless', 'a.', 'Not having a letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letterless', 'a.', 'Illiterate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettern', 'n.', 'See Lecturn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letterpress', 'n.', 'Print; letters and words impressed on paper or other\n   material by types; -- often used of the reading matter in distinction\n   from the illustrations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letterure', 'n.', 'Letters; literature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letterwood', 'n.', 'The beautiful and highly elastic wood of a tree of the\n   genus Brosimum (B. Aubletii), found in Guiana; -- so called from black\n   spots in it which bear some resemblance to hieroglyphics; also called\n   snakewood, and leopardwood. It is much used for bows and for walking\n   sticks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettic', 'a.', 'Of or pertaining to the Letts; Lettish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettic', 'a.', 'Of or pertaining to a branch of the Slavic family,\n   subdivided into Lettish, Lithuanian, and Old Prussian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettic', 'n.', 'The language of the Letts; Lettish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettic', 'n.', 'The language of the Lettic race, including Lettish,\n   Lithuanian, and Old Prussian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettish', 'a.', 'Of or pertaining to the Letts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettish', 'n.', 'The language spoken by the Letts. See Lettic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettrure', 'n.', 'See Letterure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letts', 'n. pl.', 'An Indo-European people, allied to the Lithuanians and\n   Old Prussians, and inhabiting a part of the Baltic provinces of Russia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lettuce', 'n.', 'A composite plant of the genus Lactuca (L. sativa), the\n   leaves of which are used as salad. Plants of this genus yield a milky\n   juice, from which lactucarium is obtained. The commonest wild lettuce\n   of the United States is L. Canadensis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Letuary', 'n.', 'Electuary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Let-up', 'n.', 'Abatement; also, cessation; as, it blew a gale for three\n   days without any let-up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leuc-', '', 'Same as Leuco-.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucadendron', 'n.', 'A genus of evergreen shrubs from the Cape of Good\n   Hope, having handsome foliage. Leucadendron argenteum is the silverboom\n   of the colonists.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucaniline', 'n.', 'A colorless, crystalline, organic base, obtained from\n   rosaniline by reduction, and also from other sources. It forms\n   colorless salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leuchaemia', 'n.', 'See Leucocythaemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucic', 'a.', 'Alt. of Leucinic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucinic', 'a.', 'Pertaining to, or designating, an acid obtained from\n   leucin, and called also oxycaproic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucin', 'n.', 'A white, crystalline, nitrogenous substance formed in the\n   decomposition of albuminous matter by pancreatic digestion, by the\n   action of boiling dilute sulphuric acid, and by putrefaction. It is\n   also found as a constituent of various tissues and organs, as the\n   spleen, pancreas, etc., and likewise in the vegetable kingdom.\n   Chemically it is to be considered as amido-caproic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucite', 'n.', 'A mineral having a glassy fracture, occurring in\n   translucent trapezohedral crystals. It is a silicate of alumina and\n   potash. It is found in the volcanic rocks of Italy, especially at\n   Vesuvius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucite', 'n.', 'A leucoplast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucitic', 'a.', 'Containing leucite; as, leucitic rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucitoid', 'n.', 'The trapezohedron or tetragonal trisoctahedron; -- so\n   called as being the form of the mineral leucite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leuco-', '', 'Alt. of Leuc-');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leuc-', '', 'A combining form signifying white, colorless; specif. (Chem.),\n   denoting an extensive series of colorless organic compounds, obtained\n   by reduction from certain other colored compounds; as, leucaniline,\n   leucaurin, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucocyte', 'n.', 'A colorless corpuscle, as one of the white blood\n   corpuscles, or those found in lymph, marrow of bone, connective tissue,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucocythaemia', 'n.', 'Alt. of Leucocythemia');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucocythemia', 'n.', 'A disease in which the white corpuscles of the blood\n   are largely increased in number, and there is enlargement of the\n   spleen, or the lymphatic glands; leuchaemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucocytogenesis', 'n.', 'The formation of leucocytes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucoethiopic', 'a.', 'White and black; -- said of a white animal of a\n   black species, or the albino of the negro race.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucoethiops', 'n.', 'An albino.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucoline', 'n.', 'A nitrogenous organic base from coal tar, and identical\n   with quinoline. Cf. Quinoline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucoma', 'n.', 'A white opacity in the cornea of the eye; -- called also\n   albugo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucomaine', 'n.', 'An animal base or alkaloid, appearing in the tissue\n   during life; hence, a vital alkaloid, as distinguished from a ptomaine\n   or cadaveric poison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leuconic', 'a.', 'Pertaining to, or designating, a complex organic acid,\n   obtained as a yellowish white gum by the oxidation of croconic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucopathy', 'n.', 'The state of an albino, or of a white child of black\n   parents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucophane', 'n.', 'A mineral of a greenish yellow color; it is a silicate\n   of glucina, lime, and soda with fluorine. Called also leucophanite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucophlegmacy', 'n.', 'A dropsical habit of body, or the commencement of\n   anasarca; paleness, with viscid juices and cold sweats.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucophlegmatic', 'a.', 'Having a dropsical habit of body, with a white\n   bloated skin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucophyll', 'n.', 'A colorless substance isomeric with chlorophyll,\n   contained in parts of plants capable of becoming green.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucophyllous', 'a.', 'Having white or silvery foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucoplast', 'n.', 'Alt. of Leucoplastid');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucoplastid', 'n.', 'One of certain very minute whitish or colorless\n   granules occurring in the protoplasm of plants and supposed to be the\n   nuclei around which starch granules will form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucopyrite', 'n.', 'A mineral of a color between white and steel-gray,\n   with a metallic luster, and consisting chiefly of arsenic and iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucorrhoea', 'n.', 'A discharge of a white, yellowish, or greenish, viscid\n   mucus, resulting from inflammation or irritation of the membrane lining\n   the genital organs of the female; the whites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucoryx', 'n.', 'A large antelope of North Africa (Oryx leucoryx), allied\n   to the gemsbok.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucoscope', 'n.', 'An instrument, devised by Professor Helmholtz, for\n   testing the color perception of the eye, or for comparing different\n   lights, as to their constituent colors or their relative whiteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucosoid', 'a.', 'Like or pertaining to the Leucosoidea, a tribe of marine\n   crabs including the box crab or Calappa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucosphere', 'n.', 'The inner corona.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucoturic', 'a.', 'Pertaining to, or designating, a nitrogenous organic\n   substance of the uric acid group, called leucoturic acid or oxalantin.\n   See Oxalantin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucous', 'a.', 'White; -- applied to albinos, from the whiteness of their\n   skin and hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leucoxene', 'n.', 'A nearly opaque white mineral, in part identical with\n   titanite, observed in some igneous rocks as the result of the\n   alteration of titanic iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leukaemia', 'n.', 'Leucocythaemia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leuke', 'n.', 'Alt. of Leukeness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leukeness', 'n.', 'See Luke, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leukoplast', 'n.', 'See Leucoplast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levana', 'n.', 'A goddess who protected newborn infants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levant', 'a.', 'Rising or having risen from rest; -- said of cattle. See\n   Couchant and levant, under Couchant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levant', 'n.', 'The countries washed by the eastern part of the\n   Mediterranean and its contiguous waters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levant', 'n.', 'A levanter (the wind so called).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levant', 'a.', 'Eastern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levant', 'v. i.', 'To run away from ones debts; to decamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levanter', 'v.', 'One who levants, or decamps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levanter', 'n.', 'A strong easterly wind peculiar to the Mediterranean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levantine', 'n.', 'Of or pertaining to the Levant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levantine', 'n.', 'A native or inhabitant of the Levant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levantine', 'n.', 'A stout twilled silk fabric, formerly made in the\n   Levant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levari facias', '', 'A writ of execution at common law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levation', 'n.', 'The act of raising; elevation; upward motion, as that\n   produced by the action of a levator muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levator', 'n.', 'A muscle that serves to raise some part, as the lip or the\n   eyelid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levator', 'n.', 'A surgical instrument used to raise a depressed part of\n   the skull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leve', 'a.', 'Dear. See Lief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leve', 'n. & v.', 'Same as 3d & 4th Leave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leve', 'v. i.', 'To live.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leve', 'v. t.', 'To believe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leve', 'v. t.', 'To grant; -- used esp. in exclamations or prayers followed\n   by a dependent clause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levee', 'n.', 'The act of rising.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levee', 'n.', 'A morning assembly or reception of visitors, -- in\n   distinction from a soiree, or evening assembly; a matinee; hence, also,\n   any general or somewhat miscellaneous gathering of guests, whether in\n   the daytime or evening; as, the presidents levee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levee', 'v. t.', 'To attend the levee or levees of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levee', 'n.', 'An embankment to prevent inundation; as, the levees along\n   the Mississippi; sometimes, the steep bank of a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levee', 'v. t.', 'To keep within a channel by means of levees; as, to levee\n   a river.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levee en masse', '', 'See Levy in mass, under Levy, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leveful', 'n.', 'Allowable; permissible; lawful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'n.', 'A line or surface to which, at every point, a vertical or\n   plumb line is perpendicular; a line or surface which is everywhere\n   parallel to the surface of still water; -- this is the true level, and\n   is a curve or surface in which all points are equally distant from the\n   center of the earth, or rather would be so if the earth were an exact\n   sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'n.', 'A horizontal line or plane; that is, a straight line or a\n   plane which is tangent to a true level at a given point and hence\n   parallel to the horizon at that point; -- this is the apparent level at\n   the given point.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'n.', 'An approximately horizontal line or surface at a certain\n   degree of altitude, or distance from the center of the earth; as, to\n   climb from the level of the coast to the level of the plateau and then\n   descend to the level of the valley or of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'n.', 'Hence, figuratively, a certain position, rank, standard,\n   degree, quality, character, etc., conceived of as in one of several\n   planes of different elevation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'n.', 'A uniform or average height; a normal plane or altitude; a\n   condition conformable to natural law or which will secure a level\n   surface; as, moving fluids seek a level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'n.', 'An instrument by which to find a horizontal line, or adjust\n   something with reference to a horizontal line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'n.', 'A measurement of the difference of altitude of two points,\n   by means of a level; as, to take a level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'n.', 'A horizontal passage, drift, or adit, in a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'a.', 'Even; flat; having no part higher than another; having, or\n   conforming to, the curvature which belongs to the undisturbed liquid\n   parts of the earths surface; as, a level field; level ground; the\n   level surface of a pond or lake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'a.', 'Coinciding or parallel with the plane of the horizon;\n   horizontal; as, the telescope is now level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'a.', 'Even with anything else; of the same height; on the same\n   line or plane; on the same footing; of equal importance; -- followed by\n   with, sometimes by to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'a.', 'Straightforward; direct; clear; open.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'a.', 'Well balanced; even; just; steady; impartial; as, a level\n   head; a level understanding. [Colloq.]');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'a.', 'Of even tone; without rising or falling inflection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leveled', 'imp. & p. p.', 'of Level');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levelled', '', 'of Level');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leveling', 'p. pr. & vb. n.', 'of Level');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levelling', '', 'of Level');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'v. t.', 'To make level; to make horizontal; to bring to the\n   condition of a level line or surface; hence, to make flat or even; as,\n   to level a road, a walk, or a garden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'v. t.', 'To bring to a lower level; to overthrow; to topple down;\n   to reduce to a flat surface; to lower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'v. t.', 'To bring to a horizontal position, as a gun; hence, to\n   point in taking aim; to aim; to direct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'v. t.', 'Figuratively, to bring to a common level or plane, in\n   respect of rank, condition, character, privilege, etc.; as, to level\n   all the ranks and conditions of men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'v. t.', 'To adjust or adapt to a certain level; as, to level\n   remarks to the capacity of children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'v. i.', 'To be level; to be on a level with, or on an equality\n   with, something; hence, to accord; to agree; to suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Level', 'v. i.', 'To aim a gun, spear, etc., horizontally; hence, to aim or\n   point a weapon in direct line with the mark; fig., to direct the eye,\n   mind, or effort, directly to an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leveler', 'n.', 'One who, or that which, levels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leveler', 'n.', 'One who would remove social inequalities or distinctions;\n   a socialist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leveling', 'n.', 'The act or operation of making level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leveling', 'n.', 'The art or operation of using a leveling instrument for\n   finding a horizontal line, for ascertaining the differences of level\n   between different points of the earths surface included in a survey,\n   for establishing grades, etc., as in finding the descent of a river, or\n   locating a line of railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levelism', 'n.', 'The disposition or endeavor to level all distinctions of\n   rank in society.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levelly', 'adv.', 'In an even or level manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levelness', 'n.', 'The state or quality of being level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leven', 'n.', 'Lightning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lever', 'a.', 'More agreeable; more pleasing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lever', 'adv.', 'Rather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lever', 'n.', 'A rigid piece which is capable of turning about one point,\n   or axis (the fulcrum), and in which are two or more other points where\n   forces are applied; -- used for transmitting and modifying force and\n   motion. Specif., a bar of metal, wood, or other rigid substance, used\n   to exert a pressure, or sustain a weight, at one point of its length,\n   by receiving a force or power at a second, and turning at a third on a\n   fixed point called a fulcrum. It is usually named as the first of the\n   six mechanical powers, and is of three kinds, according as either the\n   fulcrum F, the weight W, or the power P, respectively, is situated\n   between the other two, as in the figures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lever', 'n.', 'A bar, as a capstan bar, applied to a rotatory piece to turn\n   it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lever', 'n.', 'An arm on a rock shaft, to give motion to the shaft or to\n   obtain motion from it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leverage', 'n.', 'The action of a lever; mechanical advantage gained by the\n   lever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leveret', 'n.', 'A hare in the first year of its age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leverock', 'n.', 'A lark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leverwood', 'n.', 'The American hop hornbeam (Ostrya Virginica), a small\n   tree with very tough wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levesel', 'n.', 'A leafy shelter; a place covered with foliage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levet', 'n.', 'A trumpet call for rousing soldiers; a reveille.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leviable', 'a.', 'Fit to be levied; capable of being assessed and\n   collected; as, sums leviable by course of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leviathan', 'n.', 'An aquatic animal, described in the book of Job, ch.\n   xli., and mentioned in other passages of Scripture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leviathan', 'n.', 'The whale, or a great whale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levier', 'n.', 'One who levies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levigable', 'v. t.', 'Capable of being levigated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levigate', 'a.', 'Made less harsh or burdensome; alleviated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levigated', 'imp. & p. p.', 'of Levigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levigating', 'p. pr. & vb. n.', 'of Levigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levigate', 'v. t.', 'To make smooth in various senses');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levigate', 'v. t.', 'To free from grit; to reduce to an impalpable powder\n   or paste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levigate', 'v. t.', 'To mix thoroughly, as liquids or semiliquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levigate', 'v. t.', 'To polish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levigate', 'v. t.', 'To make smooth in action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levigate', 'v. t.', 'Technically, to make smooth by rubbing in a moist\n   condition between hard surfaces, as in grinding pigments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levigate', 'a.', 'Made smooth, as if polished.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levigation', 'n.', 'The act or operation of levigating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levin', 'n.', 'Lightning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leviner', 'n.', 'A swift hound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levir', 'n.', 'A husbands brother; -- used in reference to levirate\n   marriages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levirate', 'a.', 'Alt. of Leviratical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leviratical', 'a.', 'Of, pertaining to, or in accordance with, a law of the\n   ancient Israelites and other tribes and races, according to which a\n   woman, whose husband died without issue, was married to the husbands\n   brother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leviration', 'n.', 'Levirate marriage or marriages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levirostres', 'n. pl.', 'A group of birds, including the hornbills,\n   kingfishers, and related forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levitate', 'v. i.', 'To rise, or tend to rise, as if lighter than the\n   surrounding medium; to become buoyant; -- opposed to gravitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levitate', 'v. t.', 'To make buoyant; to cause to float in the air; as, to\n   levitate a table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levitation', 'n.', 'Lightness; buoyancy; act of making light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levitation', 'n.', 'The act or process of making buoyant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levite', 'n.', 'One of the tribe or family of Levi; a descendant of Levi;\n   esp., one subordinate to the priests (who were of the same tribe) and\n   employed in various duties connected with the tabernacle first, and\n   afterward the temple, such as the care of the building, bringing of\n   wood and other necessaries for the sacrifices, the music of the\n   services, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levite', 'n.', 'A priest; -- so called in contempt or ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levitical', 'a.', 'Of or pertaining to a Levite or the Levites.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levitical', 'a.', 'Priestly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levitical', 'a.', 'Of or pertaining to, or designating, the law contained\n   in the book of Leviticus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levitically', 'adv.', 'After the manner of the Levites; in accordance with\n   the levitical law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leviticus', 'n.', 'The third canonical book of the Old Testament,\n   containing the laws and regulations relating to the priests and Levites\n   among the Hebrews, or the body of the ceremonial law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levity', 'n.', 'The quality of weighing less than something else of equal\n   bulk; relative lightness, especially as shown by rising through, or\n   floating upon, a contiguous substance; buoyancy; -- opposed to gravity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levity', 'n.', 'Lack of gravity and earnestness in deportment or character;\n   trifling gayety; frivolity; sportiveness; vanity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levity', 'n.', 'Lack of steadiness or constancy; disposition to change;\n   fickleness; volatility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levo-', '', 'A prefix from L. laevus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levo-', '', 'Pertaining to, or toward, the left; as, levorotatory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levo-', '', 'Turning the plane of polarized light to the left; as,\n   levotartaric acid; levoracemic acid; levogyratory crystals, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levogyrate', 'a.', 'Turning or twisting the plane of polarization towards\n   the left, as levulose, levotartaric acid, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levorotatory', 'a.', 'Turning or rotating the plane of polarization towards\n   the left; levogyrate, as levulose, left-handed quartz crystals, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levulin', 'n.', 'A substance resembling dextrin, obtained from the bulbs of\n   the dahlia, the artichoke, and other sources, as a colorless, spongy,\n   amorphous material. It is so called because by decomposition it yields\n   levulose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levulinic', 'a.', 'Pertaining to, or denoting, an acid (called also\n   acetyl-propionic acid), C5H8O3, obtained by the action of dilute acids\n   on various sugars (as levulose).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levulosan', 'n.', 'An unfermentable carbohydrate obtained by gently heating\n   levulose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levulose', 'n.', 'A sirupy variety of sugar, rarely obtained crystallized,\n   occurring widely in honey, ripe fruits, etc., and hence called also\n   fruit sugar. It is called levulose, because it rotates the plane of\n   polarization to the left.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levies', 'pl. ', 'of Levy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levy', 'n.', 'A name formerly given in Pennsylvania, Maryland, and Virginia\n   to the Spanish real of one eighth of a dollar (or 12/ cents), valued at\n   eleven pence when the dollar was rated at 7s. 6d.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levy', 'n.', 'The act of levying or collecting by authority; as, the levy\n   of troops, taxes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levy', 'n.', 'That which is levied, as an army, force, tribute, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levy', 'n.', 'The taking or seizure of property on executions to satisfy\n   judgments, or on warrants for the collection of taxes; a collecting by\n   execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levied', 'imp. & p. p.', 'of Levy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levying', 'p. pr. & vb. n.', 'of Levy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levy', 'v. t.', 'To raise, as a siege.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levy', 'v. t.', 'To raise; to collect; said of troops, to form into an army\n   by enrollment, conscription, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levy', 'v. t.', 'To raise or collect by assessment; to exact by authority;\n   as, to levy taxes, toll, tribute, or contributions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levy', 'v. t.', 'To gather or exact; as, to levy money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levy', 'v. t.', 'To erect, build, or set up; to make or construct; to raise\n   or cast up; as, to levy a mill, dike, ditch, a nuisance, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levy', 'v. t.', 'To take or seize on execution; to collect by execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levy', 'v. i.', 'To seize property, real or personal, or subject it to the\n   operation of an execution; to make a levy; as, to levy on property; the\n   usual mode of levying, in England, is by seizing the goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levyne', 'n.', 'Alt. of Levynite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Levynite', 'n.', 'A whitish, reddish, or yellowish, transparent or\n   translucent mineral, allied to chabazite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lew', 'a.', 'Lukewarm; tepid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lewd', 'superl.', 'Not clerical; laic; laical; hence, unlearned; simple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lewd', 'superl.', 'Belonging to the lower classes, or the rabble; idle and\n   lawless; bad; vicious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lewd', 'superl.', 'Given to the promiscuous indulgence of lust; dissolute;\n   lustful; libidinous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lewd', 'superl.', 'Suiting, or proceeding from, lustfulness; involving\n   unlawful sexual desire; as, lewd thoughts, conduct, or language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lewdster', 'n.', 'A lewd person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lewis', 'n.', 'Alt. of Lewisson');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lewisson', 'n.', 'An iron dovetailed tenon, made in sections, which can be\n   fitted into a dovetail mortise; -- used in hoisting large stones, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lewisson', 'n.', 'A kind of shears used in cropping woolen cloth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leges', 'pl. ', 'of Lex');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lex', 'n.', 'Law; as, lex talionis, the law of retaliation; lex terrae, the\n   law of the land; lex fori, the law of the forum or court; lex loci, the\n   law of the place; lex mercatoria, the law or custom of merchants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexical', 'a.', 'Of or pertaining to a lexicon, to lexicography, or words;\n   according or conforming to a lexicon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexicographer', 'n.', 'The author or compiler of a lexicon or dictionary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexicographic', 'a.', 'Alt. of Lexicographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexicographical', 'a.', 'Of or pertaining to, or according to,\n   lexicography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexicographist', 'n.', 'A lexicographer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexicography', 'n.', 'The art, process, or occupation of making a lexicon\n   or dictionary; the principles which are applied in making dictionaries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexicologist', 'n.', 'One versed in lexicology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexicology', 'n.', 'The science of the derivation and signification of\n   words; that branch of learning which treats of the signification and\n   application of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexicon', 'n.', 'A vocabulary, or book containing an alphabetical\n   arrangement of the words in a language or of a considerable number of\n   them, with the definition of each; a dictionary; especially, a\n   dictionary of the Greek, Hebrew, or Latin language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexiconist', 'n.', 'A writer of a lexicon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexigraphic', 'a.', 'Of or pertaining to lexigraphy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexigraphy', 'n.', 'The art or practice of defining words; definition of\n   words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexiphanic', 'a.', 'Using, or interlarded with, pretentious words;\n   bombastic; as, a lexiphanic writer or speaker; lexiphanic writing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexiphanicism', 'n.', 'The use of pretentious words, language, or style.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lexipharmic', 'a.', 'See Alexipharmic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ley', 'v. t. & i.', 'To lay; to wager.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ley', 'n.', 'Law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ley', 'n.', 'See Lye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ley', 'n.', 'Grass or meadow land; a lea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ley', 'a.', 'Fallow; unseeded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leyden jar', '', 'Alt. of Leyden phial');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leyden phial', '', 'A glass jar or bottle used to accumulate electricity.\n   It is coated with tin foil, within and without, nearly to its top, and\n   is surmounted by a brass knob which communicates with the inner\n   coating, for the purpose of charging it with electricity. It is so\n   named from having been invented in Leyden, Holland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leyser', 'n.', 'Leisure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Leze majesty', '', 'Any crime committed against the sovereign power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lherzolite', 'n.', 'An igneous rock consisting largely of chrysolite, with\n   pyroxene and picotite (a variety of spinel containing chromium).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Li', 'n.', 'A Chinese measure of distance, being a little more than one\n   third of a mile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Li', 'n.', 'A Chinese copper coin; a cash. See Cash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liabilities', 'pl. ', 'of Liability');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liability', 'n.', 'The state of being liable; as, the liability of an\n   insurer; liability to accidents; liability to the law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liability', 'n.', 'That which one is under obligation to pay, or for which\n   one is liable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liability', 'n.', 'the sum of ones pecuniary obligations; -- opposed to\n   assets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liable', 'v. t.', 'Bound or obliged in law or equity; responsible;\n   answerable; as, the surety is liable for the debt of his principal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liable', 'v. t.', 'Exposed to a certain contingency or casualty, more or\n   less probable; -- with to and an infinitive or noun; as, liable to\n   slip; liable to accident.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liableness', 'n.', 'Quality of being liable; liability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liage', 'n.', 'Union by league; alliance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liaison', 'n.', 'A union, or bond of union; an intimacy; especially, an\n   illicit intimacy between a man and a woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liane', 'n.', 'Alt. of Liana');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liana', 'n.', 'A luxuriant woody plant, climbing high trees and having\n   ropelike stems. The grapevine often has the habit of a liane. Lianes\n   are abundant in the forests of the Amazon region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liar', 'n.', 'A person who knowingly utters falsehood; one who lies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liard', 'a.', 'Gray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liard', 'n.', 'A French copper coin of one fourth the value of a sou.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lias', 'n.', 'The lowest of the three divisions of the Jurassic period; a\n   name given in England and Europe to a series of marine limestones\n   underlying the Oolite. See the Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liassic', 'a.', 'Of the age of the Lias; pertaining to the Lias formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liassic', 'n.', 'Same as Lias.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lib', 'v. t.', 'To castrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libament', 'n.', 'Libation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libant', 'a.', 'Sipping; touching lightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libation', 'n.', 'The act of pouring a liquid or liquor, usually wine,\n   either on the ground or on a victim in sacrifice, in honor of some\n   deity; also, the wine or liquid thus poured out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libatory', 'a.', 'Pertaining to libation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libbard', 'n.', 'A leopard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libbards bane', '', 'Leopards bane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libel', 'n.', 'A brief writing of any kind, esp. a declaration, bill,\n   certificate, request, supplication, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libel', 'n.', 'Any defamatory writing; a lampoon; a satire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libel', 'n.', 'A malicious publication expressed either in print or in\n   writing, or by pictures, effigies, or other signs, tending to expose\n   another to public hatred, contempt, or ridicule. Such publication is\n   indictable at common law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libel', 'n.', 'The crime of issuing a malicious defamatory publication.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libel', 'n.', 'A written declaration or statement by the plaintiff of his\n   cause of action, and of the relief he seeks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libeled', 'imp. & p. p.', 'of Libel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libelled', '', 'of Libel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libeling', 'p. pr. & vb. n.', 'of Libel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libelling', '', 'of Libel');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libel', 'v. t.', 'To defame, or expose to public hatred, contempt, or\n   ridicule, by a writing, picture, sign, etc.; to lampoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libel', 'v. t.', 'To proceed against by filing a libel, particularly\n   against a ship or goods.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libel', 'v. i.', 'To spread defamation, written or printed; -- with\n   against.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libelant', 'n.', 'One who libels; one who institutes a suit in an\n   ecclesiastical or admiralty court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libeler', 'n.', 'One who libels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libelist', 'n.', 'A libeler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Li bella', 'n.', 'A small balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Li bella', 'n.', 'A level, or leveling instrument.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libellulid', 'n.', 'A dragon fly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libelluloid', 'a.', 'Like or pertaining to the dragon flies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libelous', 'a.', 'Containing or involving a libel; defamatory; containing\n   that which exposes some person to public hatred, contempt, or ridicule;\n   as, a libelous pamphlet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liber', 'n.', 'The inner bark of plants, lying next to the wood. It usually\n   contains a large proportion of woody, fibrous cells, and is, therefore,\n   the part from which the fiber of the plant is obtained, as that of\n   hemp, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberal', 'a.', 'Free by birth; hence, befitting a freeman or gentleman;\n   refined; noble; independent; free; not servile or mean; as, a liberal\n   ancestry; a liberal spirit; liberal arts or studies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberal', 'a.', 'Bestowing in a large and noble way, as a freeman;\n   generous; bounteous; open-handed; as, a liberal giver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberal', 'a.', 'Bestowed in a large way; hence, more than sufficient;\n   abundant; bountiful; ample; profuse; as, a liberal gift; a liberal\n   discharge of matter or of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberal', 'a.', 'Not strict or rigorous; not confined or restricted to the\n   literal sense; free; as, a liberal translation of a classic, or a\n   liberal construction of law or of language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberal', 'a.', 'Not narrow or contracted in mind; not selfish; enlarged in\n   spirit; catholic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberal', 'a.', 'Free to excess; regardless of law or moral restraint;\n   licentious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberal', 'a.', 'Not bound by orthodox tenets or established forms in\n   political or religious philosophy; independent in opinion; not\n   conservative; friendly to great freedom in the constitution or\n   administration of government; having tendency toward democratic or\n   republican, as distinguished from monarchical or aristocratic, forms;\n   as, liberal thinkers; liberal Christians; the Liberal party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberal', 'n.', 'One who favors greater freedom in political or religious\n   matters; an opponent of the established systems; a reformer; in English\n   politics, a member of the Liberal party, so called. Cf. Whig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberalism', 'n.', 'Liberal principles; the principles and methods of the\n   liberals in politics or religion; specifically, the principles of the\n   Liberal party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberalist', 'n.', 'A liberal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberalistic', 'a.', 'Pertaining to, or characterized by, liberalism; as,\n   liberalistic opinions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberalities', 'pl. ', 'of Liberality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberality', 'n.', 'The quality or state of being liberal; liberal\n   disposition or practice; freedom from narrowness or prejudice;\n   generosity; candor; charity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberality', 'n.', 'A gift; a gratuity; -- sometimes in the plural; as, a\n   prudent man is not impoverished by his liberalities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberalization', 'n.', 'The act of liberalizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberalized', 'imp. & p. p.', 'of Liberalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberalizing', 'p. pr. & vb. n.', 'of Liberalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberalize', 'v. t.', 'To make liberal; to free from narrow views or\n   prejudices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberalizer', 'n.', 'One who, or that which, liberalizes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberally', 'adv.', 'In a liberal manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberated', 'imp. & p. p.', 'of Liberate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberating', 'p. pr. & vb. n.', 'of Liberate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberate', 'a.', 'To release from restraint or bondage; to set at liberty;\n   to free; to manumit; to disengage; as, to liberate a slave or prisoner;\n   to liberate the mind from prejudice; to liberate gases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberation', 'n.', 'The act of liberating or the state of being liberated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberator', 'n.', 'One who, or that which, liberates; a deliverer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberatory', 'a.', 'Tending, or serving, to liberate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libertarian', 'a.', 'Pertaining to liberty, or to the doctrine of free\n   will, as opposed to the doctrine of necessity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libertarian', 'n.', 'One who holds to the doctrine of free will.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libertarianism', 'n.', 'Libertarian principles or doctrines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberticide', 'n.', 'The destruction of civil liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberticide', 'n.', 'A destroyer of civil liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libertinage', 'n.', 'Libertinism; license.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libertine', 'n.', 'A manumitted slave; a freedman; also, the son of a\n   freedman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libertine', 'n.', 'One of a sect of Anabaptists, in the fifteenth and early\n   part of the sixteenth century, who rejected many of the customs and\n   decencies of life, and advocated a community of goods and of women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libertine', 'n.', 'One free from restraint; one who acts according to his\n   impulses and desires; now, specifically, one who gives rein to lust; a\n   rake; a debauchee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libertine', 'n.', 'A defamatory name for a freethinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libertine', 'n.', 'Free from restraint; uncontrolled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libertine', 'n.', 'Dissolute; licentious; profligate; loose in morals; as,\n   libertine principles or manners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libertinism', 'n.', 'The state of a libertine or freedman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libertinism', 'n.', 'Licentious conduct; debauchery; lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libertinism', 'n.', 'Licentiousness of principle or opinion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberties', 'pl. ', 'of Liberty');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberty', 'n.', 'The state of a free person; exemption from subjection to\n   the will of another claiming ownership of the person or services;\n   freedom; -- opposed to slavery, serfdom, bondage, or subjection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberty', 'n.', 'Freedom from imprisonment, bonds, or other restraint upon\n   locomotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberty', 'n.', 'A privilege conferred by a superior power; permission\n   granted; leave; as, liberty given to a child to play, or to a witness\n   to leave a court, and the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberty', 'n.', 'Privilege; exemption; franchise; immunity enjoyed by\n   prescription or by grant; as, the liberties of the commercial cities of\n   Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberty', 'n.', 'The place within which certain immunities are enjoyed, or\n   jurisdiction is exercised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberty', 'n.', 'A certain amount of freedom; permission to go freely\n   within certain limits; also, the place or limits within which such\n   freedom is exercised; as, the liberties of a prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberty', 'n.', 'A privilege or license in violation of the laws of\n   etiquette or propriety; as, to permit, or take, a liberty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberty', 'n.', 'The power of choice; freedom from necessity; freedom from\n   compulsion or constraint in willing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberty', 'n.', 'A curve or arch in a bit to afford room for the tongue of\n   the horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liberty', 'n.', 'Leave of absence; permission to go on shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libethenite', 'n.', 'A mineral of an olive-green color, commonly in\n   orthorhombic crystals. It is a hydrous phosphate of copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libidinist', 'n.', 'One given to lewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libidinosity', 'n.', 'The state or quality of being libidinous;\n   libidinousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libidinous', 'a.', 'Having lustful desires; characterized by lewdness;\n   sensual; lascivious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libken', 'n.', 'Alt. of Libkin');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libkin', 'n.', 'A house or lodging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Librae', 'pl. ', 'of Libra');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libra', 'n.', 'The Balance; the seventh sign in the zodiac, which the sun\n   enters at the autumnal equinox in September, marked thus / in almanacs,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libra', 'n.', 'A southern constellation between Virgo and Scorpio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libral', 'a.', 'Of a pound weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Librarian', 'n.', 'One who has the care or charge of a library.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Librarian', 'n.', 'One who copies manuscript books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Librarianship', 'n.', 'The office of a librarian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libraries', 'pl. ', 'of Library');
  INSERT INTO words (word, word_type, description) VALUES  (    'Library', 'n.', 'A considerable collection of books kept for use, and not\n   as merchandise; as, a private library; a public library.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Library', 'n.', 'A building or apartment appropriated for holding such a\n   collection of books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Librated', 'imp. & p. p.', 'of Librate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Librating', 'p. pr. & vb. n.', 'of Librate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Librate', 'v. i.', 'To vibrate as a balance does before resting in\n   equilibrium; hence, to be poised.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Librate', 'v. t.', 'To poise; to balance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libration', 'n.', 'The act or state of librating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libration', 'n.', 'A real or apparent libratory motion, like that of a\n   balance before coming to rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libration point', 'n.', 'any one of five points in the plane of a system of\n   two large astronomical bodies orbiting each other, as the Earth-moon\n   system, where the gravitational pull of the two bodies on an object are\n   approximately equal, and in opposite directions. A solid object moving\n   in the same velocity and direction as such a libration point will\n   remain in gravitational equilibrium with the two bodies of the system\n   and not fall toward either body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libratory', 'a.', 'Balancing; moving like a balance, as it tends to an\n   equipoise or level.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Librettist', 'n.', 'One who makes a libretto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Librettos', 'pl. ', 'of Libretto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libretti', 'pl. ', 'of Libretto');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libretto', 'n.', 'A book containing the words of an opera or extended piece\n   of music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libretto', 'n.', 'The words themselves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libriform', 'a.', 'Having the form of liber, or resembling liber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Libyan', 'a.', 'Of or pertaining to Libya, the ancient name of that part of\n   Africa between Egypt and the Atlantic Ocean, or of Africa as a whole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lice', 'n.', 'pl. of Louse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licensable', 'a.', 'That can be licensed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'License', 'n.', 'Authority or liberty given to do or forbear any act;\n   especially, a formal permission from the proper authorities to perform\n   certain acts or to carry on a certain business, which without such\n   permission would be illegal; a grant of permission; as, a license to\n   preach, to practice medicine, to sell gunpowder or intoxicating\n   liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'License', 'n.', 'The document granting such permission.');
  INSERT INTO words (word, word_type, description) VALUES  (    'License', 'n.', 'Excess of liberty; freedom abused, or used in contempt of\n   law or decorum; disregard of law or propriety.');
  INSERT INTO words (word, word_type, description) VALUES  (    'License', 'n.', 'That deviation from strict fact, form, or rule, in which\n   an artist or writer indulges, assuming that it will be permitted for\n   the sake of the advantage or effect gained; as, poetic license;\n   grammatical license, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licensed', 'imp. & p. p.', 'of License');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licensing', 'p. pr. & vb. n.', 'of License');
  INSERT INTO words (word, word_type, description) VALUES  (    'License', 'v. t.', 'To permit or authorize by license; to give license to;\n   as, to license a man to preach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licensed', 'a.', 'Having a license; permitted or authorized by license; as,\n   a licensed victualer; a licensed traffic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licensee', 'n.', 'The person to whom a license is given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licenser', 'n.', 'One who gives a license; as, a licenser of the press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licensure', 'n.', 'A licensing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licentiate', 'n.', 'One who has a license to exercise a profession; as, a\n   licentiate in medicine or theology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licentiate', 'n.', 'A friar authorized to receive confessions and grant\n   absolution in all places, independently of the local clergy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licentiate', 'n.', 'One who acts without restraint, or takes a liberty, as\n   if having a license therefor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licentiate', 'n.', 'On the continent of Europe, a university degree\n   intermediate between that of bachelor and that of doctor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licentiate', 'v. t.', 'To give a license to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licentious', 'a.', 'Characterized by license; passing due bounds;\n   excessive; abusive of freedom; wantonly offensive; as, a licentious\n   press.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licentious', 'a.', 'Unrestrained by law or morality; lawless; immoral;\n   dissolute; lewd; lascivious; as, a licentious man; a licentious life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lich', 'a.', 'Like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lich', 'a.', 'A dead body; a corpse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichen', 'n.', 'One of a class of cellular, flowerless plants, (technically\n   called Lichenes), having no distinction of leaf and stem, usually of\n   scaly, expanded, frond-like forms, but sometimes erect or pendulous and\n   variously branched. They derive their nourishment from the air, and\n   generate by means of spores. The species are very widely distributed,\n   and form irregular spots or patches, usually of a greenish or yellowish\n   color, upon rocks, trees, and various bodies, to which they adhere with\n   great tenacity. They are often improperly called rock moss or tree\n   moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichen', 'n.', 'A name given to several varieties of skin disease, esp. to\n   one characterized by the eruption of small, conical or flat, reddish\n   pimples, which, if unchecked, tend to spread and produce great and even\n   fatal exhaustion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichened', 'a.', 'Belonging to, or covered with, lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichenic', 'a.', 'Of, pertaining to, or obtained from, lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licheniform', 'a.', 'Having the form of a lichen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichenin', 'n.', 'A substance isomeric with starch, extracted from several\n   species of moss and lichen, esp. from Iceland moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichenographic', 'a.', 'Alt. of Lichenographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichenographical', 'a.', 'Of or pertaining to lichenography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichenographist', 'n.', 'One who describes lichens; one versed in\n   lichenography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichenography', 'n.', 'A description of lichens; the science which\n   illustrates the natural history of lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichenologist', 'n.', 'One versed in lichenology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichenology', 'n.', 'The science which treats of lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichenous', 'a.', 'Of, pertaining to, or resembling, lichens; abounding in\n   lichens; covered with lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichi', 'n.', 'See Litchi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichwale', 'n.', 'The gromwell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lichwort', 'n.', 'An herb, the wall pellitory. See Pellitory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licit', 'a.', 'Lawful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licitation', 'n.', 'The act of offering for sale to the highest bidder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licked', 'imp. & p. p.', 'of Lick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licking', 'p. pr. & vb. n.', 'of Lick');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lick', 'v. t.', 'To draw or pass the tongue over; as, a dog licks his\n   masters hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lick', 'v. t.', 'To lap; to take in with the tongue; as, a dog or cat licks\n   milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lick', 'v.', 'A stroke of the tongue in licking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lick', 'v.', 'A quick and careless application of anything, as if by a\n   stroke of the tongue, or of something which acts like a tongue; as, to\n   put on colors with a lick of the brush. Also, a small quantity of any\n   substance so applied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lick', 'v.', 'A place where salt is found on the surface of the earth, to\n   which wild animals resort to lick it up; -- often, but not always, near\n   salt springs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lick', 'v. t.', 'To strike with repeated blows for punishment; to flog; to\n   whip or conquer, as in a pugilistic encounter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lick', 'n.', 'A slap; a quick stroke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licker', 'n.', 'One who, or that which, licks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lickerish', 'a.', 'Eager; craving; urged by desire; eager to taste or\n   enjoy; greedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lickerish', 'a.', 'Tempting the appetite; dainty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lickerish', 'a.', 'Lecherous; lustful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lickerous', 'a.', 'Lickerish; eager; lustful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licking', 'n.', 'A lapping with the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licking', 'n.', 'A flogging or castigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lickpenny', 'n.', 'A devourer or absorber of money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lick-spigot', 'n.', 'A tapster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lick-spittle', 'n.', 'An abject flatterer or parasite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licorice', 'n.', 'A plant of the genus Glycyrrhiza (G. glabra), the root of\n   which abounds with a sweet juice, and is much used in demulcent\n   compositions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licorice', 'n.', 'The inspissated juice of licorice root, used as a\n   confection and for medicinal purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licorous', 'a.', 'See Lickerish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Licour', 'n.', 'Liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lictor', 'n.', 'An officer who bore an ax and fasces or rods, as ensigns of\n   his office. His duty was to attend the chief magistrates when they\n   appeared in public, to clear the way, and cause due respect to be paid\n   to them, also to apprehend and punish criminals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lid', 'n.', 'That which covers the opening of a vessel or box, etc.; a\n   movable cover; as, the lid of a chest or trunk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lid', 'n.', 'The cover of the eye; an eyelid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lid', 'n.', 'The cover of the spore cases of mosses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lid', 'n.', 'A calyx which separates from the flower, and falls off in a\n   single piece, as in the Australian Eucalypti.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lid', 'n.', 'The top of an ovary which opens transversely, as in the fruit\n   of the purslane and the tree which yields Brazil nuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lidded', 'a.', 'Covered with a lid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lidge', 'n.', 'Same as Ledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lidless', 'a.', 'Having no lid, or not covered with the lids, as the eyes;\n   hence, sleepless; watchful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lie', 'n.', 'See Lye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lie', 'n.', 'A falsehood uttered or acted for the purpose of deception; an\n   intentional violation of truth; an untruth spoken with the intention to\n   deceive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lie', 'n.', 'A fiction; a fable; an untruth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lie', 'n.', 'Anything which misleads or disappoints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lied', 'imp. & p. p.', 'of Lie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lying', 'p. pr. & vb. n.', 'of Lie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lie', 'v. i.', 'To utter falsehood with an intention to deceive; to say or\n   do that which is intended to deceive another, when he a right to know\n   the truth, or when morality requires a just representation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lay', 'imp.', 'of Lie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lain', 'p. p.', 'of Lie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lien', '', 'of Lie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lying', 'p. pr. & vb. n.', 'of Lie');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lie', 'adj.', 'To rest extended on the ground, a bed, or any support; to\n   be, or to put ones self, in an horizontal position, or nearly so; to\n   be prostate; to be stretched out; -- often with down, when predicated\n   of living creatures; as, the book lies on the table; the snow lies on\n   the roof; he lies in his coffin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lie', 'adj.', 'To be situated; to occupy a certain place; as, Ireland lies\n   west of England; the meadows lie along the river; the ship lay in port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lie', 'adj.', 'To abide; to remain for a longer or shorter time; to be in a\n   certain state or condition; as, to lie waste; to lie fallow; to lie\n   open; to lie hid; to lie grieving; to lie under ones displeasure; to\n   lie at the mercy of the waves; the paper does not lie smooth on the\n   wall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lie', 'adj.', 'To be or exist; to belong or pertain; to have an abiding\n   place; to consist; -- with in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lie', 'adj.', 'To lodge; to sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lie', 'adj.', 'To be still or quiet, like one lying down to rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lie', 'adj.', 'To be sustainable; to be capable of being maintained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lie', 'n.', 'The position or way in which anything lies; the lay, as of\n   land or country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieberkuhn', 'n.', 'A concave metallic mirror attached to the object-glass\n   end of a microscope, to throw down light on opaque objects; a\n   reflector.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieberkuhns glands', '', 'The simple tubular glands of the small\n   intestines; -- called also crypts of Lieberkuhn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieder', 'pl. ', 'of Lied');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lied', 'n.', 'A lay; a German song. It differs from the French chanson, and\n   the Italian canzone, all three being national.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liedertafel', 'n.', 'A popular name for any society or club which meets for\n   the practice of male part songs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lief', 'n.', 'Same as Lif.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lief', 'n.', 'Dear; beloved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lief', 'n.', 'Pleasing; agreeable; acceptable; preferable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lief', 'adv.', 'Willing; disposed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lief', 'n.', 'A dear one; a sweetheart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lief', 'adv.', 'Gladly; willingly; freely; -- now used only in the phrases,\n   had as lief, and would as lief; as, I had, or would, as lief go as not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liefsome', 'a.', 'Pleasing; delightful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liegance', 'n.', 'Same as Ligeance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liege', 'a.', 'Sovereign; independent; having authority or right to\n   allegiance; as, a liege lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liege', 'a.', 'Serving an independent sovereign or master; bound by a\n   feudal tenure; obliged to be faithful and loyal to a superior, as a\n   vassal to his lord; faithful; loyal; as, a liege man; a liege subject.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liege', 'a.', 'Full; perfect; complete; pure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liege', 'n.', 'A free and independent person; specif., a lord paramount; a\n   sovereign.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liege', 'n.', 'The subject of a sovereign or lord; a liegeman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liegemen', 'pl. ', 'of Liegeman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liegeman', 'n.', 'Same as Liege, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieger', 'n.', 'A resident ambassador.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liegiancy', 'n.', 'See Ligeance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lien', 'obs. p. p.', 'of Lie. See Lain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lien', 'n.', 'A legal claim; a charge upon real or personal property for\n   the satisfaction of some debt or duty; a right in one to control or\n   hold and retain the property of another until some claim of the former\n   is paid or satisfied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lienal', 'a.', 'Of or pertaining to the spleen; splenic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lienculi', 'pl. ', 'of Lienculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lienculus', 'n.', 'One of the small nodules sometimes found in the\n   neighborhood of the spleen; an accessory or supplementary spleen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieno-intestinal', 'a.', 'Of or pertaining to the spleen and intestine; as,\n   the lieno-intestinal vein of the frog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lienteric', 'a.', 'Of or pertaining to, or of the nature of, a lientery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lienteric', 'n.', 'A lientery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lientery', 'n.', 'A diarrhea, in which the food is discharged imperfectly\n   digested, or with but little change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lier', 'n.', 'One who lies down; one who rests or remains, as in\n   concealment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lierne rib', '', 'In Gothic vaulting, any rib which does not spring from\n   the impost and is not a ridge rib, but passes from one boss or\n   intersection of the principal ribs to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieu', 'n.', 'Place; room; stead; -- used only in the phrase in lieu of,\n   that is, instead of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieutenancy', 'n.', 'The office, rank, or commission, of a lieutenant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieutenancy', 'n.', 'The body of lieutenants or subordinates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieutenant', 'n.', 'An officer who supplies the place of a superior in his\n   absence; a representative of, or substitute for, another in the\n   performance of any duty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieutenant', 'n.', 'A commissioned officer in the army, next below a\n   captain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieutenant', 'n.', 'A commissioned officer in the British navy, in rank\n   next below a commander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieutenant', 'n.', 'A commissioned officer in the United States navy, in\n   rank next below a lieutenant commander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieutenant general', '', 'An army officer in rank next below a general and\n   next above a major general.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieutenantry', 'n.', 'See Lieutenancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieutenantship', 'n.', 'Same as Lieutenancy, 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lieve', 'a.', 'Same as Lief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lif', 'n.', 'The fiber by which the petioles of the date palm are bound\n   together, from which various kinds of cordage are made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lives', 'pl. ', 'of Life');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'The state of being which begins with generation, birth, or\n   germination, and ends with death; also, the time during which this\n   state continues; that state of an animal or plant in which all or any\n   of its organs are capable of performing all or any of their functions;\n   -- used of all animal and vegetable organisms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'Of human beings: The union of the soul and body; also, the\n   duration of their union; sometimes, the deathless quality or existence\n   of the soul; as, man is a creature having an immortal life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'The potential principle, or force, by which the organs of\n   animals and plants are started and continued in the performance of\n   their several and cooperative functions; the vital force, whether\n   regarded as physical or spiritual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'Figuratively: The potential or animating principle, also, the\n   period of duration, of anything that is conceived of as resembling a\n   natural organism in structure or functions; as, the life of a state, a\n   machine, or a book; authority is the life of government.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'A certain way or manner of living with respect to conditions,\n   circumstances, character, conduct, occupation, etc.; hence, human\n   affairs; also, lives, considered collectively, as a distinct class or\n   type; as, low life; a good or evil life; the life of Indians, or of\n   miners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'Animation; spirit; vivacity; vigor; energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'That which imparts or excites spirit or vigor; that upon\n   which enjoyment or success depends; as, he was the life of the company,\n   or of the enterprise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'The living or actual form, person, thing, or state; as, a\n   picture or a description from the life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'A person; a living being, usually a human being; as, many\n   lives were sacrificed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'The system of animal nature; animals in general, or\n   considered collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'An essential constituent of life, esp. the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'A history of the acts and events of a life; a biography; as,\n   Johnson wrote the life of Milton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'Enjoyment in the right use of the powers; especially, a\n   spiritual existence; happiness in the favor of God; heavenly felicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life', 'n.', 'Something dear to one as ones existence; a darling; -- used\n   as a term of endearment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifeblood', 'n.', 'The blood necessary to life; vital blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifeblood', 'n.', 'Fig.: That which gives strength and energy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifeboat', 'n.', 'A strong, buoyant boat especially designed for saving the\n   lives of shipwrecked people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifeful', 'a.', 'Full of vitality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life-giving', 'a.', 'Giving life or spirit; having power to give life;\n   inspiriting; invigorating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifehold', 'n.', 'Land held by a life estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifeless', 'a.', 'Destitute of life, or deprived of life; not containing,\n   or inhabited by, living beings or vegetation; dead, or apparently dead;\n   spiritless; powerless; dull; as, a lifeless carcass; lifeless matter; a\n   lifeless desert; a lifeless wine; a lifeless story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifelike', 'a.', 'Like a living being; resembling life; giving an accurate\n   representation; as, a lifelike portrait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifelong', 'a.', 'Lasting or continuing through life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifely', 'a.', 'In a lifelike manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifemate', 'n.', 'Companion for life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifen', 'v. t.', 'To enliven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life-preserver', 'n.', 'An apparatus, made in very various forms, and of\n   various materials, for saving one from drowning by buoying up the body\n   while in the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life-saving', 'a.', 'That saves life, or is suited to save life, esp. from\n   drowning; as, the life-saving service; a life-saving station.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life-size', 'a.', 'Of full size; of the natural size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifesome', 'a.', 'Animated; sprightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifespring', 'n.', 'Spring or source of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifestring', 'n.', 'A nerve, or string, that is imagined to be essential to\n   life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifetime', 'n.', 'The time that life continues.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Life-weary', 'a.', 'Weary of living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liflode', 'n.', 'Livelihood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'The sky; the atmosphere; the firmament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifted', 'imp. & p. p.', 'of Lift');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifting', 'p. pr. & vb. n.', 'of Lift');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'v. t.', 'To move in a direction opposite to that of gravitation; to\n   raise; to elevate; to bring up from a lower place to a higher; to\n   upheave; sometimes implying a continued support or holding in the\n   higher place; -- said of material things; as, to lift the foot or the\n   hand; to lift a chair or a burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'v. t.', 'To raise, elevate, exalt, improve, in rank, condition,\n   estimation, character, etc.; -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'v. t.', 'To bear; to support.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'v. t.', 'To collect, as moneys due; to raise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'v. t.', 'To steal; to carry off by theft (esp. cattle); as, to lift\n   a drove of cattle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'v. i.', 'To try to raise something; to exert the strength for\n   raising or bearing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'v. i.', 'To rise; to become or appear raised or elevated; as, the\n   fog lifts; the land lifts to a ship approaching it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'v. t.', 'To live by theft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'Act of lifting; also, that which is lifted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'The space or distance through which anything is lifted; as, a\n   long lift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'Help; assistance, as by lifting; as, to give one a lift in a\n   wagon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'That by means of which a person or thing lifts or is lifted');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'A hoisting machine; an elevator; a dumb waiter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'A handle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'An exercising machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'A rise; a degree of elevation; as, the lift of a lock in\n   canals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'A lift gate. See Lift gate, below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'A rope leading from the masthead to the extremity of a yard\n   below; -- used for raising or supporting the end of the yard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'One of the steps of a cone pulley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'A layer of leather in the heel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lift', 'n.', 'That portion of the vibration of a balance during which the\n   impulse is given.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liftable', 'a.', 'Such as can be lifted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifter', 'n.', 'One who, or that which, lifts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifter', 'n.', 'A tool for lifting loose sand from the mold; also, a\n   contrivance attached to a cope, to hold the sand together when the cope\n   is lifted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lifting', 'a.', 'Used in, or for, or by, lifting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lig', 'v. i.', 'To recline; to lie still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligament', 'n.', 'Anything that ties or unites one thing or part to\n   another; a bandage; a bond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligament', 'n.', 'A tough band or plate of dense, fibrous, connective\n   tissue or fibrocartilage serving to unite bones or form joints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligament', 'n.', 'A band of connective tissue, or a membranous fold, which\n   supports or retains an organ in place; as, the gastrophrenic ligament,\n   connecting the diaphragm and stomach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligamental', 'a.', 'Alt. of Ligamentous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligamentous', 'a.', 'Composing a ligament; of the nature of a ligament;\n   binding; as, a strong ligamentous membrane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligan', 'n.', 'Goods sunk in the sea, with a buoy attached in order that\n   they may be found again. See Jetsam and Flotsam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligate', 'v. t.', 'To tie with a ligature; to bind around; to bandage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligation', 'n.', 'The act of binding, or the state of being bound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligation', 'n.', 'That which binds; bond; connection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligator', 'n.', 'An instrument for ligating, or for placing and fastening a\n   ligature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligature', 'n.', 'The act of binding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligature', 'n.', 'Anything that binds; a band or bandage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligature', 'n.', 'A thread or string for tying the blood vessels,\n   particularly the arteries, to prevent hemorrhage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligature', 'n.', 'A thread or wire used to remove tumors, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligature', 'n.', 'The state of being bound or stiffened; stiffness; as, the\n   ligature of a joint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligature', 'n.', 'Impotence caused by magic or charms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligature', 'n.', 'A curve or line connecting notes; a slur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligature', 'n.', 'A double character, or a type consisting of two or more\n   letters or characters united, as ae, /, /.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligature', 'v. t.', 'To ligate; to tie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lige', 'v. t. & i.', 'To lie; to tell lies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligeance', 'n.', 'The connection between sovereign and subject by which\n   they were mutually bound, the former to protection and the securing of\n   justice, the latter to faithful service; allegiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligement', 'n.', 'See Ledgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligge', 'v. i.', 'To lie or recline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligger', 'n.', 'A baited line attached to a float, for night fishing. See\n   Leger, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligger', 'a.', 'See Ledger, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'That agent, force, or action in nature by the operation of\n   which upon the organs of sight, objects are rendered visible or\n   luminous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'That which furnishes, or is a source of, light, as the sun,\n   a star, a candle, a lighthouse, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'The time during which the light of the sun is visible; day;\n   especially, the dawn of day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'The brightness of the eye or eyes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'The medium through which light is admitted, as a window, or\n   window pane; a skylight; in architecture, one of the compartments of a\n   window made by a mullion or mullions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'Life; existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'Open view; a visible state or condition; public observation;\n   publicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'The power of perception by vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'That which illumines or makes clear to the mind; mental or\n   spiritual illumination; enlightenment; knowledge; information.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'Prosperity; happiness; joy; felicity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'The manner in which the light strikes upon a picture; that\n   part of a picture which represents those objects upon which the light\n   is supposed to fall; the more illuminated part of a landscape or other\n   scene; -- opposed to shade. Cf. Chiaroscuro.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'Appearance due to the particular facts and circumstances\n   presented to view; point of view; as, to state things fairly and put\n   them in the right light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'One who is conspicuous or noteworthy; a model or example;\n   as, the lights of the age or of antiquity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'A firework made by filling a case with a substance which\n   burns brilliantly with a white or colored flame; as, a Bengal light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl', 'Having light; not dark or obscure; bright; clear; as,\n   the apartment is light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl', 'White or whitish; not intense or very marked; not of a\n   deep shade; moderately colored; as, a light color; a light brown; a\n   light complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighted', 'imp. & p. p.', 'of Light');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lit', '', 'of Light');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighting', 'p. pr. & vb. n.', 'of Light');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'To set fire to; to cause to burn; to set burning; to ignite;\n   to kindle; as, to light a candle or lamp; to light the gas; --\n   sometimes with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'To give light to; to illuminate; to fill with light; to\n   spread over with light; -- often with up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'n.', 'To attend or conduct with a light; to show the way to by\n   means of a light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'v. i.', 'To become ignited; to take fire; as, the match will not\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'v. i.', 'To be illuminated; to receive light; to brighten; -- with\n   up; as, the room lights up very well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Having little, or comparatively little, weight; not\n   tending to the center of gravity with force; not heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Not burdensome; easy to be lifted, borne, or carried by\n   physical strength; as, a light burden, or load.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Easy to be endured or performed; not severe; not\n   difficult; as, a light affliction or task.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Easy to be digested; not oppressive to the stomach; as,\n   light food; also, containing little nutriment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Not heavily armed; armed with light weapons; as, light\n   troops; a troop of light horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Not encumbered; unembarrassed; clear of impediments;\n   hence, active; nimble; swift.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Not heavily burdened; not deeply laden; not\n   sufficiently ballasted; as, the ship returned light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Slight; not important; as, a light error.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Well leavened; not heavy; as, light bread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Not copious or heavy; not dense; not inconsiderable;\n   as, a light rain; a light snow; light vapors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Not strong or violent; moderate; as, a light wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Not pressing heavily or hard upon; hence, having an\n   easy, graceful manner; delicate; as, a light touch; a light style of\n   execution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Easy to admit influence; inconsiderate; easily\n   influenced by trifling considerations; unsteady; unsettled; volatile;\n   as, a light, vain person; a light mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Indulging in, or inclined to, levity; wanting dignity\n   or solemnity; trifling; gay; frivolous; airy; unsubstantial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Not quite sound or normal; somewhat impaired or\n   deranged; dizzy; giddy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Easily bestowed; inconsiderately rendered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Wanton; unchaste; as, a woman of light character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Not of the legal, standard, or usual weight; clipped;\n   diminished; as, light coin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'superl.', 'Loose; sandy; easily pulverized; as, a light soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'adv.', 'Lightly; cheaply.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'v. t.', 'To lighten; to ease of a burden; to take off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighted', 'imp. & p. p.', 'of Light');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lit', '', 'of Light');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighting', 'p. pr. & vb. n.', 'of Light');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'v. i.', 'To dismount; to descend, as from a horse or carriage; to\n   alight; -- with from, off, on, upon, at, in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'v. i.', 'To feel light; to be made happy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'v. i.', 'To descend from flight, and rest, perch, or settle, as a\n   bird or insect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'v. i.', 'To come down suddenly and forcibly; to fall; -- with on\n   or upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light', 'v. i.', 'To come by chance; to happen; -- with on or upon;\n   formerly with into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightable', 'a.', 'Such as can be lighted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-armed', 'a.', 'Armed with light weapons or accouterments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-boat', 'n.', 'Light-ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighte', '', 'imp. of Light, to alight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighten', 'v. i.', 'To descend; to light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightened', 'imp. & p. p.', 'of Lighten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightening', 'p. pr. & vb. n.', 'of Lighten');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighten', 'v. i.', 'To burst forth or dart, as lightning; to shine with, or\n   like, lightning; to display a flash or flashes of lightning; to flash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighten', 'v. i.', 'To grow lighter; to become less dark or lowering; to\n   brighten; to clear, as the sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighten', 'v. t.', 'To make light or clear; to light; to illuminate; as, to\n   lighten an apartment with lamps or gas; to lighten the streets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighten', 'v. t.', 'To illuminate with knowledge; to enlighten.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighten', 'v. t.', 'To emit or disclose in, or as in, lightning; to flash\n   out, like lightning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighten', 'v. t.', 'To free from trouble and fill with joy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighten', 'v. t.', 'To make lighter, or less heavy; to reduce in weight; to\n   relieve of part of a load or burden; as, to lighten a ship by\n   unloading; to lighten a load or burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighten', 'v. t.', 'To make less burdensome or afflictive; to alleviate;\n   as, to lighten the cares of life or the burden of grief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighten', 'v. t.', 'To cheer; to exhilarate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighter', 'n.', 'One who, or that which, lights; as, a lighter of lamps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighter', 'n.', 'A large boat or barge, mainly used in unloading or loading\n   vessels which can not reach the wharves at the place of shipment or\n   delivery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighter', 'v. t.', 'To convey by a lighter, as to or from the shore; as, to\n   lighter the cargo of a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighterage', 'n.', 'The price paid for conveyance of goods on a lighter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighterage', 'n.', 'The act of unloading into a lighter, or of conveying by\n   a lighter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightermen', 'pl. ', 'of Lighterman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighterman', 'n.', 'A person employed on, or who manages, a lighter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-fingered', 'a.', 'Dexterous in taking and conveying away; thievish;\n   pilfering; addicted to petty thefts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-foot', 'a.', 'Alt. of Light-footed');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-footed', 'a.', 'Having a light, springy step; nimble in running or\n   dancing; active; as, light-foot Iris.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightful', 'a.', 'Full of light; bright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-handed', 'a.', 'Not having a full complement of men; as, a vessel\n   light-handed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-headed', 'a.', 'Disordered in the head; dizzy; delirious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-headed', 'a.', 'Thoughtless; heedless; volatile; unsteady; fickle;\n   loose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-hearted', 'a.', 'Free from grief or anxiety; gay; cheerful; merry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-heeled', 'a.', 'Lively in walking or running; brisk; light-footed.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Light-horseman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-horseman', 'n.', 'A soldier who serves in the light horse. See under\n   5th Light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-horseman', 'n.', 'A West Indian fish of the genus Ephippus,\n   remarkable for its high dorsal fin and brilliant colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighthouses', 'pl. ', 'of Lighthouse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighthouse', 'n.', 'A tower or other building with a powerful light at top,\n   erected at the entrance of a port, or at some important point on a\n   coast, to serve as a guide to mariners at night; a pharos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighting', 'n.', 'A name sometimes applied to the process of annealing\n   metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-legged', 'a.', 'Nimble; swift of foot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightless', 'a.', 'Destitute of light; dark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightly', 'adv.', 'With little weight; with little force; as, to tread\n   lightly; to press lightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightly', 'adv.', 'Swiftly; nimbly; with agility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightly', 'adv.', 'Without deep impression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightly', 'adv.', 'In a small degree; slightly; not severely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightly', 'adv.', 'With little effort or difficulty; easily; readily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightly', 'adv.', 'Without reason, or for reasons of little weight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightly', 'adv.', 'Commonly; usually.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightly', 'adv.', 'Without dejection; cheerfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightly', 'adv.', 'Without heed or care; with levity; gayly; airily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightly', 'adv.', 'Not chastely; wantonly.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Lightman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightman', 'n.', 'A man who carries or takes care of a light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-minded', 'a.', 'Unsettled; unsteady; volatile; not considerate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightness', 'n.', 'The state, condition, or quality, of being light or not\n   heavy; buoyancy; levity; fickleness; nimbleness; delicacy; grace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightness', 'n.', 'Illumination, or degree of illumination; as, the\n   lightness of a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightness', 'n.', 'Absence of depth or of duskiness in color; as, the\n   lightness of a tint; lightness of complexion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightning', 'n.', 'A discharge of atmospheric electricity, accompanied by a\n   vivid flash of light, commonly from one cloud to another, sometimes\n   from a cloud to the earth. The sound produced by the electricity in\n   passing rapidly through the atmosphere constitutes thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightning', 'n.', 'The act of making bright, or the state of being made\n   bright; enlightenment; brightening, as of the mental powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightning', 'vb. n.', 'Lightening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-o-love', 'n.', 'An old tune of a dance, the name of which made it a\n   proverbial expression of levity, especially in love matters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-o-love', 'n.', 'Hence: A light or wanton woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightroom', 'n.', 'A small room from which the magazine of a naval vessel\n   is lighted, being separated from the magazine by heavy glass windows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lights', 'n. pl.', 'The lungs of an animal or bird; -- sometimes coarsely\n   applied to the lungs of a human being.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-ship', 'n.', 'A vessel carrying at the masthead a brilliant light,\n   and moored off a shoal or place of dangerous navigation as a guide for\n   mariners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightsome', 'a.', 'Having light; lighted; not dark or gloomy; bright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightsome', 'a.', 'Gay; airy; cheering; exhilarating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Light-winged', 'a.', 'Having light and active wings; volatile; fleeting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lightwood', 'n.', 'Pine wood abounding in pitch, used for torches in the\n   Southern United States; pine knots, dry sticks, and the like, for\n   kindling a fire quickly or making a blaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lighty', 'a.', 'Illuminated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lign-aloes', 'n.', 'Aloes wood, or agallochum. See Agallochum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lign-aloes', 'n.', 'A fragrant tree mentioned in the Bible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligneous', 'a.', 'Made of wood; consisting of wood; of the nature of, or\n   resembling, wood; woody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligniferous', 'a.', 'Yielding or producing wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignification', 'n.', 'A change in the character of a cell wall, by which\n   it becomes harder. It is supposed to be due to an incrustation of\n   lignin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligniform', 'a.', 'Like wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignified', 'imp. & p. p.', 'of Lignify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignifying', 'p. pr. & vb. n.', 'of Lignify');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignify', 'v. t.', 'To convert into wood or into a ligneous substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignify', 'v. i.', 'To become wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignin', 'n.', 'A substance characterizing wood cells and differing from\n   cellulose in its conduct with certain chemical reagents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligniperdous', 'a.', 'Wood-destroying; -- said of certain insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignireose', 'n.', 'See Lignin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignite', 'n.', 'Mineral coal retaining the texture of the wood from which\n   it was formed, and burning with an empyreumatic odor. It is of more\n   recent origin than the anthracite and bituminous coal of the proper\n   coal series. Called also brown coal, wood coal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignitic', 'a.', 'Containing lignite; resembling, or of the nature of,\n   lignite; as, lignitic clay.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignitiferous', 'a.', 'Producing or containing lignite; lignitic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignoceric', 'a.', 'Pertaining to, or designating, an acid of the formic\n   acid series, found in the tar, wax, or paraffine obtained by distilling\n   certain kinds of wood, as the beech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignone', 'n.', 'See Lignin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignose', 'a.', 'Alt. of Lignous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignous', 'a.', 'Ligneous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignose', 'n.', 'See Lignin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignose', 'n.', 'An explosive compound of wood fiber and nitroglycerin. See\n   Nitroglycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignum rhodium', '', 'The fragrant wood of several shrubs and trees,\n   especially of species of Rhodorhiza from the Canary Islands, and of the\n   West Indian Amyris balsamifera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lignum-vitae', 'n.', 'A tree (Guaiacum officinale) found in the warm\n   latitudes of America, from which the guaiacum of medicine is procured.\n   Its wood is very hard and heavy, and is used for various mechanical\n   purposes, as for the wheels of ships blocks, cogs, bearings, and the\n   like. See Guaiacum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligroin', 'n.', 'A trade name applied somewhat indefinitely to some of the\n   volatile products obtained in refining crude petroleum. It is a complex\n   and variable mixture of several hydrocarbons, generally boils below\n   170 Fahr., and is more inflammable than safe kerosene. It is used as a\n   solvent, as a carburetant for air gas, and for illumination in special\n   lamps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligsam', 'n.', 'Same as Ligan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligulae', 'pl. ', 'of Ligula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligulas', 'pl. ', 'of Ligula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligula', 'n.', 'See Ligule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligula', 'n.', 'The central process, or front edge, of the labium of\n   insects. It sometimes serves as a tongue or proboscis, as in bees.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligula', 'n.', 'A tongue-shaped lobe of the parapodia of annelids. See\n   Parapodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligulate', 'a.', 'Alt. of Ligulated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligulated', 'a.', 'Like a bandage, or strap; strap-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligulated', 'a.', 'Composed of ligules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligule', 'n.', 'The thin and scarious projection from the upper end of the\n   sheath of a leaf of grass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligule', 'n.', 'A strap-shaped corolla of flowers of Compositae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligule', 'n.', 'A band of white matter in the wall of fourth ventricle of\n   the brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liguliflorous', 'a.', 'Bearing only ligulate flowers; -- said of a large\n   suborder of composite plants, such as the dandelion, lettuce, hawkweed,\n   etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligure', 'n.', 'A kind of precious stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ligustrin', 'n.', 'A bitter principle found in the bark of the privet\n   (Ligustrum vulgare), and extracted as a white crystalline substance\n   with a warm, bitter taste; -- called also ligustron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likable', 'a.', 'Such as can be liked; such as to attract liking; as, a\n   likable person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'superl.', 'Having the same, or nearly the same, appearance,\n   qualities, or characteristics; resembling; similar to; similar; alike;\n   -- often with in and the particulars of the resemblance; as, they are\n   like each other in features, complexion, and many traits of character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'superl.', 'Equal, or nearly equal; as, fields of like extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'superl.', 'Having probability; affording probability; probable;\n   likely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'superl.', 'Inclined toward; disposed to; as, to feel like taking a\n   walk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'n.', 'That which is equal or similar to another; the counterpart;\n   an exact resemblance; a copy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'n.', 'A liking; a preference; inclination; -- usually in pl.; as,\n   we all have likes and dislikes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'a.', 'In a manner like that of; in a manner similar to; as, do not\n   act like him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'a.', 'In a like or similar manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'a.', 'Likely; probably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liked', 'imp. & p. p.', 'of Like');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liking', 'p. pr. & vb. n.', 'of Like');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'a.', 'To suit; to please; to be agreeable to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'a.', 'To be pleased with in a moderate degree; to approve; to take\n   satisfaction in; to enjoy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'a.', 'To liken; to compare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'v. i.', 'To be pleased; to choose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'v. i.', 'To have an appearance or expression; to look; to seem to\n   be (in a specified condition).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like', 'v. i.', 'To come near; to avoid with difficulty; to escape\n   narrowly; as, he liked to have been too late. Cf. Had like, under Like,\n   a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likeable', 'a.', 'See Likable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likehood', 'n.', 'Likelihood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likelihood', 'n.', 'Appearance; show; sign; expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likelihood', 'n.', 'Likeness; resemblance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likelihood', 'n.', 'Appearance of truth or reality; probability;\n   verisimilitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likeliness', 'n.', 'Likelihood; probability.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likeliness', 'n.', 'Suitableness; agreeableness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likely', 'a.', 'Worthy of belief; probable; credible; as, a likely story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likely', 'a.', 'Having probability; having or giving reason to expect; --\n   followed by the infinitive; as, it is likely to rain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likely', 'a.', 'Similar; like; alike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likely', 'a.', 'Such as suits; good-looking; pleasing; agreeable; handsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likely', 'a.', 'Having such qualities as make success probable; well\n   adapted to the place; promising; as, a likely young man; a likely\n   servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likely', 'adv.', 'In all probability; probably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Like-minded', 'a.', 'Having a like disposition or purpose; of the same\n   mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likened', 'imp. & p. p.', 'of Liken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likening', 'p. pr. & vb. n.', 'of Liken');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liken', 'a.', 'To allege, or think, to be like; to represent as like; to\n   compare; as, to liken life to a pilgrimage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liken', 'a.', 'To make or cause to be like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likeness', 'n.', 'The state or quality of being like; similitude;\n   resemblance; similarity; as, the likeness of the one to the other is\n   remarkable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likeness', 'n.', 'Appearance or form; guise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likeness', 'n.', 'That which closely resembles; a portrait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likeness', 'n.', 'A comparison; parable; proverb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likerous', 'n.', 'Alt. of Likerousness');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likerousness', 'n.', 'See Lickerish, Lickerishness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Likewise', 'n.', 'In like manner; also; moreover; too. See Also.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liking', 'p. a.', 'Looking; appearing; as, better or worse liking. See\n   Like, to look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liking', 'n.', 'The state of being pleasing; a suiting. See On liking,\n   below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liking', 'n.', 'The state of being pleased with, or attracted toward, some\n   thing or person; hence, inclination; desire; pleasure; preference; --\n   often with for, formerly with to; as, it is an amusement I have no\n   liking for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liking', 'n.', 'Appearance; look; figure; state of body as to health or\n   condition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilac', 'n.', 'A shrub of the genus Syringa. There are six species, natives\n   of Europe and Asia. Syringa vulgaris, the common lilac, and S. Persica,\n   the Persian lilac, are frequently cultivated for the fragrance and\n   beauty of their purplish or white flowers. In the British colonies\n   various other shrubs have this name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilac', 'n.', 'A light purplish color like that of the flower of the\n   purplish lilac.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilacin', 'n.', 'See Syringin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liliaceous', 'a.', 'Of or pertaining to a natural order of which the lily,\n   tulip, and hyacinth are well-known examples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liliaceous', 'a.', 'Like the blossom of a lily in general form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilial', 'a.', 'Having a general resemblance to lilies or to liliaceous\n   plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilied', 'a.', 'Covered with, or having many, lilies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lill', 'v. i.', 'To loll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilliputian', 'n.', 'One belonging to a very diminutive race described in\n   Swifts \"Voyage to Lilliput.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilliputian', 'n.', 'A person or thing of very small size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilliputian', 'a.', 'Of or pertaining to the imaginary island of Lilliput\n   described by Swift, or to its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilliputian', 'a.', 'Of very small size; diminutive; dwarfed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilly-pilly', 'n.', 'An Australian myrtaceous tree (Eugenia Smithii),\n   having smooth ovate leaves, and panicles of small white flowers. The\n   wood is hard and fine-grained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilt', 'v. i.', 'To do anything with animation and quickness, as to skip,\n   fly, or hop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilt', 'v. i.', 'To sing cheerfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilt', 'v. t.', 'To utter with spirit, animation, or gayety; to sing with\n   spirit and liveliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilt', 'n.', 'Animated, brisk motion; spirited rhythm; sprightliness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilt', 'n.', 'A lively song or dance; a cheerful tune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilies', 'pl. ', 'of Lily');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lily', 'n.', 'A plant and flower of the genus Lilium, endogenous bulbous\n   plants, having a regular perianth of six colored pieces, six stamens,\n   and a superior three-celled ovary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lily', 'n.', 'A name given to handsome flowering plants of several genera,\n   having some resemblance in color or form to a true lily, as Pancratium,\n   Crinum, Amaryllis, Nerine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lily', 'n.', 'That end of a compass needle which should point to the north;\n   -- so called as often ornamented with the figure of a lily or\n   fleur-de-lis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lily-handed', 'a.', 'Having white, delicate hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lily-livered', 'a.', 'White-livered; cowardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lilywort', 'n.', 'Any plant of the Lily family or order.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lim', 'n.', 'A limb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lima', 'n.', 'The capital city of Peru, in South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limaceous', 'a.', 'Pertaining to, or like, Limax, or the slugs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limacina', 'n.', 'A genus of small spiral pteropods, common in the Arctic\n   and Antarctic seas. It contributes to the food of the right whales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lima/on', 'n.', 'A curve of the fourth degree, invented by Pascal. Its\n   polar equation is r = a cos / + b.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limaille', 'n.', 'Filings of metal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liman', 'n.', 'The deposit of slime at the mouth of a river; slime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limation', 'n.', 'The act of filing or polishing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limature', 'n.', 'The act of filing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limature', 'n.', 'That which is filed off; filings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limax', 'n.', 'A genus of airbreathing mollusks, including the common\n   garden slugs. They have a small rudimentary shell. The breathing pore\n   is on the right side of the neck. Several species are troublesome in\n   gardens. See Slug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limb', 'n.', 'A part of a tree which extends from the trunk and separates\n   into branches and twigs; a large branch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limb', 'n.', 'An arm or a leg of a human being; a leg, arm, or wing of an\n   animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limb', 'n.', 'A thing or person regarded as a part or member of, or\n   attachment to, something else.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limb', 'n.', 'An elementary piece of the mechanism of a lock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limb', 'v. t.', 'To supply with limbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limb', 'v. t.', 'To dismember; to tear off the limbs of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limb', 'n.', 'A border or edge, in certain special uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limb', 'n.', 'The border or upper spreading part of a monopetalous corolla,\n   or of a petal, or sepal; blade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limb', 'n.', 'The border or edge of the disk of a heavenly body, especially\n   of the sun and moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limb', 'n.', 'The graduated margin of an arc or circle, in an instrument\n   for measuring angles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbat', 'n.', 'A cooling periodical wind in the Isle of Cyprus, blowing\n   from the northwest from eight oclock, A. M., to the middle of the day\n   or later.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbate', 'a.', 'Bordered, as when one color is surrounded by an edging of\n   another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbec', 'n.', 'An alembic; a still.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbec', 'v. t.', 'To distill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbed', 'a.', 'Having limbs; -- much used in composition; as,\n   large-limbed; short-limbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limber', 'n.', 'The shafts or thills of a wagon or carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limber', 'n.', 'The detachable fore part of a gun carriage, consisting of\n   two wheels, an axle, and a shaft to which the horses are attached. On\n   top is an ammunition box upon which the cannoneers sit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limber', 'n.', 'Gutters or conduits on each side of the keelson to afford a\n   passage for water to the pump well.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbered', 'imp. & p. p.', 'of Limber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbering', 'p. pr. & vb. n.', 'of Limber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limber', 'v. t.', 'To attach to the limber; as, to limber a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limber', 'a.', 'Easily bent; flexible; pliant; yielding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limber', 'v. t.', 'To cause to become limber; to make flexible or pliant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limberness', 'n.', 'The quality or state of being limber; flexibleness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbless', 'a.', 'Destitute of limbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbmeal', 'adv.', 'Piecemeal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbo', 'n.', 'Alt. of Limbus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbus', 'n.', 'An extramundane region where certain classes of souls were\n   supposed to await the judgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbus', 'n.', 'Hence: Any real or imaginary place of restraint or\n   confinement; a prison; as, to put a man in limbo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbus', 'n.', 'A border or margin; as, the limbus of the cornea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limbous', 'a.', 'With slightly overlapping borders; -- said of a suture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lime', 'n.', 'A thong by which a dog is led; a leash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lime', 'n.', 'The linden tree. See Linden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lime', 'n.', 'A fruit allied to the lemon, but much smaller; also, the tree\n   which bears it. There are two kinds; Citrus Medica, var. acida which is\n   intensely sour, and the sweet lime (C. Medica, var. Limetta) which is\n   only slightly sour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lime', 'n.', 'Birdlime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lime', 'n.', 'Oxide of calcium; the white or gray, caustic substance,\n   usually called quicklime, obtained by calcining limestone or shells,\n   the heat driving off carbon dioxide and leaving lime. It develops great\n   heat when treated with water, forming slacked lime, and is an essential\n   ingredient of cement, plastering, mortar, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limed', 'imp. & p. p.', 'of Lime');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liming', 'p. pr. & vb. n.', 'of Lime');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lime', 'v. t.', 'To smear with a viscous substance, as birdlime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lime', 'v. t.', 'To entangle; to insnare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lime', 'v. t.', 'To treat with lime, or oxide or hydrate of calcium; to\n   manure with lime; as, to lime hides for removing the hair; to lime\n   sails in order to whiten them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lime', 'v. t.', 'To cement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limehound', 'n.', 'A dog used in hunting the wild boar; a leamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limekiln', 'n.', 'A kiln or furnace in which limestone or shells are burned\n   and reduced to lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limenean', 'a.', 'Of or pertaining to Lima, or to the inhabitants of Lima,\n   in Peru.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limenean', 'n.', 'A native or inhabitant of Lima.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limer', 'n.', 'A limehound; a limmer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limestone', 'n.', 'A rock consisting chiefly of calcium carbonate or\n   carbonate of lime. It sometimes contains also magnesium carbonate, and\n   is then called magnesian or dolomitic limestone. Crystalline limestone\n   is called marble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lime twig', '', 'See under 4th Lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lime-twigged', 'a.', 'Beset with snares; insnared, as with birdlime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limewater', 'n.', 'Water impregnated with lime; esp., an artificial\n   solution of lime for medicinal purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limicolae', 'n. pl.', 'A group of shore birds, embracing the plovers,\n   sandpipers, snipe, curlew, etc. ; the Grallae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limicoline', 'a.', 'Shore-inhabiting; of or pertaining to the Limicolae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liminess', 'n.', 'The state or quality of being limy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limit', 'v. t.', 'That which terminates, circumscribes, restrains, or\n   confines; the bound, border, or edge; the utmost extent; as, the limit\n   of a walk, of a town, of a country; the limits of human knowledge or\n   endeavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limit', 'v. t.', 'The space or thing defined by limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limit', 'v. t.', 'That which terminates a period of time; hence, the period\n   itself; the full time or extent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limit', 'v. t.', 'A restriction; a check; a curb; a hindrance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limit', 'v. t.', 'A determining feature; a distinguishing characteristic; a\n   differentia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limit', 'v. t.', 'A determinate quantity, to which a variable one\n   continually approaches, and may differ from it by less than any given\n   difference, but to which, under the law of variation, the variable can\n   never become exactly equivalent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limited', 'imp. & p. p.', 'of Limit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limiting', 'p. pr. & vb. n.', 'of Limit');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limit', 'v. t.', 'To apply a limit to, or set a limit for; to terminate,\n   circumscribe, or restrict, by a limit or limits; as, to limit the\n   acreage of a crop; to limit the issue of paper money; to limit ones\n   ambitions or aspirations; to limit the meaning of a word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limit', 'v. i.', 'To beg, or to exercise functions, within a certain\n   limited region; as, a limiting friar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitable', 'a.', 'Capable of being limited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitaneous', 'v. t.', 'Of or pertaining to a limit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitarian', 'a.', 'Tending to limit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitary', 'v. t.', 'Placed at the limit, as a guard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitary', 'v. t.', 'Confined within limits; limited in extent, authority,\n   power, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitary', 'v. t.', 'Limiting, or tending to limit; restrictive.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ries', 'pl. ', 'of Limitary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitary', 'n.', 'That which serves to limit; a boundary; border land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitary', 'n.', 'A limiter. See Limiter, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitate', 'v. t.', 'Bounded by a distinct line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitation', 'v. t.', 'The act of limiting; the state or condition of being\n   limited; as, the limitation of his authority was approved by the\n   council.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitation', 'v. t.', 'That which limits; a restriction; a qualification; a\n   restraining condition, defining circumstance, or qualifying conception;\n   as, limitations of thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitation', 'v. t.', 'A certain precinct within which friars were allowed\n   to beg, or exercise their functions; also, the time during which they\n   were permitted to exercise their functions in such a district.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitation', 'v. t.', 'A limited time within or during which something is\n   to be done.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitation', 'v. t.', 'A certain period limited by statute after which the\n   claimant shall not enforce his claims by suit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitation', 'v. t.', 'A settling of an estate or property by specific\n   rules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitation', 'v. t.', 'A restriction of power; as, a constitutional\n   limitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limited', 'a.', 'Confined within limits; narrow; circumscribed; restricted;\n   as, our views of nature are very limited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitedly', 'adv.', 'With limitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitedness', 'n.', 'The quality of being limited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limiter', 'n.', 'One who, or that which, limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limiter', 'n.', 'A friar licensed to beg within certain bounds, or whose\n   duty was limited to a certain district.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitive', 'a.', 'Involving a limit; as, a limitive law, one designed to\n   limit existing powers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitless', 'a.', 'Having no limits; unbounded; boundless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limitour', 'n.', 'See Limiter, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limmer', 'a.', 'Limber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limmer', 'n.', 'A limehound; a leamer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limmer', 'n.', 'A mongrel, as a cross between the mastiff and hound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limmer', 'n.', 'A low, base fellow; also, a prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limmer', 'n.', 'A man rope at the side of a ladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limned', 'imp. & p. p.', 'of Limn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limning', 'p. pr. & vb. n.', 'of Limn');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limn', 'v. t.', 'To draw or paint; especially, to represent in an artistic\n   way with pencil or brush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limn', 'v. t.', 'To illumine, as books or parchments, with ornamental\n   figures, letters, or borders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lim naea', 'n.', 'A genus of fresh-water air-breathing mollusks, abundant\n   in ponds and streams; -- called also pond snail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limner', 'n.', 'A painter; an artist');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limner', 'n.', 'One who paints portraits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limner', 'n.', 'One who illuminates books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limniad', 'n.', 'See Limoniad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limning', 'n.', 'The act, process, or art of one who limns; the picture or\n   decoration so produced.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limoges', 'n.', 'A city of Southern France.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limoniad', 'n.', 'A nymph of the meadows; -- called also Limniad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limonin', 'n.', 'A bitter, white, crystalline substance found in orange and\n   lemon seeds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limonite', 'n.', 'Hydrous sesquioxide of iron, an important ore of iron,\n   occurring in stalactitic, mammillary, or earthy forms, of a dark brown\n   color, and yellowish brown powder. It includes bog iron. Also called\n   brown hematite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limosis', 'n.', 'A ravenous appetite caused by disease; excessive and\n   morbid hunger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limous', 'a.', 'Muddy; slimy; thick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limped', 'imp. & p. p.', 'of Limp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limping', 'p. pr. & vb. n.', 'of Limp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limp', 'v. i.', 'To halt; to walk lamely. Also used figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limp', 'n.', 'A halt; the act of limping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limp', 'n.', 'A scraper for removing poor ore or refuse from the sieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limp', 'a.', 'Flaccid; flabby, as flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limp', 'a.', 'Lacking stiffness; flimsy; as, a limp cravat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limper', 'n.', 'One who limps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limpet', 'n.', 'In a general sense, any hatshaped, or conical, gastropod\n   shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limpet', 'n.', 'Any one of many species of marine shellfish of the order\n   Docoglossa, mostly found adhering to rocks, between tides.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limpet', 'n.', 'Any species of Siphonaria, a genus of limpet-shaped\n   Pulmonifera, living between tides, on rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limpet', 'n.', 'A keyhole limpet. See Fissurella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limpid', 'a.', 'Characterized by clearness or transparency; clear; as, a\n   limpid stream.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limpidity', 'n.', 'The quality or state of being limpid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limpidness', 'n.', 'Quality of being limpid; limpidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limpin', 'n.', 'A limpet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limpingly', 'adv.', 'In a limping manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limpitude', 'n.', 'Limpidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limpkin', 'n.', 'Either one of two species of wading birds of the genus\n   Aramus, intermediate between the cranes and rails. The limpkins are\n   remarkable for the great length of the toes. One species (A. giganteus)\n   inhabits Florida and the West Indies; the other (A. scolopaceus) is\n   found in South America. Called also courlan, and crying bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limpness', 'n.', 'The quality or state of being limp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limpsy', 'a.', 'Alt. of Limsy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limsy', 'a.', 'Limp; flexible; flimsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limu', 'n.', 'The Hawaiian name for seaweeds. Over sixty kinds are used as\n   food, and have species names, as Limu Lipoa, Limu palawai, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limule', 'n.', 'A limulus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limuloidea', 'n. pl.', 'An order of Merostomata, including among living\n   animals the genus Limulus, with various allied fossil genera, mostly of\n   the Carboniferous period. Called also Xiphosura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limuli', 'pl. ', 'of Limulus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limulus', 'n.', 'The only existing genus of Merostomata. It includes only a\n   few species from the East Indies, and one (Limulus polyphemus) from the\n   Atlantic coast of North America. Called also Molucca crab, king crab,\n   horseshoe crab, and horsefoot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limy', 'a.', 'Smeared with, or consisting of, lime; viscous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limy', 'a.', 'Containing lime; as, a limy soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Limy', 'a.', 'Resembling lime; having the qualities of lime.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lin', 'v. i.', 'To yield; to stop; to cease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lin', 'v. t.', 'To cease from.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lin', 'n.', 'A pool or collection of water, particularly one above or below\n   a fall of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lin', 'n.', 'A waterfall, or cataract; as, a roaring lin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lin', 'n.', 'A steep ravine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linage', 'n.', 'See Lineage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linament', 'n.', 'Lint; esp., lint made into a tent for insertion into\n   wounds or ulcers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linarite', 'n.', 'A hydrous sulphate of lead and copper occurring in bright\n   blue monoclinic crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linch', 'n.', 'A ledge; a right-angled projection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linchi', 'n.', 'An esculent swallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linchpin', 'n.', 'A pin used to prevent the wheel of a vehicle from sliding\n   off the axletree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lincoln green', '', 'A color of cloth formerly made in Lincoln, England;\n   the cloth itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lincture', 'n.', 'Alt. of Linctus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linctus', 'n.', 'Medicine taken by licking with the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lind', 'n.', 'The linden. See Linden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linden', 'n.', 'A handsome tree (Tilia Europaea), having cymes of light\n   yellow flowers, and large cordate leaves. The tree is common in Europe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linden', 'n.', 'In America, the basswood, or Tilia Americana.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lindia', 'n.', 'A peculiar genus of rotifers, remarkable for the absence of\n   ciliated disks. By some zoologists it is thought to be like the\n   ancestral form of the Arthropoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lindiform', 'a.', 'Resembling the genus Lindia; -- said of certain apodous\n   insect larvae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'Flax; linen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'The longer and finer fiber of flax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lined', 'imp. & p. p.', 'of Line');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lining', 'p. pr. & vb. n.', 'of Line');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'v. t.', 'To cover the inner surface of; as, to line a cloak with\n   silk or fur; to line a box with paper or tin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'v. t.', 'To put something in the inside of; to fill; to supply, as\n   a purse with money.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'v. t.', 'To place persons or things along the side of for security\n   or defense; to strengthen by adding anything; to fortify; as, to line\n   works with soldiers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'v. t.', 'To impregnate; -- applied to brute animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A linen thread or string; a slender, strong cord; also, a\n   cord of any thickness; a rope; a hawser; as, a fishing line; a line for\n   snaring birds; a clothesline; a towline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A more or less threadlike mark of pen, pencil, or graver; any\n   long mark; as, a chalk line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'The course followed by anything in motion; hence, a road or\n   route; as, the arrow descended in a curved line; the place is remote\n   from lines of travel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'Direction; as, the line of sight or vision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A row of letters, words, etc., written or printed; esp., a\n   row of words extending across a page or column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A short letter; a note; as, a line from a friend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A verse, or the words which form a certain number of feet,\n   according to the measure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'Course of conduct, thought, occupation, or policy; method of\n   argument; department of industry, trade, or intellectual activity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'That which has length, but not breadth or thickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'The exterior limit of a figure, plat, or territory; boundary;\n   contour; outline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A threadlike crease marking the face or the hand; hence,\n   characteristic mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'Lineament; feature; figure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A straight row; a continued series or rank; as, a line of\n   houses, or of soldiers; a line of barriers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A series or succession of ancestors or descendants of a given\n   person; a family or race; as, the ascending or descending line; the\n   line of descent; the male line; a line of kings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A connected series of public conveyances, and hence, an\n   established arrangement for forwarding merchandise, etc.; as, a line of\n   stages; an express line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A circle of latitude or of longitude, as represented on a\n   map.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'The equator; -- usually called the line, or equinoctial line;\n   as, to cross the line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A long tape, or a narrow ribbon of steel, etc., marked with\n   subdivisions, as feet and inches, for measuring; a tapeline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A measuring line or cord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'That which was measured by a line, as a field or any piece of\n   land set apart; hence, allotted place of abode.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'Instruction; doctrine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'The proper relative position or adjustment of parts, not as\n   to design or proportion, but with reference to smooth working; as, the\n   engine is in line or out of line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'The track and roadbed of a railway; railroad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A row of men who are abreast of one another, whether side by\n   side or some distance apart; -- opposed to column.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'The regular infantry of an army, as distinguished from\n   militia, guards, volunteer corps, cavalry, artillery, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A trench or rampart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'Dispositions made to cover extended positions, and presenting\n   a front in but one direction to an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'Form of a vessel as shown by the outlines of vertical,\n   horizontal, and oblique sections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'One of the straight horizontal and parallel prolonged strokes\n   on and between which the notes are placed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A number of shares taken by a jobber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A series of various qualities and values of the same general\n   class of articles; as, a full line of hosiery; a line of merinos, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'The wire connecting one telegraphic station with another, or\n   the whole of a system of telegraph wires under one management and name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'The reins with which a horse is guided by his driver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'n.', 'A measure of length; one twelfth of an inch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'v. t.', 'To mark with a line or lines; to cover with lines; as, to\n   line a copy book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'v. t.', 'To represent by lines; to delineate; to portray.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'v. t.', 'To read or repeat line by line; as, to line out a hymn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Line', 'v. t.', 'To form into a line; to align; as, to line troops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineage', 'n.', 'Descent in a line from a common progenitor; progeny; race;\n   descending line of offspring or ascending line of parentage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineal', 'a.', 'Descending in a direct line from an ancestor; hereditary;\n   derived from ancestors; -- opposed to collateral; as, a lineal descent\n   or a lineal descendant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineal', 'a.', 'Inheriting by direct descent; having the right by direct\n   descent to succeed (to).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineal', 'a.', 'Composed of lines; delineated; as, lineal designs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineal', 'a.', 'In the direction of a line; of or pertaining to a line;\n   measured on, or ascertained by, a line; linear; as, lineal magnitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineality', 'n.', 'The quality of being lineal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineally', 'adv.', 'In a lineal manner; as, the prince is lineally\n   descended from the Conqueror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineament', 'n.', 'One of the outlines, exterior features, or distinctive\n   marks, of a body or figure, particularly of the face; feature; form;\n   mark; -- usually in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linear', 'a.', 'Of or pertaining to a line; consisting of lines; in a\n   straight direction; lineal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linear', 'a.', 'Like a line; narrow; of the same breadth throughout, except\n   at the extremities; as, a linear leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linearensate', 'a.', 'Having the form of a sword, but very long and narrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linearly', 'adv.', 'In a linear manner; with lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linear-shaped', 'a.', 'Of a linear shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineary', 'a.', 'Linear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineate', 'a.', 'Alt. of Lineated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineated', 'a.', 'Marked with lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineated', 'a.', 'Marked longitudinally with depressed parallel lines; as,\n   a lineate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineation', 'n.', 'Delineation; a line or lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineature', 'n.', 'Anything having outline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linemen', 'pl. ', 'of Lineman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineman', 'n.', 'One who carries the line in surveying, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineman', 'n.', 'A man employed to examine the rails of a railroad to see\n   if they are in good condition; also, a man employed to repair telegraph\n   lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linen', 'n.', 'Made of linen; as, linen cloth; a linen stocking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linen', 'n.', 'Resembling linen cloth; white; pale.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linen', 'n.', 'Thread or cloth made of flax or (rarely) of hemp; -- used in\n   a general sense to include cambric, shirting, sheeting, towels,\n   tablecloths, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linen', 'n.', 'Underclothing, esp. the shirt, as being, in former times,\n   chiefly made of linen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linener', 'n.', 'A dealer in linen; a linen draper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineolate', 'a.', 'Marked with little lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lineolate', 'a.', 'Marked longitudinally with fine lines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liner', 'n.', 'One who lines, as, a liner of shoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liner', 'n.', 'A vessel belonging to a regular line of packets; also, a\n   line-of-battle ship; a ship of the line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liner', 'n.', 'A thin piece placed between two parts to hold or adjust\n   them, fill a space, etc.; a shim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liner', 'n.', 'A lining within the cylinder, in which the piston works and\n   between which and the outer shell of the cylinder a space is left to\n   form a steam jacket.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liner', 'n.', 'A slab on which small pieces of marble, tile, etc., are\n   fastened for grinding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liner', 'n.', 'A ball which, when struck, flies through the air in a nearly\n   straight line not far from the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ling', '', 'A noun suffix, commonly having a diminutive or a depreciatory\n   force; as in duckling, gosling, hireling, fosterling, firstling,\n   underling.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ling', '', 'An adverbial suffix; as, darkling, flatling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ling', 'a.', 'A large, marine, gadoid fish (Molva vulgaris) of Northern\n   Europe and Greenland. It is valued as a food fish and is largely salted\n   and dried. Called also drizzle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ling', 'a.', 'The burbot of Lake Ontario.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ling', 'a.', 'An American hake of the genus Phycis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ling', 'a.', 'A New Zealand food fish of the genus Genypterus. The name is\n   also locally applied to other fishes, as the cultus cod, the mutton\n   fish, and the cobia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ling', 'n.', 'Heather (Calluna vulgaris).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linga', 'n.', 'Alt. of Lingam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingam', 'n.', 'The phallic symbol under which Siva is principally\n   worshiped in his character of the creative and reproductive power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ling-bird', 'n.', 'The European meadow pipit; -- called also titling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingel', 'n.', 'A shoemakers thread.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingel', 'n.', 'A little tongue or thong of leather; a lacing for belts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingence', 'n.', 'A linctus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingered', 'imp. & p. p.', 'of Linger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingering', 'p. pr. & vb. n.', 'of Linger');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linger', 'a.', 'To delay; to loiter; to remain or wait long; to be slow or\n   reluctant in parting or moving; to be slow in deciding; to be in\n   suspense; to hesitate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linger', 'v. t.', 'To protract; to draw out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linger', 'v. t.', 'To spend or pass in a lingering manner; -- with out; as,\n   to linger out ones days on a sick bed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingerer', 'n.', 'One who lingers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingering', 'a.', 'Delaying.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingering', 'a.', 'Drawn out in time; remaining long; protracted; as, a\n   lingering disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingeringly', 'adv.', 'With delay; slowly; tediously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linget', 'n.', 'An ingot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingism', 'n.', 'A mode of treating certain diseases, as obesity, by\n   gymnastics; -- proposed by Pehr Henrik Ling, a Swede. See Kinesiatrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingle', 'n.', 'See Lingel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingo', 'n.', 'Language; speech; dialect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingoa wood', '', 'Amboyna wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingot', 'n.', 'A linget or ingot; also, a mold for casting metals. See\n   Linget.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguae', 'pl. ', 'of Lingua');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingua', 'n.', 'A tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingua', 'n.', 'A median process of the labium, at the under side of the\n   mouth in insects, and serving as a tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguacious', 'a.', 'Given to the use of the tongue; loquacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguadental', 'a.', 'Formed or uttered by the joint use of the tongue and\n   teeth, or rather that part of the gum just above the front teeth;\n   dentolingual, as the letters d and t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguadental', 'n.', 'An articulation pronounced by the aid or use of the\n   tongue and teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingua Franca', '', 'The commercial language of the Levant, -- a mixture of\n   the languages of the people of the region and of foreign traders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingual', 'a.', 'Of or pertaining to the tongue; uttered by the aid of the\n   tongue; glossal; as, the lingual nerves; a lingual letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingual', 'n.', 'A consonant sound formed by the aid of the tongue; -- a\n   term especially applied to certain articulations (as those of t, d, th,\n   and n) and to the letters denoting them.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguality', 'n.', 'The quality of being lingual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguatulida', 'n. pl.', 'Same as Linguatulina.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguatulina', 'n. pl.', 'An order of wormlike, degraded, parasitic\n   arachnids. They have two pairs of retractile hooks, near the mouth.\n   Called also Pentastomida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguidental', 'a. & n.', 'Linguadental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguiform', 'a.', 'Having the form of the tongue; tongue-shaped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguist', 'n.', 'A master of the use of language; a talker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguist', 'n.', 'A person skilled in languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguistic', 'a.', 'Alt. of Linguistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguistical', 'a.', 'Of or pertaining to language; relating to\n   linguistics, or to the affinities of languages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguistically', 'adv.', 'In a linguistic manner; from the point of view of\n   a linguist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linguistics', 'n.', 'The science of languages, or of the origin,\n   signification, and application of words; glossology.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-lae', 'pl. ', 'of Lingula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingula', 'n.', 'A tonguelike process or part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingula', 'n.', 'Any one of numerous species of brachiopod shells belonging\n   to the genus Lingula, and related genera. See Brachiopoda, and\n   Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lingulate', 'a.', 'Shaped like the tongue or a strap; ligulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linigerous', 'a.', 'Bearing flax; producing linen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liniment', 'n.', 'A liquid or semiliquid preparation of a consistence\n   thinner than an ointment, applied to the skin by friction, esp. one\n   used as a sedative or a stimulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lining', 'n.', 'The act of one who lines; the act or process of making\n   lines, or of inserting a lining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lining', 'n.', 'That which covers the inner surface of anything, as of a\n   garment or a box; also, the contents of anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Link', 'n.', 'A torch made of tow and pitch, or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Link', 'n.', 'A single ring or division of a chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Link', 'n.', 'Hence: Anything, whether material or not, which binds\n   together, or connects, separate things; a part of a connected series; a\n   tie; a bond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Link', 'n.', 'Anything doubled and closed like a link; as, a link of\n   horsehair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Link', 'n.', 'Any one of the several elementary pieces of a mechanism, as\n   the fixed frame, or a rod, wheel, mass of confined liquid, etc., by\n   which relative motion of other parts is produced and constrained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Link', 'n.', 'Any intermediate rod or piece for transmitting force or\n   motion, especially a short connecting rod with a bearing at each end;\n   specifically (Steam Engine), the slotted bar, or connecting piece, to\n   the opposite ends of which the eccentric rods are jointed, and by means\n   of which the movement of the valve is varied, in a link motion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Link', 'n.', 'The length of one joint of Gunters chain, being the\n   hundredth part of it, or 7.92 inches, the chain being 66 feet in\n   length. Cf. Chain, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Link', 'n.', 'A bond of affinity, or a unit of valence between atoms; --\n   applied to a unit of chemical force or attraction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Link', 'n.', 'Sausages; -- because linked together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linked', 'imp. & p. p.', 'of Link');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linking', 'p. pr. & vb. n.', 'of Link');
  INSERT INTO words (word, word_type, description) VALUES  (    'Link', 'v. t.', 'To connect or unite with a link or as with a link; to\n   join; to attach; to unite; to couple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Link', 'v. i.', 'To be connected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linkage', 'n.', 'The act of linking; the state of being linked; also, a\n   system of links.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linkage', 'n.', 'Manner of linking or of being linked; -- said of the union\n   of atoms or radicals in the molecule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linkage', 'n.', 'A system of straight lines or bars, fastened together by\n   joints, and having certain of their points fixed in a plane. It is used\n   to describe straight lines and curves in the plane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linkboy', 'n.', 'Alt. of Linkman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linkman', 'n.', 'A boy or man that carried a link or torch to light\n   passengers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Link motion', '', 'A valve gear, consisting of two eccentrics with their\n   rods, giving motion to a slide valve by an adjustable connecting bar,\n   called the link, in such a way that the motion of the engine can be\n   reversed, or the cut-off varied, at will; -- used very generally in\n   locomotives and marine engines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linkwork', 'n.', 'A fabric consisting of links made of metal or other\n   material fastened together; also, a chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linkwork', 'n.', 'Mechanism in which links, or intermediate connecting\n   pieces, are employed to transmit motion from one part to another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linnaea borealis', '', 'The twin flower which grows in cold northern\n   climates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linnaean', 'a.', 'Alt. of Linnean');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linnean', 'a.', 'Of or pertaining to Linnaeus, the celebrated Swedish\n   botanist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linnaeite', 'n.', 'A mineral of pale steel-gray color and metallic luster,\n   occurring in isometric crystals, and also massive. It is a sulphide of\n   cobalt containing some nickel or copper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linne', 'n.', 'Flax. See Linen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linnet', 'n.', 'Any one of several species of fringilline birds of the\n   genera Linota, Acanthis, and allied genera, esp. the common European\n   species (L. cannabina), which, in full summer plumage, is chestnut\n   brown above, with the breast more or less crimson. The feathers of its\n   head are grayish brown, tipped with crimson. Called also gray linnet,\n   red linnet, rose linnet, brown linnet, lintie, lintwhite, gorse\n   thatcher, linnet finch, and greater redpoll. The American redpoll\n   linnet (Acanthis linaria) often has the crown and throat rosy. See\n   Redpoll, and Twite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linoleate', 'n.', 'A salt of linoleic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linoleic', 'a.', 'Pertaining to, or derived from, linoleum, or linseed oil;\n   specifically (Chem.), designating an organic acid, a thin yellow oil,\n   found combined as a salt of glycerin in oils of linseed, poppy, hemp,\n   and certain nuts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linoleum', 'n.', 'Linseed oil brought to various degrees of hardness by\n   some oxidizing process, as by exposure to heated air, or by treatment\n   with chloride of sulphur. In this condition it is used for many of the\n   purposes to which India rubber has been applied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linoleum', 'n.', 'A kind of floor cloth made by laying hardened linseed oil\n   mixed with ground cork on a canvas backing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linoxin', 'n.', 'A resinous substance obtained as an oxidation product of\n   linoleic acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linsang', 'n.', 'Any viverrine mammal of the genus Prionodon, inhabiting\n   the East Indies and Southern Asia. The common East Indian linsang (P.\n   gracilis) is white, crossed by broad, black bands. The Guinea linsang\n   (Porana Richardsonii) is brown with black spots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linseed', 'n.', 'The seeds of flax, from which linseed oil is obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linsey', 'n.', 'Linsey-woolsey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linsey-woolsey', 'n.', 'Cloth made of linen and wool, mixed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linsey-woolsey', 'n.', 'Jargon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linsey-woolsey', 'a.', 'Made of linen and wool; hence, of different and\n   unsuitable parts; mean.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linstock', 'n.', 'A pointed forked staff, shod with iron at the foot, to\n   hold a lighted match for firing cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lint', 'n.', 'Flax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lint', 'n.', 'Linen scraped or otherwise made into a soft, downy or fleecy\n   substance for dressing wounds and sores; also, fine ravelings, down,\n   fluff, or loose short fibers from yarn or fabrics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lintel', 'n.', 'A horizontal member spanning an opening, and carrying the\n   superincumbent weight by means of its strength in resisting crosswise\n   fracture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lintie', 'n.', 'Alt. of Lintwhite');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lintwhite', 'n.', 'See Linnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lintseed', 'n.', 'See Linseed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Linum', 'n.', 'A genus of herbaceous plants including the flax (Linum\n   usitatissimum).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lion', 'n.', 'A large carnivorous feline mammal (Felis leo), found in\n   Southern Asia and in most parts of Africa, distinct varieties occurring\n   in the different countries. The adult male, in most varieties, has a\n   thick mane of long shaggy hair that adds to his apparent size, which is\n   less than that of the largest tigers. The length, however, is sometimes\n   eleven feet to the base of the tail. The color is a tawny yellow or\n   yellowish brown; the mane is darker, and the terminal tuft of the tail\n   is black. In one variety, called the maneless lion, the male has only a\n   slight mane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lion', 'n.', 'A sign and a constellation; Leo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lion', 'n.', 'An object of interest and curiosity, especially a person who\n   is so regarded; as, he was quite a lion in London at that time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lionced', 'a.', 'Adorned with lions heads; having arms terminating in\n   lions heads; -- said of a cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lioncel', 'n.', 'A small lion, especially one of several borne in the same\n   coat of arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lionel', 'n.', 'The whelp of a lioness; a young lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lioness', 'n.', 'A female lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lionet', 'n.', 'A young or small lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lion-heart', 'n.', 'A very brave person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lion-hearted', 'a.', 'Very brave; brave and magnanimous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lionhood', 'n.', 'State of being a lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lionism', 'n.', 'An attracting of attention, as a lion; also, the treating\n   or regarding as a lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lionized', 'imp. & p. p.', 'of Lionize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lionizing', 'p. pr. & vb. n.', 'of Lionize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lionize', 'v. t.', 'To treat or regard as a lion or object of great\n   interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lionize', 'v. t.', 'To show the lions or objects of interest to; to conduct\n   about among objects of interest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lionlike', 'a.', 'Like a lion; brave as a lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lionly', 'a.', 'Like a lion; fierce.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lions ear', '', 'A name given in Western South America to certain plants\n   with shaggy tomentose leaves, as species of Culcitium, and Espeletia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lions foot', '', 'A composite plant of the genus Prenanthes, of which\n   several species are found in the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lions foot', '', 'The edelweiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lionship', 'n.', 'The state of being a lion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lions leaf', '', 'A South European plant of the genus Leontice (L.\n   leontopetalum), the tuberous roots of which contain so much alkali that\n   they are sometimes used as a substitute for soap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lions tail', '', 'A genus of labiate plants (Leonurus); -- so called from\n   a fancied resemblance of its flower spikes to the tuft of a lions\n   tail. L. Cardiaca is the common motherwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lions teeth', 'pl. ', 'of Lions tooth');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lions tooth', '', 'See Leontodon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lip', 'n.', 'One of the two fleshy folds which surround the orifice of the\n   mouth in man and many other animals. In man the lips are organs of\n   speech essential to certain articulations. Hence, by a figure they\n   denote the mouth, or all the organs of speech, and sometimes speech\n   itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lip', 'n.', 'An edge of an opening; a thin projecting part of anything; a\n   kind of short open spout; as, the lip of a vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lip', 'n.', 'The sharp cutting edge on the end of an auger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lip', 'n.', 'One of the two opposite divisions of a labiate corolla.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lip', 'n.', 'The odd and peculiar petal in the Orchis family. See\n   Orchidaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lip', 'n.', 'One of the edges of the aperture of a univalve shell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipped', 'imp. & p. p.', 'of Lip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipping', 'p. pr. & vb. n.', 'of Lip');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lip', 'v. t.', 'To touch with the lips; to put the lips to; hence, to kiss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lip', 'v. t.', 'To utter; to speak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lip', 'v. t.', 'To clip; to trim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipaemia', 'n.', 'A condition in which fat occurs in the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipans', 'n. pl.', 'A tribe of North American Indians, inhabiting the\n   northern part of Mexico. They belong to the Tinneh stock, and are\n   closely related to the Apaches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liparian', 'n.', 'Any species of a family (Liparidae) of destructive\n   bombycid moths, as the tussock moths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liparite', 'n.', 'A quartzose trachyte; rhyolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipic', 'a.', 'Pertaining to, or derived from, fat. The word was formerly\n   used specifically to designate a supposed acid obtained by the\n   oxidation of oleic acid, tallow, wax, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipinic', 'a.', 'Lipic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipless', 'a.', 'Having no lips.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liplet', 'n.', 'A little lip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipocephala', 'n. pl.', 'Same as Lamellibranchia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipochrin', 'n.', 'A yellow coloring matter, soluble in ether, contained in\n   the small round fat drops in the retinal epithelium cells. It is best\n   obtained from the eyes of frogs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipogram', 'n.', 'A writing composed of words not having a certain letter\n   or letters; -- as in the Odyssey of Tryphiodorus there was no A in the\n   first book, no B in the second, and so on.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipogrammatic', 'a.', 'Omitting a letter; composed of words not having a\n   certain letter or letters; as, lipogrammatic writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipogrammatist', 'n.', 'One who makes a lipogram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipoma', 'n.', 'A tumor consisting of fat or adipose tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipothymic', 'a.', 'Tending to swoon; fainting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipothymous', 'a.', 'Pertaining, or given, to swooning; fainting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipothymy', 'n.', 'A fainting; a swoon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipped', 'a.', 'Having a lip or lips; having a raised or rounded edge\n   resembling the lip; -- often used in composition; as, thick-lipped,\n   thin-lipped, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipped', 'a.', 'Labiate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lippitude', 'n.', 'Soreness of eyes; the state of being blear-eyed;\n   blearedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipse', 'v. i.', 'To lisp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lipyl', 'n.', 'A hypothetical radical of glycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquable', 'v. i.', 'Capable of being melted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquate', 'v. i.', 'To melt; to become liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquate', 'v. t.', 'To separate by fusion, as a more fusible from a less\n   fusible material.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquation', 'n.', 'The act or operation of making or becoming liquid; also,\n   the capacity of becoming liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquation', 'n.', 'The process of separating, by heat, an easily fusible\n   metal from one less fusible; eliquation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquefacient', 'n.', 'That which serves to liquefy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquefacient', 'n.', 'An agent, as mercury, iodine, etc., which promotes\n   the liquefying processes of the system, and increases the secretions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquefaction', 'n.', 'The act or operation of making or becoming liquid;\n   especially, the conversion of a solid into a liquid by the sole agency\n   of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquefaction', 'n.', 'The state of being liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquefaction', 'n.', 'The act, process, or method, of reducing a gas or\n   vapor to a liquid by means of cold or pressure; as, the liquefaction of\n   oxygen or hydrogen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquefiable', 'a.', 'Capable of being changed from a solid to a liquid\n   state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquefier', 'n.', 'That which liquefies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquefied', 'imp. & p. p.', 'of Liquefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquefying', 'p. pr. & vb. n.', 'of Liquefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquefy', 'v. t.', 'To convert from a solid form to that of a liquid; to\n   melt; to dissolve; and technically, to melt by the sole agency of heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquefy', 'v. i.', 'To become liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquescency', 'n.', 'The quality or state of being liquescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquescent', 'a.', 'Tending to become liquid; inclined to melt; melting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liqueur', 'n.', 'An aromatic alcoholic cordial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquid', 'a.', 'Flowing freely like water; fluid; not solid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquid', 'a.', 'Being in such a state that the component parts move freely\n   among themselves, but do not tend to separate from each other as the\n   particles of gases and vapors do; neither solid nor aeriform; as,\n   liquid mercury, in distinction from mercury solidified or in a state of\n   vapor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquid', 'a.', 'Flowing or sounding smoothly or without abrupt transitions\n   or harsh tones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquid', 'a.', 'Pronounced without any jar or harshness; smooth; as, l and\n   r are liquid letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquid', 'a.', 'Fluid and transparent; as, the liquid air.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquid', 'a.', 'Clear; definite in terms or amount.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquid', 'n.', 'A substance whose parts change their relative position on\n   the slightest pressure, and therefore retain no definite form; any\n   substance in the state of liquidity; a fluid that is not aeriform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquid', 'n.', 'A letter which has a smooth, flowing sound, or which flows\n   smoothly after a mute; as, l and r, in bla, bra. M and n also are\n   called liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidambar', 'n.', 'A genus consisting of two species of tall trees having\n   star-shaped leaves, and woody burlike fruit. Liquidambar styraciflua is\n   the North American sweet qum, and L. Orientalis is found in Asia Minor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidambar', 'n.', 'The balsamic juice which is obtained from these trees\n   by incision. The liquid balsam of the Oriental tree is liquid storax.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidamber', 'n.', 'See Liquidambar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidated', 'imp. & p. p.', 'of Liquidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidating', 'p. pr. & vb. n.', 'of Liquidate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidate', 'v. t.', 'To determine by agreement or by litigation the\n   precise amount of (indebtedness); or, where there is an indebtedness to\n   more than one person, to determine the precise amount of (each\n   indebtedness); to make the amount of (an indebtedness) clear and\n   certain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidate', 'v. t.', 'In an extended sense: To ascertain the amount, or the\n   several amounts, of , and apply assets toward the discharge of (an\n   indebtedness).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidate', 'v. t.', 'To discharge; to pay off, as an indebtedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidate', 'v. t.', 'To make clear and intelligible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidate', 'v. t.', 'To make liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidation', 'n.', 'The act or process of liquidating; the state of being\n   liquidated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidator', 'n.', 'One who, or that which, liquidates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidator', 'n.', 'An officer appointed to conduct the winding up of a\n   company, to bring and defend actions and suits in its name, and to do\n   all necessary acts on behalf of the company.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidity', 'n.', 'The state or quality of being liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidized', 'imp. & p. p.', 'of Liquidize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidizing', 'p. pr. & vb. n.', 'of Liquidize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidize', 'v. t.', 'To render liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidly', 'adv.', 'In a liquid manner; flowingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquidness', 'n.', 'The quality or state of being liquid; liquidity;\n   fluency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquor', 'n.', 'Any liquid substance, as water, milk, blood, sap, juice, or\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquor', 'n.', 'Specifically, alcoholic or spirituous fluid, either\n   distilled or fermented, as brandy, wine, whisky, beer, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquor', 'n.', 'A solution of a medicinal substance in water; --\n   distinguished from tincture and aqua.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquored', 'imp. & p. p.', 'of Liquor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquoring', 'p. pr. & vb. n.', 'of Liquor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquor', 'v. t.', 'To supply with liquor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquor', 'v. t.', 'To grease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquorice', 'n.', 'See Licorice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquorish', 'a.', 'See Lickerish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liquorous', 'a.', 'Eagerly desirous. See Lickerish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lire', 'pl. ', 'of Lira');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lira', 'n.', 'An Italian coin equivalent in value to the French franc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lirella', 'n.', 'A linear apothecium furrowed along the middle; the fruit\n   of certain lichens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lirelliform', 'a.', 'Like a lirella.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liriodendra', 'pl. ', 'of Liriodendron');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liriodendron', 'n.', 'A genus of large and very beautiful trees of North\n   America, having smooth, shining leaves, and handsome, tuliplike\n   flowers; tulip tree; whitewood; -- called also canoewood. Liriodendron\n   tulipifera is the only extant species, but there were several others in\n   the Cretaceous epoch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liripipe', 'n.', 'See Liripoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liripoop', 'n.', 'A pendent part of the old clerical tippet; afterwards, a\n   tippet; a scarf; -- worn also by doctors, learned men, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liripoop', 'n.', 'Acuteness; smartness; also, a smart trick or stratagem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liripoop', 'n.', 'A silly person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liroconite', 'n.', 'A hydrated arseniate of copper, occurring in obtuse\n   pyramidal crystals of a sky-blue or verdigris-green color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lisbon', 'n.', 'A sweet, light-colored species of wine, produced in the\n   province of Estremadura, and so called as being shipped from Lisbon, in\n   Portugal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lisle', 'n.', 'A city of France celebrated for certain manufactures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lisne', 'n.', 'A cavity or hollow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lisped', 'imp. & p. p.', 'of Lisp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lisping', 'p. pr. & vb. n.', 'of Lisp');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lisp', 'v. i.', 'To pronounce the sibilant letter s imperfectly; to give s\n   and z the sound of th; -- a defect common among children.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lisp', 'v. i.', 'To speak with imperfect articulation; to mispronounce, as\n   a child learning to talk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lisp', 'v. i.', 'To speak hesitatingly with a low voice, as if afraid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lisp', 'v. t.', 'To pronounce with a lisp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lisp', 'v. t.', 'To utter with imperfect articulation; to express with\n   words pronounced imperfectly or indistinctly, as a child speaks; hence,\n   to express by the use of simple, childlike language.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lisp', 'v. t.', 'To speak with reserve or concealment; to utter timidly or\n   confidentially; as, to lisp treason.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lisp', 'n.', 'The habit or act of lisping. See Lisp, v. i., 1.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lisper', 'n.', 'One who lisps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lispingly', 'adv.', 'With a lisp; in a lisping manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liss', 'n.', 'Release; remission; ease; relief.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liss', 'v. t.', 'To free, as from care or pain; to relieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lissencephala', 'n. pl.', 'A general name for all those placental mammals\n   that have a brain with few or no cerebral convolutions, as Rodentia,\n   Insectivora, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lissom', 'a.', 'Alt. of Lissome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lissome', 'a.', 'Limber; supple; flexible; lithe; lithesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lissome', 'a.', 'Light; nimble; active.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'n.', 'A line inclosing or forming the extremity of a piece of\n   ground, or field of combat; hence, in the plural (lists), the ground or\n   field inclosed for a race or combat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'v. t.', 'To inclose for combat; as, to list a field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'v. i.', 'To hearken; to attend; to listen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'v. t.', 'To listen or hearken to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'v. i.', 'To desire or choose; to please.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'v. i.', 'To lean; to incline; as, the ship lists to port.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'n.', 'Inclination; desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'n.', 'An inclination to one side; as, the ship has a list to\n   starboard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'n.', 'A strip forming the woven border or selvedge of cloth,\n   particularly of broadcloth, and serving to strengthen it; hence, a\n   strip of cloth; a fillet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'n.', 'A limit or boundary; a border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'n.', 'The lobe of the ear; the ear itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'n.', 'A stripe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'n.', 'A roll or catalogue, that is row or line; a record of names;\n   as, a list of names, books, articles; a list of ratable estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'n.', 'A little square molding; a fillet; -- called also listel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'n.', 'A narrow strip of wood, esp. sapwood, cut from the edge of a\n   plank or board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'n.', 'A piece of woolen cloth with which the yarns are grasped by a\n   workman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'n.', 'The first thin coat of tin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'n.', 'A wirelike rim of tin left on an edge of the plate after it\n   is coated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listed', 'imp. & p. p.', 'of List');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listing', 'p. pr. & vb. n.', 'of List');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'v. t.', 'To sew together, as strips of cloth, so as to make a show\n   of colors, or form a border.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'v. t.', 'To cover with list, or with strips of cloth; to put list\n   on; as, to list a door; to stripe as if with list.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'v. t.', 'To enroll; to place or register in a list.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'v. t.', 'To engage, as a soldier; to enlist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'v. t.', 'To cut away a narrow strip, as of sapwood, from the edge\n   of; as, to list a board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'List', 'v. i.', 'To engage in public service by enrolling ones name; to\n   enlist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listel', 'n.', 'Same as List, n., 6.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listened', 'imp. & p. p.', 'of Listen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listening', 'p. pr. & vb. n.', 'of Listen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listen', 'v. i.', 'To give close attention with the purpose of hearing; to\n   give ear; to hearken; to attend.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listen', 'v. i.', 'To give heed; to yield to advice; to follow admonition;\n   to obey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listen', 'v. t.', 'To attend to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listener', 'n.', 'One who listens; a hearkener.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lister', 'n.', 'One who makes a list or roll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lister', 'n.', 'Same as Leister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listerian', 'a.', 'Of or pertaining to listerism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listerism', 'n.', 'The systematic use of antiseptics in the performance of\n   operations and the treatment of wounds; -- so called from Joseph\n   Lister, an English surgeon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listful', 'a.', 'Attentive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listing', 'n.', 'The act or process of one who lists (in any sense of the\n   verb); as, the listing of a door; the listing of a stock at the Stock\n   Exchange.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listing', 'n.', 'The selvedge of cloth; list.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listing', 'n.', 'The sapwood cut from the edge of a board.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listing', 'n.', 'The throwing up of the soil into ridges, -- a method\n   adopted in the culture of beets and some garden crops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Listless', 'a.', 'Having no desire or inclination; indifferent; heedless;\n   spiritless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lit', '', 'a form of the imp. & p. p. of Light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litanies', 'pl. ', 'of Litany');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litany', 'n.', 'A solemn form of supplication in the public worship of\n   various churches, in which the clergy and congregation join, the former\n   leading and the latter responding in alternate sentences. It is usually\n   of a penitential character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litarge', 'n.', 'Litharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litchi', 'n.', 'The fruit of a tree native to China (Nephelium Litchi). It\n   is nutlike, having a rough but tender shell, containing an aromatic\n   pulp, and a single large seed. In the dried fruit which is exported the\n   pulp somewhat resembles a raisin in color and form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lite', 'adv., & n.', 'Little.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liter', 'n.', 'Alt. of Litre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litre', 'n.', 'A measure of capacity in the metric system, being a cubic\n   decimeter, equal to 61.022 cubic inches, or 2.113 American pints, or\n   1.76 English pints.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literacy', 'n.', 'State of being literate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literal', 'a.', 'According to the letter or verbal expression; real; not\n   figurative or metaphorical; as, the literal meaning of a phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literal', 'a.', 'Following the letter or exact words; not free.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literal', 'a.', 'Consisting of, or expressed by, letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literal', 'a.', 'Giving a strict or literal construction; unimaginative;\n   matter-of fast; -- applied to persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literal', 'n.', 'Literal meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literalism', 'n.', 'That which accords with the letter; a mode of\n   interpreting literally; adherence to the letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literalism', 'n.', 'The tendency or disposition to represent objects\n   faithfully, without abstraction, conventionalities, or idealization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literalist', 'n.', 'One who adheres to the letter or exact word; an\n   interpreter according to the letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literalty', 'n.', 'The state or quality of being literal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literalization', 'n.', 'The act of literalizing; reduction to a literal\n   meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literalized', 'imp. & p. p.', 'of Literalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literalizing', 'p. pr. & vb. n.', 'of Literalize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literalize', 'v. t.', 'To make literal; to interpret or put in practice\n   according to the strict meaning of the words; -- opposed to\n   spiritualize; as, to literalize Scripture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literalizer', 'n.', 'A literalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literally', 'adv.', 'According to the primary and natural import of words;\n   not figuratively; as, a man and his wife can not be literally one\n   flesh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literally', 'adv.', 'With close adherence to words; word by word.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literalness', 'n.', 'The quality or state of being literal; literal import.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literary', 'a.', 'Of or pertaining to letters or literature; pertaining to\n   learning or learned men; as, literary fame; a literary history;\n   literary conversation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literary', 'a.', 'Versed in, or acquainted with, literature; occupied with\n   literature as a profession; connected with literature or with men of\n   letters; as, a literary man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literate', 'a.', 'Instructed in learning, science, or literature; learned;\n   lettered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literate', 'n.', 'One educated, but not having taken a university degree;\n   especially, such a person who is prepared to take holy orders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literate', 'n.', 'A literary man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literati', 'n. pl.', 'Learned or literary men. See Literatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literatim', 'adv.', 'Letter for letter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literation', 'n.', 'The act or process of representing by letters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literator', 'n.', 'One who teaches the letters or elements of knowledge; a\n   petty schoolmaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literator', 'n.', 'A person devoted to the study of literary trifles, esp.\n   trifles belonging to the literature of a former age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literator', 'n.', 'A learned person; a literatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literature', 'n.', 'Learning; acquaintance with letters or books.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literature', 'n.', 'The collective body of literary productions, embracing\n   the entire results of knowledge and fancy preserved in writing; also,\n   the whole body of literary productions or writings upon a given\n   subject, or in reference to a particular science or branch of\n   knowledge, or of a given country or period; as, the literature of\n   Biblical criticism; the literature of chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literature', 'n.', 'The class of writings distinguished for beauty of style\n   or expression, as poetry, essays, or history, in distinction from\n   scientific treatises and works which contain positive knowledge;\n   belles-lettres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literature', 'n.', 'The occupation, profession, or business of doing\n   literary work.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literati', 'pl. ', 'of Literatus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Literatus', 'n.', 'A learned man; a man acquainted with literature; --\n   chiefly used in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-lith', '', 'Alt. of -lite');
  INSERT INTO words (word, word_type, description) VALUES  (    '-lite', '', 'Combining forms fr. Gr. li`qos a stone; -- used chiefly in\n   naming minerals and rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lith', '', '3d pers. sing. pres. of Lie, to recline, for lieth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lith', 'n.', 'A joint or limb; a division; a member; a part formed by\n   growth, and articulated to, or symmetrical with, other parts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithaemia', 'n.', 'A condition in which uric (lithic) acid is present in\n   the blood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithagogue', 'n.', 'A medicine having, or supposed to have, the power of\n   expelling calculous matter with the urine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litharge', 'n.', 'Lead monoxide; a yellowish red substance, obtained as an\n   amorphous powder, or crystallized in fine scales, by heating lead\n   moderately in a current of air or by calcining lead nitrate or\n   carbonate. It is used in making flint glass, in glazing earthenware, in\n   making red lead minium, etc. Called also massicot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithargyrum', 'n.', 'Crystallized litharge, obtained by fusion in the form\n   of fine yellow scales.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithate', 'n.', 'A salt of lithic or uric acid; a urate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithe', 'v. i. & i.', 'To listen or listen to; to hearken to.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithe', 'a.', 'Mild; calm; as, lithe weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithe', 'a.', 'Capable of being easily bent; pliant; flexible; limber; as,\n   the elephants lithe proboscis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithe', 'a.', 'To smooth; to soften; to palliate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithely', 'adv.', 'In a lithe, pliant, or flexible manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litheness', 'n.', 'The quality or state of being lithe; flexibility;\n   limberness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lither', 'a.', 'Bad; wicked; false; worthless; slothful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litherly', 'a.', 'Crafty; cunning; mischievous; wicked; treacherous; lazy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithesome', 'a.', 'Pliant; limber; flexible; supple; nimble; lissom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithia', 'n.', 'The oxide of lithium; a strong alkaline caustic similar to\n   potash and soda, but weaker. See Lithium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithiasis', 'n.', 'The formation of stony concretions or calculi in any\n   part of the body, especially in the bladder and urinary passages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithic', 'a.', 'Of or pertaining to stone; as, lithic architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithic', 'a.', 'Pertaining to the formation of uric-acid concretions\n   (stone) in the bladder and other parts of the body; as, lithic\n   diathesis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithic', 'n.', 'A medicine which tends to prevent stone in the bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithic', 'a.', 'Pertaining to or denoting lithium or some of its compounds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithiophilite', 'n.', 'A phosphate of manganese and lithium; a variety of\n   triphylite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithium', 'n.', 'A metallic element of the alkaline group, occurring in\n   several minerals, as petalite, spodumene, lepidolite, triphylite, etc.,\n   and otherwise widely disseminated, though in small quantities.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litho', '', 'A combining form from Gr. li`qos, stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithobilic', 'a.', 'Pertaining to or designating an organic acid of the\n   tartaric acid series, distinct from lithofellic acid, but, like it,\n   obtained from certain bile products, as bezoar stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithocarp', 'n.', 'Fossil fruit; a fruit petrified; a carpolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithochromatics', 'n.', 'See Lithochromics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithochromics', 'n.', 'The art of printing colored pictures on canvas from\n   oil paintings on stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithoclast', 'n.', 'An instrument for crushing stones in the bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithocyst', 'n.', 'A sac containing small, calcareous concretions\n   (otoliths). They are found in many Medusae, and other invertebrates,\n   and are supposed to be auditory organs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithodome', 'n.', 'Any one of several species of bivalves, which form holes\n   in limestone, in which they live; esp., any species of the genus\n   Lithodomus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithodomous', 'a.', 'Like, or pertaining to, Lithodomus; lithophagous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithodomus', 'n.', 'A genus of elongated bivalve shells, allied to the\n   mussels, and remarkable for their ability to bore holes for shelter, in\n   solid limestone, shells, etc. Called also Lithophagus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithofellic', 'a.', 'Pertaining to, or designating, a crystalline, organic\n   acid, resembling cholic acid, found in the biliary intestinal\n   concretions (bezoar stones) common in certain species of antelope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithofracteur', 'n.', 'An explosive compound of nitroglycerin. See\n   Nitroglycerin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithogenesy', 'n.', 'The doctrine or science of the origin of the minerals\n   composing the globe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithogenous', 'a.', 'Stone-producing; -- said of polyps which form coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithoglyph', 'n.', 'An engraving on a gem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithoglypher', 'n.', 'One who curs or engraves precious stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithoglyphic', 'a.', 'Of or pertaining to the art of cutting and engraving\n   precious stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithoglyptics', 'n.', 'The art of cutting and engraving gems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithographed', 'imp. & p. p.', 'of Lithograph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithographing', 'p. pr. & vb. n.', 'of Lithograph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithograph', 'v. t.', 'To trace on stone by the process of lithography so\n   as to transfer the design to paper by printing; as, to lithograph a\n   design; to lithograph a painting. See Lithography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithograph', 'n.', 'A print made by lithography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithographer', 'n.', 'One who lithographs; one who practices lithography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithographic', 'a.', 'Alt. of Lithographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithographical', 'a.', 'Of or pertaining to lithography; made by\n   lithography; as, the lithographic art; a lithographic picture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithography', 'n.', 'The art or process of putting designs or writing, with\n   a greasy material, on stone, and of producing printed impressions\n   therefrom. The process depends, in the main, upon the antipathy between\n   grease and water, which prevents a printing ink containing oil from\n   adhering to wetted parts of the stone not covered by the design. See\n   Lithographic limestone, under Lithographic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithoid', 'a.', 'Alt. of Lithoidal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithoidal', 'a.', 'Like a stone; having a stony structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litholatry', 'n.', 'The worship of a stone or stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithologic', 'a.', 'Alt. of Lithological');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithological', 'a.', 'Of or pertaining to the character of a rock, as\n   derived from the nature and mode of aggregation of its mineral\n   contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithological', 'a.', 'Of or pertaining to lithology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithologically', 'adv.', 'From a lithological point of view; as, to\n   consider a stratum lithologically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithologist', 'n.', 'One who is skilled in lithology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithology', 'n.', 'The science which treats of rocks, as regards their\n   mineral constitution and classification, and their mode of occurrence\n   in nature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithology', 'n.', 'A treatise on stones found in the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithomancy', 'n.', 'Divination by means of stones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithomarge', 'n.', 'A clay of a fine smooth texture, and very sectile.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithonthriptic', 'a. & n.', 'Alt. of Lithonthryptic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithonthryptic', 'a. & n.', 'Same as Lithontriptic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithontriptic', 'a.', 'Having the quality of, or used for, dissolving or\n   destroying stone in the bladder or kidneys; as, lithontriptic forceps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithontriptic', 'n.', 'A lithontriptic remedy or agent, as distilled water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithontriptist', 'n.', 'Same as Lithotriptist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithontriptor', 'n.', 'See Lithotriptor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithophagous', 'a.', 'Eating or swallowing stones or gravel, as the\n   ostrich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithophagous', 'a.', 'Eating or destroying stone; -- applied to various\n   animals which make burrows in stone, as many bivalve mollusks, certain\n   sponges, annelids, and sea urchins. See Lithodomus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithophane', 'n.', 'Porcelain impressed with figures which are made\n   distinct by transmitted light, -- as when hung in a window, or used as\n   a lamp shade.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithophosphor', 'n.', 'A stone that becomes phosphoric by heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithophosphoric', 'a.', 'Pertaining to lithophosphor; becoming phosphoric\n   by heat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithophotography', 'n.', 'Same as Photolithography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithophyll', 'n.', 'A fossil leaf or impression of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithophyse', 'n.', 'A spherulitic cavity often with concentric chambers,\n   observed in some volcanic rocks, as in rhyolitic lavas. It is supposed\n   to be produced by expanding gas, whence the name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithophyte', 'n.', 'A hard, or stony, plantlike organism, as the\n   gorgonians, corals, and corallines, esp. those gorgonians having a\n   calcareous axis. All the lithophytes except the corallines are animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithophytic', 'a.', 'Of or pertaining to lithophytes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithophytous', 'a.', 'Lithophytic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithosian', 'n.', 'Any one of various species of moths belonging to the\n   family Lithosidae. Many of them are beautifully colored.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotint', 'n.', 'A kind of lithography by which the effect of a tinted\n   drawing is produced, as if made with India ink.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotint', 'n.', 'A picture produced by this process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotome', 'n.', 'A stone so formed by nature as to appear as if cut by\n   art.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotome', 'n.', 'An instrument used for cutting the bladder in operations\n   for the stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotomic', 'a.', 'Alt. of Lithotomical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotomical', 'a.', 'Pertaining to, or performed by, lithotomy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotomist', 'n.', 'One who performs the operation of cutting for stone in\n   the bladder, or one who is skilled in the operation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotomy', 'n.', 'The operation, art, or practice of cutting for stone in\n   the bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotripsy', 'n.', 'The operation of crushing a stone in the bladder with\n   an instrument called lithotriptor or lithotrite; lithotrity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotriptic', 'a. & n.', 'Same as Lithontriptic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotriptist', 'n.', 'One skilled in breaking and extracting stone in the\n   bladder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotriptor', 'n.', 'An instrument for triturating the stone in the\n   bladder; a lithotrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotrite', '', 'Alt. of Lithotritor');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotritor', '', 'A lithotriptor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotritist', 'n.', 'A lithotriptist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotrity', 'n.', 'The operation of breaking a stone in the bladder into\n   small pieces capable of being voided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotype', 'n.', 'A kind of stereotype plate made by lithotypy; also, that\n   which in printed from it. See Lithotypy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotyped', 'imp. & p. p.', 'of Lithotype');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotyping', 'p. pr. & vb. n.', 'of Lithotype');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotype', 'v. t.', 'To prepare for printing with plates made by the\n   process of lithotypy. See Lithotypy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotypic', 'a.', 'Of, pertaining to, or produced by, lithotypy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithotypy', 'n.', 'The art or process of making a kind of hard,\n   stereotypeplate, by pressing into a mold, taken from a page of type or\n   other matter, a composition of gum shell-lac and sand of a fine\n   quality, together with a little tar and linseed oil, all in a heated\n   state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithoxyl', 'n.', 'Petrified wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithuanian', 'a.', 'Of or pertaining to Lithuania (formerly a principality\n   united with Poland, but now Russian and Prussian territory).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithuanian', 'n.', 'A native, or one of the people, of Lithuania; also, the\n   language of the Lithuanian people.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lithy', 'a.', 'Easily bent; pliable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigable', 'a.', 'Such as can be litigated.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigant', 'a.', 'Disposed to litigate; contending in law; engaged in a\n   lawsuit; as, the parties litigant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigant', 'n.', 'A person engaged in a lawsuit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigated', 'imp. & p. p.', 'of Litigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigating', 'p. pr. & vb. n.', 'of Litigate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigate', 'v. t.', 'To make the subject of a lawsuit; to contest in law;\n   to prosecute or defend by pleadings, exhibition of evidence, and\n   judicial debate in a court; as, to litigate a cause.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigate', 'v. i.', 'To carry on a suit by judicial process.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigation', 'n.', 'The act or process of litigating; a suit at law; a\n   judicial contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigator', 'n.', 'One who litigates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigious', 'a.', 'Inclined to judicial contest; given to the practice of\n   contending in law; guarrelsome; contentious; fond of litigation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigious', 'a.', 'Subject to contention; disputable; controvertible;\n   debatable; doubtful; precarious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigious', 'a.', 'Of or pertaining to legal disputes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigiously', 'adv.', 'In a litigious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litigiousness', 'n.', 'The state of being litigious; disposition to engage\n   in or carry on lawsuits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litmus', 'n.', 'A dyestuff extracted from certain lichens (Roccella\n   tinctoria, Lecanora tartarea, etc.), as a blue amorphous mass which\n   consists of a compound of the alkaline carbonates with certain coloring\n   matters related to orcin and orcein.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litotes', 'n.', 'A diminution or softening of statement for the sake of\n   avoiding censure or increasing the effect by contrast with the\n   moderation shown in the form of expression; as, \" a citizen of no mean\n   city,\" that is, of an illustrious city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litraneter', 'n.', 'An instrument for ascertaining the specific gravity of\n   liquids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litre', 'n.', 'Same as Liter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litter', 'n.', 'A bed or stretcher so arranged that a person, esp. a sick\n   or wounded person, may be easily carried in or upon it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litter', 'n.', 'Straw, hay, etc., scattered on a floor, as bedding for\n   animals to rest on; also, a covering of straw for plants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litter', 'n.', 'Things lying scattered about in a manner indicating\n   slovenliness; scattered rubbish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litter', 'n.', 'Disorder or untidiness resulting from scattered rubbish, or\n   from thongs lying about uncared for; as, a room in a state of litter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litter', 'n.', 'The young brought forth at one time, by a sow or other\n   multiparous animal, taken collectively. Also Fig.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Littered', 'imp. & p. p.', 'of Litter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Littering', 'p. pr. & vb. n.', 'of Litter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litter', 'v. t.', 'To supply with litter, as cattle; to cover with litter,\n   as the floor of a stall.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litter', 'v. t.', 'To put into a confused or disordered condition; to strew\n   with scattered articles; as, to litter a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litter', 'v. t.', 'To give birth to; to bear; -- said of brutes, esp. those\n   which produce more than one at a birth, and also of human beings, in\n   abhorrence or contempt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litter', 'v. i.', 'To be supplied with litter as bedding; to sleep or make\n   ones bed in litter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litter', 'v. i.', 'To produce a litter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litterateur', 'n.', 'One who occupies himself with literature; a literary\n   man; a literatus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Littery', 'a.', 'Covered or encumbered with litter; consisting of or\n   constituting litter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Little', 'a.', 'Small in size or extent; not big; diminutive; -- opposed to\n   big or large; as, a little body; a little animal; a little piece of\n   ground; a little hill; a little distance; a little child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Little', 'a.', 'Short in duration; brief; as, a little sleep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Little', 'a.', 'Small in quantity or amount; not much; as, a little food; a\n   little air or water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Little', 'a.', 'Small in dignity, power, or importance; not great;\n   insignificant; contemptible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Little', 'a.', 'Small in force or efficiency; not strong; weak; slight;\n   inconsiderable; as, little attention or exertion;little effort; little\n   care or diligence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Little', 'a.', 'Small in extent of views or sympathies; narrow; shallow;\n   contracted; mean; illiberal; ungenerous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Little', 'n.', 'That which is little; a small quantity, amount, space, or\n   the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Little', 'n.', 'A small degree or scale; miniature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Little', 'adv.', 'In a small quantity or degree; not much; slightly;\n   somewhat; -- often with a preceding it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Little-ease', 'n.', 'An old slang name for the pillory, stocks, etc., of a\n   prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Littleness', 'n.', 'The state or quality of being little; as, littleness of\n   size, thought, duration, power, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Littoral', 'a.', 'Of or pertaining to a shore, as of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Littoral', 'a.', 'Inhabiting the seashore, esp. the zone between high-water\n   and low-water mark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Littorina', 'n.', 'A genus of small pectinibranch mollusks, having thick\n   spiral shells, abundant between tides on nearly all rocky seacoasts.\n   They feed on seaweeds. The common periwinkle is a well-known example.\n   See Periwinkle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Littress', 'n.', 'A smooth kind of cartridge paper used for making cards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litate', 'a.', 'Forked, with the points slightly curved outward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lituiform', 'a.', 'Having the form of a lituus; like a lituite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lituite', 'n.', 'Any species of ammonites of the genus Lituites. They are\n   found in the Cretaceous formation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liturate', 'a.', 'Having indistinct spots, paler at their margins.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liturate', 'a.', 'Spotted, as if from abrasions of the surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liturgic', '', 'Alt. of Liturgical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liturgical', '', 'Pertaining to, of or the nature of, a liturgy; of or\n   pertaining to public prayer and worship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liturgically', 'adv.', 'In the manner of a liturgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liturgics', 'n.', 'The science of worship; history, doctrine, and\n   interpretation of liturgies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liturgiologist', 'n.', 'One versed in liturgiology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liturgiology', 'n.', 'The science treating of liturgical matters; a\n   treatise on, or description of, liturgies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liturgist', 'n.', 'One who favors or adheres strictly to a liturgy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liturgies', 'pl. ', 'of Liturgy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liturgy', 'a.', 'An established formula for public worship, or the entire\n   ritual for public worship in a church which uses prescribed forms; a\n   formulary for public prayer or devotion. In the Roman Catholic Church\n   it includes all forms and services in any language, in any part of the\n   world, for the celebration of Mass.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Litui', 'pl. ', 'of Lituus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lituus', 'n.', 'A curved staff used by the augurs in quartering the\n   heavens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lituus', 'n.', 'An instrument of martial music; a kind of trumpet of a\n   somewhat curved form and shrill note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lituus', 'n.', 'A spiral whose polar equation is r2/ = a; that is, a curve\n   the square of whose radius vector varies inversely as the angle which\n   the radius vector makes with a given line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livable', 'a.', 'Such as can be lived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livable', 'a.', 'Such as in pleasant to live in; fit or suitable to live\n   in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lived', 'imp. & p. p.', 'of Live');
  INSERT INTO words (word, word_type, description) VALUES  (    'Living', 'p. pr. & vb. n.', 'of Live');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'v. i.', 'To be alive; to have life; to have, as an animal or a\n   plant, the capacity of assimilating matter as food, and to be dependent\n   on such assimilation for a continuance of existence; as, animals and\n   plants that live to a great age are long in reaching maturity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'v. i.', 'To pass ones time; to pass life or time in a certain\n   manner, as to habits, conduct, or circumstances; as, to live in ease or\n   affluence; to live happily or usefully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'v. i.', 'To make ones abiding place or home; to abide; to dwell;\n   to reside.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'v. i.', 'To be or continue in existence; to exist; to remain; to be\n   permanent; to last; -- said of inanimate objects, ideas, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'v. i.', 'To enjoy or make the most of life; to be in a state of\n   happiness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'v. i.', 'To feed; to subsist; to be nourished or supported; -- with\n   on; as, horses live on grass and grain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'v. i.', 'To have a spiritual existence; to be quickened, nourished,\n   and actuated by divine influence or faith.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'v. i.', 'To be maintained in life; to acquire a livelihood; to\n   subsist; -- with on or by; as, to live on spoils.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'v. i.', 'To outlast danger; to float; -- said of a ship, boat,\n   etc.; as, no ship could live in such a storm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'v. t.', 'To spend, as ones life; to pass; to maintain; to continue\n   in, constantly or habitually; as, to live an idle or a useful life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'v. t.', 'To act habitually in conformity with; to practice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'a.', 'Having life; alive; living; not dead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'a.', 'Being in a state of ignition; burning; having active\n   properties; as, a live coal; live embers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'a.', 'Full of earnestness; active; wide awake; glowing; as, a live\n   man, or orator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'a.', 'Vivid; bright.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'a.', 'Imparting power; having motion; as, the live spindle of a\n   lathe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live', 'n.', 'Life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lived', 'a.', 'Having life; -- used only in composition; as, long-lived;\n   short-lived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Live-forever', 'n.', 'A plant (Sedum Telephium) with fleshy leaves, which\n   has extreme powers of resisting drought; garden ox-pine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livelihed', 'n.', 'See Livelihood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livelihood', 'n.', 'Subsistence or living, as dependent on some means of\n   support; support of life; maintenance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livelihood', 'n.', 'Liveliness; appearance of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livelily', 'adv.', 'In a lively manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liveliness', 'n.', 'The quality or state of being lively or animated;\n   sprightliness; vivacity; animation; spirit; as, the liveliness of\n   youth, contrasted with the gravity of age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liveliness', 'n.', 'An appearance of life, animation, or spirit; as, the\n   liveliness of the eye or the countenance in a portrait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liveliness', 'n.', 'Briskness; activity; effervescence, as of liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livelode', 'n.', 'Course of life; means of support; livelihood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livelong', 'a.', 'Whole; entire; long in passing; -- used of time, as day\n   or night, in adverbial phrases, and usually with a sense of\n   tediousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livelong', 'a.', 'Lasting; durable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lively', 'superl.', 'Endowed with or manifesting life; living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lively', 'superl.', 'Brisk; vivacious; active; as, a lively youth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lively', 'superl.', 'Gay; airy; animated; spirited.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lively', 'superl.', 'Representing life; lifelike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lively', 'superl.', 'Bright; vivid; glowing; strong; vigorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lively', 'adv.', 'In a brisk, active, or animated manner; briskly;\n   vigorously.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lively', 'adv.', 'With strong resemblance of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liver', 'n.', 'One who, or that which, lives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liver', 'n.', 'A resident; a dweller; as, a liver in Brooklyn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liver', 'n.', 'One whose course of life has some marked characteristic\n   (expressed by an adjective); as, a free liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liver', 'n.', 'A very large glandular and vascular organ in the visceral\n   cavity of all vertebrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liver', 'n.', 'The glossy ibis (Ibis falcinellus); -- said to have given\n   its name to the city of Liverpool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liver-colored', 'a.', 'Having a color like liver; dark reddish brown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livered', 'a.', 'Having (such) a liver; used in composition; as,\n   white-livered.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liver-grown', 'a.', 'Having an enlarged liver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liveried', 'a.', 'Wearing a livery. See Livery, 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livering', 'n.', 'A kind of pudding or sausage made of liver or pork.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liverleaf', 'n.', 'Same as Liverwort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liverwort', 'n.', 'A ranunculaceous plant (Anemone Hepatica) with pretty\n   white or bluish flowers and a three-lobed leaf; -- called also squirrel\n   cups.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liverwort', 'n.', 'A flowerless plant (Marchantia polymorpha), having an\n   irregularly lobed, spreading, and forking frond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liveries', 'pl. ', 'of Livery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery', 'n.', 'The act of delivering possession of lands or tenements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery', 'n.', 'The writ by which possession is obtained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery', 'n.', 'Release from wardship; deliverance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery', 'n.', 'That which is delivered out statedly or formally, as\n   clothing, food, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery', 'n.', 'The uniform clothing issued by feudal superiors to their\n   retainers and serving as a badge when in military service.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery', 'n.', 'The peculiar dress by which the servants of a nobleman or\n   gentleman are distinguished; as, a claret-colored livery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery', 'n.', 'Hence, also, the peculiar dress or garb appropriated by any\n   association or body of persons to their own use; as, the livery of the\n   London tradesmen, of a priest, of a charity school, etc.; also, the\n   whole body or company of persons wearing such a garb, and entitled to\n   the privileges of the association; as, the whole livery of London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery', 'n.', 'Hence, any characteristic dress or outward appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery', 'n.', 'An allowance of food statedly given out; a ration, as to a\n   family, to servants, to horses, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery', 'n.', 'The feeding, stabling, and care of horses for compensation;\n   boarding; as, to keep ones horses at livery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery', 'n.', 'The keeping of horses in readiness to be hired temporarily\n   for riding or driving; the state of being so kept.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery', 'n.', 'A low grade of wool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery', 'v. t.', 'To clothe in, or as in, livery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liverymen', 'pl. ', 'of Liveryman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liveryman', 'n.', 'One who wears a livery, as a servant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liveryman', 'n.', 'A freeman of the city, in London, who, having paid\n   certain fees, is entitled to wear the distinguishing dress or livery of\n   the company to which he belongs, and also to enjoy certain other\n   privileges, as the right of voting in an election for the lord mayor,\n   sheriffs, chamberlain, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liveryman', 'n.', 'One who keeps a livery stable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livery stable', '', 'A stable where horses are kept for hire, and where\n   stabling is provided. See Livery, n., 3 (e) (f) & (g).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lives', 'n.', 'pl. of Life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lives', 'a. & adv.', 'Alive; living; with life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livid', 'a.', 'Black and blue; grayish blue; of a lead color; discolored,\n   as flesh by contusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lividity', 'n.', 'The state or quality of being livid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lividness', 'n.', 'Lividity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laving', 'v. i.', 'Being alive; having life; as, a living creature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laving', 'v. i.', 'Active; lively; vigorous; -- said esp. of states of the\n   mind, and sometimes of abstract things; as, a living faith; a living\n   principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laving', 'v. i.', 'Issuing continually from the earth; running; flowing;\n   as, a living spring; -- opposed to stagnant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laving', 'v. i.', 'Producing life, action, animation, or vigor; quickening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Laving', 'v. i.', 'Ignited; glowing with heat; burning; live.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Living', 'n.', 'The state of one who, or that which, lives; lives; life;\n   existence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Living', 'n.', 'Manner of life; as, riotous living; penurious living;\n   earnest living.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Living', 'n.', 'Means of subsistence; sustenance; estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Living', 'n.', 'Power of continuing life; the act of living, or living\n   comfortably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Living', 'n.', 'The benefice of a clergyman; an ecclesiastical charge which\n   a minister receives.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livingly', 'adv.', 'In a living state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livingness', 'n.', 'The state or quality of being alive; possession of\n   energy or vigor; animation; quickening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livonian', 'a.', 'Of or pertaining to Livonia, a district of Russia near\n   the Baltic Sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livinian', 'n.', 'A native or an inhabitant of Livonia; the language\n   (allied to the Finnish) of the Livonians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livor', 'n.', 'Malignity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livraison', 'n.', 'A part of a book or literary composition printed and\n   delivered by itself; a number; a part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Livre', 'n.', 'A French money of account, afterward a silver coin equal to\n   20 sous. It is not now in use, having been superseded by the franc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lixivial', 'a.', 'Impregnated with, or consisting of, alkaline salts\n   extracted from wood ashes; impregnated with a salt or salts like a\n   lixivium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lixivial', 'a.', 'Of the color of lye; resembling lye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lixivial', 'a.', 'Having the qualities of alkaline salts extracted from\n   wood ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lixiviate', 'a.', 'Alt. of Lixivited');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lixivited', 'a.', 'Of or pertaining to lye or lixivium; of the quality of\n   alkaline salts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lixivited', 'a.', 'Impregnated with salts from wood ashes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lixiviated', 'imp. & p. p.', 'of Lixiviate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lixiviating', 'p. pr. & vb. n.', 'of Lixiviate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lixiviate', 'v. t.', 'To subject to a washing process for the purpose of\n   separating soluble material from that which is insoluble; to leach, as\n   ashes, for the purpose of extracting the alkaline substances.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lixiviation', 'n.', 'Lixiviating; the process of separating a soluble\n   substance form one that is insoluble, by washing with some solvent, as\n   water; leaching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lixivious', 'a.', 'See Lixivial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lixivium', 'n.', 'A solution of alkaline salts extracted from wood ashes;\n   hence, any solution obtained by lixiviation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lixt', '', '2d pers. sing. pres. of Lige, to lie, to tell lies, --\n   contracted for ligest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Liza', 'n.', 'The American white mullet (Mugil curema).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lizard', 'n.', 'Any one of the numerous species of reptiles belonging to\n   the order Lacertilia; sometimes, also applied to reptiles of other\n   orders, as the Hatteria.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lizard', 'n.', 'A piece of rope with thimble or block spliced into one or\n   both of the ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lizard', 'n.', 'A piece of timber with a forked end, used in dragging a\n   heavy stone, a log, or the like, from a field.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lizards tail', '', 'A perennial plant of the genus Saururus (S. cernuus),\n   growing in marshes, and having white flowers crowded in a slender\n   terminal spike, somewhat resembling in form a lizards tail; whence the\n   name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Llama', 'n.', 'A South American ruminant (Auchenia llama), allied to the\n   camels, but much smaller and without a hump. It is supposed to be a\n   domesticated variety of the guanaco. It was formerly much used as a\n   beast of burden in the Andes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Llandeilo group', '', 'A series of strata in the lower Silurian formations\n   of Great Britain; -- so named from Llandeilo in Southern Wales. See\n   Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Llanero', 'n.', 'One of the inhabitants of the llanos of South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Llanos', 'pl. ', 'of Llano');
  INSERT INTO words (word, word_type, description) VALUES  (    'Llano', 'n.', 'An extensive plain with or without vegetation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lloyds', 'n.', 'An association of underwriters and others in London, for\n   the collection and diffusion of marine intelligence, the insurance,\n   classification, registration, and certifying of vessels, and the\n   transaction of business of various kinds connected with shipping.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lloyds', 'n.', 'A part of the Royal Exchange, in London, appropriated to\n   the use of underwriters and insurance brokers; -- called also Lloyds\n   Rooms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lo', 'interj.', 'Look; see; behold; observe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loach', 'n.', 'Any one of several small, fresh-water, cyprinoid fishes of\n   the genera Cobitis, Nemachilus, and allied genera, having six or more\n   barbules around the mouth. They are found in Europe and Asia. The\n   common European species (N. barbatulus) is used as a food fish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Load', 'v.', 'A burden; that which is laid on or put in anything for\n   conveyance; that which is borne or sustained; a weight; as, a heavy\n   load.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Load', 'v.', 'The quantity which can be carried or drawn in some specified\n   way; the contents of a cart, barrow, or vessel; that which will\n   constitute a cargo; lading.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Load', 'v.', 'That which burdens, oppresses, or grieves the mind or\n   spirits; as, a load of care.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Load', 'v.', 'A particular measure for certain articles, being as much as\n   may be carried at one time by the conveyance commonly used for the\n   article measured; as, a load of wood; a load of hay; specifically, five\n   quarters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Load', 'v.', 'The charge of a firearm; as, a load of powder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Load', 'v.', 'Weight or violence of blows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Load', 'v.', 'The work done by a steam engine or other prime mover when\n   working.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loaded', 'imp. & p. p.', 'of Load');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loading', 'p. pr. & vb. n.', 'of Load');
  INSERT INTO words (word, word_type, description) VALUES  (    'Load', 'v. t.', 'To lay a load or burden on or in, as on a horse or in a\n   cart; to charge with a load, as a gun; to furnish with a lading or\n   cargo, as a ship; hence, to add weight to, so as to oppress or\n   embarrass; to heap upon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Load', 'v. t.', 'To adulterate or drug; as, to load wine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Load', 'v. t.', 'To magnetize.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loader', 'n.', 'One who, or that which, loads; a mechanical contrivance for\n   loading, as a gun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loading', 'n.', 'The act of putting a load on or into.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loading', 'n.', 'A load; cargo; burden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loadmanage', 'n.', 'Alt. of Lodemanage');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodemanage', 'n.', 'Pilotage; skill of a pilot or loadsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loadsman', 'n.', 'Alt. of Lodesman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodesman', 'n.', 'A pilot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loadstar', 'n.', 'Alt. of Lodestar');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodestar', 'n.', 'A star that leads; a guiding star; esp., the polestar;\n   the cynosure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loadstone', 'n.', 'Alt. of Lodestone');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodestone', 'n.', 'A piece of magnetic iron ore possessing polarity like a\n   magnetic needle. See Magnetite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loaves', 'pl. ', 'of Loaf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loaf', 'n.', 'Any thick lump, mass, or cake; especially, a large regularly\n   shaped or molded mass, as of bread, sugar, or cake.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loafed', 'imp. & p. p.', 'of Loaf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loafing', 'p. pr. & vb. n.', 'of Loaf');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loaf', 'v. i.', 'To spend time in idleness; to lounge or loiter about.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loaf', 'v. t.', 'To spend in idleness; -- with away; as, to loaf time away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loafer', 'n.', 'One who loafs; a lazy lounger.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loam', 'n.', 'A kind of soil; an earthy mixture of clay and sand, with\n   organic matter to which its fertility is chiefly due.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loam', 'n.', 'A mixture of sand, clay, and other materials, used in making\n   molds for large castings, often without a pattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loamed', 'imp. & p. p.', 'of Loam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loaming', 'p. pr. & vb. n.', 'of Loam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loam', 'v. i.', 'To cover, smear, or fill with loam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loamy', 'a.', 'Consisting of loam; partaking of the nature of loam;\n   resembling loam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loan', 'n.', 'A loanin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loan', 'n.', 'The act of lending; a lending; permission to use; as, the\n   loan of a book, money, services.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loan', 'n.', 'That which one lends or borrows, esp. a sum of money lent at\n   interest; as, he repaid the loan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loaned', 'imp. & p. p.', 'of Loan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loaning', 'p. pr. & vb. n.', 'of Loan');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loan', 'n. t.', 'To lend; -- sometimes with out.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loanable', 'a.', 'Such as can be lent; available for lending; as, loanable\n   funds; -- used mostly in financial business and writings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loanin', 'n.', 'Alt. of Loaning');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loaning', 'n.', 'An open space between cultivated fields through which\n   cattle are driven, and where the cows are sometimes milked; also, a\n   lane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loanmonger', 'n.', 'A dealer in, or negotiator of, loans.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loath', 'a.', 'Hateful; odious; disliked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loath', 'a.', 'Filled with disgust or aversion; averse; unwilling;\n   reluctant; as, loath to part.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathed', 'imp. & p. p.', 'of Loathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathing', 'p. pr. & vb. n.', 'of Loathe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathe', 'v. t.', 'To feel extreme disgust at, or aversion for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathe', 'v. t.', 'To dislike greatly; to abhor; to hate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathe', 'v. i.', 'To feel disgust or nausea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loather', 'n.', 'One who loathes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathful', 'a.', 'Full of loathing; hating; abhorring.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathful', 'a.', 'Causing a feeling of loathing; disgusting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathing', 'n.', 'Extreme disgust; a feeling of aversion, nausea,\n   abhorrence, or detestation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathingly', 'adv.', 'With loathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathliness', 'n.', 'Loathsomeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathly', 'a.', 'Loathsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathly', 'adv.', 'Unwillingly; reluctantly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathly', 'adv.', '(/) So as to cause loathing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathness', 'n.', 'Unwillingness; reluctance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathsome', 'a.', 'Fitted to cause loathing; exciting disgust; disgusting.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loathy', 'a.', 'Loathsome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loaves', 'n.', 'pl. of Loaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lob', 'n.', 'A dull, heavy person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lob', 'n.', 'Something thick and heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobbed', 'imp. & p. p.', 'of Lob');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobbing', 'p. pr. & vb. n.', 'of Lob');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lob', 'v. t.', 'To let fall heavily or lazily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lob', 'v. t.', 'See Cob, v. t.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lob', 'n.', 'The European pollock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobar', 'a.', 'Of or pertaining to a lobe; characterized by, or like, a\n   lobe or lobes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobate', 'a.', 'Alt. of Lobated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobated', 'a.', 'Consisting of, or having, lobes; lobed; as, a lobate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobated', 'a.', 'Having lobes; -- said of the tails of certain fishes\n   having the integument continued to the bases of the fin rays.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobated', 'a.', 'Furnished with membranous flaps, as the toes of a coot.\n   See Illust. (m) under Aves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobately', 'adv.', 'As a lobe; so as to make a lobe; in a lobate manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobbish', 'a.', 'Like a lob; consisting of lobs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobbies', 'pl. ', 'of Lobby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobby', 'n.', 'A passage or hall of communication, especially when large\n   enough to serve also as a waiting room. It differs from an antechamber\n   in that a lobby communicates between several rooms, an antechamber to\n   one only; but this distinction is not carefully preserved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobby', 'n.', 'That part of a hall of legislation not appropriated to the\n   official use of the assembly; hence, the persons, collectively, who\n   frequent such a place to transact business with the legislators; any\n   persons, not members of a legislative body, who strive to influence its\n   proceedings by personal agency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobby', 'n.', 'An apartment or passageway in the fore part of an\n   old-fashioned cabin under the quarter-deck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobby', 'n.', 'A confined place for cattle, formed by hedges. trees, or\n   other fencing, near the farmyard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobbied', 'imp. & p. p.', 'of Lobby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobbying', 'p. pr. & vb. n.', 'of Lobby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobby', 'v. i.', 'To address or solicit members of a legislative body in\n   the lobby or elsewhere, with the purpose to influence their votes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobby', 'v. t.', 'To urge the adoption or passage of by soliciting members\n   of a legislative body; as, to lobby a bill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobbyist', 'n.', 'A member of the lobby; a person who solicits members of a\n   legislature for the purpose of influencing legislation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobcock', 'n.', 'A dull, sluggish person; a lubber; a lob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobe', 'n.', 'Any projection or division, especially one of a somewhat\n   rounded form');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobe', 'n.', 'A rounded projection or division of a leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobe', 'n.', 'A membranous flap on the sides of the toes of certain birds,\n   as the coot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobe', 'n.', 'A round projecting part of an organ, as of the liver, lungs,\n   brain, etc. See Illust. of Brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobe', 'n.', 'The projecting part of a cam wheel or of a non-circular gear\n   wheel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobed', 'a.', 'Having lobes; lobate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobefoot', 'n.', 'A bird having lobate toes; esp., a phalarope.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobe-footed', 'a.', 'Lobiped.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobelet', 'n.', 'A small lobe; a lobule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobelia', 'n.', 'A genus of plants, including a great number of species.\n   Lobelia inflata, or Indian tobacco, is an annual plant of North\n   America, whose leaves contain a poisonous white viscid juice, of an\n   acrid taste. It has often been used in medicine as an emetic,\n   expectorant, etc. L. cardinalis is the cardinal flower, remarkable for\n   the deep and vivid red color of its flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobeliaceous', 'a.', 'Of or pertaining to a natural order of plants of\n   which the genus Lobelia is the type.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobelin', 'n.', 'A yellowish green resin from Lobelia, used as an emetic\n   and diaphoretic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobeline', 'n.', 'A poisonous narcotic alkaloid extracted from the leaves\n   of Indian tobacco (Lobelia inflata) as a yellow oil, having a\n   tobaccolike taste and odor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobiped', 'a.', 'Having lobate toes, as a coot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loblolly', 'n.', 'Gruel; porridge; -- so called among seamen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobosa', 'n. pl.', 'An order of Rhizopoda, in which the pseudopodia are\n   thick and irregular in form, as in the Amoeba.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobscouse', 'n.', 'A combination of meat with vegetables, bread, etc.,\n   usually stewed, sometimes baked; an olio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobsided', 'a.', 'See Lopsided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobspound', 'n.', 'A prison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobster', 'n.', 'Any large macrurous crustacean used as food, esp. those of\n   the genus Homarus; as the American lobster (H. Americanus), and the\n   European lobster (H. vulgaris). The Norwegian lobster (Nephrops\n   Norvegicus) is similar in form. All these have a pair of large unequal\n   claws. The spiny lobsters of more southern waters, belonging to\n   Palinurus, Panulirus, and allied genera, have no large claws. The\n   fresh-water crayfishes are sometimes called lobsters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobular', 'a.', 'Like a lobule; pertaining to a lobule or lobules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobulate', 'a.', 'Alt. of Lobulated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobulated', 'a.', 'Made up of, or divided into, lobules; as, a lobulated\n   gland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobule', 'n.', 'A small lobe; a subdivision of a lobe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobulette', 'n.', 'A little lobule, or subdivision of a lobule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lobworm', 'n.', 'The lugworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Local', 'a.', 'Of or pertaining to a particular place, or to a definite\n   region or portion of space; restricted to one place or region; as, a\n   local custom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Local', 'n.', 'A train which receives and deposits passengers or freight\n   along the line of the road; a train for the accommodation of a certain\n   district.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Local', 'n.', 'On newspaper cant, an item of news relating to the place\n   where the paper is published.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locale', 'n.', 'A place, spot, or location.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locale', 'n.', 'A principle, practice, form of speech, or other thing of\n   local use, or limited to a locality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Localism', 'n.', 'The state or quality of being local; affection for a\n   particular place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Localism', 'n.', 'A method of speaking or acting peculiar to a certain\n   district; a local idiom or phrase.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Localitiees', 'pl. ', 'of Locality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locality', 'n.', 'The state, or condition, of belonging to a definite\n   place, or of being contained within definite limits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locality', 'n.', 'Position; situation; a place; a spot; esp., a\n   geographical place or situation, as of a mineral or plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locality', 'n.', 'Limitation to a county, district, or place; as, locality\n   of trial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locality', 'n.', 'The perceptive faculty concerned with the ability to\n   remember the relative positions of places.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Localization', 'n.', 'Act of localizing, or state of being localized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Localized', 'imp. & p. p.', 'of Localize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Localizing', 'p. pr. & vb. n.', 'of Localize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Localize', 'v. t.', 'To make local; to fix in, or assign to, a definite\n   place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locally', 'adv.', 'With respect to place; in place; as, to be locally\n   separated or distant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Located', 'imp. & p. p.', 'of Locate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locating', 'p. pr. & vb. n.', 'of Locate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locate', 'v. t.', 'To place; to set in a particular spot or position.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locate', 'v. t.', 'To designate the site or place of; to define the limits\n   of; as, to locate a public building; to locate a mining claim; to\n   locate (the land granted by) a land warrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locate', 'v. i.', 'To place ones self; to take up ones residence; to\n   settle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Location', 'n.', 'The act or process of locating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Location', 'n.', 'Situation; place; locality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Location', 'n.', 'That which is located; a tract of land designated in\n   place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Location', 'n.', 'A leasing on rent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Location', 'n.', 'A contract for the use of a thing, or service of a\n   person, for hire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Location', 'n.', 'The marking out of the boundaries, or identifying the\n   place or site of, a piece of land, according to the description given\n   in an entry, plan, map, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locative', 'a.', 'Indicating place, or the place where, or wherein; as, a\n   locative adjective; locative case of a noun.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locative', 'n.', 'The locative case.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locator', 'n.', 'One who locates, or is entitled to locate, land or a\n   mining claim.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locellate', 'a.', 'Divided into secondary compartments or cells, as where\n   one cavity is separated into several smaller ones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loch', 'n.', 'A lake; a bay or arm of the sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loch', 'n.', 'A kind of medicine to be taken by licking with the tongue; a\n   lambative; a lincture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lochaber ax', '', 'Alt. of Lochaber axe');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lochaber axe', '', 'A weapon of war, consisting of a pole armed with an\n   axhead at its end, formerly used by the Scotch Highlanders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lochage', 'n.', 'An officer who commanded a company; a captain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lochan', 'n.', 'A small lake; a pond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loche', 'n.', 'See Loach.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lochia', 'n. pl.', 'The discharge from the womb and vagina which follows\n   childbirth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lochial', 'a.', 'Of or pertaining to the lochia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'n.', 'A tuft of hair; a flock or small quantity of wool, hay, or\n   other like substance; a tress or ringlet of hair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'n.', 'Anything that fastens; specifically, a fastening, as for a\n   door, a lid, a trunk, a drawer, and the like, in which a bolt is moved\n   by a key so as to hold or to release the thing fastened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'n.', 'A fastening together or interlacing; a closing of one thing\n   upon another; a state of being fixed or immovable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'n.', 'A place from which egress is prevented, as by a lock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'n.', 'The barrier or works which confine the water of a stream or\n   canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'n.', 'An inclosure in a canal with gates at each end, used in\n   raising or lowering boats as they pass from one level to another; --\n   called also lift lock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'n.', 'That part or apparatus of a firearm by which the charge is\n   exploded; as, a matchlock, flintlock, percussion lock, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'n.', 'A device for keeping a wheel from turning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'n.', 'A grapple in wrestling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locked', 'imp. & p. p.', 'of Lock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locking', 'p. pr. & vb. n.', 'of Lock');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'v. t.', 'To fasten with a lock, or as with a lock; to make fast; to\n   prevent free movement of; as, to lock a door, a carriage wheel, a\n   river, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'v. t.', 'To prevent ingress or access to, or exit from, by\n   fastening the lock or locks of; -- often with up; as, to lock or lock\n   up, a house, jail, room, trunk. etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'v. t.', 'To fasten in or out, or to make secure by means of, or as\n   with, locks; to confine, or to shut in or out -- often with up; as, to\n   lock ones self in a room; to lock up the prisoners; to lock up ones\n   silver; to lock intruders out of the house; to lock money into a vault;\n   to lock a child in ones arms; to lock a secret in ones breast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'v. t.', 'To link together; to clasp closely; as, to lock arms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'v. t.', 'To furnish with locks; also, to raise or lower (a boat) in\n   a lock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'v. t.', 'To seize, as the sword arm of an antagonist, by turning\n   the left arm around it, to disarm him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock', 'v. i.', 'To become fast, as by means of a lock or by interlacing;\n   as, the door locks close.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lockage', 'n.', 'Materials for locks in a canal, or the works forming a\n   lock or locks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lockage', 'n.', 'Toll paid for passing the locks of a canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lockage', 'n.', 'Amount of elevation and descent made by the locks of a\n   canal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock-down', 'n.', 'A contrivance to fasten logs together in rafting; --\n   used by lumbermen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locked-jaw', 'n.', 'See Lockjaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locken', 'obs. p. p.', 'of Lock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locken', 'n.', 'The globeflower (Trollius).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locker', 'n.', 'One who, or that which, locks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locker', 'n.', 'A drawer, cupboard, compartment, or chest, esp. one in a\n   ship, that may be closed with a lock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locket', 'n.', 'A small lock; a catch or spring to fasten a necklace or\n   other ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locket', 'n.', 'A little case for holding a miniature or lock of hair,\n   usually suspended from a necklace or watch chain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock hospital', '', 'A hospital for the treatment of venereal diseases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lockjaw', 'n.', 'A contraction of the muscles of the jaw by which its\n   motion is suspended; a variety of tetanus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lockless', 'a.', 'Destitute of a lock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lockman', 'n.', 'A public executioner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lockout', 'n.', 'The closing of a factory or workshop by an employer,\n   usually in order to bring the workmen to satisfactory terms by a\n   suspension of wages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lockram', 'n.', 'A kind of linen cloth anciently used in England,\n   originally imported from Brittany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locksmith', 'n.', 'An artificer whose occupation is to make or mend locks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock step', '', 'A mode of marching by a body of men going one after\n   another as closely as possible, in which the leg of each moves at the\n   same time with the corresponding leg of the person before him.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock stitch', '', 'A peculiar sort of stitch formed by the locking of two\n   threads together, as in the work done by some sewing machines. See\n   Stitch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lockup', 'n.', 'A place where persons under arrest are temporarily locked\n   up; a watchhouse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lock-weir', 'n.', 'A waste weir for a canal, discharging into a lock\n   chamber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locky', 'a.', 'Having locks or tufts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loco', 'adv.', 'A direction in written or printed music to return to the\n   proper pitch after having played an octave higher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loco', 'n.', 'A plant (Astragalus Hornii) growing in the Southwestern\n   United States, which is said to poison horses and cattle, first making\n   them insane. The name is also given vaguely to several other species of\n   the same genus. Called also loco weed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locofoco', 'n.', 'A friction match.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locofoco', 'n.', 'A nickname formerly given to a member of the Democratic\n   party.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locomotion', 'n.', 'The act of moving from place to place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locomotion', 'n.', 'The power of moving from place to place, characteristic\n   of the higher animals and some of the lower forms of plant life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locomotive', 'a.', 'Moving from place to place; changing place, or able to\n   change place; as, a locomotive animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locomotive', 'a.', 'Used in producing motion; as, the locomotive organs of\n   an animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locomotive', 'n.', 'A locomotive engine; a self-propelling wheel carriage,\n   especially one which bears a steam boiler and one or more steam engines\n   which communicate motion to the wheels and thus propel the carriage, --\n   used to convey goods or passengers, or to draw wagons, railroad cars,\n   etc. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locomotiveness', 'n.', 'Alt. of Locomotivity');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locomotivity', 'n.', 'The power of changing place.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locomotor', 'a.', 'Of or pertaining to movement or locomotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loculament', 'n.', 'The cell of a pericarp in which the seed is lodged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locular', 'a.', 'Of or relating to the cell or compartment of an ovary,\n   etc.; in composition, having cells; as trilocular.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loculate', 'a.', 'Divided into compartments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locule', 'n.', 'A little hollow; a loculus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loculicidal', 'a.', 'Dehiscent through the middle of the back of each cell;\n   -- said of capsules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loculose', 'a.', 'Alt. of Loculous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loculous', 'a.', 'Divided by internal partitions into cells, as the pith of\n   the pokeweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loculi', 'pl. ', 'of Loculus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loculus', 'n.', 'One of the spaces between the septa in the Anthozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loculus', 'n.', 'One of the compartments of a several-celled ovary;\n   loculament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locum tenens', '', 'A substitute or deputy; one filling an office for a\n   time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loci', 'pl. ', 'of Locus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loca', 'pl. ', 'of Locus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locus', 'n.', 'A place; a locality.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locus', 'n.', 'The line traced by a point which varies its position\n   according to some determinate law; the surface described by a point or\n   line that moves according to a given law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locust', 'n.', 'Any one of numerous species of long-winged, migratory,\n   orthopterous insects, of the family Acrididae, allied to the\n   grasshoppers; esp., (Edipoda, / Pachytylus, migratoria, and Acridium\n   perigrinum, of Southern Europe, Asia, and Africa. In the United States\n   the related species with similar habits are usually called\n   grasshoppers. See Grasshopper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locust', 'n.', 'The locust tree. See Locust Tree (definition, note, and\n   phrases).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locusta', 'n.', 'The spikelet or flower cluster of grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locustella', 'n.', 'The European cricket warbler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locustic', 'a.', 'Pertaining to, or derived from, the locust; -- formerly\n   used to designate a supposed acid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locusting', 'p. a.', 'Swarming and devastating like locusts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locust tree', '', 'A large North American tree of the genus Robinia (R.\n   Pseudacacia), producing large slender racemes of white, fragrant,\n   papilionaceous flowers, and often cultivated as an ornamental tree. In\n   England it is called acacia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locution', 'n.', 'Speech or discourse; a phrase; a form or mode of\n   expression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locutory', 'n.', 'A room for conversation; especially, a room in\n   monasteries, where the monks were allowed to converse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodde', 'n.', 'The capelin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lode', 'n.', 'A water course or way; a reach of water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lode', 'n.', 'A metallic vein; any regular vein or course, whether metallic\n   or not.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodemanage', 'n.', 'Pilotage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lode-ship', 'n.', 'An old name for a pilot boat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodesman', 'n.', 'Same as Loadsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodestar', 'n.', 'Same as Loadstar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodestone', 'n.', 'Same as Loadstone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'n.', 'A shelter in which one may rest; as: (a) A shed; a rude\n   cabin; a hut; as, an Indians lodge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'n.', 'A small dwelling house, as for a gamekeeper or gatekeeper of\n   an estate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'n.', 'A den or cave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'n.', 'The meeting room of an association; hence, the regularly\n   constituted body of members which meets there; as, a masonic lodge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'n.', 'The chamber of an abbot, prior, or head of a college.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'n.', 'The space at the mouth of a level next the shaft, widened to\n   permit wagons to pass, or ore to be deposited for hoisting; -- called\n   also platt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'n.', 'A collection of objects lodged together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'n.', 'A family of North American Indians, or the persons who\n   usually occupy an Indian lodge, -- as a unit of enumeration, reckoned\n   from four to six persons; as, the tribe consists of about two hundred\n   lodges, that is, of about a thousand individuals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodged', 'imp. & p. p.', 'of Lodge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodging', 'p. pr. & vb. n.', 'of Lodge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'v. i.', 'To rest or remain a lodge house, or other shelter; to\n   rest; to stay; to abide; esp., to sleep at night; as, to lodge in York\n   Street.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'v. i.', 'To fall or lie down, as grass or grain, when overgrown or\n   beaten down by the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'v. i.', 'To come to a rest; to stop and remain; as, the bullet\n   lodged in the bark of a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'n.', 'To give shelter or rest to; especially, to furnish a\n   sleeping place for; to harbor; to shelter; hence, to receive; to hold.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'n.', 'To drive to shelter; to track to covert.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'n.', 'To deposit for keeping or preservation; as, the men lodged\n   their arms in the arsenal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'n.', 'To cause to stop or rest in; to implant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodge', 'n.', 'To lay down; to prostrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodgeable', 'a.', 'That may be or can be lodged; as, so many persons are\n   not lodgeable in this village.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodgeable', 'a.', 'Capable of affording lodging; fit for lodging in.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodged', 'a.', 'Lying down; -- used of beasts of the chase, as couchant is\n   of beasts of prey.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodgement', 'n.', 'See Lodgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodger', 'n.', 'One who, or that which, lodges; one who occupies a hired\n   room in anothers house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodging', 'n.', 'The act of one who, or that which, lodges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodging', 'n.', 'A place of rest, or of temporary habitation; esp., a\n   sleeping apartment; -- often in the plural with a singular meaning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodging', 'n.', 'Abiding place; harbor; cover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodgment', 'v.', 'The act of lodging, or the state of being lodged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodgment', 'v.', 'A lodging place; a room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodgment', 'v.', 'An accumulation or collection of something deposited in a\n   place or remaining at rest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodgment', 'v.', 'The occupation and holding of a position, as by a\n   besieging party; an instrument thrown up in a captured position; as, to\n   effect a lodgment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lodicule', 'n.', 'One of the two or three delicate membranous scales which\n   are next to the stamens in grasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loellingite', 'n.', 'A tin-white arsenide of iron, isomorphous with\n   arsenopyrite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loess', 'n.', 'A quaternary deposit, usually consisting of a fine yellowish\n   earth, on the banks of the Rhine and other large rivers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loevens larva', '', 'The peculiar larva of Polygordius. See Polygordius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loffe', 'v. i.', 'To laugh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loft', 'n.', 'That which is lifted up; an elevation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loft', 'n.', 'The room or space under a roof and above the ceiling of the\n   uppermost story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loft', 'n.', 'A gallery or raised apartment in a church, hall, etc.; as, an\n   organ loft.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loft', 'n.', 'A floor or room placed above another; a story.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loft', 'a.', 'Lofty; proud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loftily', 'adv.', 'In a lofty manner or position; haughtily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loftiness', 'n.', 'The state or quality of being lofty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lofty', 'superl.', 'Lifted high up; having great height; towering; high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lofty', 'superl.', 'Fig.: Elevated in character, rank, dignity, spirit,\n   bearing, language, etc.; exalted; noble; stately; characterized by\n   pride; haughty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Log', 'n.', 'A Hebrew measure of liquids, containing 2.37 gills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Log', 'n.', 'A bulky piece of wood which has not been shaped by hewing or\n   sawing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Log', 'n.', 'An apparatus for measuring the rate of a ships motion through\n   the water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Log', 'n.', 'Hence: The record of the rate of ships speed or of her daily\n   progress; also, the full nautical record of a ships cruise or voyage;\n   a log slate; a log book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Log', 'n.', 'A record and tabulated statement of the work done by an\n   engine, as of a steamship, of the coal consumed, and of other items\n   relating to the performance of machinery during a given time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Log', 'n.', 'A weight or block near the free end of a hoisting rope to\n   prevent it from being drawn through the sheave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logged', 'imp. & p. p.', 'of Log');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logging', 'p. pr. & vb. n.', 'of Log');
  INSERT INTO words (word, word_type, description) VALUES  (    'Log', 'v. t.', 'To enter in a ships log book; as, to log the miles run.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Log', 'v. i.', 'To engage in the business of cutting or transporting logs\n   for timber; to get out logs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Log', 'v. i.', 'To move to and fro; to rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logan', 'n.', 'A rocking or balanced stone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logaoedic', 'a.', 'Composed of dactyls and trochees so arranged as to\n   produce a movement like that of ordinary speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logarithm', 'n.', 'One of a class of auxiliary numbers, devised by John\n   Napier, of Merchiston, Scotland (1550-1617), to abridge arithmetical\n   calculations, by the use of addition and subtraction in place of\n   multiplication and division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logarithmetic', 'a.', 'Alt. of Logarithmetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logarithmetical', 'a.', 'See Logarithmic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logarithmetically', 'adv.', 'Logarithmically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logarithmic', 'a.', 'Alt. of Logarithmical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logarithmical', 'a.', 'Of or pertaining to logarithms; consisting of\n   logarithms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logarithmically', 'adv.', 'By the use of logarithms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Log-chip', 'n.', 'A thin, flat piece of board in the form of a quadrant of\n   a circle attached to the log line; -- called also log-ship. See 2d Log,\n   n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logcock', 'n.', 'The pileated woodpecker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loge', 'n.', 'A lodge; a habitation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loggan', 'n.', 'See Logan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loggat', 'n.', 'A small log or piece of wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loggat', 'n.', 'An old game in England, played by throwing pieces of wood\n   at a stake set in the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logge', 'n. & v.', 'See Lodge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logged', 'a.', 'Made slow and heavy in movement; water-logged.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logger', 'n.', 'One engaged in logging. See Log, v. i.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loggerhead', 'n.', 'A blockhead; a dunce; a numskull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loggerhead', 'n.', 'A spherical mass of iron, with a long handle, used to\n   heat tar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loggerhead', 'n.', 'An upright piece of round timber, in a whaleboat, over\n   which a turn of the line is taken when it is running out too fast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loggerhead', 'n.', 'A very large marine turtle (Thalassochelys caretta, /\n   caouana), common in the warmer parts of the Atlantic Ocean, from Brazil\n   to Cape Cod; -- called also logger-headed turtle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loggerhead', 'n.', 'An American shrike (Lanius Ludovicianus), similar to\n   the butcher bird, but smaller. See Shrike.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loggerheaded', 'a.', 'Dull; stupid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loggerheads', 'n.', 'The knapweed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loggia', 'n.', 'A roofed open gallery. It differs from a veranda in being\n   more architectural, and in forming more decidedly a part of the main\n   edifice to which it is attached; from a porch, in being intended not\n   for entrance but for an out-of-door sitting-room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logging', 'n.', 'The business of felling trees, cutting them into logs, and\n   transporting the logs to sawmills or to market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logic', 'n.', 'The science or art of exact reasoning, or of pure and formal\n   thought, or of the laws according to which the processes of pure\n   thinking should be conducted; the science of the formation and\n   application of general notions; the science of generalization,\n   judgment, classification, reasoning, and systematic arrangement;\n   correct reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logic', 'n.', 'A treatise on logic; as, Mills Logic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logical', 'a.', 'Of or pertaining to logic; used in logic; as, logical\n   subtilties.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logical', 'a.', 'According to the rules of logic; as, a logical argument or\n   inference; the reasoning is logical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logical', 'a.', 'Skilled in logic; versed in the art of thinking and\n   reasoning; as, he is a logical thinker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logicality', 'n.', 'Logicalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logically', 'adv.', 'In a logical manner; as, to argue logically.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logicalness', 'n.', 'The quality of being logical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logician', 'n.', 'A person skilled in logic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logics', 'n.', 'See Logic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logistic', 'a.', 'Alt. of Logistical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logistical', 'a.', 'Logical.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logistical', 'a.', 'Sexagesimal, or made on the scale of 60; as, logistic,\n   or sexagesimal, arithmetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logistics', 'n.', 'That branch of the military art which embraces the\n   details of moving and supplying armies. The meaning of the word is by\n   some writers extended to include strategy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logistics', 'n.', 'A system of arithmetic, in which numbers are expressed\n   in a scale of 60; logistic arithmetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logmen', 'pl. ', 'of Logman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logman', 'n.', 'A man who carries logs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logodaedaly', 'n.', 'Verbal legerdemain; a playing with words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logogram', 'n.', 'A word letter; a phonogram, that, for the sake of\n   brevity, represents a word; as, |, i. e., t, for it. Cf. Grammalogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logographer', 'n.', 'A chronicler; one who writes history in a condensed\n   manner with short simple sentences.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logographer', 'n.', 'One skilled in logography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logographic', 'a.', 'Alt. of Logographical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logographical', 'a.', 'Of or pertaining to logography.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logography', 'n.', 'A method of printing in which whole words or syllables,\n   cast as single types, are used.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logography', 'n.', 'A mode of reporting speeches without using shorthand,\n   -- a number of reporters, each in succession, taking down three or four\n   words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logogriph', 'n.', 'A sort of riddle in which it is required to discover a\n   chosen word from various combinations of its letters, or of some of its\n   letters, which form other words; -- thus, to discover the chosen word\n   chatter form cat, hat, rat, hate, rate, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logomachist', 'n.', 'One who contends about words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logomachy', 'n.', 'Contention in words merely, or a contention about words;\n   a war of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logomachy', 'n.', 'A game of word making.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logometric', 'a.', 'Serving to measure or ascertain chemical equivalents;\n   stoichiometric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logos', 'n.', 'A word; reason; speech.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logos', 'n.', 'The divine Word; Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logothete', '', 'An accountant; under Constantine, an officer of the\n   empire; a receiver of revenue; an administrator of a department.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logotype', 'n.', 'A single type, containing two or more letters; as, ae,\n   Ae, /, /, /, etc. ; -- called also ligature.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logroll', 'v. i. & t.', 'To engage in logrolling; to accomplish by\n   logrolling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logroller', 'n.', 'One who engages in logrolling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logrolling', 'n.', 'The act or process of rolling logs from the place where\n   they were felled to the stream which floats them to the sawmill or to\n   market. In this labor neighboring camps of loggers combine to assist\n   each other in turn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logrolling', 'n.', 'Hence: A combining to assist another in consideration\n   of receiving assistance in return; -- sometimes used of a disreputable\n   mode of accomplishing political schemes or ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Log-ship', 'n.', 'A part of the log. See Log-chip, and 2d Log, n., 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logwood', 'n.', 'The heartwood of a tree (Haematoxylon Campechianum), a\n   native of South America, It is a red, heavy wood, containing a\n   crystalline substance called haematoxylin, and is used largely in\n   dyeing. An extract from this wood is used in medicine as an astringent.\n   Also called Campeachy wood, and bloodwood.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-logy', '', 'A combining form denoting a discourse, treatise, doctrine,\n   theory, science; as, theology, geology, biology, mineralogy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Logy', 'a.', 'Heavy or dull in respect to motion or thought; as, a logy\n   horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lohock', 'n.', 'See Loch, a medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loimic', 'a.', 'Of or pertaining to the plague or contagious disorders.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loin', 'n.', 'That part of a human being or quadruped, which extends on\n   either side of the spinal column between the hip bone and the false\n   ribs. In human beings the loins are also called the reins. See Illust.\n   of Beef.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loir', 'n.', 'A large European dormouse (Myoxus glis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loitered', 'imp. & p. p.', 'of Loiter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loitering', 'p. pr. & vb. n.', 'of Loiter');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loiter', 'v. i.', 'To be slow in moving; to delay; to linger; to be\n   dilatory; to spend time idly; to saunter; to lag behind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loiter', 'v. i.', 'To wander as an idle vagrant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loiterer', 'n.', 'One who loiters; an idler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loiterer', 'n.', 'An idle vagrant; a tramp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loiteringly', 'adv.', 'In a loitering manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lok', 'n.', 'Alt. of Loki');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loki', 'n.', 'The evil deity, the author of all calamities and mischief,\n   answering to the African of the Persians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Locao', 'n.', 'A green vegetable dye imported from China.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loke', 'n.', 'A private path or road; also, the wicket or hatch of a door.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lokorys', 'n.', 'Liquorice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loligo', 'n.', 'A genus of cephalopods, including numerous species of\n   squids, common on the coasts of America and Europe. They are much used\n   for fish bait.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lolled', 'imp. & p. p.', 'of Loll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lolling', 'p. pr. & vb. n.', 'of Loll');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loll', 'v. i.', 'To act lazily or indolently; to recline; to lean; to throw\n   ones self down; to lie at ease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loll', 'v. i.', 'To hand extended from the mouth, as the tongue of an ox or\n   a log when heated with labor or exertion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loll', 'v. i.', 'To let the tongue hang from the mouth, as an ox, dog, or\n   other animal, when heated by labor; as, the ox stood lolling in the\n   furrow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loll', 'v. t.', 'To let hang from the mouth, as the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lollard', 'n.', 'One of a sect of early reformers in Germany.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lollard', 'n.', 'One of the followers of Wyclif in England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lollardism', 'n.', 'Alt. of Lollardy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lollardy', 'n.', 'The doctrines or principles of the Lollards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loller', 'n.', 'One who lolls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loller', 'n.', 'An idle vagabond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loller', 'n.', 'A Lollard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lollingly', 'adv.', 'In a lolling manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lollipop', 'n.', 'A kind of sugar confection which dissolves easily in the\n   mouth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lollop', 'v. i.', 'To move heavily; to lounge or idle; to loll.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lomata', 'pl. ', 'of Loma');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loma', 'n.', 'A lobe; a membranous fringe or flap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lomatinous', 'a.', 'Furnished with lobes or flaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lombard', 'a.', 'Of or pertaining to Lombardy, or the inhabitants of\n   Lombardy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lombard', 'n.', 'A native or inhabitant of Lombardy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lombard', 'n.', 'A money lender or banker; -- so called because the\n   business of banking was first carried on in London by Lombards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lombard', 'n.', 'Same as Lombard-house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lombard', 'n.', 'A form of cannon formerly in use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lombardeer', 'n.', 'A pawnbroker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lombard-house', 'n.', 'Alt. of Lombar-house');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lombar-house', 'n.', 'A bank or a pawnbrokers shop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lombar-house', 'n.', 'A public institution for lending money to the poor at\n   a moderate interest, upon articles deposited and pledged; -- called\n   also mont de piete.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lombardic', 'a.', 'Of or pertaining to Lombardy of the Lombards.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loment', 'n.', 'An elongated pod, consisting, like the legume, of two\n   valves, but divided transversely into small cells, each containing a\n   single seed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lomentaceous', 'a.', 'Of the nature of a loment; having fruits like\n   loments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lomonite', 'n.', 'Same as Laumontite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lompish', 'a.', 'Lumpish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lond', 'n.', 'Land.');
  INSERT INTO words (word, word_type, description) VALUES  (    'London', 'n.', 'The capital city of England.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Londoner', 'n.', 'A native or inhabitant of London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Londonism', 'n.', 'A characteristic of Londoners; a mode of speaking\n   peculiar to London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Londonize', 'v. i.', 'To impart to (one) a manner or character like that\n   which distinguishes Londoners.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Londonize', 'v. i.', 'To imitate the manner of the people of London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lone', 'n.', 'A lane. See Loanin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lone', 'a.', 'Being without a companion; being by ones self; also, sad\n   from lack of companionship; lonely; as, a lone traveler or watcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lone', 'a.', 'Single; unmarried, or in widowhood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lone', 'a.', 'Being apart from other things of the kind; being by itself;\n   also, apart from human dwellings and resort; as, a lone house.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lone', 'a.', 'Unfrequented by human beings; solitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loneliness', 'n.', 'The condition of being lonely; solitude; seclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loneliness', 'n.', 'The state of being unfrequented by human beings; as,\n   the loneliness of a road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loneliness', 'n.', 'Love of retirement; disposition to solitude.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loneliness', 'n.', 'A feeling of depression resulting from being alone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lonely', 'superl.', 'Sequestered from company or neighbors; solitary;\n   retired; as, a lonely situation; a lonely cell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lonely', 'superl.', 'Alone, or in want of company; forsaken.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lonely', 'superl.', 'Not frequented by human beings; as, a lonely wood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lonely', 'superl.', 'Having a feeling of depression or sadness resulting\n   from the consciousness of being alone; lonesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loneness', 'n.', 'Solitude; seclusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lonesome', 'superl.', 'Secluded from society; not frequented by human\n   beings; solitary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lonesome', 'superl.', 'Conscious of, and somewhat depressed by, solitude;\n   as, to feel lonesome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'superl.', 'Drawn out in a line, or in the direction of length;\n   protracted; extended; as, a long line; -- opposed to short, and\n   distinguished from broad or wide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'superl.', 'Drawn out or extended in time; continued through a\n   considerable tine, or to a great length; as, a long series of events; a\n   long debate; a long drama; a long history; a long book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'superl.', 'Slow in passing; causing weariness by length or\n   duration; lingering; as, long hours of watching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'superl.', 'Occurring or coming after an extended interval; distant\n   in time; far away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'superl.', 'Extended to any specified measure; of a specified\n   length; as, a span long; a yard long; a mile long, that is, extended to\n   the measure of a mile, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'superl.', 'Far-reaching; extensive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'superl.', 'Prolonged, or relatively more prolonged, in utterance;\n   -- said of vowels and syllables. See Short, a., 13, and Guide to\n   Pronunciation, // 22, 30.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'n.', 'A note formerly used in music, one half the length of a\n   large, twice that of a breve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'n.', 'A long sound, syllable, or vowel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'n.', 'The longest dimension; the greatest extent; -- in the phrase,\n   the long and the short of it, that is, the sum and substance of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'adv.', 'To a great extent in apace; as, a long drawn out line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'adv.', 'To a great extent in time; during a long time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'adv.', 'At a point of duration far distant, either prior or\n   posterior; as, not long before; not long after; long before the\n   foundation of Rome; long after the Conquest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'adv.', 'Through the whole extent or duration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'adv.', 'Through an extent of time, more or less; -- only in\n   question; as, how long will you be gone?');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'prep.', 'By means of; by the fault of; because of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longed', 'imp. & p. p.', 'of Long');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longing', 'p. pr. & vb. n.', 'of Long');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'a.', 'To feel a strong or morbid desire or craving; to wish for\n   something with eagerness; -- followed by an infinitive, or by after or\n   for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long', 'a.', 'To belong; -- used with to, unto, or for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longan', 'n.', 'A pulpy fruit related to the litchi, and produced by an\n   evergreen East Indian tree (Nephelium Longan).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longanimity', 'n.', 'Disposition to bear injuries patiently; forbearance;\n   patience.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-armed', 'a.', 'Having long arms; as, the long-armed ape or gibbon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longbeak', 'n.', 'The American redbellied snipe (Macrorhamphus\n   scolopaceus); -- called also long-billed dowitcher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longboat', 'n.', 'Formerly, the largest boat carried by a merchant vessel,\n   corresponding to the launch of a naval vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longbow', 'n.', 'The ordinary bow, not mounted on a stock; -- so called in\n   distinction from the crossbow when both were used as weapons of war.\n   Also, sometimes, such a bow of about the height of a man, as\n   distinguished from a much shorter one.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-breathed', 'a.', 'Having the power of retaining the breath for a long\n   time; long-winded.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-drawn', 'a.', 'Extended to a great length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longe', 'n.', 'A thrust. See Lunge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longe', 'n.', 'The training ground for a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longe', 'n.', 'Same as 4th Lunge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longer', 'n.', 'One who longs for anything.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longeval', 'a.', 'Long-loved; longevous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longevity', 'n.', 'Long duration of life; length of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longevous', 'a.', 'Living a long time; of great age.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longhand', 'n.', 'The written characters used in the common method of\n   writing; -- opposed to shorthand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longheaded', 'a.', 'Having unusual foresight or sagacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longhorn', 'n.', 'A long-horned animal, as a cow, goat, or beetle. See\n   Long-horned.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-horned', 'a.', 'Having a long horn or horns; as, a long-horned goat,\n   or cow; having long antennae, as certain beetles (Longicornia).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longicorn', 'a.', 'Long-horned; pertaining to the Longicornia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longicorn', 'n.', 'One of the Longicornia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longicornia', 'n. pl.', 'A division of beetles, including a large number of\n   species, in which the antennae are very long. Most of them, while in\n   the larval state, bore into the wood or beneath the bark of trees, and\n   some species are very destructive to fruit and shade trees. See Apple\n   borer, under Apple, and Locust beetle, under Locust.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longilateral', 'a.', 'Having long sides especially, having the form of a\n   long parallelogram.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longiloquence', 'n.', 'Long-windedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longimanous', 'a.', 'Having long hands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longimetry', 'n.', 'The art or practice of measuring distances or lengths.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longing', 'n.', 'An eager desire; a craving; a morbid appetite; an earnest\n   wish; an aspiration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longingly', 'adv.', 'With longing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longinquity', 'n.', 'Greatness of distance; remoteness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longipalp', 'n.', 'One of a tribe of beetles, having long maxillary palpi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longipennate', 'a.', 'Having long wings, or quills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longipennes', 'n. pl.', 'A group of longwinged sea birds, including the\n   gulls, petrels, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longipennine', 'a.', 'Of or pertaining to the Longipennes; longipennate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longirostres', 'pl. ', 'of Longiroster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longirosters', 'pl. ', 'of Longiroster');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longiroster', 'n.', 'One of the Longirostres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longirostral', 'a.', 'Having a long bill; of or pertaining to the\n   Longirostres.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longirostres', 'n. pl.', 'A group of birds characterized by having long\n   slender bills, as the sandpipers, curlews, and ibises. It is now\n   regarded as an artificial division.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longish', 'a.', 'Somewhat long; moderately long.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longitude', 'n.', 'Length; measure or distance along the longest line; --\n   distinguished from breadth or thickness; as, the longitude of a room;\n   rare now, except in a humorous sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longitude', 'n.', 'The arc or portion of the equator intersected between\n   the meridian of a given place and the meridian of some other place from\n   which longitude is reckoned, as from Greenwich, England, or sometimes\n   from the capital of a country, as from Washington or Paris. The\n   longitude of a place is expressed either in degrees or in time; as,\n   that of New York is 74 or 4 h. 56 min. west of Greenwich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longitude', 'n.', 'The distance in degrees, reckoned from the vernal\n   equinox, on the ecliptic, to a circle at right angles to the ecliptic\n   passing through the heavenly body whose longitude is designated; as,\n   the longitude of Capella is 79.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longitudinal', 'a.', 'Of or pertaining to longitude or length; as,\n   longitudinal distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longitudinal', 'a.', 'Extending in length; in the direction of the length;\n   running lengthwise, as distinguished from transverse; as, the\n   longitudinal diameter of a body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longitudinal', 'n.', 'A railway sleeper lying parallel with the rail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longitudinally', 'adv.', 'In the direction of length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longlegs', 'n.', 'A daddy longlegs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-lived', 'a.', 'Having a long life; having constitutional peculiarities\n   which make long life probable; lasting long; as, a long-lived tree;\n   they are a longlived family; long-lived prejudices.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longly', 'adv.', 'With longing desire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longly', 'adv.', 'For a long time; hence, wearisomely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longmynd rocks', '', 'The sparingly fossiliferous conglomerates, grits,\n   schists, and states of Great Britain, which lie at the base of the\n   Cambrian system; -- so called, because typically developed in the\n   Longmynd Hills, Shropshire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longness', 'n.', 'Length.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longnose', 'n.', 'The European garfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long primer', '', 'A kind of type, in size between small pica and\n   bourgeois.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longshanks', 'n.', 'The stilt.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longshore', 'a.', 'Belonging to the seashore or a seaport; along and on the\n   shore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longshoremen', 'pl. ', 'of Longshoreman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longshoreman', 'n.', 'One of a class of laborers employed about the wharves\n   of a seaport, especially in loading and unloading vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-sight', 'n.', 'Long-sightedness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-sighted', 'a.', 'Able to see objects at a great distance; hence,\n   having great foresight; sagacious; farseeing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-sighted', 'a.', 'Able to see objects distinctly at a distance, but not\n   close at hand; hypermetropic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-sightedness', 'n.', 'The state or condition of being long-sighted;\n   hence, sagacity; shrewdness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-sightedness', 'n.', 'See Hypermetropia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longsome', 'a.', 'Extended in length; tiresome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longspun', 'a.', 'Spun out, or extended, to great length; hence,\n   long-winded; tedious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longspur', 'n.', 'Any one of several species of fringilline birds of the\n   genus Calcarius (or Plectrophanes), and allied genera. The Lapland\n   longspur (C. Lapponicus), the chestnut-colored longspur (C. ornatus),\n   and other species, inhabit the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-stop', 'n.', 'One who is set to stop balls which pass the wicket\n   keeper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-sufferance', 'n.', 'Forbearance to punish or resent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-suffering', 'n.', 'Bearing injuries or provocation for a long time;\n   patient; not easily provoked.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-suffering', 'n.', 'Long patience of offense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longtail', 'n.', 'An animal, particularly a log, having an uncut tail. Cf.\n   Curtail. Dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-tongue', 'n.', 'The wryneck.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-tongued', 'a.', 'Having a long tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-tongued', 'a.', 'Talkative; babbling; loquacious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longulite', 'n.', 'A kind of crystallite having a (slender) acicular form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-waisted', 'a.', 'Having a long waist; long from the armpits to the\n   armpits to the bottom of the waist; -- said of persons.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-waisted', 'a.', 'Long from the part about the neck or shoulder, or\n   from the armpits, to the bottom of the weist, or to the skirt; -- said\n   of garments; as, a long-waisted coat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longways', 'adv.', 'Lengthwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Long-winded', 'a.', 'Long-breathed; hence, tediously long in speaking;\n   consuming much time; as, a long-winded talker.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Longwise', 'adv.', 'Lengthwise.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loo', 'n.', 'An old game played with five, or three, cards dealt to each\n   player from a full pack. When five cards are used the highest card is\n   the knave of clubs or (if so agreed upon) the knave of trumps; --\n   formerly called lanterloo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loo', 'n.', 'A modification of the game of \"all fours\" in which the players\n   replenish their hands after each round by drawing each a card from the\n   pack.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looed', 'imp. & p. p.', 'of Loo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looing', 'p. pr. & vb. n.', 'of Loo');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loo', 'v. t.', 'To beat in the game of loo by winning every trick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loob', 'n.', 'The clay or slimes washed from tin ore in dressing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loobily', 'a.', 'Loobylike; awkward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loobily', 'adv.', 'Awkwardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loobies', 'pl. ', 'of Looby');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looby', 'n.', 'An awkward, clumsy fellow; a lubber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looch', 'n.', 'See 2d Loch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loof', 'n.', 'The spongelike fibers of the fruit of a cucurbitaceous plant\n   (Luffa Aegyptiaca); called also vegetable sponge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loof', 'n.', 'Formerly, some appurtenance of a vessel which was used in\n   changing her course; -- probably a large paddle put over the lee bow to\n   help bring her head nearer to the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loof', 'n.', 'The part of a ships side where the planking begins to curve\n   toward bow and stern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loof', 'v. i.', 'See Luff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looked', 'imp. & p. p.', 'of Look');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looking', 'p. pr. & vb. n.', 'of Look');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'v. i.', 'To direct the eyes for the purpose of seeing something; to\n   direct the eyes toward an object; to observe with the eyes while\n   keeping them directed; -- with various prepositions, often in a special\n   or figurative sense. See Phrases below.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'v. i.', 'To direct the attention (to something); to consider; to\n   examine; as, to look at an action.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'v. i.', 'To seem; to appear; to have a particular appearance; as,\n   the patient looks better; the clouds look rainy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'v. i.', 'To have a particular direction or situation; to face; to\n   front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'v. i.', 'In the imperative: see; behold; take notice; take care;\n   observe; -- used to call attention.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'v. i.', 'To show ones self in looking, as by leaning out of a\n   window; as, look out of the window while I speak to you. Sometimes used\n   figuratively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'v. i.', 'To await the appearance of anything; to expect; to\n   anticipate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'v. t.', 'To look at; to turn the eyes toward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'v. t.', 'To seek; to search for.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'v. t.', 'To expect.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'v. t.', 'To influence, overawe, or subdue by looks or presence as,\n   to look down opposition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'v. t.', 'To express or manifest by a look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'n.', 'The act of looking; a glance; a sight; a view; -- often in\n   certain phrases; as, to have, get, take, throw, or cast, a look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'n.', 'Expression of the eyes and face; manner; as, a proud or\n   defiant look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Look', 'n.', 'Hence; Appearance; aspect; as, the house has a gloomy look;\n   the affair has a bad look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lookdown', 'n.', 'See Moonfish (b).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looker', 'n.', 'One who looks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looking', 'a.', 'Having a certain look or appearance; -- often compounded\n   with adjectives; as, good-looking, grand-looking, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looking', 'n.', 'The act of one who looks; a glance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looking', 'n.', 'The manner in which one looks; appearance; countenance;\n   face.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looking-glass', 'n.', 'A mirror made of glass on which has been placed a\n   backing of some reflecting substance, as quicksilver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lookout', 'n.', 'A careful looking or watching for any object or event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lookout', 'n.', 'The place from which such observation is made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lookout', 'n.', 'A person engaged in watching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lookout', 'n.', 'Object or duty of forethought and care; responsibility.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lool', 'n.', 'A vessel used to receive the washings of ores of metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loom', 'n.', 'See Loon, the bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loom', 'n.', 'A frame or machine of wood or other material, in which a\n   weaver forms cloth out of thread; a machine for interweaving yarn or\n   threads into a fabric, as in knitting or lace making.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loom', 'n.', 'That part of an oar which is near the grip or handle and\n   inboard from the rowlock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loomed', 'imp. & p. p.', 'of Loom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looming', 'p. pr. & vb. n.', 'of Loom');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loom', 'v. i.', 'To appear above the surface either of sea or land, or to\n   appear enlarged, or distorted and indistinct, as a distant object, a\n   ship at sea, or a mountain, esp. from atmospheric influences; as, the\n   ship looms large; the land looms high.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loom', 'v. i.', 'To rise and to be eminent; to be elevated or ennobled, in\n   a moral sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loom', 'n.', 'The state of looming; esp., an unnatural and indistinct\n   appearance of elevation or enlargement of anything, as of land or of a\n   ship, seen by one at sea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loom-gale', 'n.', 'A gentle gale of wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looming', 'n.', 'The indistinct and magnified appearance of objects seen in\n   particular states of the atmosphere. See Mirage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loon', 'n.', 'A sorry fellow; a worthless person; a rogue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loon', 'n.', 'Any one of several aquatic, wed-footed, northern birds of the\n   genus Urinator (formerly Colymbus), noted for their expertness in\n   diving and swimming under water. The common loon, or great northern\n   diver (Urinator imber, or Colymbus torquatus), and the red-throated\n   loon or diver (U. septentrionalis), are the best known species. See\n   Diver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loony', 'a.', 'See Luny.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loop', 'n.', 'A mass of iron in a pasty condition gathered into a ball for\n   the tilt hammer or rolls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loop', 'n.', 'A fold or doubling of a thread, cord, rope, etc., through\n   which another thread, cord, etc., can be passed, or which a hook can be\n   hooked into; an eye, as of metal; a staple; a noose; a bight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loop', 'n.', 'A small, narrow opening; a loophole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loop', 'n.', 'A curve of any kind in the form of a loop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loop', 'n.', 'A wire forming part of a main circuit and returning to the\n   point from which it starts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loop', 'n.', 'The portion of a vibrating string, air column, etc., between\n   two nodes; -- called also ventral segment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looped', 'imp. & p. p.', 'of Loop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looping', 'p. pr. & vb. n.', 'of Loop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loop', 'v. t.', 'To make a loop of or in; to fasten with a loop or loops;\n   -- often with up; as, to loop a string; to loop up a curtain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looped', 'a.', 'Bent, folded, or tied, so as to make a loop; as, a looped\n   wire or string.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looped', 'a.', 'Full of holes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looper', 'n.', 'An instrument, as a bodkin, for forming a loop in yarn, a\n   cord, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looper', 'n.', 'The larva of any species of geometrid moths. See Geometrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loophole', 'n.', 'A small opening, as in the walls of fortification, or in\n   the bulkhead of a ship, through which small arms or other weapons may\n   be discharged at an enemy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loophole', 'n.', 'A hole or aperture that gives a passage, or the means of\n   escape or evasion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loopholed', 'a.', 'Provided with loopholes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loopie', 'a.', 'Deceitful; cunning; sly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looping', 'n.', 'The running together of the matter of an ore into a mass,\n   when the ore is only heated for calcination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looping', 'p. pr. & vb. n.', 'of Loop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looplight', 'n.', 'A small narrow opening or window in a tower or fortified\n   wall; a loophole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loord', 'n.', 'A dull, stupid fellow; a drone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loos', 'n.', 'Praise; fame; reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'superl.', 'Unbound; untied; unsewed; not attached, fastened,\n   fixed, or confined; as, the loose sheets of a book.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'superl.', 'Free from constraint or obligation; not bound by duty,\n   habit, etc. ; -- with from or of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'superl.', 'Not tight or close; as, a loose garment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'superl.', 'Not dense, close, compact, or crowded; as, a cloth of\n   loose texture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'superl.', 'Not precise or exact; vague; indeterminate; as, a loose\n   style, or way of reasoning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'superl.', 'Not strict in matters of morality; not rigid according\n   to some standard of right.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'superl.', 'Unconnected; rambling.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'superl.', 'Lax; not costive; having lax bowels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'superl.', 'Dissolute; unchaste; as, a loose man or woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'superl.', 'Containing or consisting of obscene or unchaste\n   language; as, a loose epistle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'n.', 'Freedom from restraint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'n.', 'A letting go; discharge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loosed', 'imp. & p. p.', 'of Loose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loosing', 'p. pr. & vb. n.', 'of Loose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'a.', 'To untie or unbind; to free from any fastening; to remove\n   the shackles or fastenings of; to set free; to relieve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'a.', 'To release from anything obligatory or burdensome; to\n   disengage; hence, to absolve; to remit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'a.', 'To relax; to loosen; to make less strict.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'a.', 'To solve; to interpret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loose', 'v. i.', 'To set sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loosely', 'adv.', 'In a loose manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loosened', 'imp. & p. p.', 'of Loosen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loosening', 'p. pr. & vb. n.', 'of Loosen');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loosen', 'v. t.', 'To make loose; to free from tightness, tension,\n   firmness, or fixedness; to make less dense or compact; as, to loosen a\n   string, or a knot; to loosen a rock in the earth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loosen', 'v. t.', 'To free from restraint; to set at liberty..');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loosen', 'v. t.', 'To remove costiveness from; to facilitate or increase\n   the alvine discharges of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loosen', 'v. i.', 'To become loose; to become less tight, firm, or compact.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loosener', 'n.', 'One who, or that which, loosens.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looseness', 'n.', 'The state, condition, or quality, of being loose; as,\n   the looseness of a cord; looseness of style; looseness of morals or of\n   principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loosestrife', 'n.', 'The name of several species of plants of the genus\n   Lysimachia, having small star-shaped flowers, usually of a yellow\n   color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loosestrife', 'n.', 'Any species of the genus Lythrum, having purple, or,\n   in some species, crimson flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loosish', 'a.', 'Somewhat loose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loot', 'n.', 'The act of plundering.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loot', 'n.', 'Plunder; booty; especially, the boot taken in a conquered or\n   sacked city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looted', 'imp. & p. p.', 'of Loot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looting', 'p. pr. & vb. n.', 'of Loot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loot', 'v. t. & i.', 'To plunder; to carry off as plunder or a prize\n   lawfully obtained by war.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Looter', 'n.', 'A plunderer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loover', 'n.', 'See Louver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lop', 'n.', 'A flea.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lopped', 'imp. & p. p.', 'of Lop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lopping', 'p. pr. & vb. n.', 'of Lop');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lop', 'v. t.', 'To cut off as the top or extreme part of anything; to sho/\n   -- by cutting off the extremities; to cut off, or remove as superfluous\n   parts; as, to lop a tree or its branches.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lop', 'v. t.', 'To cut partly off and bend down; as, to lop bushes in a\n   hedge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lop', 'n.', 'That which is lopped from anything, as branches from a tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lop', 'v. i.', 'To hang downward; to be pendent; to lean to one side.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lop', 'v. t.', 'To let hang down; as, to lop the head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lop', 'a.', 'Hanging down; as, lop ears; -- used also in compound\n   adjectives; as, lopeared; lopsided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lope', 'imp.', 'of Leap.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loped', 'imp. & p. p.', 'of Lope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loping', 'p. pr. & vb. n.', 'of Lope');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lope', 'v. i.', 'To leap; to dance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lope', 'v. i.', 'To move with a lope, as a horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lope', 'n.', 'A leap; a long step.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lope', 'n.', 'An easy gait, consisting of long running strides or leaps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lopeared', 'a.', 'Having ears which lop or hang down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lopeman', 'n.', 'Leaper; ropedancer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loper', 'n.', 'One who, or that which, lopes; esp., a horse that lopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loper', 'n.', 'A swivel at one end of a ropewalk, used in laying the\n   strands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lophine', 'n.', 'A nitrogenous organic base obtained by the oxidation of\n   amarine, and regarded as a derivative of benzoic aldehyde. It is\n   obtained in long white crystalline tufts, -- whence its name.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lophiomys', 'n.', 'A very singular rodent (Lophiomys Imhausi) of\n   Northeastern Africa. It is the only known representative of a special\n   family (Lophiomyidae), remarkable for the structure of the skull. It\n   has handlike feet, and the hair is peculiar in structure and\n   arrangement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lophobranch', 'a.', 'Of or pertaining to the Lophobranchii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lophobranch', 'n.', 'One of the Lophobranchii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lophobranchiate', 'a.', 'Of or pertaining to the Lophobranchii.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lophobranchii', 'n. pl.', 'An order of teleostean fishes, having the gills\n   arranged in tufts on the branchial arches, as the Hippocampus and\n   pipefishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lophophore', 'n.', 'A disk which surrounds the mouth and bears the\n   tentacles of the Bryozoa. See Phylactolemata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lophopoda', 'n. pl.', 'Same as Phylactolemata.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lophostea', 'pl. ', 'of Lophosteon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lophosteons', 'pl. ', 'of Lophosteon');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lophosteon', 'n.', 'The central keel-bearing part of the sternum in birds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loppard', 'n.', 'A tree, the top of which has been lopped off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lopper', 'n.', 'One who lops or cuts off.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loppered', 'imp. & p. p.', 'of Lopper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loppering', 'p. pr. & vb. n.', 'of Lopper');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lopper', 'v. i.', 'To turn sour and coagulate from too long standing, as\n   milk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lopping', 'n.', 'A cutting off, as of branches; that which is cut off;\n   leavings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loppy', 'a.', 'Somewhat lop; inclined to lop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lopseed', 'n.', 'A perennial herb (Phryma Leptostachya), having slender\n   seedlike fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lopsided', 'a.', 'Leaning to one side because of some defect of structure;\n   as, a lopsided ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lopsided', 'a.', 'Unbalanced; poorly proportioned; full of idiosyncrasies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loquacious', 'a.', 'Given to continual talking; talkative; garrulous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loquacious', 'a.', 'Speaking; expressive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loquacious', 'a.', 'Apt to blab and disclose secrets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loquaciously', 'adv.', 'In a loquacious manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loquaciousness', 'n.', 'Loquacity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loquacity', 'n.', 'The habit or practice of talking continually or\n   excessively; inclination to talk too much; talkativeness; garrulity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loquat', 'n.', 'The fruit of the Japanese medlar (Photinia Japonica). It is\n   as large as a small plum, but grows in clusters, and contains four or\n   five large seeds. Also, the tree itself.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loral', 'n.', 'Of or pertaining to the lores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorate', 'a.', 'Having the form of a thong or strap; ligulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorcha', 'n.', 'A kind of light vessel used on the coast of China, having\n   the hull built on a European model, and the rigging like that of a\n   Chinese junk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lord', 'n.', 'A hump-backed person; -- so called sportively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lord', 'n.', 'One who has power and authority; a master; a ruler; a\n   governor; a prince; a proprietor, as of a manor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lord', 'n.', 'A titled nobleman., whether a peer of the realm or not; a\n   bishop, as a member of the House of Lords; by courtesy; the son of a\n   duke or marquis, or the eldest son of an earl; in a restricted sense, a\n   boron, as opposed to noblemen of higher rank.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lord', 'n.', 'A title bestowed on the persons above named; and also, for\n   honor, on certain official persons; as, lord advocate, lord\n   chamberlain, lord chancellor, lord chief justice, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lord', 'n.', 'A husband.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lord', 'n.', 'One of whom a fee or estate is held; the male owner of feudal\n   land; as, the lord of the soil; the lord of the manor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lord', 'n.', 'The Supreme Being; Jehovah.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lord', 'n.', 'The Savior; Jesus Christ.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lord', 'v. t.', 'To invest with the dignity, power, and privileges of a\n   lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lord', 'v. t.', 'To rule or preside over as a lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorded', 'imp. & p. p.', 'of Lord');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lording', 'p. pr. & vb. n.', 'of Lord');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lord', 'v. i.', 'To play the lord; to domineer; to rule with arbitrary or\n   despotic sway; -- sometimes with over; and sometimes with it in the\n   manner of a transitive verb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lording', 'n.', 'The son of a lord; a person of noble lineage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lording', 'n.', 'A little lord; a lordling; a lord, in contempt or\n   ridicule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordkin', 'n.', 'A little lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordlike', 'a.', 'Befitting or like a lord; lordly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordlike', 'a.', 'Haughty; proud; insolent; arrogant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordliness', 'n.', 'The state or quality of being lordly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordling', 'n.', 'A little or insignificant lord.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordly', 'superl.', 'Suitable for a lord; of or pertaining to a lord;\n   resembling a lord; hence, grand; noble; dignified; honorable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordly', 'superl.', 'Proud; haughty; imperious; insolent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordly', 'adv.', 'In a lordly manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordolatry', 'n.', 'Worship of, or reverence for, a lord as such.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordosis', 'n.', 'A curvature of the spine forwards, usually in the lumbar\n   region.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordosis', 'n.', 'Any abnormal curvature of the bones.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lords and Ladies', '', 'The European wake-robin (Arum maculatum), -- those\n   with purplish spadix the lords, and those with pale spadix the ladies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordship', 'n.', 'The state or condition of being a lord; hence (with his\n   or your), a title applied to a lord (except an archbishop or duke, who\n   is called Grace) or a judge (in Great Britain), etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordship', 'n.', 'Seigniory; domain; the territory over which a lord holds\n   jurisdiction; a manor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lordship', 'n.', 'Dominion; power; authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lore', 'n.', 'The space between the eye and bill, in birds, and the\n   corresponding region in reptiles and fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lore', 'n.', 'The anterior portion of the cheeks of insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lore', 'obs. imp. & p. p.', 'Lost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lore', 'v. t.', 'That which is or may be learned or known; the knowledge\n   gained from tradition, books, or experience; often, the whole body of\n   knowledge possessed by a people or class of people, or pertaining to a\n   particular subject; as, the lore of the Egyptians; priestly lore; legal\n   lore; folklore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lore', 'v. t.', 'That which is taught; hence, instruction; wisdom; advice;\n   counsel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lore', 'v. t.', 'Workmanship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loreal', 'a.', 'Alt. of Loral');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loral', 'a.', 'Of or pertaining to the lore; -- said of certain feathers of\n   birds, scales of reptiles, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorel', 'n.', 'A good for nothing fellow; a vagabond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loren', 'obs. strong p. p.', 'of Lose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loresman', 'n.', 'An instructor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorette', 'n.', 'In France, a name for a woman who is supported by her\n   lovers, and devotes herself to idleness, show, and pleasure; -- so\n   called from the church of Notre Dame de Lorette, in Paris, near which\n   many of them resided.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorettine', 'n.', 'One of a order of nuns founded in 1812 at Loretto, in\n   Kentucky. The members of the order (called also Sisters of Loretto, or\n   Friends of Mary at the Foot of the Cross) devote themselves to the\n   cause of education and the care of destitute orphans, their labors\n   being chiefly confined to the Western United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorgnette', 'n.', 'An opera glass');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorgnette', 'n.', 'elaborate double eyeglasses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lori', 'n.', 'Same as Lory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loricae', 'pl. ', 'of Lorica');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorica', 'n.', 'A cuirass, originally of leather, afterward of plates of\n   metal or horn sewed on linen or the like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorica', 'n.', 'Lute for protecting vessels from the fire.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorica', 'n.', 'The protective case or shell of an infusorian or rotifer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loricata', 'n. pl.', 'A suborder of edentates, covered with bony plates,\n   including the armadillos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loricata', 'n. pl.', 'The crocodilia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loricated', 'imp. & p. p.', 'of Loricate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loricating', 'p. pr. & vb. n.', 'of Loricate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loricate', 'v. t.', 'To cover with some protecting substance, as with lute,\n   a crust, coating, or plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loricate', 'v.', 'Covered with a shell or exterior made of plates somewhat\n   like a coat of mail, as in the armadillo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loricate', 'n.', 'An animal covered with bony scales, as crocodiles among\n   reptiles, and the pangolins among mammals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorication', 'n.', 'The act of loricating; the protecting substance put on;\n   a covering of scales or plates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorikeet', 'n.', 'Any one numerous species of small brush-tongued parrots\n   or lories, found mostly in Australia, New Guinea and the adjacent\n   islands, with some forms in the East Indies. They are arboreal in their\n   habits and feed largely upon the honey of flowers. They belong to\n   Trichoglossus, Loriculus, and several allied genera.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorimer', 'n.', 'Alt. of Loriner');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loriner', 'n.', 'A maker of bits, spurs, and metal mounting for bridles and\n   saddles; hence, a saddler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loring', 'n.', 'Instructive discourse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loriot', 'n.', 'The golden oriole of Europe. See Oriole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loris', 'n.', 'Any one of several species of small lemurs of the genus\n   Stenops. They have long, slender limbs and large eyes, and are arboreal\n   in their habits. The slender loris (S. gracilis), of Ceylon, in one of\n   the best known species.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorn', 'a.', 'Lost; undone; ruined.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorn', 'a.', 'Forsaken; abandoned; solitary; bereft; as, a lone, lorn\n   woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorries', 'pl. ', 'of Lorry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorrie', 'n.', 'Alt. of Lorry');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lorry', 'n.', 'A small cart or wagon, as those used on the tramways in\n   mines to carry coal or rubbish; also, a barrow or truck for shifting\n   baggage, as at railway stations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lories', 'pl. ', 'of Lory');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lory', 'n.', 'Any one of many species of small parrots of the family\n   Trichoglossidae, generally having the tongue papillose at the tip, and\n   the mandibles straighter and less toothed than in common parrots. They\n   are found in the East Indies, Australia, New Guinea, and the adjacent\n   islands. They feed mostly on soft fruits and on the honey of flowers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Los', 'n.', 'Praise. See Loos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Losable', 'a.', 'Such as can be lost.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Losange', 'n.', 'See Lozenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Losing', 'imp. & p. p.', 'of Lose');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lose', 'v. t.', 'To part with unintentionally or unwillingly, as by\n   accident, misfortune, negligence, penalty, forfeit, etc.; to be\n   deprived of; as, to lose money from ones purse or pocket, or in\n   business or gaming; to lose an arm or a leg by amputation; to lose men\n   in battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lose', 'v. t.', 'To cease to have; to possess no longer; to suffer\n   diminution of; as, to lose ones relish for anything; to lose ones\n   health.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lose', 'v. t.', 'Not to employ; to employ ineffectually; to throw away; to\n   waste; to squander; as, to lose a day; to lose the benefits of\n   instruction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lose', 'v. t.', 'To wander from; to miss, so as not to be able to and; to\n   go astray from; as, to lose ones way.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lose', 'v. t.', 'To ruin; to destroy; as destroy; as, the ship was lost on\n   the ledge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lose', 'v. t.', 'To be deprived of the view of; to cease to see or know the\n   whereabouts of; as, he lost his companion in the crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lose', 'v. t.', 'To fail to obtain or enjoy; to fail to gain or win; hence,\n   to fail to catch with the mind or senses; to miss; as, I lost a part of\n   what he said.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lose', 'v. t.', 'To cause to part with; to deprive of.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lose', 'v. t.', 'To prevent from gaining or obtaining.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lose', 'v. i.', 'To suffer loss, disadvantage, or defeat; to be worse off,\n   esp. as the result of any kind of contest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Losel', 'n.', 'One who loses by sloth or neglect; a worthless person; a\n   lorel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Losel', 'a.', 'Wasteful; slothful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Losenger', 'n.', 'A flatterer; a deceiver; a cozener.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Losengerie', 'n.', 'Flattery; deceit; trickery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loser', 'n.', 'One who loses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Losing', 'a.', 'Given to flattery or deceit; flattering; cozening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Losing', 'v. t.', 'Causing or incurring loss; as, a losing game or\n   business.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Losingly', 'adv.', 'In a manner to incur loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loss', 'v. t.', 'The act of losing; failure; destruction; privation; as,\n   the loss of property; loss of money by gaming; loss of health or\n   reputation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loss', 'v. t.', 'The state of losing or having lost; the privation, defect,\n   misfortune, harm, etc., which ensues from losing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loss', 'v. t.', 'That which is lost or from which one has parted; waste; --\n   opposed to gain or increase; as, the loss of liquor by leakage was\n   considerable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loss', 'v. t.', 'The state of being lost or destroyed; especially, the\n   wreck or foundering of a ship or other vessel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loss', 'v. t.', 'Failure to gain or win; as, loss of a race or battle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loss', 'v. t.', 'Failure to use advantageously; as, loss of time.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loss', 'v. t.', 'Killed, wounded, and captured persons, or captured\n   property.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loss', 'v. t.', 'Destruction or diminution of value, if brought about in a\n   manner provided for in the insurance contract (as destruction by fire\n   or wreck, damage by water or smoke), or the death or injury of an\n   insured person; also, the sum paid or payable therefor; as, the losses\n   of the company this year amount to a million of dollars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lossful', 'a.', 'Detrimental.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lossless', 'a.', 'Free from loss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lost', 'v. t.', 'Parted with unwillingly or unintentionally; not to be\n   found; missing; as, a lost book or sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lost', 'v. t.', 'Parted with; no longer held or possessed; as, a lost limb;\n   lost honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lost', 'v. t.', 'Not employed or enjoyed; thrown away; employed\n   ineffectually; wasted; squandered; as, a lost day; a lost opportunity\n   or benefit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lost', 'v. t.', 'Having wandered from, or unable to find, the way;\n   bewildered; perplexed; as, a child lost in the woods; a stranger lost\n   in London.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lost', 'v. t.', 'Ruined or destroyed, either physically or morally; past\n   help or hope; as, a ship lost at sea; a woman lost to virtue; a lost\n   soul.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lost', 'v. t.', 'Hardened beyond sensibility or recovery; alienated;\n   insensible; as, lost to shame; lost to all sense of honor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lost', 'v. t.', 'Not perceptible to the senses; no longer visible; as, an\n   island lost in a fog; a person lost in a crowd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lost', 'v. t.', 'Occupied with, or under the influence of, something, so as\n   to be insensible of external things; as, to be lost in thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lot', 'n.', 'That which happens without human design or forethought;\n   chance; accident; hazard; fortune; fate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lot', 'n.', 'Anything (as a die, pebble, ball, or slip of paper) used in\n   determining a question by chance, or without mans choice or will; as,\n   to cast or draw lots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lot', 'n.', 'The part, or fate, which falls to one, as it were, by chance,\n   or without his planning.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lot', 'n.', 'A separate portion; a number of things taken collectively; as,\n   a lot of stationery; -- colloquially, sometimes of people; as, a sorry\n   lot; a bad lot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lot', 'n.', 'A distinct portion or plot of land, usually smaller than a\n   field; as, a building lot in a city.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lot', 'n.', 'A large quantity or number; a great deal; as, to spend a lot\n   of money; lots of people think so.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lot', 'n.', 'A prize in a lottery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotted', 'imp. & p. p.', 'of Lot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotting', 'p. pr. & vb. n.', 'of Lot');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lot', 'v. t.', 'To allot; to sort; to portion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lote', 'n.', 'A large tree (Celtis australis), found in the south of\n   Europe. It has a hard wood, and bears a cherrylike fruit. Called also\n   nettle tree.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lote', 'n.', 'The European burbot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lote', 'v. i.', 'To lurk; to lie hid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loth', 'a.', 'Alt. of Lothsome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lothly', 'a.', 'Alt. of Lothsome');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lothsome', 'a.', 'See Loath, Loathly, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lothario', 'n.', 'A gay seducer of women; a libertine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotion', 'n.', 'A washing, especially of the skin for the purpose of\n   rendering it fair.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotion', 'n.', 'A liquid preparation for bathing the skin, or an injured or\n   diseased part, either for a medicinal purpose, or for improving its\n   appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loto', 'n.', 'See Lotto.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotong', 'n.', 'An East Indian monkey (Semnopithecus femoralis).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotophagi', 'n. pl.', 'A people visited by Ulysses in his wanderings. They\n   subsisted on the lotus. See Lotus (b), and Lotus-eater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotos', 'n.', 'See Lotus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotteries', 'pl. ', 'of Lottery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lottery', 'n.', 'A scheme for the distribution of prizes by lot or chance;\n   esp., a gaming scheme in which one or more tickets bearing particular\n   numbers draw prizes, and the rest of tickets are blanks. Fig. : An\n   affair of chance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lottery', 'n.', 'Allotment; thing allotted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotto', 'n.', 'A game of chance, played with cards, on which are inscribed\n   numbers, and any contrivance (as a wheel containing numbered balls) for\n   determining a set of numbers by chance. The player holding a card\n   having on it the set of numbers drawn from the wheel takes the stakes\n   after a certain percentage of them has been deducted for the dealer. A\n   variety of lotto is called keno.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loture', 'n.', 'See Lotion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotus', 'n.', 'A name of several kinds of water lilies; as Nelumbium\n   speciosum, used in religious ceremonies, anciently in Egypt, and to\n   this day in Asia; Nelumbium luteum, the American lotus; and Nymphaea\n   Lotus and N. caerulea, the respectively white-flowered and\n   blue-flowered lotus of modern Egypt, which, with Nelumbium speciosum,\n   are figured on its ancient monuments.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotus', 'n.', 'The lotus of the lotuseaters, probably a tree found in\n   Northern Africa, Sicily, Portugal, and Spain (Zizyphus Lotus), the\n   fruit of which is mildly sweet. It was fabled by the ancients to make\n   strangers who ate of it forget their native country, or lose all desire\n   to return to it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotus', 'n.', 'The lote, or nettle tree. See Lote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotus', 'n.', 'A genus (Lotus) of leguminous plants much resembling clover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotus', 'n.', 'An ornament much used in Egyptian architecture, generally\n   asserted to have been suggested by the Egyptian water lily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotus-eater', 'n.', 'Alt. of Lotos-eater');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lotos-eater', 'n.', 'One who ate the fruit or leaf of the lotus, and, as a\n   consequence, gave himself up to indolence and daydreams; one of the\n   Lotophagi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Louchettes', 'n. pl.', 'Goggles intended to rectify strabismus by\n   permitting vision only directly in front.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loud', 'superl.', 'Having, making, or being a strong or great sound; noisy;\n   striking the ear with great force; as, a loud cry; loud thunder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loud', 'superl.', 'Clamorous; boisterous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loud', 'superl.', 'Emphatic; impressive; urgent; as, a loud call for united\n   effort.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loud', 'superl.', 'Ostentatious; likely to attract attention; gaudy; as, a\n   loud style of dress; loud colors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loud', 'adv.', 'With loudness; loudly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loudful', 'a.', 'Noisy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loudly', 'adv.', 'In a loud manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loud-mouthed', 'a.', 'Having a loud voice; talking or sounding noisily;\n   noisily impudent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loudness', 'n.', 'The quality or state of being loud.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loud-voiced', 'a.', 'Having a loud voice; noisy; clamorous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lough', 'n.', 'A loch or lake; -- so spelt in Ireland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lough', 'obs. strong imp.', 'of Laugh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Louis dor', '', 'Formerly, a gold coin of France nominally worth twenty\n   shillings sterling, but of varying value; -- first struck in 1640.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lonis quatorze', '', 'Of, pertaining to, or resembling, the art or style of\n   the times of Louis XIV. of France; as, Louis quatorze architecture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Louk', 'n.', 'An accomplice; a \"pal.\"');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lounged', 'imp. & p. p.', 'of Lounge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lounging', 'p. pr. & vb. n.', 'of Lounge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lounge', 'a.', 'To spend time lazily, whether lolling or idly sauntering;\n   to pass time indolently; to stand, sit, or recline, in an indolent\n   manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lounge', 'n.', 'An idle gait or stroll; the state of reclining indolently;\n   a place of lounging.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lounge', 'n.', 'A piece of furniture resembling a sofa, upon which one may\n   lie or recline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lounger', 'n.', 'One who lounges; ar idler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loup', 'n.', 'See 1st Loop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loup-cervier', 'n.', 'The Canada lynx. See Lynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loup-loup', 'n.', 'The Pomeranian or Spitz dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loups', 'n. pl.', 'The Pawnees, a tribe of North American Indians whose\n   principal totem was the wolf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lour', 'n.', 'An Asiatic sardine (Clupea Neohowii), valued for its oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Louri', 'n.', 'See Lory.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lice', 'pl. ', 'of Louse');
  INSERT INTO words (word, word_type, description) VALUES  (    'Louse', 'n.', 'Any one of numerous species of small, wingless, suctorial,\n   parasitic insects belonging to a tribe (Pediculina), now usually\n   regarded as degraded Hemiptera. To this group belong of the lice of man\n   and other mammals; as, the head louse of man (Pediculus capitis), the\n   body louse (P. vestimenti), and the crab louse (Phthirius pubis), and\n   many others. See Crab louse, Dog louse, Cattle louse, etc., under Crab,\n   Dog, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Louse', 'n.', 'Any one of numerous small mandibulate insects, mostly\n   parasitic on birds, and feeding on the feathers. They are known as\n   Mallophaga, or bird lice, though some occur on the hair of mammals.\n   They are usually regarded as degraded Pseudoneuroptera. See Mallophaga.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Louse', 'n.', 'Any one of the numerous species of aphids, or plant lice.\n   See Aphid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Louse', 'n.', 'Any small crustacean parasitic on fishes. See Branchiura,\n   and Ichthvophthira.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Louse', 'v. t.', 'To clean from lice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lousewort', 'n.', 'Any species of Pedicularis, a genus of perennial herbs.\n   It was said to make sheep that fed on it lousy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lousily', 'adv.', 'In a lousy manner; in a mean, paltry manner; scurvily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lousiness', 'n.', 'The state or quality of being lousy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lousy', 'a.', 'Infested with lice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lousy', 'a.', 'Mean; contemptible; as, lousy knave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lout', 'v. i.', 'To bend; to box; to stoop.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lout', 'n.', 'A clownish, awkward fellow; a bumpkin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lout', 'v. t.', 'To treat as a lout or fool; to neglect; to disappoint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loutish', 'a.', 'Clownish; rude; awkward.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loutou', 'n.', 'A crested black monkey (Semnopithecus maurus) of Java.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Louver', 'n.', 'Alt. of Louvre');
  INSERT INTO words (word, word_type, description) VALUES  (    'Louvre', 'n.', 'A small lantern. See Lantern, 2 (a).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovable', 'a.', 'Having qualities that excite, or are fitted to excite,\n   love; worthy of love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovage', 'n.', 'An umbelliferous plant (Levisticum officinale), sometimes\n   used in medicine as an aromatic stimulant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'n.', 'A feeling of strong attachment induced by that which delights\n   or commands admiration; preeminent kindness or devotion to another;\n   affection; tenderness; as, the love of brothers and sisters.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'n.', 'Especially, devoted attachment to, or tender or passionate\n   affection for, one of the opposite sex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'n.', 'Courtship; -- chiefly in the phrase to make love, i. e., to\n   court, to woo, to solicit union in marriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'n.', 'Affection; kind feeling; friendship; strong liking or desire;\n   fondness; good will; -- opposed to hate; often with of and an object.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'n.', 'Due gratitude and reverence to God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'n.', 'The object of affection; -- often employed in endearing\n   address.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'n.', 'Cupid, the god of love; sometimes, Venus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'n.', 'A thin silk stuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'n.', 'A climbing species of Clematis (C. Vitalba).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'n.', 'Nothing; no points scored on one side; -- used in counting\n   score at tennis, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loved', 'imp. & p. p.', 'of Love');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loving', 'p. pr. & vb. n.', 'of Love');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'n.', 'To have a feeling of love for; to regard with affection or\n   good will; as, to love ones children and friends; to love ones\n   country; to love ones God.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'n.', 'To regard with passionate and devoted affection, as that of\n   one sex for the other.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'n.', 'To take delight or pleasure in; to have a strong liking or\n   desire for, or interest in; to be pleased with; to like; as, to love\n   books; to love adventures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love', 'v. i.', 'To have the feeling of love; to be in love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loveable', 'a.', 'See Lovable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love-drury', 'n.', 'Affection.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovee', 'n.', 'One who is loved.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loveful', 'a.', 'Full of love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loveless', 'a.', 'Void of love; void of tenderness or kindness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loveless', 'a.', 'Not attracting love; unattractive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovelily', 'adv.', 'In manner to excite love; amiably.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loveliness', 'n.', 'The state or quality of being lovely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovelock', 'n.', 'A long lock of hair hanging prominently by itself; an\n   earlock; -- worn by men of fashion in the reigns of Elizabeth and James\n   I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovelorn', 'a.', 'Forsaken by ones love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovely', 'superl.', 'Having such an appearance as excites, or is fitted to\n   excite, love; beautiful; charming; very pleasing in form, looks, tone,\n   or manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovely', 'superl.', 'Lovable; amiable; having qualities of any kind which\n   excite, or are fitted to excite, love or friendship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovely', 'superl.', 'Loving; tender.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovely', 'superl.', 'Very pleasing; -- applied loosely to almost anything\n   which is not grand or merely pretty; as, a lovely view; a lovely\n   valley; a lovely melody.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovely', 'adv.', 'In a manner to please, or to excite love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love-making', 'n.', 'Courtship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovemonger', 'n.', 'One who deals in affairs of love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lover', 'n.', 'One who loves; one who is in love; -- usually limited, in\n   the singular, to a person of the male sex.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lover', 'n.', 'A friend; one strongly attached to another; one who greatly\n   desires the welfare of any person or thing; as, a lover of his country.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lover', 'n.', 'One who has a strong liking for anything, as books, science,\n   or music.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lover', 'n.', 'Alt. of Lovery');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovery', 'n.', 'See Louver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loverwise', 'adv.', 'As lovers do.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love-sick', 'a.', 'Languishing with love or amorous desire; as, a love-sick\n   maid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love-sick', 'a.', 'Originating in, or expressive of, languishing love.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Love-sickness', 'n.', 'The state of being love-sick.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovesome', 'a.', 'Lovely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loving', 'a.', 'Affectionate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loving', 'a.', 'Expressing love or kindness; as, loving words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loving-kindness', 'n.', 'Tender regard; mercy; favor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovingly', 'adv.', 'With love; affectionately.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovingness', 'n.', 'Affection; kind regard.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lovyer', 'n.', 'A lover.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', '', 'strong imp. of Laugh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowed', 'imp. & p. p.', 'of Low');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowing', 'p. pr. & vb. n.', 'of Low');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'v. i.', 'To make the calling sound of cows and other bovine animals;\n   to moo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'n.', 'The calling sound ordinarily made by cows and other bovine\n   animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'n.', 'A hill; a mound; a grave.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'n.', 'Fire; a flame; a light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'v. i.', 'To burn; to blaze.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Occupying an inferior position or place; not high or\n   elevated; depressed in comparison with something else; as, low ground;\n   a low flight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Not rising to the usual height; as, a man of low stature;\n   a low fence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Near the horizon; as, the sun is low at four oclock in\n   winter, and six in summer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Sunk to the farthest ebb of the tide; as, low tide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Beneath the usual or remunerative rate or amount, or the\n   ordinary value; moderate; cheap; as, the low price of corn; low wages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Not loud; as, a low voice; a low sound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Depressed in the scale of sounds; grave; as, a low pitch;\n   a low note.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Made, as a vowel, with a low position of part of the\n   tongue in relation to the palate; as, / (/m), / (all). See Guide to\n   Pronunciation, // 5, 10, 11.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Near, or not very distant from, the equator; as, in the\n   low northern latitudes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Numerically small; as, a low number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Wanting strength or animation; depressed; dejected; as,\n   low spirits; low in spirits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Depressed in condition; humble in rank; as, men of low\n   condition; the lower classes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Mean; vulgar; base; dishonorable; as, a person of low\n   mind; a low trick or stratagem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Not elevated or sublime; not exalted or diction; as, a\n   low comparison.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Submissive; humble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Deficient in vital energy; feeble; weak; as, a low pulse;\n   made low by sickness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Moderate; not intense; not inflammatory; as, low heat; a\n   low temperature; a low fever.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Smaller than is reasonable or probable; as, a low\n   estimate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'superl.', 'Not rich, high seasoned, or nourishing; plain; simple;\n   as, a low diet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'n.', 'The lowest trump, usually the deuce; the lowest trump dealt or\n   drawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'adv.', 'In a low position or manner; not aloft; not on high; near\n   the ground.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'adv.', 'Under the usual price; at a moderate price; cheaply; as, he\n   sold his wheat low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'adv.', 'In a low mean condition; humbly; meanly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'adv.', 'In time approaching our own.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'adv.', 'With a low voice or sound; not loudly; gently; as, to speak\n   low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'adv.', 'With a low musical pitch or tone.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'adv.', 'In subjection, poverty, or disgrace; as, to be brought low\n   by oppression, by want, or by vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'adv.', 'In a path near the equator, so that the declination is\n   small, or near the horizon, so that the altitude is small; -- said of\n   the heavenly bodies with reference to the diurnal revolution; as, the\n   moon runs low, that is, is comparatively near the horizon when on or\n   near the meridian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low', 'v. t.', 'To depress; to lower.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowbell', 'n.', 'A bell used in fowling at night, to frighten birds, and,\n   with a sudden light, to make them fly into a net.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowbell', 'n.', 'A bell to be hung on the neck of a sheep.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowbell', 'v. t.', 'To frighten, as with a lowbell.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowborn', 'a.', 'Born in a low condition or rank; -- opposed to highborn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowbred', 'a.', 'Bred, or like one bred, in a low condition of life;\n   characteristic or indicative of such breeding; rude; impolite; vulgar;\n   as, a lowbred fellow; a lowbred remark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low-church', 'a.', 'Not placing a high estimate on ecclesiastical\n   organizations or forms; -- applied especially to Episcopalians, and\n   opposed to high-church. See High Church, under High.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low-churchism', 'n.', 'The principles of the low-church party.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-men', 'pl. ', 'of Low-churchman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low-churchman', 'n.', 'One who holds low-church principles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low-churchmanship', 'n.', 'The state of being a low-churchman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lower', 'a.', 'Compar. of Low, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowered', 'imp. & p. p.', 'of Lower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowering', 'p. pr. & vb. n.', 'of Lower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lower', 'a.', 'To let descend by its own weight, as something suspended; to\n   let down; as, to lower a bucket into a well; to lower a sail or a boat;\n   sometimes, to pull down; as, to lower a flag.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lower', 'a.', 'To reduce the height of; as, to lower a fence or wall; to\n   lower a chimney or turret.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lower', 'a.', 'To depress as to direction; as, to lower the aim of a gun;\n   to make less elevated as to object; as, to lower ones ambition,\n   aspirations, or hopes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lower', 'a.', 'To reduce the degree, intensity, strength, etc., of; as, to\n   lower the temperature of anything; to lower ones vitality; to lower\n   distilled liquors.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lower', 'a.', 'To bring down; to humble; as, to lower ones pride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lower', 'a.', 'To reduce in value, amount, etc. ; as, to lower the price of\n   goods, the rate of interest, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lower', 'v. i.', 'To fall; to sink; to grow less; to diminish; to decrease;\n   as, the river lowered as rapidly as it rose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowered', 'imp. & p. p.', 'of Lower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowering', 'p. pr. & vb. n.', 'of Lower');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lower', 'v. i.', 'To be dark, gloomy, and threatening, as clouds; to be\n   covered with dark and threatening clouds, as the sky; to show\n   threatening signs of approach, as a tempest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lower', 'v. i.', 'To frown; to look sullen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lower', 'n.', 'Cloudiness; gloominess.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lower', 'n.', 'A frowning; sullenness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lower-case', 'a.', 'Pertaining to, or kept in, the lower case; -- used to\n   denote the small letters, in distinction from capitals and small\n   capitals. See the Note under 1st Case, n., 3.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowering', 'a.', 'Dark and threatening; gloomy; sullen; as, lowering clouds\n   or sky.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loweringly', 'adv.', 'In a lowering manner; with cloudiness or threatening\n   gloom.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowermost', 'superl.', 'Lowest.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowery', 'a.', 'Cloudy; gloomy; lowering; as, a lowery sky; lowery weather.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowgh', '', 'Alt. of Lowh');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowh', '', 'strong imp. of Laugh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowing', 'n.', 'The calling sound made by cows and other bovine animals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowish', 'a.', 'Somewhat low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowk', 'n.', 'See Louk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowland', 'n.', 'Land which is low with respect to the neighboring country;\n   a low or level country; -- opposed to highland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowlander', 'n.', 'A native or inhabitant of the Lowlands, especially of\n   the Lowlands of Scotland, as distinguished from Highlander.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowlihood', 'n.', 'Alt. of Lowlihead');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowlihead', 'n.', 'A lowly state.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowlily', 'adv.', 'In a lowly place or manner; humbly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowliness', 'n.', 'The state or quality of being lowly; humility;\n   humbleness of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowliness', 'n.', 'Low condition, especially as to manner of life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low-lived', 'a.', 'Characteristic of, or like, one bred in a low and vulgar\n   condition of life; mean dishonorable; contemptible; as, low-lived\n   dishonesty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowly', 'a.', 'Not high; not elevated in place; low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowly', 'a.', 'Low in rank or social importance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowly', 'a.', 'Not lofty or sublime; humble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowly', 'a.', 'Having a low esteem of ones own worth; humble; meek; free\n   from pride.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowly', 'adv.', 'In a low manner; humbly; meekly; modestly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowly', 'adv.', 'In a low condition; meanly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low-minded', 'a.', 'Inclined in mind to low or unworthy things; showing a\n   base mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low-mindedness', 'n.', 'The quality of being lowminded; meanness; baseness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lown', 'n.', 'A low fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low-necked', 'a.', 'Cut low in the neck; decollete; -- said of a womans\n   dress.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowness', 'n.', 'The state or quality of being low.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low-pressure', 'a.', 'Having, employing, or exerting, a low degree of\n   pressure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lowry', 'n.', 'An open box car used on railroads. Compare Lorry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low-spirited', 'a.', 'Deficient in animation and courage; dejected;\n   depressed; not sprightly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low-studded', 'a.', 'Furnished or built with short studs; as, a low-studded\n   house or room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Low-thoughted', 'a.', 'Having ones thoughts directed toward mean or\n   insignificant subjects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loxodromic', 'a.', 'Pertaining to sailing on rhumb lines; as, loxodromic\n   tables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loxodromics', 'n.', 'The art or method of sailing on the loxodromic or\n   rhumb line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loxodremism', 'n.', 'The act or process of tracing a loxodromic curve; the\n   act of moving as if in a loxodromic curve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loxodromy', 'n.', 'The science of loxodromics.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loy', 'n.', 'A long, narrow spade for stony lands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loyal', 'a.', 'Faithful to law; upholding the lawful authority; faithful\n   and true to the lawful government; faithful to the prince or sovereign\n   to whom one is subject; unswerving in allegiance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loyal', 'a.', 'True to any person or persons to whom one owes fidelity,\n   especially as a wife to her husband, lovers to each other, and friend\n   to friend; constant; faithful to a cause or a principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loyalist', 'n.', 'A person who adheres to his sovereign or to the lawful\n   authority; especially, one who maintains his allegiance to his prince\n   or government, and defends his cause in times of revolt or revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loyally', 'adv.', 'In a loyal manner; faithfully.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loyalness', 'n.', 'Loyalty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Loyalty', 'n.', 'The state or quality of being loyal; fidelity to a\n   superior, or to duty, love, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lozenge', 'n.', 'A diamond-shaped figure usually with the upper and lower\n   angles slightly acute, borne upon a shield or escutcheon. Cf. Fusil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lozenge', 'n.', 'A form of the escutcheon used by women instead of the\n   shield which is used by men.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lozenge', 'n.', 'A figure with four equal sides, having two acute and two\n   obtuse angles; a rhomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lozenge', 'n.', 'Anything in the form of lozenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lozenge', 'n.', 'A small cake of sugar and starch, flavored, and often\n   medicated. -- originally in the form of a lozenge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lozenged', 'a.', 'Alt. of Lozenge-shaped');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lozenge-shaped', 'a.', 'Having the form of a lozenge or rhomb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lozengy', 'a.', 'Divided into lozenge-shaped compartments, as the field or\n   a bearing, by lines drawn in the direction of the bend sinister.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lu', 'n. & v. t.', 'See Loo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubbard', 'n.', 'A lubber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubbard', 'a.', 'Lubberly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubber', 'n.', 'A heavy, clumsy, or awkward fellow; a sturdy drone; a\n   clown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubberly', 'a.', 'Like a lubber; clumsy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubberly', 'adv.', 'Clumsily; awkwardly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubric', 'a.', 'Alt. of Lubrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubrical', 'a.', 'Having a smooth surface; slippery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubrical', 'a.', 'Lascivious; wanton; lewd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubricant', 'a.', 'Lubricating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubricant', 'n.', 'That which lubricates; specifically, a substance, as\n   oil, grease, plumbago, etc., used for reducing the friction of the\n   working parts of machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubricate', 'v. t.', 'To make smooth or slippery; as, mucilaginous and\n   saponaceous remedies lubricate the parts to which they are applied.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubricate', 'v. t.', 'To apply a lubricant to, as oil or tallow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubrication', 'n.', 'The act of lubricating; the act of making slippery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubricator', 'n.', 'One who, or that which, lubricates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubricator', 'n.', 'A contrivance, as an oil cup, for supplying a lubricant\n   to machinery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubricitate', 'v. i.', 'See Lubricate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubricity', 'n.', 'Smoothness; freedom from friction; also, property, which\n   diminishes friction; as, the lubricity of oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubricity', 'n.', 'Slipperiness; instability; as, the lubricity of fortune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubricity', 'n.', 'Lasciviousness; propensity to lewdness; lewdness;\n   lechery; incontinency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubricous', 'a.', 'Lubric.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubrification', 'n.', 'Alt. of Lubrifaction');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lubrifaction', 'n.', 'The act of lubricating, or making smooth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucarne', 'n.', 'A dormer window.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucchese', 'n. sing. & pl.', 'A native or inhabitant of Lucca, in Tuscany;\n   in the plural, the people of Lucca.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luce', 'n.', 'A pike when full grown.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucency', 'n.', 'The quality of being lucent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucent', 'a.', 'Shining; bright; resplendent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucern', 'n.', 'A sort of hunting dog; -- perhaps from Lucerne, in\n   Switzerland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucern', 'n.', 'An animal whose fur was formerly much in request (by some\n   supposed to be the lynx).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucern', 'n.', 'A leguminous plant (Medicago sativa), having bluish purple\n   cloverlike flowers, cultivated for fodder; -- called also alfalfa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucern', 'n.', 'A lamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucernal', 'a.', 'Of or pertaining to a lamp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucernaria', 'n.', 'A genus of acalephs, having a bell-shaped body with\n   eight groups of short tentacles around the margin. It attaches itself\n   by a sucker at the base of the pedicel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucernarian', 'a.', 'Of or pertaining to the Lucernarida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucernarian', 'n.', 'One of the Lucernarida.');
  INSERT INTO words (word, word_type, description) VALUES  (    'lucernarida', 'n. pl.', 'A division of acalephs, including Lucernaria and\n   allied genera; -- called also Calycozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'lucernarida', 'n. pl.', 'A more extensive group of acalephs, including both\n   the true lucernarida and the Discophora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucerne', 'n.', 'See Lucern, the plant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucid', 'n.', 'Shining; bright; resplendent; as, the lucid orbs of heaven.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucid', 'n.', 'Clear; transparent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucid', 'n.', 'Presenting a clear view; easily understood; clear.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucid', 'n.', 'Bright with the radiance of intellect; not darkened or\n   confused by delirium or madness; marked by the regular operations of\n   reason; as, a lucid interval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucidity', 'n.', 'The quality or state of being lucid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucidly', 'adv.', 'In a lucid manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucidness', 'n.', 'The quality of being lucid; lucidity.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucifer', 'n.', 'The planet Venus, when appearing as the morning star; --\n   applied in Isaiah by a metaphor to a king of Babylon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucifer', 'n.', 'Hence, Satan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucifer', 'n.', 'A match made of a sliver of wood tipped with a combustible\n   substance, and ignited by friction; -- called also lucifer match, and\n   locofoco. See Locofoco.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucifer', 'n.', 'A genus of free-swimming macruran Crustacea, having a\n   slender body and long appendages.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luciferian', 'a.', 'Of or pertaining to Lucifer; having the pride of\n   Lucifer; satanic; devilish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luciferian', 'a.', 'Of or pertaining to the Luciferians or their leader.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luciferian', 'n.', 'One of the followers of Lucifer, bishop of Cagliari, in\n   the fourth century, who separated from the orthodox churches because\n   they would not go as far as he did in opposing the Arians.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luciferous', 'a.', 'Giving light; affording light or means of discovery.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luciferously', 'adv.', 'In a luciferous manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucific', 'a.', 'Producing light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luciform', 'a.', 'Having, in some respects, the nature of light; resembling\n   light.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucifrian', 'a.', 'Luciferian; satanic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucimeter', 'n.', 'an instrument for measuring the intensity of light; a\n   photometer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luck', 'n.', 'That which happens to a person; an event, good or ill,\n   affecting ones interests or happiness, and which is deemed casual; a\n   course or series of such events regarded as occurring by chance;\n   chance; hap; fate; fortune; often, ones habitual or characteristic\n   fortune; as, good, bad, ill, or hard luck. Luck is often used for good\n   luck; as, luck is better than skill.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luckily', 'adv.', 'In a lucky manner; by good fortune; fortunately; -- used\n   in a good sense; as, they luckily escaped injury.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luckiness', 'n.', 'The state or quality of being lucky; as, the luckiness\n   of a man or of an event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luckiness', 'n.', 'Good fortune; favorable issue or event.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luckless', 'a.', 'Being without luck; unpropitious; unfortunate; unlucky;\n   meeting with ill success or bad fortune; as, a luckless gamester; a\n   luckless maid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucky', 'superl.', 'Favored by luck; fortunate; meeting with good success\n   or good fortune; -- said of persons; as, a lucky adventurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucky', 'superl.', 'Producing, or resulting in, good by chance, or\n   unexpectedly; favorable; auspicious; fortunate; as, a lucky mistake; a\n   lucky cast; a lucky hour.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucky proach', '', 'See Fatherlasher.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucrative', 'a.', 'Yielding lucre; gainful; profitable; making increase of\n   money or goods; as, a lucrative business or office.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucrative', 'a.', 'Greedy of gain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucratively', 'adv.', 'In a lucrative manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucre', 'n.', 'Gain in money or goods; profit; riches; -- often in an ill\n   sense.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucriferous', 'a.', 'Gainful; profitable.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucrific', 'a.', 'Producing profit; gainful.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luctation', 'n.', 'Effort to overcome in contest; struggle; endeavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luctual', 'a.', 'Producing grief; saddening.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucubrated', 'imp. & p. p.', 'of Lucubrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucubrated', 'p. pr. & vb. n.', 'of Lucubrate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucubrate', 'n.', 'To study by candlelight or a lamp; to study by night.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucubrate', 'v. t.', 'To elaborate, perfect, or compose, by night study or\n   by laborious endeavor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucubration', 'n.', 'The act of lucubrating, or studying by candlelight;\n   nocturnal study; meditation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucubration', 'n.', 'That which is composed by night; that which is\n   produced by meditation in retirement; hence (loosely) any literary\n   composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucubrator', 'n.', 'One who studies by night; also, one who produces\n   lucubrations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucubratory', 'a.', 'Composed by candlelight, or by night; of or pertaining\n   to night studies; laborious or painstaking.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucule', 'n.', 'A spot or fleck on the sun brighter than the surrounding\n   surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luculent', 'a.', 'Lucid; clear; transparent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luculent', 'a.', 'Clear; evident; luminous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luculent', 'a.', 'Bright; shining in beauty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luculently', 'adv.', 'In a luculent manner; clearly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucullite', 'n.', 'A variety of black limestone, often polished for\n   ornamental purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lucuma', 'n.', 'An American genus of sapotaceous trees bearing sweet and\n   edible fruits.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luddite', 'n.', 'One of a number of riotous persons in England, who for six\n   years (1811-17) tried to prevent the use of labor-saving machinery by\n   breaking it, burning factories, etc.; -- so called from Ned Lud, a\n   half-witted man who some years previously had broken stocking frames.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ludibrious', 'a.', 'Sportive; ridiculous; wanton.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ludibund', 'a.', 'Sportive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ludicrous', 'a.', 'Adapted to excite laughter, without scorn or contempt;\n   sportive.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ludification', 'n.', 'The act of deriding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ludificatory', 'a.', 'Making sport; tending to excite derision.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ludlamite', 'n.', 'A mineral occurring in small, green, transparent,\n   monoclinic crystals. It is a hydrous phosphate of iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ludlow group', '', 'A subdivision of the British Upper Silurian lying below\n   the Old Red Sandstone; -- so named from the Ludlow, in Western England.\n   See the Chart of Geology.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ludwigite', 'n.', 'A borate of iron and magnesia, occurring in fibrous\n   masses of a blackish green color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lues', 'n.', 'Disease, especially of a contagious kind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luff', 'n.', 'The side of a ship toward the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luff', 'n.', 'The act of sailing a ship close to the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luff', 'n.', 'The roundest part of a ships bow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luff', 'n.', 'The forward or weather leech of a sail, especially of the\n   jib, spanker, and other fore-and-aft sails.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luffed', 'imp. & p. p.', 'of Luff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luffing', 'p. pr. & vb. n.', 'of Luff');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luff', 'v. i.', 'To turn the head of a vessel toward the wind; to sail\n   nearer the wind; to turn the tiller so as to make the vessel sail\n   nearer the wind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luffer', 'n.', 'See Louver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lug', 'n.', 'The ear, or its lobe.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lug', 'n.', 'That which projects like an ear, esp. that by which anything\n   is supported, carried, or grasped, or to which a support is fastened;\n   an ear; as, the lugs of a kettle; the lugs of a founders flask; the\n   lug (handle) of a jug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lug', 'n.', 'A projecting piece to which anything, as a rod, is attached,\n   or against which anything, as a wedge or key, bears, or through which a\n   bolt passes, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lug', 'n.', 'The leather loop or ear by which a shaft is held up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lug', 'n.', 'The lugworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lugged', 'imp. & p. p.', 'of Lug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lugging', 'p. pr. & vb. n.', 'of Lug');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lug', 'v. i.', 'To pull with force; to haul; to drag along; to carry with\n   difficulty, as something heavy or cumbersome.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lug', 'v. i.', 'To move slowly and heavily.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lug', 'n.', 'The act of lugging; as, a hard lug; that which is lugged; as,\n   the pack is a heavy lug.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lug', 'n.', 'Anything which moves slowly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lug', 'n.', 'A rod or pole.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lug', 'n.', 'A measure of length, being 16/ feet; a rod, pole, or perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luggage', 'n.', 'That which is lugged; anything cumbrous and heavy to be\n   carried; especially, a travelers trunks, baggage, etc., or their\n   contents.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lugger', 'n.', 'A small vessel having two or three masts, and a running\n   bowsprit, and carrying lugsails. See Illustration in Appendix.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lugger', 'n.', 'An Indian falcon (Falco jugger), similar to the European\n   lanner and the American prairie falcon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lugmark', 'n.', 'A mark cut into the ear of an animal to identify it; an\n   earmark.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lugsail', 'n.', 'A square sail bent upon a yard that hangs obliquely to the\n   mast and is raised or lowered with the sail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lugubrious', 'a.', 'Mournful; indicating sorrow, often ridiculously or\n   feignedly; doleful; woful; pitiable; as, a whining tone and a\n   lugubrious look.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lugworm', 'n.', 'A large marine annelid (Arenicola marina) having a row of\n   tufted gills along each side of the back. It is found burrowing in\n   sandy beaches, both in America and Europe, and is used for bait by\n   European fishermen. Called also lobworm, and baitworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luke', 'a.', 'Moderately warm; not hot; tepid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lukewarm', 'a.', 'Moderately warm; neither cold nor hot; tepid; not ardent;\n   not zealous; cool; indifferent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lulled', 'imp. & p. p.', 'of Lull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lulling', 'p. pr. & vb. n.', 'of Lull');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lull', 'v. t.', 'To cause to rest by soothing influences; to compose; to\n   calm; to soothe; to quiet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lull', 'v. i.', 'To become gradually calm; to subside; to cease or abate\n   for a time; as, the storm lulls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lull', 'n.', 'The power or quality of soothing; that which soothes; a\n   lullaby.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lull', 'n.', 'A temporary cessation of storm or confusion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'lullaby', 'v. t.', 'A song to quiet babes or lull them to sleep; that which\n   quiets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'lullaby', 'v. t.', 'Hence: Good night; good-by.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luller', 'n.', 'One who, or that which, lulls.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lullingly', 'adv.', 'In a lulling manner; soothingly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lum', 'n.', 'A chimney.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lum', 'n.', 'A ventilating chimney over the shaft of a mine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lum', 'n.', 'A woody valley; also, a deep pool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumachel', 'n.', 'Alt. of Lumachella');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumachella', 'n.', 'A grayish brown limestone, containing fossil shells,\n   which reflect a beautiful play of colors. It is also called fire\n   marble, from its fiery reflections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbaginous', 'a.', 'Of or pertaining to lumbago.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbago', 'n.', 'A rheumatic pain in the loins and the small of the back.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbar', 'a.', 'Alt. of Lumbal');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbal', 'a.', 'Of, pertaining to, or near, the loins; as, the lumbar\n   arteries.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumber', 'n.', 'A pawnbrokers shop, or room for storing articles put in\n   pawn; hence, a pledge, or pawn.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumber', 'n.', 'Old or refuse household stuff; things cumbrous, or bulky\n   and useless, or of small value.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumber', 'n.', 'Timber sawed or split into the form of beams, joists,\n   boards, planks, staves, hoops, etc.; esp., that which is smaller than\n   heavy timber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbered', 'imp. & p. p.', 'of Lumber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbering', 'p. pr. & vb. n.', 'of Lumber');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumber', 'b. t.', 'To heap together in disorder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumber', 'b. t.', 'To fill or encumber with lumber; as, to lumber up a\n   room.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumber', 'v. i.', 'To move heavily, as if burdened.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumber', 'v. i.', 'To make a sound as if moving heavily or clumsily; to\n   rumble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumber', 'v. i.', 'To cut logs in the forest, or prepare timber for market.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumberer', 'n.', 'One employed in lumbering, cutting, and getting logs from\n   the forest for lumber; a lumberman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbering', 'n.', 'The business of cutting or getting timber or logs from\n   the forest for lumber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbermen', 'pl. ', 'of Lumberman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumberman', 'n.', 'One who is engaged in lumbering as a business or\n   employment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbosacral', 'n.', 'Of or pertaining to the loins and sacrum; as, the\n   lumbosacral nerve, a branch of one of the lumber nerves which passes\n   over the sacrum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbric', 'n.', 'An earthworm, or a worm resembling an earthworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbrical', 'a.', 'Resembling a worm; as, the lumbrical muscles of the\n   hands of the hands and feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbrical', 'n.', 'A lumbrical muscle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbriciform', 'a.', 'Resembling an earthworm; vermiform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbricoid', 'a.', 'Like an earthworm; belonging to the genus Lumbricus, or\n   family Lumbricidae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumbricus', 'n.', 'A genus of annelids, belonging to the Oligochaeta, and\n   including the common earthworms. See Earthworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luminant', 'a.', 'Luminous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luminaries', 'pl. ', 'of Luminary');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luminary', 'n.', 'Any body that gives light, especially one of the heavenly\n   bodies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luminary', 'n.', 'One who illustrates any subject, or enlightens mankind;\n   as, Newton was a distinguished luminary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luminate', 'v. t.', 'To illuminate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumination', 'n.', 'Illumination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumine', 'v. i.', 'To illumine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luminiferous', 'a.', 'Producing light; yielding light; transmitting light;\n   as, the luminiferous ether.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luminosity', 'n.', 'The quality or state of being luminous; luminousness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luminous', 'a.', 'Shining; emitting or reflecting light; brilliant; bright;\n   as, the is a luminous body; a luminous color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luminous', 'a.', 'Illuminated; full of light; bright; as, many candles made\n   the room luminous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luminous', 'a.', 'Enlightened; intelligent; also, clear; intelligible; as,\n   a luminous mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lummox', 'n.', 'A fat, ungainly, stupid person; an awkward bungler.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lump', 'n.', 'A small mass of matter of irregular shape; an irregular or\n   shapeless mass; as, a lump of coal; a lump of iron ore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lump', 'n.', 'A mass or aggregation of things.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lump', 'n.', 'A projection beneath the breech end of a gun barrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumped', 'imp. & p. p.', 'of Lump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumping', 'p. pr. & vb. n.', 'of Lump');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lump', 'v. i.', 'To throw into a mass; to unite in a body or sum without\n   distinction of particulars.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lump', 'v. i.', 'To take in the gross; to speak of collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lump', 'v. i.', 'To get along with as one can, although displeased; as, if\n   he does nt like it, he can lump it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumper', 'n.', 'The European eelpout; -- called also lumpen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumper', 'n.', 'One who lumps.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumper', 'n.', 'A laborer who is employed to load or unload vessels when in\n   harbor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumpfish', 'n.', 'A large, thick, clumsy, marine fish (Cyclopterus lumpus)\n   of Europe and America. The color is usually translucent sea green,\n   sometimes purplish. It has a dorsal row of spiny tubercles, and three\n   rows on each side, but has no scales. The ventral fins unite and form a\n   ventral sucker for adhesion to stones and seaweeds. Called also\n   lumpsucker, cock-paddle, sea owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumping', 'a.', 'Bulky; heavy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumpish', 'a.', 'Like a lump; inert; gross; heavy; dull; spiritless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumpsucker', 'n.', 'The lumprish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lumpy', 'superl.', 'Full of lumps, or small compact masses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luna', 'n.', 'The moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luna', 'n.', 'Silver.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunacies', 'pl. ', 'of Lunacy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunacy', 'n.', 'Insanity or madness; properly, the kind of insanity which\n   is broken by intervals of reason, -- formerly supposed to be influenced\n   by the changes of the moon; any form of unsoundness of mind, except\n   idiocy; mental derangement or alienation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunacy', 'n.', 'A morbid suspension of good sense or judgment, as through\n   fanaticism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunar', 'a.', 'Of or pertaining to the moon; as, lunar observations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunar', 'a.', 'Resembling the moon; orbed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunar', 'a.', 'Measured by the revolutions of the moon; as, a lunar month.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunar', 'a.', 'Influenced by the moon, as in growth, character, or\n   properties; as, lunar herbs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunar', 'n.', 'A lunar distance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunar', 'n.', 'The middle bone of the proximal series of the carpus; --\n   called also semilunar, and intermedium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunarian', 'n.', 'An inhabitant of the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunary', 'a.', 'Lunar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunary', 'n.', 'The herb moonwort or \"honesty\".');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunary', 'n.', 'A low fleshy fern (Botrychium Lunaria) with lunate segments\n   of the leaf or frond.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunate', 'a.', 'Alt. of Lunated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunated', 'a.', 'Crescent-shaped; as, a lunate leaf; a lunate beak; a\n   lunated cross.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunatic', 'a.', 'Affected by lunacy; insane; mad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunatic', 'a.', 'Of or pertaining to, or suitable for, an insane person;\n   evincing lunacy; as, lunatic gibberish; a lunatic asylum.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunatic', 'n.', 'A person affected by lunacy; an insane person, esp. one\n   who has lucid intervals; a madman; a person of unsound mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunation', 'n.', 'The period of a synodic revolution of the moon, or the\n   time from one new moon to the next; varying in length, at different\n   times, from about 29/ to 29/ days, the average length being 29 d.,\n   12h., 44m., 2.9s.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunch', 'n.', 'A luncheon; specifically, a light repast between breakfast\n   and dinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunched', 'imp. & p. p.', 'of Lunch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunching', 'p. pr. & vb. n.', 'of Lunch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunch', 'v. i.', 'To take luncheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luncheon', 'n.', 'A lump of food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luncheon', 'n.', 'A portion of food taken at any time except at a regular\n   meal; an informal or light repast, as between breakfast and dinner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luncheon', 'v. i.', 'To take luncheon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lune', 'n.', 'Anything in the shape of a half moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lune', 'n.', 'A figure in the form of a crescent, bounded by two\n   intersecting arcs of circles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lune', 'n.', 'A fit of lunacy or madness; a period of frenzy; a crazy or\n   unreasonable freak.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunet', 'n.', 'A little moon or satellite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunette', 'n.', 'A fieldwork consisting of two faces, forming a salient\n   angle, and two parallel flanks. See Bastion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunette', 'n.', 'A half horseshoe, which wants the sponge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunette', 'n.', 'A kind of watch crystal which is more than ordinarily\n   flattened in the center; also, a species of convexoconcave lens for\n   spectacles.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunette', 'n.', 'A piece of felt to cover the eye of a vicious horse.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunette', 'n.', 'Any surface of semicircular or segmental form; especially,\n   the piece of wall between the curves of a vault and its springing line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunette', 'n.', 'An iron shoe at the end of the stock of a gun carriage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lung', 'n.', 'An organ for aerial respiration; -- commonly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunge', 'n.', 'A sudden thrust or pass, as with a sword.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunged', 'imp. & p. p.', 'of Lunge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunging', 'p. pr. & vb. n.', 'of Lunge');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunge', 'v. i.', 'To make a lunge.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunge', 'v. t.', 'To cause to go round in a ring, as a horse, while holding\n   his halter.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunge', 'n.', 'Same as Namaycush.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunged', 'a.', 'Having lungs, or breathing organs similar to lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lungfish', 'n.', 'Any fish belonging to the Dipnoi; -- so called because\n   they have both lungs and gills.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lung-grown', 'a.', 'Having lungs that adhere to the pleura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lungie', 'n.', 'A guillemot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lungis', 'n.', 'A lingerer; a dull, drowsy fellow.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lungless', 'a.', 'Being without lungs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lungoor', 'n.', 'A long-tailed monkey (Semnopithecus schislaceus), from the\n   mountainous districts of India.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lungworm', 'n.', 'Any one of several species of parasitic nematoid worms\n   which infest the lungs and air passages of cattle, sheep, and other\n   animals, often proving fatal. The lungworm of cattle (Strongylus\n   micrurus) and that of sheep (S. filaria) are the best known.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lungwort', 'n.', 'An herb of the genus Pulmonaria (P. officinalis), of\n   Europe; -- so called because the spotted appearance of the leaves\n   resembles that of a diseased lung.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lungwort', 'n.', 'Any plant of the genus Mertensia (esp. M. Virginica and\n   M. Sibirica) plants nearly related to Pulmonaria. The American lungwort\n   is Mertensia Virginica, Virginia cowslip.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunicurrent', 'a.', 'Having relation to changes in currents that depend on\n   the moons phases.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luniform', 'a.', 'Resembling the moon in shape.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunisolar', 'a.', 'Resulting from the united action, or pertaining to the\n   mutual relations, of the sun and moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunistice', 'n.', 'The farthest point of the moons northing and southing,\n   in its monthly revolution.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunitidal', 'a.', 'Pertaining to tidal movements dependent on the moon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunt', 'n.', 'The match cord formerly used in firing cannon.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunt', 'n.', 'A puff of smoke.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunulae', 'pl. ', 'of Lunula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunula', 'n.', 'Same as Lunule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunular', 'a.', 'Having a form like that of the new moon; shaped like a\n   crescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunulate', 'a.', 'Alt. of Lunulated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunulated', 'a.', 'Resembling a small crescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunule', 'n.', 'Anything crescent-shaped; a crescent-shaped part or mark; a\n   lunula, a lune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunule', 'n.', 'A lune. See Lune.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunule', 'n.', 'A small or narrow crescent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunule', 'n.', 'A special area in front of the beak of many bivalve shells.\n   It sometimes has the shape of a double crescent, but is oftener\n   heart-shaped. See Illust. of Bivalve.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunulet', 'n.', 'A small spot, shaped like a half-moon or crescent; as, the\n   lunulet on the wings of many insects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lunulite', 'n.', 'Any bryozoan of the genus Lunulites, having a more or\n   less circular form.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Luny', 'a.', 'Crazy; mentally unsound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lupercal', 'a.', 'Of or pertaining to the Lupercalia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lupercal', 'n.', 'A grotto on the Palatine Hill sacred to Lupercus, the\n   Lycean Pan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lupercalia', 'n. pl.', 'A feast of the Romans in honor of Lupercus, or Pan.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lupine', 'n.', 'A leguminous plant of the genus Lupinus, especially L.\n   albus, the seeds of which have been used for food from ancient times.\n   The common species of the Eastern United States is L. perennis. There\n   are many species in California.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lupine', 'n.', 'Wolfish; ravenous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lupinin', 'n.', 'A glucoside found in the seeds of several species of\n   lupine, and extracted as a yellowish white crystalline substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lupinine', 'n.', 'An alkaloid found in several species of lupine (Lupinus\n   luteus, L. albus, etc.), and extracted as a bitter crystalline\n   substance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lupulin', 'n.', 'A bitter principle extracted from hops.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lupulin', 'n.', 'The fine yellow resinous powder found upon the strobiles\n   or fruit of hops, and containing this bitter principle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lupuline', 'n.', 'An alkaloid extracted from hops as a colorless volatile\n   liquid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lupulinic', 'a.', 'Pertaining to, or obtained from, hops; specifically,\n   designating an acid obtained by the decomposition of lupulin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lupus', 'n.', 'A cutaneous disease occurring under two distinct forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lupus', 'n.', 'The Wolf, a constellation situated south of Scorpio.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lurcation', 'n.', 'Gluttony; gormandizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lurch', 'v. i.', 'To swallow or eat greedily; to devour; hence, to swallow\n   up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lurch', 'n.', 'An old game played with dice and counters; a variety of the\n   game of tables.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lurch', 'n.', 'A double score in cribbage for the winner when his adversary\n   has been left in the lurch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lurch', 'v. t.', 'To leave in the lurch; to cheat.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lurch', 'v. t.', 'To steal; to rob.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lurch', 'n.', 'A sudden roll of a ship to one side, as in heavy weather;\n   hence, a swaying or staggering movement to one side, as that by a\n   drunken man. Fig.: A sudden and capricious inclination of the mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lurched', 'imp. & p. p.', 'of Lurch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lurching', 'p. pr. & vb. n.', 'of Lurch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lurch', 'v. i.', 'To roll or sway suddenly to one side, as a ship or a\n   drunken man.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lurch', 'v. i.', 'To withdraw to one side, or to a private place; to lurk.');
