  INSERT INTO words (word, word_type, description) VALUES  (    'Lycea', 'pl. ', 'of Lyceum');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyceum', 'n.', 'A place of exercise with covered walks, in the suburbs of\n   Athens, where Aristotle taught philosophy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyceum', 'n.', 'A house or apartment appropriated to instruction by\n   lectures or disquisitions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyceum', 'n.', 'A higher school, in Europe, which prepares youths for the\n   university.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyceum', 'n.', 'An association for debate and literary improvement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyche', 'a.', 'Like.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lychee', 'n.', 'See Litchi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lych gate', '', 'See under Lich.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lychnis', 'n.', 'A genus of Old World plants belonging to the Pink family\n   (Caryophyllaceae). Most of the species have brilliantly colored flowers\n   and cottony leaves, which may have anciently answered as wicks for\n   lamps. The botanical name is in common use for the garden species. The\n   corn cockle (Lychnis Githago) is a common weed in wheat fields.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lychnobite', 'n.', 'One who labors at night and sleeps in the day.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lychnoscope', 'n.', 'Same as Low side window, under Low, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lycine', 'n.', 'A weak base identical with betaine; -- so called because\n   found in the boxthorn (Lycium barbarum). See Betaine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lycoperdon', 'n.', 'A genus of fungi, remarkable for the great quantity of\n   spores, forming a fine dust, which is thrown out like smoke when the\n   plant is compressed or burst; puffball.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lycopod', 'n.', 'A plant of the genus Lycopodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lycopode', 'n.', 'Same as Lycopodium powder. See under Lycopodium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lycopodiaceous', 'a.', 'Belonging, or relating, to the Lycopodiaceae, an\n   order of cryptogamous plants (called also club mosses) with branching\n   stems, and small, crowded, one-nerved, and usually pointed leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lycopodite', 'n.', 'An old name for a fossil club moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lycopodium', 'n.', 'A genus of mosslike plants, the type of the order\n   Lycopodiaceae; club moss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lycotropous', 'a.', 'Campylotropous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyden', 'n.', 'See Leden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lydian', 'a.', 'Of or pertaining to Lydia, a country of Asia Minor, or to\n   its inhabitants; hence, soft; effeminate; -- said especially of one of\n   the ancient Greek modes or keys, the music in which was of a soft,\n   pathetic, or voluptuous character.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lydine', 'n.', 'A violet dye derived from aniline.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lye', 'n.', 'A strong caustic alkaline solution of potassium salts,\n   obtained by leaching wood ashes. It is much used in making soap, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lye', 'n.', 'A short side line, connected with the main line; a turn-out; a\n   siding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lye', 'n.', 'A falsehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyencephala', 'n. pl.', 'A group of Mammalia, including the marsupials and\n   monotremes; -- so called because the corpus callosum is rudimentary.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyencephalous', 'a.', 'Pertaining to, or characteristic of, the\n   Lyencephala.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyerman', 'n.', 'The cicada.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lygodium', 'n.', 'A genus of ferns with twining or climbing fronds, bearing\n   stalked and variously-lobed divisions in pairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lying', 'p. pr. & vb. n.', 'of Lie, to tell a falsehood.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lying', 'p. pr. & vb. n.', 'of Lie, to be supported horizontally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lying-in', 'n.', 'The state attending, and consequent to, childbirth;\n   confinement.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lying-in', 'n.', 'The act of bearing a child.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyingly', 'adv.', 'In a lying manner; falsely.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyken', 'v. t.', 'To please; -- chiefly used impersonally.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lym', 'n.', 'Alt. of Lymhound');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymhound', 'n.', 'A dog held in a leam; a bloodhound; a limehound.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymail', 'n.', 'See Limaille.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyme grass', '', 'A coarse perennial grass of several species of Elymus,\n   esp. E. Canadensis, and the European E. arenarius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymph', 'n.', 'A spring of water; hence, water, or a pure, transparent\n   liquid like water.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymph', 'n.', 'An alkaline colorless fluid, contained in the lymphatic\n   vessels, coagulable like blood, but free from red blood corpuscles. It\n   is absorbed from the various tissues and organs of the body, and is\n   finally discharged by the thoracic and right lymphatic ducts into the\n   great veins near the heart.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymph', 'n.', 'A fibrinous material exuded from the blood vessels in\n   inflammation. In the process of healing it is either absorbed, or is\n   converted into connective tissue binding the inflamed surfaces\n   together.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphadenitis', 'n.', 'Inflammation of the lymphatic glands; -- called also\n   lymphitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphadenoma', 'n.', 'See Lymphoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphangeitis', 'n.', 'Inflammation of the lymphatic vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphangial', 'a.', 'Of or pertaining to the lymphatics, or lymphoid\n   tissue; lymphatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphate', 'a.', 'Alt. of Lymphated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphated', 'a.', 'Frightened into madness; raving.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphatic', 'a.', 'pertaining to, containing, or conveying lymph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphatic', 'a.', 'Madly enthusiastic; frantic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphatic', 'n.', 'One of the lymphatic or absorbent vessels, which carry\n   lymph and discharge it into the veins; lymph duct; lymphatic duct.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphatic', 'n.', 'A mad enthusiast; a lunatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphitis', 'n.', 'See Lymphadenitis.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphogenic', 'a.', 'Connected with, or formed in, the lymphatic glands.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphography', 'n.', 'A description of the lymphatic vessels, their origin\n   and uses.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphoid', 'a.', 'Resembling lymph; also, resembling a lymphatic gland;\n   adenoid; as, lymphoid tissue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphoma', 'n.', 'A tumor having a structure resembling that of a lymphatic\n   gland; -- called also lymphadenoma.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lymphy', 'a.', 'Containing, or like, lymph.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyn', 'n.', 'A waterfall. See Lin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyncean', 'a.', 'Of or pertaining to the lynx.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lynched', 'imp. & p. p.', 'of Lynch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lynching', 'p. pr. & vb. n.', 'of Lynch');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lynch', 'v. t.', 'To inflict punishment upon, especially death, without the\n   forms of law, as when a mob captures and hangs a suspected person. See\n   Lynch law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyncher', 'n.', 'One who assists in lynching.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lynch law', '', 'The act or practice by private persons of inflicting\n   punishment for crimes or offenses, without due process of law.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lynde', 'n.', 'Alt. of Lynden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lynden', 'n.', 'See Linden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyne', 'n.', 'Linen.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lynx', 'n.', 'Any one of several species of feline animals of the genus\n   Felis, and subgenus Lynx. They have a short tail, and usually a pencil\n   of hair on the tip of the ears.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lynx', 'n.', 'One of the northern constellations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lynx-eyed', 'a.', 'Having acute sight.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyonnaise', 'a.', 'Applied to boiled potatoes cut into small pieces and\n   heated in oil or butter. They are usually flavored with onion and\n   parsley.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyopomata', 'n. pl.', 'An order of brachiopods, in which the valves of\n   shell are not articulated by a hinge. It includes the Lingula, Discina,\n   and allied forms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyra', 'n.', 'A northern constellation, the Harp, containing a white star\n   of the first magnitude, called Alpha Lyrae, or Vega.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyra', 'n.', 'The middle portion of the ventral surface of the fornix of\n   the brain; -- so called from the arrangement of the lines with which it\n   is marked in the human brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyraid', 'n.', 'Same as Lyrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyrate', 'a.', 'Alt. of Lyrated');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyrated', 'a.', 'Lyre-shaped, or spatulate and oblong, with small lobes\n   toward the base; as, a lyrate leaf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyrated', 'a.', 'Shaped like a lyre, as the tail of the blackcock, or that\n   of the lyre bird.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyre', 'n.', 'A stringed instrument of music; a kind of harp much used by\n   the ancients, as an accompaniment to poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyre', 'n.', 'One of the constellations; Lyra. See Lyra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyre bird', '', 'Any one of two or three species of Australian birds of the\n   genus Menura. The male is remarkable for having the sixteen tail\n   feathers very long and, when spread, arranged in the form of a lyre.\n   The common lyre bird (Menura superba), inhabiting New South Wales, is\n   about the size of a grouse. Its general color is brown, with rufous\n   color on the throat, wings, tail coverts and tail. Called also lyre\n   pheasant and lyre-tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyric', 'a.', 'Alt. of Lyrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyrical', 'a.', 'Of or pertaining to a lyre or harp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyrical', 'a.', 'Fitted to be sung to the lyre; hence, also, appropriate\n   for song; -- said especially of poetry which expresses the individual\n   emotions of the poet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyric', 'n.', 'A lyric poem; a lyrical composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyric', 'n.', 'A composer of lyric poems.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyric', 'n.', 'A verse of the kind usually employed in lyric poetry; --\n   used chiefly in the plural.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyric', 'n.', 'The words of a song.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyrically', 'adv.', 'In a lyrical manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyricism', 'n.', 'A lyric composition.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyrid', 'n.', 'One of the group of shooting stars which come into the air\n   in certain years on or about the 19th of April; -- so called because\n   the apparent path among the stars the stars if produced back wards\n   crosses the constellation Lyra.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyrie', 'n.', 'A European fish (Peristethus cataphractum), having the body\n   covered with bony plates, and having three spines projecting in front\n   of the nose; -- called also noble, pluck, pogge, sea poacher, and armed\n   bullhead.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyriferous', 'a.', 'Having a lyre-shaped shoulder girdle, as certain\n   fishes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyrism', 'n.', 'The act of playing on a lyre or harp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyrist', 'n.', 'A musician who plays on the harp or lyre; a composer of\n   lyrical poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lysimeter', 'n.', 'An instrument for measuring the water that percolates\n   through a certain depth of soil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lysis', 'n.', 'The resolution or favorable termination of a disease, coming\n   on gradually and not marked by abrupt change.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyssa', 'n.', 'Hydrophobia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyterian', 'a.', 'Terminating a disease; indicating the end of a disease.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lythe', 'n.', 'The European pollack; -- called also laith, and leet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lythe', 'a.', 'Soft; flexible.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lythonthriptic', 'a.', 'Alt. of Lythontriptic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lythontriptic', 'a.', 'See Lithontriptic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lyttae', 'pl. ', 'of Lytta');
  INSERT INTO words (word, word_type, description) VALUES  (    'Lytta', 'n.', 'A fibrous and muscular band lying within the longitudinal\n   axis of the tongue in many mammals, as the dog.\n   M () M, the thirteenth letter of the English alphabet, is a vocal\n   consonant, and from the manner of its formation, is called the\n   labio-nasal consonant. See Guide to Pronunciation, // 178-180, 242.');
  INSERT INTO words (word, word_type, description) VALUES  (    'M', '', 'As a numeral, M stands for one thousand, both in English and\n   Latin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'M', 'n.', 'A quadrat, the face or top of which is a perfect square; also,\n   the size of such a square in any given size of type, used as the unit\n   of measurement for that type: 500 ms of pica would be a piece of\n   matter whose length and breadth in pica ms multiplied together produce\n   that number.');
  INSERT INTO words (word, word_type, description) VALUES  (    'M', 'n.', 'A brand or stigma, having the shape of an M, formerly impressed\n   on one convicted of manslaughter and admitted to the benefit of clergy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ma', 'n.', 'A childs word for mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ma', 'n.', 'In Oriental countries, a respectful form of address given to a\n   woman; mother.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Ma', 'conj.', 'But; -- used in cautionary phrases; as, \"Vivace, ma non\n   troppo presto\" (i. e., lively, but not too quick).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maa', 'n.', 'The common European gull (Larus canus); -- called also mar.\n   See New, a gull.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maad', 'p. p.', 'Made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maalin', 'n.', 'The sparrow hawk.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maalin', 'n.', 'The kestrel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maam', 'n.', 'Madam; my lady; -- a colloquial contraction of madam often\n   used in direct address, and sometimes as an appellation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maara shell', '', 'A large, pearly, spiral, marine shell (Turbo\n   margaritaceus), from the Pacific Islands. It is used as an ornament.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maasha', 'n.', 'An East Indian coin, of about one tenth of the weight of a\n   rupee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maat', 'a.', 'Dejected; sorrowful; downcast.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad', 'n.', 'A slattern.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad', 'n.', 'The name of a female fairy, esp. the queen of the fairies; and\n   hence, sometimes, any fairy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mabble', 'v. t.', 'To wrap up.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mabby', 'n.', 'A spirituous liquor or drink distilled from potatoes; --\n   used in the Barbadoes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mabolo', 'n.', 'A kind of persimmon tree (Diospyros discolor) from the\n   Philippine Islands, now introduced into the East and West Indies. It\n   bears an edible fruit as large as a quince.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mac', '', 'A prefix, in names of Scotch origin, signifying son.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaco', 'n.', 'Any one of several species of lemurs, as the ruffed lemur\n   (Lemur macaco), and the ring-tailed lemur (L. catta).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macacus', 'n.', 'A genus of monkeys, found in Asia and the East Indies.\n   They have short tails and prominent eyebrows.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macadamization', 'n.', 'The process or act of macadamizing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macadamized', 'imp. & p. p.', 'of Macadamize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macadamizing', 'p. pr. & vb. n.', 'of Macadamize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macadamize', 'v. t.', 'To cover, as a road, or street, with small, broken\n   stones, so as to form a smooth, hard, convex surface.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maoadam road', '', 'A macadamized road.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macao', 'n.', 'A macaw.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaque', 'n.', 'Any one of several species of short-tailed monkeys of the\n   genus Macacus; as, M. maurus, the moor macaque of the East Indies.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaranga gum', '', 'A gum of a crimson color, obtained from a tree\n   (Macaranga Indica) that grows in the East Indies. It is used in taking\n   impressions of coins, medallions, etc., and sometimes as a medicine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macarize', 'v. t.', 'To congratulate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaronis', 'pl. ', 'of Macaroni');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaronies', 'pl. ', 'of Macaroni');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaroni', 'n.', 'Long slender tubes made of a paste chiefly of wheat\n   flour, and used as an article of food; Italian or Genoese paste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaroni', 'n.', 'A medley; something droll or extravagant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaroni', 'n.', 'A sort of droll or fool.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaroni', 'n.', 'A finical person; a fop; -- applied especially to English\n   fops of about 1775.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaroni', 'n.', 'The designation of a body of Maryland soldiers in the\n   Revolutionary War, distinguished by a rich uniform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaronian', 'a.', 'Alt. of Macaronic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaronic', 'a.', 'Pertaining to, or like, macaroni (originally a dish of\n   mixed food); hence, mixed; confused; jumbled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaronic', 'a.', 'Of or pertaining to the burlesque composition called\n   macaronic; as, macaronic poetry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaronic', 'n.', 'A heap of thing confusedly mixed together; a jumble.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaronic', 'n.', 'A kind of burlesque composition, in which the vernacular\n   words of one or more modern languages are intermixed with genuine Latin\n   words, and with hybrid formed by adding Latin terminations to other\n   roots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaroon', 'n.', 'A small cake, composed chiefly of the white of eggs,\n   almonds, and sugar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaroon', 'n.', 'A finical fellow, or macaroni.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macartney', 'n.', 'A fire-backed pheasant. See Fireback.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macassar oil', '', 'A kind of oil formerly used in dressing the hair; -- so\n   called because originally obtained from Macassar, a district of the\n   Island of Celebes. Also, an imitation of the same, of perfumed castor\n   oil and olive oil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macauco', 'n.', 'Any one of several species of small lemurs, as Lemur\n   murinus, which resembles a rat in size.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macavahu', 'n.', 'A small Brazilian monkey (Callithrix torquatus), --\n   called also collared teetee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macaw', 'n.', 'Any parrot of the genus Sittace, or Macrocercus. About\n   eighteen species are known, all of them American. They are large and\n   have a very long tail, a strong hooked bill, and a naked space around\n   the eyes. The voice is harsh, and the colors are brilliant and strongly\n   contrasted.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maccabean', 'a.', 'Of or pertaining to Judas Maccabeus or to the Maccabees;\n   as, the Maccabean princes; Maccabean times.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maccabees', 'n. pl.', 'The name given later times to the Asmonaeans, a\n   family of Jewish patriots, who headed a religious revolt in the reign\n   of Antiochus IV., 168-161 B. C., which led to a period of freedom for\n   Israel.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maccabees', 'n. pl.', 'The name of two ancient historical books, which give\n   accounts of Jewish affairs in or about the time of the Maccabean\n   princes, and which are received as canonical books in the Roman\n   Catholic Church, but are included in the Apocrypha by Protestants. Also\n   applied to three books, two of which are found in some MSS. of the\n   Septuagint.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maccaboy', 'n.', 'Alt. of Maccoboy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maccoboy', 'n.', 'A kind of snuff.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macco', 'n.', 'A gambling game in vogue in the eighteenth century.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mace', 'n.', 'A money of account in China equal to one tenth of a tael;\n   also, a weight of 57.98 grains.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mace', 'n.', 'A kind of spice; the aril which partly covers nutmegs. See\n   Nutmeg.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mace', 'n.', 'A heavy staff or club of metal; a spiked club; -- used as\n   weapon in war before the general use of firearms, especially in the\n   Middle Ages, for breaking metal armor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mace', 'n.', 'A staff borne by, or carried before, a magistrate as an\n   ensign of his authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mace', 'n.', 'An officer who carries a mace as an emblem of authority.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mace', 'n.', 'A knobbed mallet used by curriers in dressing leather to make\n   it supple.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mace', 'n.', 'A rod for playing billiards, having one end suited to resting\n   on the table and pushed with one hand.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macedonian', 'a.', 'Belonging, or relating, to Macedonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macedonian', 'n.', 'A native or inhabitant of Macedonia.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macedonian', 'n.', 'One of a certain religious sect, followers of\n   Macedonius, Bishop of Constantinople, in the fourth century, who held\n   that the Holy Ghost was a creature, like the angels, and a servant of\n   the Father and the Son.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macedonianism', 'n.', 'The doctrines of Macedonius.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macer', 'n.', 'A mace bearer; an officer of a court.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macerated', 'imp. & p. p.', 'of Macerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macerating', 'p. pr. & vb. n.', 'of Macerate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macerate', 'v. t.', 'To make lean; to cause to waste away.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macerate', 'v. t.', 'To subdue the appetites of by poor and scanty diet; to\n   mortify.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macerate', 'v. t.', 'To soften by steeping in a liquid, with or without\n   heat; to wear away or separate the parts of by steeping; as, to\n   macerate animal or vegetable fiber.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macerater', 'n.', 'One who, or that which, macerates; an apparatus for\n   converting paper or fibrous matter into pulp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maceration', 'n.', 'The act or process of macerating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machaerodus', 'n.', 'Alt. of Machairodus');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machairodus', 'n.', 'A genus of extinct mammals allied to the cats, and\n   having in the upper jaw canine teeth of remarkable size and strength;\n   -- hence called saber-toothed tigers.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machete', 'n.', 'A large heavy knife resembling a broadsword, often two or\n   three feet in length, -- used by the inhabitants of Spanish America as\n   a hatchet to cut their way through thickets, and for various other\n   purposes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machiavelian', 'a.', 'Of or pertaining to Machiavel, or to his supposed\n   principles; politically cunning; characterized by duplicity or bad\n   faith; crafty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machiavelian', 'n.', 'One who adopts the principles of Machiavel; a cunning\n   and unprincipled politician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machiavelism', 'n.', 'Alt. of Machiavelianism');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machiavelianism', 'n.', 'The supposed principles of Machiavel, or practice\n   in conformity to them; political artifice, intended to favor arbitrary\n   power.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machicolated', 'a.', 'Having machicolations.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machicolation', 'n.', 'An opening between the corbels which support a\n   projecting parapet, or in the floor of a gallery or the roof of a\n   portal, shooting or dropping missiles upen assailants attacking the\n   base of the walls. Also, the construction of such defenses, in general,\n   when of this character. See Illusts. of Battlement and Castle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machicolation', 'n.', 'The act of discharging missiles or pouring burning\n   or melted substances upon assailants through such apertures.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machicoulis', 'n.', 'Same as Machicolation.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machinal', 'a.', 'Of or pertaining to machines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machinated', 'imp. & p. p.', 'of Machinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machinating', 'p. pr. & vb. n.', 'of Machinate');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machinate', 'v. i.', 'To plan; to contrive; esp., to form a scheme with the\n   purpose of doing harm; to contrive artfully; to plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machinate', 'v. t.', 'To contrive, as a plot; to plot; as, to machinate\n   evil.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machination', 'n.', 'The act of machinating.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machination', 'n.', 'That which is devised; a device; a hostile or\n   treacherous scheme; an artful design or plot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machinator', 'n.', 'One who machinates, or forms a scheme with evil\n   designs; a plotter or artful schemer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machine', 'n.', 'In general, any combination of bodies so connected that\n   their relative motions are constrained, and by means of which force and\n   motion may be transmitted and modified, as a screw and its nut, or a\n   lever arranged to turn about a fulcrum or a pulley about its pivot,\n   etc.; especially, a construction, more or less complex, consisting of a\n   combination of moving parts, or simple mechanical elements, as wheels,\n   levers, cams, etc., with their supports and connecting framework,\n   calculated to constitute a prime mover, or to receive force and motion\n   from a prime mover or from another machine, and transmit, modify, and\n   apply them to the production of some desired mechanical effect or work,\n   as weaving by a loom, or the excitation of electricity by an electrical\n   machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machine', 'n.', 'Any mechanical contrivance, as the wooden horse with which\n   the Greeks entered Troy; a coach; a bicycle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machine', 'n.', 'A person who acts mechanically or at will of another.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machine', 'n.', 'A combination of persons acting together for a common\n   purpose, with the agencies which they use; as, the social machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machine', 'n.', 'A political organization arranged and controlled by one or\n   more leaders for selfish, private or partisan ends.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machine', 'n.', 'Supernatural agency in a poem, or a superhuman being\n   introduced to perform some exploit.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machined', 'imp. & p. p.', 'of Machine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machining', 'p. pr. & vb. n.', 'of Machine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machine', 'v. t.', 'To subject to the action of machinery; to effect by aid\n   of machinery; to print with a printing machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machiner', 'n.', 'One who or operates a machine; a machinist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machinery', 'n.', 'Machines, in general, or collectively.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machinery', 'n.', 'The working parts of a machine, engine, or instrument;\n   as, the machinery of a watch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machinery', 'n.', 'The supernatural means by which the action of a poetic\n   or fictitious work is carried on and brought to a catastrophe; in an\n   extended sense, the contrivances by which the crises and conclusion of\n   a fictitious narrative, in prose or verse, are effected.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machinery', 'n.', 'The means and appliances by which anything is kept in\n   action or a desired result is obtained; a complex system of parts\n   adapted to a purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machining', 'a.', 'Of or pertaining to the machinery of a poem; acting or\n   used as a machine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machinist', 'n.', 'A constrictor of machines and engines; one versed in the\n   principles of machines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machinist', 'n.', 'One skilled in the use of machine tools.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Machinist', 'n.', 'A person employed to shift scenery in a theater.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macho', 'n.', 'The striped mullet of California (Mugil cephalus, /\n   Mexicanus).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macilency', 'n.', 'Leanness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macilent', 'a.', 'Lean; thin.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macintosh', 'n.', 'Same as Mackintosh.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mackerel', 'n.', 'A pimp; also, a bawd.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mackerel', 'n.', 'Any species of the genus Scomber, and of several related\n   genera. They are finely formed and very active oceanic fishes. Most of\n   them are highly prized for food.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mackinaw blanket', '', 'Alt. of Mackinaw');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mackinaw', '', 'A thick blanket formerly in common use in the western part\n   of the United States.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mackintosh', 'n.', 'A waterproof outer garment; -- so called from the name\n   of the inventor.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mackle', 'n.', 'Same Macule.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mackle', 'v. t. & i.', 'To blur, or be blurred, in printing, as if there\n   were a double impression.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macle', 'n.', 'Chiastolite; -- so called from the tessellated appearance of\n   a cross section. See Chiastolite.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macle', 'n.', 'A crystal having a similar tessellated appearance.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macle', 'n.', 'A twin crystal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macled', 'a.', 'Marked like macle (chiastolite).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macled', 'a.', 'Having a twin structure. See Twin, a.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macled', 'a.', 'See Mascled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maclurea', 'n.', 'A genus of spiral gastropod shells, often of large size,\n   characteristic of the lower Silurian rocks.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maclurin', 'n.', 'See Morintannic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrame lace', '', 'A coarse lace made of twine, used especially in\n   decorating furniture.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrencephalic', 'a.', 'Alt. of Macrencephalous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrencephalous', 'a.', 'Having a large brain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macro-', '', 'A combining form signifying long, large, great; as\n   macrodiagonal, macrospore.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrobiotic', 'a.', 'Long-lived.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrobiotics', 'n.', 'The art of prolonging life.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrocephalous', 'a.', 'Having a large head.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrocephalous', 'a.', 'Having the cotyledons of a dicotyledonous embryo\n   confluent, and forming a large mass compared with the rest of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macro-chemistry', 'n.', 'The science which treats of the chemical\n   properties, actions or relations of substances in quantity; --\n   distinguished from micro-chemistry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrochires', 'n. pl.', 'A division of birds including the swifts and\n   humming birds. So called from the length of the distal part of the\n   wing.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrocosm', 'n.', 'The great world; that part of the universe which is\n   exterior to man; -- contrasted with microcosm, or man. See Microcosm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrocosmic', 'a.', 'Of or pertaining to the macrocosm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrocystis', 'n.', 'An immensely long blackish seaweed of the Pacific\n   (Macrocystis pyrifera), having numerous almond-shaped air vessels.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrodactyl', 'n.', 'One of a group of wading birds (Macrodactyli) having\n   very long toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrodactylic', 'a.', 'Alt. of Macrodactylous');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrodactylous', 'a.', 'Having long toes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrodiagonal', 'n.', 'The longer of two diagonals, as of a rhombic prism.\n   See Crystallization.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrodome', 'n.', 'A dome parallel to the longer lateral axis of an\n   orthorhombic crystal. See Dome, n., 4.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrodont', 'a.', 'Having large teeth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrodont', 'n.', 'A macrodont animal.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrofarad', 'n.', 'See Megafarad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macroglossia', 'n.', 'Enlargement or hypertrophy of the tongue.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrognathic', 'a.', 'Long-jawed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrology', 'n.', 'Long and tedious talk without much substance;\n   superfluity of words.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrometer', 'n.', 'An instrument for determining the size or distance of\n   inaccessible objects by means of two reflectors on a common sextant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macron', 'n.', 'A short, straight, horizontal mark [-], placed over vowels\n   to denote that they are to be pronounced with a long sound; as, a, in\n   dame; /, in s/am, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macropetalous', 'a.', 'Having long or large petals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrophyllous', 'a.', 'Having long or large leaves.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macropinacoid', 'n.', 'One of the two planes of an orthorhombic crystal\n   which are parallel to the vertical and longer lateral (macrodiagonal)\n   axes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macropod', 'n.', 'Any one of a group of maioid crabs remarkable for the\n   length of their legs; -- called also spider crab.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macropodal', 'a.', 'Having long or large feet, or a long stem.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macropodian', 'n.', 'A macropod.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macropodous', 'a.', 'Having long legs or feet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macroprism', 'n.', 'A prism of an orthorhombic crystal between the\n   macropinacoid and the unit prism; the corresponding pyramids are called\n   macropyramids.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macropteres', 'n. pl.', 'A division of birds; the Longipennes.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macropterous', 'a.', 'Having long wings.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macropus', 'n.', 'genus of marsupials including the common kangaroo.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macropyramid', 'n.', 'See Macroprism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macroscopic', 'a.', 'Alt. of Macroscopical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macroscopical', 'a.', 'Visible to the unassisted eye; -- as opposed to\n   microscopic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrosporangium', 'n.', 'A sporangium or conceptacle containing only large\n   spores; -- opposed to microsporangium. Both are found in the genera\n   Selaginella, Isoctes, and Marsilia, plants remotely allied to ferns.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrospore', 'n.', 'One of the specially large spores of certain flowerless\n   plants, as Selaginella, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrosporic', 'a.', 'Of or pertaining to macrospores.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrotone', 'n.', 'Same as Macron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrotous', 'a.', 'Large-eared.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macroura', 'a.', 'Alt. of Macroural');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macroural', 'a.', 'Same as Macrura, Macrural, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrozoospore', 'n.', 'A large motile spore having four vibratile cilia; --\n   found in certain green algae.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrura', 'n. pl.', 'A subdivision of decapod Crustacea, having the abdomen\n   largely developed. It includes the lobster, prawn, shrimp, and many\n   similar forms. Cf. Decapoda.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrural', 'a.', 'Same as Macrurous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macruran', 'n.', 'One of the Macrura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macruroid', 'a.', 'Like or pertaining to the Macrura.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macrurous', 'a.', 'Of or pertaining to the Macrura; having a long tail.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mactation', 'n.', 'The act of killing a victim for sacrifice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mactra', 'n.', 'Any marine bivalve shell of the genus Mactra, and allied\n   genera. Many species are known. Some of them are used as food, as\n   Mactra stultorum, of Europe. See Surf clam, under Surf.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maculae', 'pl. ', 'of Macula');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macula', 'n.', 'A spot, as on the skin, or on the surface of the sun or of\n   some other luminous orb.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macula', 'n.', 'A rather large spot or blotch of color.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maculate', 'v.', 'To spot; to stain; to blur.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maculate', 'a.', 'Marked with spots or maculae; blotched; hence, defiled;\n   impure; as, most maculate thoughts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maculated', 'a.', 'Having spots or blotches; maculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maculation', 'n.', 'The act of spotting; a spot; a blemish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maculatory', 'a.', 'Causing a spot or stain.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maculature', 'n.', 'Blotting paper.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macule', 'n.', 'A spot.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macule', 'n.', 'A blur, or an appearance of a double impression, as when\n   the paper slips a little; a mackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Macule', 'v.', 'To blur; especially (Print.), to blur or double an\n   impression from type. See Mackle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maculose', 'a.', 'Of or pertaining to spots upon a surface; spotted;\n   maculate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad', '', 'p. p. of Made.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad', 'superl.', 'Disordered in intellect; crazy; insane.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad', 'superl.', 'Excited beyond self-control or the restraint of reason;\n   inflamed by violent or uncontrollable desire, passion, or appetite; as,\n   to be mad with terror, lust, or hatred; mad against political reform.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad', 'superl.', 'Proceeding from, or indicating, madness; expressing\n   distraction; prompted by infatuation, fury, or extreme rashness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad', 'superl.', 'Extravagant; immoderate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad', 'superl.', 'Furious with rage, terror, or disease; -- said of the\n   lower animals; as, a mad bull; esp., having hydrophobia; rabid; as, a\n   mad dog.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad', 'superl.', 'Angry; out of patience; vexed; as, to get mad at a\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad', 'superl.', 'Having impaired polarity; -- applied to a compass needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madded', 'imp. & p. p.', 'of Mad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madding', 'p. pr. & vb. n.', 'of Mad');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad', 'v. t.', 'To make mad or furious; to madden.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad', 'v. i.', 'To be mad; to go mad; to rave. See Madding.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad', 'n.', 'An earthworm.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madams', 'pl. ', 'of Madam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesdames', 'pl. ', 'of Madam');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madam', 'n.', 'A gentlewoman; -- an appellation or courteous form of\n   address given to a lady, especially an elderly or a married lady; --\n   much used in the address, at the beginning of a letter, to a woman. The\n   corresponding word in addressing a man is Sir.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesdames', 'pl. ', 'of Madame');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madame', 'n.', 'My lady; -- a French title formerly given to ladies of\n   quality; now, in France, given to all married women.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad-apple', 'n.', 'See Eggplant.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madbrain', 'a.', 'Hot-headed; rash.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madbrain', 'n.', 'A rash or hot-headed person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madbrained', 'a.', 'Disordered in mind; hot-headed.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madcap', 'a.', 'Inclined to wild sports; delighting in rash, absurd, or\n   dangerous amusements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madcap', 'a.', 'Wild; reckless.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madcap', 'n.', 'A person of wild behavior; an excitable, rash, violent\n   person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maddened', 'imp. & p. p.', 'of Madden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maddening', 'p. pr. & vb. n.', 'of Madden');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madden', 'v. t.', 'To make mad; to drive to madness; to craze; to excite\n   violently with passion; to make very angry; to enrage.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madden', 'v. i.', 'To become mad; to act as if mad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madder', 'n.', 'A plant of the Rubia (R. tinctorum). The root is much used\n   in dyeing red, and formerly was used in medicine. It is cultivated in\n   France and Holland. See Rubiaceous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madderwort', 'n.', 'A name proposed for any plant of the same natural order\n   (Rubiaceae) as the madder.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madding', 'a.', 'Affected with madness; raging; furious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maddish', 'a.', 'Somewhat mad.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Made', 'n.', 'See Mad, n.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Made', '', 'imp. & p. p. of Make.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Made', 'a.', 'Artificially produced; pieced together; formed by filling in;\n   as, made ground; a made mast, in distinction from one consisting of a\n   single spar.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madecass', 'n.', 'Alt. of Madecassee');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madecassee', 'n.', 'A native or inhabitant of Madagascar, or Madecassee;\n   the language of the natives of Madagascar. See Malagasy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madecassee', 'a.', 'Of or pertaining to Madagascar or its inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madefaction', 'n.', 'Alt. of Madefication');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madefication', 'n.', 'The act of madefying, or making wet; the state of\n   that which is made wet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madefied', 'imp. & p. p.', 'of Madefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madefying', 'p. pr. & vb. n.', 'of Madefy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madefy', 'v. t.', 'To make wet or moist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madegassy', 'n. & a.', 'See Madecassee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madeira', 'n.', 'A rich wine made on the Island of Madeira.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mesdemoiselles', 'pl. ', 'of Mademoiselle');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mademoiselle', 'n.', 'A French title of courtesy given to a girl or an\n   unmarried lady, equivalent to the English Miss.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mademoiselle', 'n.', 'A marine food fish (Sciaena chrysura), of the\n   Southern United States; -- called also yellowtail, and silver perch.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madge', 'n.', 'The barn owl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madge', 'n.', 'The magpie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mad-headed', 'a.', 'Wild; crack-brained.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madhouse', 'n.', 'A house where insane persons are confined; an insane\n   asylum; a bedlam.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madia', 'n.', 'A genus of composite plants, of which one species (Madia\n   sativa) is cultivated for the oil yielded from its seeds by pressure.\n   This oil is sometimes used instead of olive oil for the table.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madid', 'a.', 'Wet; moist; as, a madid eye.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madisterium', 'n.', 'An instrument to extract hairs.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madjoun', 'n.', 'An intoxicating confection from the hemp plant; -- used by\n   the Turks and Hindoos.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madly', 'a.', 'In a mad manner; without reason or understanding; wildly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madmen', 'pl. ', 'of Madman');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madman', 'n.', 'A man who is mad; lunatic; a crazy person.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madnep', 'n.', 'The masterwort (Peucedanum Ostruthium).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madness', 'a.', 'The condition of being mad; insanity; lunacy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madness', 'a.', 'Frenzy; ungovernable rage; extreme folly.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madonna', 'n.', 'My lady; -- a term of address in Italian formerly used as\n   the equivalent of Madame, but for which Signora is now substituted.\n   Sometimes introduced into English.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madonna', 'n.', 'A picture of the Virgin Mary (usually with the babe).');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madoqua', 'n.', 'A small Abyssinian antelope (Neotragus Saltiana), about\n   the size of a hare.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madrague', 'n.', 'A large fish pound used for the capture of the tunny in\n   the Mediterranean; also applied to the seines used for the same\n   purpose.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madreperl', 'n.', 'Mother-of-pearl.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madrepora', 'n.', 'A genus of reef corals abundant in tropical seas. It\n   includes than one hundred and fifty species, most of which are\n   elegantly branched.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madreporaria', 'n. pl.', 'An extensive division of Anthozoa, including most\n   of the species that produce stony corals. See Illust. of Anthozoa.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madrepore', 'n.', 'Any coral of the genus Madrepora; formerly, often\n   applied to any stony coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madreporian', 'a.', 'Alt. of Madreporic');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madreporic', 'a.', 'Resembling, or pertaining to, the genus Madrepora.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madreporiform', 'a.', 'Resembling a madreporian coral in form or structure.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madreporite', 'n.', 'A fossil coral.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madreporite', 'n.', 'The madreporic plate of echinoderms.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madrier', 'n.', 'A thick plank, used for several mechanical purposes');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madrier', 'n.', 'A plank to receive the mouth of a petard, with which it is\n   applied to anything intended to be broken down.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madrier', 'n.', 'A plank or beam used for supporting the earth in mines or\n   fortifications.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madrigal', 'n.', 'A little amorous poem, sometimes called a pastoral poem,\n   containing some tender and delicate, though simple, thought.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madrigal', 'n.', 'An unaccompanied polyphonic song, in four, five, or more\n   parts, set to secular words, but full of counterpoint and imitation,\n   and adhering to the old church modes. Unlike the freer glee, it is best\n   sung with several voices on a part. See Glee.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madrigaler', 'n.', 'A madrigalist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madrigalist', 'n.', 'A composer of madrigals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madrilenian', 'a.', 'Of or pertaining to Madrid in Spain, or to its\n   inhabitants.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madrilenian', 'n.', 'A native or inhabitant of Madrid.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madrina', 'n.', 'An animal (usually an old mare), wearing a bell and acting\n   as the leader of a troop of pack mules.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madroa', 'n.', 'A small evergreen tree or shrub (Arbutus Menziesii), of\n   California, having a smooth bark, thick shining leaves, and edible red\n   berries, which are often called madroa apples.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Madwort', 'n.', 'A genus of cruciferous plants (Alyssum) with white or\n   yellow flowers and rounded pods. A. maritimum is the commonly\n   cultivated sweet alyssum, a fragrant white-flowered annual.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maegbote', 'n.', 'Alt. of Magbote');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magbote', 'n.', 'Compensation for the injury done by slaying a kinsman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maelstrom', 'n.', 'A celebrated whirlpool on the coast of Norway.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maelstrom', 'n.', 'Also Fig. ; as, a maelstrom of vice.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maenad', 'n.', 'A Bacchante; a priestess or votary of Bacchus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maenad', 'n.', 'A frantic or frenzied woman.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maestoso', 'a. & adv.', 'Majestic or majestically; -- a direction to\n   perform a passage or piece of music in a dignified manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maestricht monitor', '', 'The Mosasaurus Hofmanni. See Mosasaurus.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maestro', 'n.', 'A master in any art, especially in music; a composer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maffle', 'v. i.', 'To stammer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maffler', 'n.', 'A stammerer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magazine', 'n.', 'A receptacle in which anything is stored, especially\n   military stores, as ammunition, arms, provisions, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magazine', 'n.', 'The building or room in which the supply of powder is\n   kept in a fortification or a ship.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magazine', 'n.', 'A chamber in a gun for holding a number of cartridges to\n   be fed automatically to the piece.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magazine', 'n.', 'A pamphlet published periodically containing\n   miscellaneous papers or compositions.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magazined', 'imp. & p. p.', 'of Magazine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magazining', 'p. pr. & vb. n.', 'of Magazine');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magazine', 'v. t.', 'To store in, or as in, a magazine; to store up for\n   use.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magaziner', 'n.', 'One who edits or writes for a magazine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magazining', 'n.', 'The act of editing, or writing for, a magazine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magazinist', 'n.', 'One who edits or writes for a magazine.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magbote', 'n.', 'See Maegbote.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magdala', 'a.', 'Designating an orange-red dyestuff obtained from\n   naphthylamine, and called magdala red, naphthalene red, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magdalen', 'n.', 'A reformed prostitute.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magdaleon', 'n.', 'A medicine in the form of a roll, a esp. a roll of\n   plaster.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magdeburg', 'n.', 'A city of Saxony.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Mage', 'n.', 'A magician.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magellanic', 'a.', 'Of or pertaining to, or named from, Magellan, the\n   navigator.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magenta', 'n.', 'An aniline dye obtained as an amorphous substance having a\n   green bronze surface color, which dissolves to a shade of red; also,\n   the color; -- so called from Magenta, in Italy, in allusion to the\n   battle fought there about the time the dye was discovered. Called also\n   fuchsine, roseine, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magged', 'a.', 'Worn; fretted; as, a magged brace.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maggiore', 'a.', 'Greater, in respect to scales, intervals, etc., when used\n   in opposition to minor; major.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maggot', 'n.', 'The footless larva of any fly. See Larval.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maggot', 'n.', 'A whim; an odd fancy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maggotiness', 'n.', 'State of being maggoty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maggotish', 'a.', 'Full of whims or fancies; maggoty.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maggot-pie', 'n.', 'A magpie.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maggoty', 'a.', 'Infested with maggots.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maggoty', 'a.', 'Full of whims; capricious.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maghet', 'n.', 'A name for daisies and camomiles of several kinds.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magi', 'n. pl.', 'A caste of priests, philosophers, and magicians, among\n   the ancient Persians; hence, any holy men or sages of the East.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magian', 'a.', 'Of or pertaining to the Magi.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magian', 'n.', 'One of the Magi, or priests of the Zoroastrian religion in\n   Persia; an adherent of the Zoroastrian religion.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magic', 'a.', 'A comprehensive name for all of the pretended arts which\n   claim to produce effects by the assistance of supernatural beings, or\n   departed spirits, or by a mastery of secret forces in nature attained\n   by a study of occult science, including enchantment, conjuration,\n   witchcraft, sorcery, necromancy, incantation, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magic', 'a.', 'Alt. of Magical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magical', 'a.', 'Pertaining to the hidden wisdom supposed to be possessed\n   by the Magi; relating to the occult powers of nature, and the producing\n   of effects by their agency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magical', 'a.', 'Performed by, or proceeding from, occult and superhuman\n   agencies; done by, or seemingly done by, enchantment or sorcery. Hence:\n   Seemingly requiring more than human power; imposing or startling in\n   performance; producing effects which seem supernatural or very\n   extraordinary; having extraordinary properties; as, a magic lantern; a\n   magic square or circle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magically', 'adv.', 'In a magical manner; by magic, or as if by magic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magician', 'n.', 'One skilled in magic; one who practices the black art; an\n   enchanter; a necromancer; a sorcerer or sorceress; a conjurer.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magilp', 'n.', 'Alt. of Magilph');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magilph', 'n.', 'See Megilp.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magister', 'n.', 'Master; sir; -- a title of the Middle Ages, given to a\n   person in authority, or to one having a license from a university to\n   teach philosophy and the liberal arts.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magisterial', 'a.', 'Of or pertaining to a master or magistrate, or one in\n   authority; having the manner of a magister; official; commanding;\n   authoritative. Hence: Overbearing; dictatorial; dogmatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magisterial', 'a.', 'Pertaining to, produced by, or of the nature of,\n   magistery. See Magistery, 2.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magisteriality', 'n.', 'Magisterialness; authoritativeness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magisterially', 'adv.', 'In a magisterial manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magisterialness', 'n.', 'The quality or state of being magisterial.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistery', 'n.', 'Mastery; powerful medical influence; renowned efficacy;\n   a sovereign remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistery', 'n.', 'A magisterial injunction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistery', 'n.', 'A precipitate; a fine substance deposited by\n   precipitation; -- applied in old chemistry to certain white\n   precipitates from metallic solutions; as, magistery of bismuth.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistracies', 'pl. ', 'of Magistracy');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistracy', 'n.', 'The office or dignity of a magistrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistracy', 'n.', 'The collective body of magistrates.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistral', 'a.', 'Pertaining to a master; magisterial; authoritative;\n   dogmatic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistral', 'a.', 'Commanded or prescribed by a magister, esp. by a doctor;\n   hence, effectual; sovereign; as, a magistral sirup.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistral', 'a.', 'Formulated extemporaneously, or for a special case; --\n   opposed to officinal, and said of prescriptions and medicines.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistral', 'n.', 'A sovereign medicine or remedy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistral', 'n.', 'A magistral line.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistral', 'n.', 'Powdered copper pyrites used in the amalgamation of ores\n   of silver, as at the Spanish mines of Mexico and South America.');
  INSERT INTO words (word, word_type, description) VALUES  (    '-ties', 'pl. ', 'of Magistrality');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistrality', 'n.', 'Magisterialness; arbitrary dogmatism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistrally', 'adv.', 'In a magistral manner.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistrate', 'n.', 'A person clothed with power as a public civil officer;\n   a public civil officer invested with the executive government, or some\n   branch of it.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistratic', 'a.', 'Alt. of Magistratical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistratical', 'a.', 'Of, pertaining to, or proceeding from, a magistrate;\n   having the authority of a magistrate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magistrature', 'n.', 'Magistracy.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magma', 'n.', 'Any crude mixture of mineral or organic matters in the state\n   of a thin paste.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magma', 'n.', 'A thick residuum obtained from certain substances after the\n   fluid parts are expressed from them; the grounds which remain after\n   treating a substance with any menstruum, as water or alcohol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magma', 'n.', 'A salve or confection of thick consistency.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magma', 'n.', 'The molten matter within the earth, the source of the\n   material of lava flows, dikes of eruptive rocks, etc.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magma', 'n.', 'The glassy base of an eruptive rock.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magma', 'n.', 'The amorphous or homogenous matrix or ground mass, as\n   distinguished from well-defined crystals; as, the magma of porphyry.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magna Charta', '', 'The great Charter, so called, obtained by the English\n   barons from King John, A. D. 1215. This name is also given to the\n   charter granted to the people of England in the ninth year of Henry\n   III., and confirmed by Edward I.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magna Charta', '', 'Hence, a fundamental constitution which guaranties\n   rights and privileges.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnality', 'n.', 'A great act or event; a great attainment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnanimity', 'n.', 'The quality of being magnanimous; greatness of mind;\n   elevation or dignity of soul; that quality or combination of qualities,\n   in character, which enables one to encounter danger and trouble with\n   tranquility and firmness, to disdain injustice, meanness and revenge,\n   and to act and sacrifice for noble objects.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnanimous', 'a.', 'Great of mind; elevated in soul or in sentiment;\n   raised above what is low, mean, or ungenerous; of lofty and courageous\n   spirit; as, a magnanimous character; a magnanimous conqueror.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnanimous', 'a.', 'Dictated by or exhibiting nobleness of soul;\n   honorable; noble; not selfish.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnanimously', 'adv.', 'In a magnanimous manner; with greatness of mind.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnase black', '', 'A black pigment which dries rapidly when mixed with\n   oil, and is of intense body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnate', '', 'A person of rank; a noble or grandee; a person of influence\n   or distinction in any sphere.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnate', '', 'One of the nobility, or certain high officers of state\n   belonging to the noble estate in the national representation of\n   Hungary, and formerly of Poland.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnes', 'n.', 'Magnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnesia', 'n.', 'A light earthy white substance, consisting of magnesium\n   oxide, and obtained by heating magnesium hydrate or carbonate, or by\n   burning magnesium. It has a slightly alkaline reaction, and is used in\n   medicine as a mild antacid laxative. See Magnesium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnesian', 'a.', 'Pertaining to, characterized by, or containing, magnesia\n   or magnesium.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnesic', 'a.', 'Pertaining to, or containing, magnesium; as, magnesic\n   oxide.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnesite', 'n.', 'Native magnesium carbonate occurring in white compact or\n   granular masses, and also in rhombohedral crystals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnesium', 'n.', 'A light silver-white metallic element, malleable and\n   ductile, quite permanent in dry air but tarnishing in moist air. It\n   burns, forming (the oxide) magnesia, with the production of a blinding\n   light (the so-called magnesium light) which is used in signaling, in\n   pyrotechny, or in photography where a strong actinic illuminant is\n   required. Its compounds occur abundantly, as in dolomite, talc,\n   meerschaum, etc. Symbol Mg. Atomic weight, 24.4. Specific gravity,\n   1.75.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnet', 'n.', 'The loadstone; a species of iron ore (the ferrosoferric or\n   magnetic ore, Fe3O4) which has the property of attracting iron and some\n   of its ores, and, when freely suspended, of pointing to the poles; --\n   called also natural magnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnet', 'n.', 'A bar or mass of steel or iron to which the peculiar\n   properties of the loadstone have been imparted; -- called, in\n   distinction from the loadstone, an artificial magnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetic', 'a.', 'Alt. of Magnetical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetical', 'a.', 'Pertaining to the magnet; possessing the properties of\n   the magnet, or corresponding properties; as, a magnetic bar of iron; a\n   magnetic needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetical', 'a.', 'Of or pertaining to, or characterized by, the earths\n   magnetism; as, the magnetic north; the magnetic meridian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetical', 'a.', 'Capable of becoming a magnet; susceptible to magnetism;\n   as, the magnetic metals.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetical', 'a.', 'Endowed with extraordinary personal power to excite the\n   feelings and to win the affections; attractive; inducing attachment.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetical', 'a.', 'Having, susceptible to, or induced by, animal\n   magnetism, so called; as, a magnetic sleep. See Magnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetic', 'n.', 'A magnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetic', 'n.', 'Any metal, as iron, nickel, cobalt, etc., which may\n   receive, by any means, the properties of the loadstone, and which then,\n   when suspended, fixes itself in the direction of a magnetic meridian.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetically', 'adv.', 'By or as by, magnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magneticalness', 'n.', 'Quality of being magnetic.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetician', 'n.', 'One versed in the science of magnetism; a magnetist.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Maneticness', 'n.', 'Magneticalness.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetics', 'n.', 'The science of magnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetiferous', 'a.', 'Producing or conducting magnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetism', 'n.', 'The property, quality, or state, of being magnetic; the\n   manifestation of the force in nature which is seen in a magnet.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetism', 'n.', 'The science which treats of magnetic phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetism', 'n.', 'Power of attraction; power to excite the feelings and to\n   gain the affections.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetist', 'n.', 'One versed in magnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetite', 'n.', 'An oxide of iron (Fe3O4) occurring in isometric\n   crystals, also massive, of a black color and metallic luster. It is\n   readily attracted by a magnet and sometimes possesses polarity, being\n   then called loadstone. It is an important iron ore. Called also\n   magnetic iron.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetizable', 'a.', 'Capable of magnetized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetization', 'n.', 'The act of magnetizing, or the state of being\n   magnetized.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetized', 'imp. & p. p.', 'of Magnetize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetizing', 'prep. & adv.', 'of Magnetize');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetize', 'v. t.', 'To communicate magnetic properties to; as, to\n   magnetize a needle.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetize', 'v. t.', 'To attract as a magnet attracts, or like a magnet; to\n   move; to influence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetize', 'v. t.', 'To bring under the influence of animal magnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetizee', 'n.', 'A person subjected to the influence of animal\n   magnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetizer', 'n.', 'One who, or that which, imparts magnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magneto-', '', 'A prefix meaning pertaining to, produced by, or in some way\n   connected with, magnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magneto-electric', 'a.', 'Alt. of Magneto-electrical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magneto-electrical', 'a.', 'Pertaining to, or characterized by, electricity\n   by the action of magnets; as, magneto-electric induction.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magneto-electricity', 'n.', 'Electricity evolved by the action of magnets.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magneto-electricity', 'n.', 'That branch of science which treats of the\n   development of electricity by the action of magnets; -- the counterpart\n   of electro-magnetism.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetograph', 'n.', 'An automatic instrument for registering, by\n   photography or otherwise, the states and variations of any of the\n   terrestrial magnetic elements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetometer', 'n.', 'An instrument for measuring the intensity of magnetic\n   forces; also, less frequently, an instrument for determining any of the\n   terrestrial magnetic elements, as the dip and declination.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetometric', 'a.', 'Pertaining to, or employed in, the measurement of\n   magnetic forces; obtained by means of a magnetometer; as, magnetometric\n   instruments; magnetometric measurements.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetomotor', 'n.', 'A voltaic series of two or more large plates,\n   producing a great quantity of electricity of low tension, and hence\n   adapted to the exhibition of electro-magnetic phenomena.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnetotherapy', 'n.', 'The treatment of disease by the application of\n   magnets to the surface of the body.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnifiable', 'a.', 'Such as can be magnified, or extolled.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnific', 'a.', 'Alt. of Magnifical');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnifical',    'a.',    'Grand; splendid; illustrious; magnificent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnificat',    'n.',    'The song of the Virgin Mary, Luke i. 46; -- so called\n   because it commences with this word in the Vulgate.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnificate',    'v. t.',    'To magnify or extol.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnification',    'n.',    'The act of magnifying; enlargement; exaggeration.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnificence',    'n.',    'The act of doing what magnificent; the state or\n   quality of being magnificent.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnificent',    'a.',    'Doing grand things; admirable in action; displaying\n   great power or opulence, especially in building, way of living, and\n   munificence.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnificent',    'a.',    'Grand in appearance; exhibiting grandeur or splendor;\n   splendid pompous.');
  INSERT INTO words (word, word_type, description) VALUES  (    'Magnificently',    'adv.',    'In a Magnificent manner.');
